{
  "name": "ZenFinance AI",
  "description": "An intelligent personal finance assistant that helps you track daily income and expenses with AI-powered insights to improve your financial well-being.",
  "requestFramePermissions": []
}

import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

const rootElement = document.getElementById('root');
if (!rootElement) {
  throw new Error("Could not find root element to mount to");
}

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

export enum TransactionType {
  INCOME = 'INCOME',
  EXPENSE = 'EXPENSE'
}

export enum IncomeCategory {
  SALARY = 'Salary',
  FREELANCING = 'Freelancing',
  BUSINESS = 'Business',
  GIFT = 'Gift',
  OTHER = 'Other'
}

export enum ExpenseCategory {
  FOOD = 'Food',
  TRANSPORT = 'Transport',
  RENT = 'Rent',
  BILLS = 'Bills',
  SHOPPING = 'Shopping',
  ENTERTAINMENT = 'Entertainment',
  HEALTH = 'Health',
  OTHER = 'Other'
}

export interface Transaction {
  id: string;
  type: TransactionType;
  amount: number;
  category: IncomeCategory | ExpenseCategory;
  date: string; // ISO string
  note: string;
  source?: string; // Specific for income
}

export interface FinancialSummary {
  totalIncome: number;
  totalExpenses: number;
  savings: number;
  previousMonthSavings: number;
  categoryBreakdown: Record<string, number>;
}

export interface Insight {
  title: string;
  description: string;
  type: 'success' | 'warning' | 'info';
}

import React from 'react';

export const CATEGORY_COLORS: Record<string, string> = {
  Food: '#f87171',
  Transport: '#fb923c',
  Rent: '#fbbf24',
  Bills: '#a855f7',
  Shopping: '#ec4899',
  Entertainment: '#6366f1',
  Health: '#14b8a6',
  Other: '#94a3b8',
  Salary: '#22c55e',
  Freelancing: '#3b82f6',
  Business: '#06b6d4',
  Gift: '#f59e0b'
};

export const CATEGORY_ICONS: Record<string, React.ReactNode> = {
  Food: <i className="fas fa-utensils"></i>,
  Transport: <i className="fas fa-car"></i>,
  Rent: <i className="fas fa-home"></i>,
  Bills: <i className="fas fa-file-invoice-dollar"></i>,
  Shopping: <i className="fas fa-shopping-bag"></i>,
  Entertainment: <i className="fas fa-gamepad"></i>,
  Health: <i className="fas fa-heartbeat"></i>,
  Other: <i className="fas fa-ellipsis-h"></i>,
  Salary: <i className="fas fa-wallet"></i>,
  Freelancing: <i className="fas fa-laptop-code"></i>,
  Business: <i className="fas fa-briefcase"></i>,
  Gift: <i className="fas fa-gift"></i>
};

export const formatCurrency = (amount: number) => {
  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
  }).format(amount);
};

import { GoogleGenAI, Type } from "@google/genai";
import { Transaction, FinancialSummary, Insight } from "../types";

const ai = new GoogleGenAI({ apiKey: process.env.API_KEY || '' });

export const getFinancialInsights = async (
  transactions: Transaction[],
  summary: FinancialSummary
): Promise<Insight[]> => {
  if (!process.env.API_KEY) {
    return [
      {
        title: "API Key Missing",
        description: "Connect your Gemini API key to get personalized insights.",
        type: "info"
      }
    ];
  }

  // Sample the last 20 transactions for context
  const recentTransactions = transactions.slice(-20).map(t => ({
    type: t.type,
    amount: t.amount,
    category: t.category,
    date: t.date.split('T')[0]
  }));

  const prompt = `
    Analyze this financial data and provide 3 short, friendly, and practical insights.

    Current Month Summary:
    - Total Income: $${summary.totalIncome}
    - Total Expenses: $${summary.totalExpenses}
    - Current Savings: $${summary.savings}
    - Previous Month Savings: $${summary.previousMonthSavings}

    Recent Transactions:
    ${JSON.stringify(recentTransactions)}

    Focus on patterns, improvements, or areas to reduce waste. Keep each insight realistic and friendly. Avoid legal financial advice.
  `;

  try {
    const response = await ai.models.generateContent({
      model: "gemini-3-flash-preview",
      contents: prompt,
      config: {
        responseMimeType: "application/json",
        responseSchema: {
          type: Type.ARRAY,
          items: {
            type: Type.OBJECT,
            properties: {
              title: { type: Type.STRING },
              description: { type: Type.STRING },
              type: { 
                type: Type.STRING,
                description: "Either 'success', 'warning', or 'info'"
              },
            },
            required: ["title", "description", "type"],
          },
        },
      },
    });

    const text = response.text;
    if (!text) return [];
    return JSON.parse(text);
  } catch (error) {
    console.error("Error fetching insights:", error);
    return [
      {
        title: "Analysis Ready",
        description: "Your spending seems stable. Consider setting a small savings goal this week.",
        type: "info"
      }
    ];
  }
};

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, FinancialSummary, IncomeCategory, ExpenseCategory } from './types';
import Dashboard from './components/Dashboard';
import TransactionList from './components/TransactionList';
import AddTransactionModal from './components/AddTransactionModal';
import Navbar from './components/Navbar';
import InsightsPanel from './components/InsightsPanel';

const App: React.FC = () => {
  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('zen_transactions');
    return saved ? JSON.parse(saved) : [];
  });

  const [activeTab, setActiveTab] = useState<'dashboard' | 'income' | 'expense' | 'insights'>('dashboard');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalType, setModalType] = useState<TransactionType>(TransactionType.EXPENSE);

  useEffect(() => {
    localStorage.setItem('zen_transactions', JSON.stringify(transactions));
  }, [transactions]);

  const summary: FinancialSummary = useMemo(() => {
    const now = new Date();
    const currentMonth = now.getMonth();
    const currentYear = now.getFullYear();
    const lastMonth = currentMonth === 0 ? 11 : currentMonth - 1;
    const lastMonthYear = currentMonth === 0 ? currentYear - 1 : currentYear;

    const currentMonthTransactions = transactions.filter(t => {
      const d = new Date(t.date);
      return d.getMonth() === currentMonth && d.getFullYear() === currentYear;
    });

    const lastMonthTransactions = transactions.filter(t => {
      const d = new Date(t.date);
      return d.getMonth() === lastMonth && d.getFullYear() === lastMonthYear;
    });

    const income = currentMonthTransactions
      .filter(t => t.type === TransactionType.INCOME)
      .reduce((sum, t) => sum + t.amount, 0);

    const expenses = currentMonthTransactions
      .filter(t => t.type === TransactionType.EXPENSE)
      .reduce((sum, t) => sum + t.amount, 0);

    const prevIncome = lastMonthTransactions
      .filter(t => t.type === TransactionType.INCOME)
      .reduce((sum, t) => sum + t.amount, 0);

    const prevExpenses = lastMonthTransactions
      .filter(t => t.type === TransactionType.EXPENSE)
      .reduce((sum, t) => sum + t.amount, 0);

    const categoryBreakdown: Record<string, number> = {};
    currentMonthTransactions.forEach(t => {
      categoryBreakdown[t.category] = (categoryBreakdown[t.category] || 0) + t.amount;
    });

    return {
      totalIncome: income,
      totalExpenses: expenses,
      savings: income - expenses,
      previousMonthSavings: prevIncome - prevExpenses,
      categoryBreakdown
    };
  }, [transactions]);

  const handleAddTransaction = (t: Omit<Transaction, 'id'>) => {
    const newTransaction = { ...t, id: Date.now().toString() };
    setTransactions(prev => [newTransaction, ...prev]);
    setIsModalOpen(false);
  };

  const handleDeleteTransaction = (id: string) => {
    setTransactions(prev => prev.filter(t => t.id !== id));
  };

  const openAddModal = (type: TransactionType) => {
    setModalType(type);
    setIsModalOpen(true);
  };

  return (
    <div className="min-h-screen pb-20 md:pb-0 md:pl-64 bg-slate-50">
      <Navbar activeTab={activeTab} setActiveTab={setActiveTab} />

      <main className="max-w-5xl mx-auto p-4 md:p-8">
        {activeTab === 'dashboard' && (
          <Dashboard 
            summary={summary} 
            transactions={transactions.slice(0, 5)} 
            onAddExpense={() => openAddModal(TransactionType.EXPENSE)}
            onAddIncome={() => openAddModal(TransactionType.INCOME)}
          />
        )}

        {activeTab === 'income' && (
          <div className="space-y-6">
            <header className="flex justify-between items-center">
              <h1 className="text-2xl font-bold text-slate-800">Income Log</h1>
              <button 
                onClick={() => openAddModal(TransactionType.INCOME)}
                className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg flex items-center gap-2 transition-all"
              >
                <i className="fas fa-plus"></i> Add Income
              </button>
            </header>
            <TransactionList 
              transactions={transactions.filter(t => t.type === TransactionType.INCOME)}
              onDelete={handleDeleteTransaction}
            />
          </div>
        )}

        {activeTab === 'expense' && (
          <div className="space-y-6">
            <header className="flex justify-between items-center">
              <h1 className="text-2xl font-bold text-slate-800">Expense Log</h1>
              <button 
                onClick={() => openAddModal(TransactionType.EXPENSE)}
                className="bg-rose-600 hover:bg-rose-700 text-white px-4 py-2 rounded-lg flex items-center gap-2 transition-all"
              >
                <i className="fas fa-plus"></i> Add Expense
              </button>
            </header>
            <TransactionList 
              transactions={transactions.filter(t => t.type === TransactionType.EXPENSE)}
              onDelete={handleDeleteTransaction}
            />
          </div>
        )}

        {activeTab === 'insights' && (
          <InsightsPanel transactions={transactions} summary={summary} />
        )}
      </main>

      {/* Quick Add Button Mobile */}
      <button 
        onClick={() => openAddModal(TransactionType.EXPENSE)}
        className="fixed bottom-24 right-6 w-14 h-14 bg-blue-600 text-white rounded-full shadow-lg md:hidden flex items-center justify-center text-xl z-10"
      >
        <i className="fas fa-plus"></i>
      </button>

      {isModalOpen && (
        <AddTransactionModal 
          type={modalType} 
          onClose={() => setIsModalOpen(false)} 
          onAdd={handleAddTransaction} 
        />
      )}
    </div>
  );
};

export default App;

import React from 'react';

