export default TransactionList;

import React, { useState } from 'react';
import { Transaction, TransactionType, IncomeCategory, ExpenseCategory } from '../types';

interface AddTransactionModalProps {
  type: TransactionType;
  onClose: () => void;
  onAdd: (t: Omit<Transaction, 'id'>) => void;
}

const AddTransactionModal: React.FC<AddTransactionModalProps> = ({ type, onClose, onAdd }) => {
  const [amount, setAmount] = useState('');
  const [category, setCategory] = useState<IncomeCategory | ExpenseCategory>(
    type === TransactionType.INCOME ? IncomeCategory.SALARY : ExpenseCategory.FOOD
  );
  const [note, setNote] = useState('');
  const [date, setDate] = useState(new Date().toISOString().split('T')[0]);

  const categories = type === TransactionType.INCOME 
    ? Object.values(IncomeCategory) 
    : Object.values(ExpenseCategory);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!amount || parseFloat(amount) <= 0) return;

    onAdd({
      type,
      amount: parseFloat(amount),
      category,
      note,
      date: new Date(date).toISOString(),
    });
  };

  return (
    <div className="fixed inset-0 bg-slate-900/40 backdrop-blur-sm flex items-center justify-center p-4 z-[100] animate-fadeIn">
      <div className="bg-white w-full max-w-md rounded-3xl shadow-2xl overflow-hidden animate-slideUp">
        <div className={`p-6 ${type === TransactionType.INCOME ? 'bg-green-600' : 'bg-rose-600'} text-white`}>
          <div className="flex justify-between items-center">
            <h2 className="text-xl font-bold">Add New {type === TransactionType.INCOME ? 'Income' : 'Expense'}</h2>
            <button onClick={onClose} className="hover:opacity-70 transition-opacity">
              <i className="fas fa-times text-xl"></i>
            </button>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="p-6 space-y-5">
          <div>
            <label className="block text-sm font-bold text-slate-700 mb-1.5">Amount</label>
            <div className="relative">
              <span className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 font-bold">$</span>
              <input 
                type="number" 
                step="0.01"
                required
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
                placeholder="0.00"
                className="w-full pl-8 pr-4 py-3 bg-slate-50 border border-slate-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all font-bold text-lg"
              />
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-bold text-slate-700 mb-1.5">Category</label>
              <select 
                value={category}
                onChange={(e) => setCategory(e.target.value as any)}
                className="w-full px-4 py-3 bg-slate-50 border border-slate-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all text-sm font-medium"
              >
                {categories.map(c => <option key={c} value={c}>{c}</option>)}
              </select>
            </div>
            <div>
              <label className="block text-sm font-bold text-slate-700 mb-1.5">Date</label>
              <input 
                type="date"
                required
                value={date}
                onChange={(e) => setDate(e.target.value)}
                className="w-full px-4 py-3 bg-slate-50 border border-slate-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all text-sm font-medium"
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-bold text-slate-700 mb-1.5">Note (Optional)</label>
            <textarea 
              value={note}
              onChange={(e) => setNote(e.target.value)}
              placeholder="What was this for?"
              rows={2}
              className="w-full px-4 py-3 bg-slate-50 border border-slate-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all text-sm resize-none"
            />
          </div>

          <button 
            type="submit"
            className={`w-full py-4 rounded-2xl text-white font-bold text-lg shadow-lg shadow-blue-200 transition-all active:scale-95 ${
              type === TransactionType.INCOME ? 'bg-green-600 hover:bg-green-700' : 'bg-rose-600 hover:bg-rose-700'
            }`}
          >
            Confirm Transaction
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddTransactionModal;

import React, { useState, useEffect } from 'react';
import { Transaction, FinancialSummary, Insight } from '../types';
import { getFinancialInsights } from '../services/geminiService';

interface InsightsPanelProps {
  transactions: Transaction[];
  summary: FinancialSummary;
}

const InsightsPanel: React.FC<InsightsPanelProps> = ({ transactions, summary }) => {
  const [insights, setInsights] = useState<Insight[]>([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchInsights = async () => {
      if (transactions.length === 0) return;
      setLoading(true);
      try {
        const results = await getFinancialInsights(transactions, summary);
        setInsights(results);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchInsights();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div className="space-y-8 animate-fadeIn">
      <header>
        <h1 className="text-2xl font-bold text-slate-800">AI Financial Coach</h1>
        <p className="text-slate-500">Intelligent observations based on your spending habits.</p>
      </header>

      {loading ? (
        <div className="flex flex-col items-center justify-center py-20 gap-4">
          <div className="w-12 h-12 border-4 border-blue-100 border-t-blue-600 rounded-full animate-spin"></div>
          <p className="text-slate-500 font-medium animate-pulse">Analyzing your patterns...</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {insights.length > 0 ? (
            insights.map((insight, idx) => (
              <div 
                key={idx} 
                className={`p-6 rounded-3xl border shadow-sm transition-all hover:shadow-md flex gap-4 ${
                  insight.type === 'success' ? 'bg-green-50/50 border-green-100' :
                  insight.type === 'warning' ? 'bg-amber-50/50 border-amber-100' :
                  'bg-blue-50/50 border-blue-100'
                }`}
              >
                <div className={`w-12 h-12 rounded-2xl flex items-center justify-center flex-shrink-0 text-xl ${
                  insight.type === 'success' ? 'bg-green-500 text-white' :
                  insight.type === 'warning' ? 'bg-amber-500 text-white' :
                  'bg-blue-500 text-white'
                }`}>
                  <i className={`fas ${
                    insight.type === 'success' ? 'fa-check-circle' :
                    insight.type === 'warning' ? 'fa-exclamation-triangle' :
                    'fa-info-circle'
                  }`}></i>
                </div>
                <div>
                  <h3 className="text-lg font-bold text-slate-800 mb-1">{insight.title}</h3>
                  <p className="text-slate-600 leading-relaxed text-sm">{insight.description}</p>
                </div>
              </div>
            ))
          ) : (
            <div className="col-span-full py-20 text-center bg-white rounded-3xl border border-dashed border-slate-200">
              <i className="fas fa-brain text-4xl text-slate-200 mb-4"></i>
              <p className="text-slate-400 font-medium">Add some transactions to see AI insights!</p>
            </div>
          )}

          {/* Tips Section */}
