import { GoogleGenAI, Type } from "@google/genai";
import { Transaction, FinancialSummary, Insight } from "../types";

export const getFinancialInsights = async (
  transactions: Transaction[],
  summary: FinancialSummary
): Promise<Insight[]> => {
  // Always create a new GoogleGenAI instance right before making an API call to ensure latest API key usage
  const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

  // Sample the last 20 transactions for context
  const recentTransactions = transactions.slice(-20).map(t => ({
    type: t.type,
    amount: t.amount,
    category: t.category,
    date: t.date.split('T')[0]
  }));

  const prompt = `
    Analyze this financial data and provide 3 short, friendly, and practical insights.

    Current Month Summary:
    - Total Income: $${summary.totalIncome}
    - Total Expenses: $${summary.totalExpenses}
    - Current Savings: $${summary.savings}
    - Previous Month Savings: $${summary.previousMonthSavings}

    Recent Transactions:
    ${JSON.stringify(recentTransactions)}

    Focus on patterns, improvements, or areas to reduce waste. Keep each insight realistic and friendly. Avoid legal financial advice.
  `;

  try {
    const response = await ai.models.generateContent({
      model: "gemini-3-flash-preview",
      contents: prompt,
      config: {
        responseMimeType: "application/json",
        responseSchema: {
          type: Type.ARRAY,
          items: {
            type: Type.OBJECT,
            properties: {
              title: { 
                type: Type.STRING,
                description: 'Brief title for the financial observation.'
              },
              description: { 
                type: Type.STRING,
                description: 'Friendly and practical explanation or tip.'
              },
              type: { 
                type: Type.STRING,
                description: "Category of the insight: 'success', 'warning', or 'info'"
              },
            },
            required: ["title", "description", "type"],
            propertyOrdering: ["title", "description", "type"],
          },
        },
      },
    });

    // Access the text property directly from the response
    const text = response.text;
    if (!text) return [];

    try {
      return JSON.parse(text);
    } catch (parseError) {
      console.error("Failed to parse Gemini response:", text);
      throw parseError;
    }
  } catch (error) {
    console.error("Error fetching financial insights:", error);
    return [
      {
        title: "Ready to Analyze",
        description: "Add more transactions to get personalized insights from your AI coach.",
        type: "info"
      }
    ];
  }
};

