                <td className="px-6 py-4">
                  <span className="text-sm text-slate-500 italic">{t.note || '-'}</span>
                </td>
                <td className="px-6 py-4">
                  <span className="text-sm text-slate-600">{new Date(t.date).toLocaleDateString('bn-BD')}</span>
                </td>
                <td className="px-6 py-4 text-right">
                  <span className={`text-sm font-bold ${t.type === 'INCOME' ? 'text-green-600' : 'text-slate-800'}`}>
                    {t.type === 'INCOME' ? '+' : '-'}{formatCurrency(t.amount)}
                  </span>
                </td>
                <td className="px-6 py-4 text-right">
                  <button 
                    onClick={() => onDelete(t.id)}
                    className="text-slate-300 hover:text-rose-500 p-2 transition-colors"
                  >
                    <i className="fas fa-trash-alt"></i>
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <div className="py-20 flex flex-col items-center justify-center text-slate-400 gap-3">
          <div className="w-16 h-16 bg-slate-50 rounded-full flex items-center justify-center text-3xl">
