import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, FinancialSummary, Insight } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingInsights, setLoadingInsights] = useState(false);

  // Persistence
  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
  }, [transactions]);

  // Summary Calculations
  const summary = useMemo(() => {
    const income = transactions
      .filter(t => t.type === TransactionType.INCOME)
      .reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions
      .filter(t => t.type === TransactionType.EXPENSE)
      .reduce((sum, t) => sum + t.amount, 0);
    return {
      totalIncome: income,
      totalExpenses: expense,
      balance: income - expense
    };
  }, [transactions]);

  // Fetch AI Insights
  const fetchInsights = async () => {
    if (transactions.length < 3) return;
    setLoadingInsights(true);
    try {
      const res = await getFinancialInsights(transactions, {
        ...summary,
        savings: summary.balance,
        previousMonthSavings: 0,
        categoryBreakdown: {},
        monthlyHistory: []
      });
      setInsights(res);
    } catch (e) {
      console.error(e);
    } finally {
      setLoadingInsights(false);
    }
  };

  useEffect(() => {
    if (transactions.length >= 3) fetchInsights();
  }, [transactions.length]);

  const addTransaction = (e: any) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const newTx: Transaction = {
      id: Date.now().toString(),
      type: modalType!,
      amount: parseFloat(formData.get('amount') as string),
      category: formData.get('category') as string,
      note: formData.get('note') as string,
      date: new Date().toISOString()
    };
    setTransactions([newTx, ...transactions]);
    setModalType(null);
  };

  const deleteTx = (id: string) => {
    if (window.confirm('আপনি কি এই লেনদেনটি মুছতে চান?')) {
      setTransactions(transactions.filter(t => t.id !== id));
    }
  };

  const clearAll = () => {
    if (window.confirm('সব ডেটা মুছে ফেলতে চান? এটি আর ফিরিয়ে আনা যাবে না।')) {
      setTransactions([]);
      setInsights([]);
    }
  };

  return (
    <div className="max-w-md mx-auto min-h-screen bg-white shadow-xl flex flex-col font-sans">
      {/* Header & Balance */}
      <header className="bg-blue-600 p-8 text-white rounded-b-[40px] shadow-lg relative overflow-hidden">
        <div className="relative z-10">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-xl font-bold opacity-90">স্পার্ক ফিন্যান্স</h1>
            <button onClick={clearAll} className="opacity-60 hover:opacity-100"><i className="fas fa-trash-alt"></i></button>
          </div>
          <p className="text-blue-100 text-sm mb-1">মোট ব্যালেন্স</p>
          <h2 className="text-4xl font-extrabold tracking-tight">{formatCurrency(summary.balance)}</h2>

          <div className="flex gap-4 mt-8">
            <div className="flex-1 bg-white/10 backdrop-blur-md p-3 rounded-2xl">
              <p className="text-[10px] uppercase font-bold text-blue-100 mb-1">মোট আয়</p>
              <p className="text-lg font-bold">৳{summary.totalIncome}</p>
            </div>
            <div className="flex-1 bg-white/10 backdrop-blur-md p-3 rounded-2xl">
              <p className="text-[10px] uppercase font-bold text-blue-100 mb-1">মোট ব্যয়</p>
              <p className="text-lg font-bold">৳{summary.totalExpenses}</p>
            </div>
          </div>
        </div>
        <div className="absolute -top-10 -right-10 w-40 h-40 bg-white/10 rounded-full blur-3xl"></div>
      </header>

      {/* Main Content */}
      <main className="flex-1 px-6 pt-8 pb-24 overflow-y-auto custom-scrollbar">
        {/* Action Buttons */}
        <div className="grid grid-cols-2 gap-4 mb-8">
          <button 
            onClick={() => setModalType(TransactionType.INCOME)}
            className="flex flex-col items-center justify-center p-4 bg-green-50 border border-green-100 text-green-700 rounded-3xl hover:bg-green-100 transition-all group"
          >
            <div className="w-12 h-12 bg-green-500 text-white rounded-full flex items-center justify-center text-xl mb-2 group-active:scale-90 transition-transform shadow-lg shadow-green-200">
              <i className="fas fa-plus"></i>
            </div>
            <span className="font-bold text-sm">আয় যোগ করুন</span>
          </button>
          <button 
            onClick={() => setModalType(TransactionType.EXPENSE)}
            className="flex flex-col items-center justify-center p-4 bg-rose-50 border border-rose-100 text-rose-700 rounded-3xl hover:bg-rose-100 transition-all group"
          >
            <div className="w-12 h-12 bg-rose-500 text-white rounded-full flex items-center justify-center text-xl mb-2 group-active:scale-90 transition-transform shadow-lg shadow-rose-200">
              <i className="fas fa-minus"></i>
            </div>
            <span className="font-bold text-sm">ব্যয় যোগ করুন</span>
          </button>
        </div>

        {/* AI Insight Section */}
        {loadingInsights ? (
          <div className="mb-8 p-4 bg-blue-50 border border-blue-100 rounded-3xl animate-pulse flex items-center gap-3">
            <div className="w-10 h-10 bg-blue-200 rounded-full"></div>
            <div className="h-4 bg-blue-200 rounded-full w-2/3"></div>
          </div>
        ) : insights.length > 0 && (
          <div className="mb-8 p-5 bg-gradient-to-br from-blue-600 to-indigo-700 text-white rounded-3xl shadow-xl shadow-blue-100 relative overflow-hidden group">
            <div className="relative z-10">
              <div className="flex items-center gap-2 mb-2">
                <i className="fas fa-lightbulb text-yellow-300"></i>
                <h3 className="text-xs font-black uppercase tracking-widest opacity-80">এআই পরামর্শ</h3>
              </div>
              <p className="text-sm font-medium leading-relaxed">{insights[0].description}</p>
            </div>
            <i className="fas fa-robot absolute -bottom-4 -right-4 text-8xl opacity-10 group-hover:rotate-12 transition-transform"></i>
          </div>
        )}

        {/* Recent Transactions */}
        <div className="space-y-4">
          <div className="flex justify-between items-center">
            <h3 className="font-bold text-slate-800">সাম্প্রতিক লেনদেন</h3>
            <span className="text-xs font-bold text-blue-600 bg-blue-50 px-2 py-1 rounded-full">{transactions.length} টি</span>
          </div>

          {transactions.length > 0 ? (
            <div className="space-y-3">
              {transactions.map(t => (
                <div key={t.id} className="flex items-center justify-between p-4 bg-slate-50 border border-slate-100 rounded-2xl group hover:bg-white hover:shadow-md transition-all">
                  <div className="flex items-center gap-4">
                    <div className={`w-10 h-10 rounded-xl flex items-center justify-center text-lg ${t.type === TransactionType.INCOME ? 'bg-green-100 text-green-600' : 'bg-rose-100 text-rose-600'}`}>
                      <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                    </div>
                    <div>
                      <p className="font-bold text-slate-800 text-sm">{t.category}</p>
                      <p className="text-[10px] text-slate-400 font-medium">{new Date(t.date).toLocaleDateString('bn-BD')} • {t.note || 'নোট নেই'}</p>
                    </div>
                  </div>
                  <div className="flex items-center gap-3">
                    <p className={`font-black text-sm ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-700'}`}>
                      {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                    </p>
                    <button onClick={() => deleteTx(t.id)} className="text-slate-300 hover:text-rose-500 opacity-0 group-hover:opacity-100 transition-opacity"><i className="fas fa-times"></i></button>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-10">
              <div className="w-16 h-16 bg-slate-50 rounded-full flex items-center justify-center mx-auto mb-3">
                <i className="fas fa-receipt text-slate-200 text-2xl"></i>
              </div>
              <p className="text-slate-400 text-sm font-medium">কোনো লেনদেন নেই</p>
            </div>
          )}
        </div>
      </main>

      {/* Simplified Modal */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm flex items-end sm:items-center justify-center p-0 sm:p-4 z-[100] animate-fadeIn">
          <div className="bg-white w-full max-w-md rounded-t-[32px] sm:rounded-[32px] overflow-hidden animate-slideUp">
            <div className={`p-6 ${modalType === TransactionType.INCOME ? 'bg-green-600' : 'bg-rose-600'} text-white flex justify-between items-center`}>
              <h2 className="text-lg font-bold">{modalType === TransactionType.INCOME ? 'আয় যোগ করুন' : 'ব্যয় যোগ করুন'}</h2>
              <button onClick={() => setModalType(null)} className="w-8 h-8 flex items-center justify-center hover:bg-black/10 rounded-full"><i className="fas fa-times"></i></button>
            </div>
            <form onSubmit={addTransaction} className="p-6 space-y-5">
              <div>
                <label className="block text-xs font-black text-slate-400 uppercase mb-2 tracking-widest">পরিমাণ</label>
                <div className="relative">
                  <span className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 font-bold">৳</span>
                  <input type="number" name="amount" required placeholder="0.00" autoFocus className="w-full pl-9 pr-4 py-4 bg-slate-50 border-2 border-slate-100 rounded-2xl focus:border-blue-500 outline-none font-black text-xl text-slate-800" />
                </div>
              </div>
              <div>
                <label className="block text-xs font-black text-slate-400 uppercase mb-2 tracking-widest">বিভাগ</label>
                <select name="category" className="w-full px-4 py-4 bg-slate-50 border-2 border-slate-100 rounded-2xl outline-none font-bold text-slate-700">
                  {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => (
                    <option key={c} value={c}>{c}</option>
                  ))}
                </select>
              </div>
              <div>
                <label className="block text-xs font-black text-slate-400 uppercase mb-2 tracking-widest">নোট</label>
                <input type="text" name="note" placeholder="কি জন্য?" className="w-full px-4 py-4 bg-slate-50 border-2 border-slate-100 rounded-2xl outline-none font-medium text-slate-700" />
              </div>
              <button type="submit" className={`w-full py-4 rounded-2xl text-white font-black text-lg shadow-xl active:scale-95 transition-transform ${modalType === TransactionType.INCOME ? 'bg-green-600 shadow-green-100' : 'bg-rose-600 shadow-rose-100'}`}>
                সংরক্ষণ করুন
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

