interface SettingsProps {
  settings: UserSettings;
  onUpdateSettings: (settings: UserSettings) => void;
}

const Settings: React.FC<SettingsProps> = ({ settings, onUpdateSettings }) => {
  // Fix: Explicitly cast enum values to string arrays to resolve 'unknown' index type errors
  const expenseCategories = Object.values(ExpenseCategory) as string[];
  const incomeCategories = Object.values(IncomeCategory) as string[];
  const allCategories = [...incomeCategories, ...expenseCategories];

  const handleColorChange = (cat: string, color: string) => {
    onUpdateSettings({
      ...settings,
      categoryColors: {
        ...settings.categoryColors,
        [cat]: color
      }
    });
  };

  const handleBudgetChange = (cat: string, amount: string) => {
    const val = parseFloat(amount) || 0;
    onUpdateSettings({
      ...settings,
      budgets: {
        ...settings.budgets,
        [cat]: val
      }
    });
  };

  const resetColors = () => {
    onUpdateSettings({
      ...settings,
      categoryColors: DEFAULT_CATEGORY_COLORS
    });
  };

  return (
    <div className="space-y-8 animate-fadeIn">
      <header>
        <h1 className="text-2xl font-bold text-slate-800">অ্যাপ সেটিংস</h1>
        <p className="text-slate-500">আপনার পছন্দমতো অ্যাপটি কাস্টমাইজ করুন।</p>
      </header>

      {/* Budget Settings Section */}
      <section className="bg-white p-8 rounded-3xl border border-slate-100 shadow-sm">
        <h3 className="text-lg font-bold text-slate-800 mb-6">মাসিক বাজেট সেট করুন</h3>
        <p className="text-sm text-slate-500 mb-6">আপনার ব্যয়ের লক্ষ্যমাত্রা ঠিক করতে প্রতিটি ক্যাটাগরির জন্য বাজেট নির্ধারণ করুন।</p>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {expenseCategories.map(cat => (
            <div key={cat} className="space-y-2">
              <label className="flex items-center gap-2 text-sm font-bold text-slate-700">
                {/* Use correct FontAwesome class application */}
                <i className={`fas ${CATEGORY_ICONS[cat] || 'fa-tag'}`}></i>
                {cat}
              </label>
              <div className="relative">
                <span className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400 font-bold text-xs">৳</span>
                <input 
                  type="number"
                  placeholder="বাজেট নেই"
                  value={settings.budgets[cat] || ''}
                  onChange={(e) => handleBudgetChange(cat, e.target.value)}
                  className="w-full pl-7 pr-3 py-2 bg-slate-50 border border-slate-200 rounded-xl text-sm font-bold text-slate-800 focus:ring-2 focus:ring-blue-500 outline-none"
                />
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Category Color Customization */}
      <section className="bg-white p-8 rounded-3xl border border-slate-100 shadow-sm">
        <div className="flex justify-between items-center mb-6">
          <h3 className="text-lg font-bold text-slate-800">ক্যাটাগরি কালার কাস্টমাইজেশন</h3>
          <button 
            onClick={resetColors}
            className="text-sm font-bold text-blue-600 hover:underline"
          >
            রিসেট করুন
          </button>
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {allCategories.map(cat => (
            <div key={cat} className="flex items-center justify-between p-4 bg-slate-50 rounded-2xl border border-slate-100">
              <div className="flex items-center gap-3">
                <div 
                  className="w-4 h-4 rounded-full shadow-inner" 
                  style={{ backgroundColor: settings.categoryColors[cat] || DEFAULT_CATEGORY_COLORS[cat] }}
                ></div>
                <span className="text-sm font-bold text-slate-700">{cat}</span>
              </div>
              <input 
                type="color" 
                value={settings.categoryColors[cat] || DEFAULT_CATEGORY_COLORS[cat]}
                onChange={(e) => handleColorChange(cat, e.target.value)}
                className="w-8 h-8 rounded-lg cursor-pointer border-none bg-transparent"
              />
            </div>
          ))}
        </div>
      </section>

      <section className="bg-slate-900 p-8 rounded-3xl text-white relative overflow-hidden">
        <div className="relative z-10">
          <h3 className="text-xl font-bold mb-2">ডেটা ব্যাকআপ</h3>
          <p className="text-slate-400 text-sm mb-6">আপনার সব লেনদেন এবং সেটিংস ব্রাউজারের লোকাল স্টোরেজে সংরক্ষিত থাকে।</p>
          <div className="flex gap-4">
            <button className="px-6 py-3 bg-white/10 hover:bg-white/20 rounded-xl text-sm font-bold transition-all border border-white/10">
              <i className="fas fa-download mr-2"></i> এক্সপোর্ট (JSON)
            </button>
            <button className="px-6 py-3 bg-rose-500/10 hover:bg-rose-500/20 text-rose-400 rounded-xl text-sm font-bold transition-all border border-rose-500/20">
              <i className="fas fa-trash-alt mr-2"></i> সব ডেটা মুছুন
            </button>
          </div>
        </div>
        <div className="absolute top-0 right-0 w-32 h-32 bg-blue-500/10 rounded-full blur-2xl"></div>
      </section>
    </div>
  );
};

export default Settings;

export enum TransactionType {
  INCOME = 'INCOME',
  EXPENSE = 'EXPENSE'
}

export interface Transaction {
  id: string;
  type: TransactionType;
  amount: number;
  category: string;
  date: string;
  note: string;
}

export interface MonthlyArchive {
  id: string;
  monthName: string;
  year: number;
  totalIncome: number;
  totalExpense: number;
  transactions: Transaction[];
}

export interface FinancialSummary {
  totalIncome: number;
  totalExpenses: number;
  balance: number;
  savings: number;
  previousMonthSavings: number;
  categoryBreakdown: Record<string, number>;
  monthlyHistory: { month: string; income: number; expense: number }[];
}

export interface Insight {
  title: string;
  description: string;
  type: 'success' | 'warning' | 'info';
}

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  // Current Month Transactions
  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  // Past Monthly Archives
  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingInsights, setLoadingInsights] = useState(false);

  // Persistence
  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
  }, [transactions]);

  useEffect(() => {
    localStorage.setItem('spark_archives', JSON.stringify(archives));
  }, [archives]);

  // Current Summary
  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    return { income, expense, balance: income - expense };
  }, [transactions]);

  // AI Insights
  useEffect(() => {
    const fetchInsights = async () => {
      if (transactions.length < 3) return;
      setLoadingInsights(true);
      try {
        const res = await getFinancialInsights(transactions, {
          totalIncome: summary.income,
          totalExpenses: summary.expense,
          balance: summary.balance,
          savings: summary.balance,
          previousMonthSavings: 0,
          categoryBreakdown: {},
          monthlyHistory: []
        });
        setInsights(res);
      } catch (e) { console.error(e); }
      finally { setLoadingInsights(false); }
    };
    fetchInsights();
  }, [transactions.length]);

  const addTransaction = (e: any) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount) return;

    const newTx: Transaction = {
      id: Date.now().toString(),
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: formData.get('note') as string,
      date: new Date().toISOString()
    };
    setTransactions([newTx, ...transactions]);
    setModalType(null);
  };

  const deleteTx = (id: string) => {
    if (window.confirm('মুছে ফেলতে চান?')) {
      setTransactions(transactions.filter(t => t.id !== id));
    }
  };

  // Archive Current Month and Clear Dashboard
  const startNewMonth = () => {
    if (transactions.length === 0) {
      alert("আর্কাইভ করার মতো কোনো লেনদেন নেই।");
      return;
    }
    if (window.confirm('চলতি মাসের হিসাব কি ইতিহাসে জমা করে ড্যাশবোর্ড পরিষ্কার করতে চান?')) {
      const now = new Date();
      const monthName = now.toLocaleString('bn-BD', { month: 'long' });
      const newArchive: MonthlyArchive = {
        id: Date.now().toString(),
        monthName,
        year: now.getFullYear(),
        totalIncome: summary.income,
        totalExpense: summary.expense,
        transactions: [...transactions]
      };
      setArchives([newArchive, ...archives]);
      setTransactions([]);
      setInsights([]);
      alert("হিসাব ইতিহাসে জমা করা হয়েছে।");
    }
  };

  const clearEverything = () => {
    if (window.confirm('সতর্কতা: আপনার বর্তমান এবং ইতিহাস সব মুছে যাবে। আপনি কি নিশ্চিত?')) {
      setTransactions([]);
      setArchives([]);
      setInsights([]);
    }
  };

  return (
    <div className="max-w-md mx-auto min-h-screen bg-white shadow-2xl flex flex-col font-sans relative">
      {/* Top Header */}
      <header className="bg-gradient-to-br from-blue-700 to-indigo-800 p-8 text-white rounded-b-[40px] shadow-xl relative z-20">
        <div className="flex justify-between items-center mb-6">
          <div className="flex items-center gap-2">
             <div className="w-8 h-8 bg-white/20 rounded-lg flex items-center justify-center">
                <i className="fas fa-sparkles text-yellow-300"></i>
             </div>
             <h1 className="text-lg font-black tracking-tight uppercase">স্পার্ক</h1>
          </div>
          <div className="flex gap-4">
            <button onClick={() => setShowHistory(true)} className="w-10 h-10 bg-white/10 rounded-full flex items-center justify-center hover:bg-white/20 transition-all">
              <i className="fas fa-history"></i>
            </button>
            <button onClick={clearEverything} className="w-10 h-10 bg-white/10 rounded-full flex items-center justify-center hover:bg-rose-500/30 transition-all">
              <i className="fas fa-sync-alt"></i>
            </button>
          </div>
        </div>

        <div className="text-center py-4">
          <p className="text-blue-100 text-xs font-bold uppercase tracking-widest mb-1 opacity-70">বর্তমান ব্যালেন্স</p>
          <h2 className="text-4xl font-black mb-6">{formatCurrency(summary.balance)}</h2>

          <div className="flex gap-3">
            <div className="flex-1 bg-white/10 backdrop-blur-md p-3 rounded-2xl border border-white/5">
              <p className="text-[10px] uppercase font-black text-blue-200 mb-1">আয়</p>
              <p className="text-sm font-bold">৳{summary.income}</p>
            </div>
            <div className="flex-1 bg-white/10 backdrop-blur-md p-3 rounded-2xl border border-white/5">
              <p className="text-[10px] uppercase font-black text-blue-200 mb-1">ব্যয়</p>
              <p className="text-sm font-bold">৳{summary.expense}</p>
            </div>
          </div>
        </div>
      </header>

      {/* Main Actions & List */}
      <main className="flex-1 px-6 pt-6 pb-24 overflow-y-auto custom-scrollbar bg-slate-50/50">
        <div className="grid grid-cols-2 gap-4 mb-8">
          <button onClick={() => setModalType(TransactionType.INCOME)} className="p-4 bg-white border border-slate-100 rounded-3xl shadow-sm hover:shadow-md transition-all flex flex-col items-center gap-2 group">
            <div className="w-10 h-10 bg-green-500 text-white rounded-full flex items-center justify-center group-active:scale-90 transition-transform">
              <i className="fas fa-plus"></i>
            </div>
            <span className="text-xs font-black text-slate-600 uppercase">আয় যোগ</span>
          </button>
          <button onClick={() => setModalType(TransactionType.EXPENSE)} className="p-4 bg-white border border-slate-100 rounded-3xl shadow-sm hover:shadow-md transition-all flex flex-col items-center gap-2 group">
            <div className="w-10 h-10 bg-rose-500 text-white rounded-full flex items-center justify-center group-active:scale-90 transition-transform">
              <i className="fas fa-minus"></i>
            </div>
            <span className="text-xs font-black text-slate-600 uppercase">ব্যয় যোগ</span>
          </button>
        </div>

        {/* AI Insight */}
        {loadingInsights ? (
          <div className="mb-8 h-20 bg-blue-50 animate-pulse rounded-3xl"></div>
        ) : insights.length > 0 && (
          <div className="mb-8 p-5 bg-blue-600 text-white rounded-3xl shadow-lg relative overflow-hidden">
            <div className="relative z-10">
              <div className="flex items-center gap-2 mb-2">
                <i className="fas fa-lightbulb text-yellow-300"></i>
                <h3 className="text-[10px] font-black uppercase tracking-widest opacity-80">এআই টিপস</h3>
              </div>
              <p className="text-xs font-medium leading-relaxed">{insights[0].description}</p>
            </div>
            <i className="fas fa-brain absolute -bottom-4 -right-4 text-7xl opacity-10"></i>
          </div>
        )}

        {/* Transactions List */}
        <div className="flex justify-between items-center mb-4">
          <h3 className="font-black text-slate-800 text-sm uppercase tracking-tighter">চলতি মাসের লেনদেন</h3>
          <button onClick={startNewMonth} className="text-[10px] font-black text-blue-600 bg-blue-50 px-3 py-1.5 rounded-full hover:bg-blue-100 transition-colors uppercase">
             নতুন মাস শুরু
          </button>
        </div>

        {transactions.length > 0 ? (
          <div className="space-y-3">
            {transactions.map(t => (
              <div key={t.id} className="flex items-center justify-between p-4 bg-white border border-slate-100 rounded-2xl group transition-all hover:border-blue-100">
                <div className="flex items-center gap-4">
                  <div className={`w-10 h-10 rounded-xl flex items-center justify-center text-lg ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-500' : 'bg-rose-50 text-rose-500'}`}>
                    <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                  </div>
                  <div>
                    <p className="font-bold text-slate-800 text-sm">{t.category}</p>
                    <p className="text-[10px] text-slate-400 font-bold uppercase">{t.note || 'নোট নেই'}</p>
                  </div>
                </div>
                <div className="flex items-center gap-3">
                  <p className={`font-black text-sm ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                    {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                  </p>
                  <button onClick={() => deleteTx(t.id)} className="text-slate-200 hover:text-rose-500 transition-colors"><i className="fas fa-times"></i></button>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center py-12 bg-white rounded-3xl border border-dashed border-slate-200">
            <i className="fas fa-receipt text-slate-200 text-3xl mb-2"></i>
            <p className="text-slate-400 text-xs font-bold uppercase">কোনো লেনদেন নেই</p>
          </div>
        )}
      </main>

      {/* History Modal */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-md z-[100] animate-fadeIn flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[40px] shadow-2xl flex flex-col overflow-hidden animate-slideUp">
             <div className="p-8 border-b border-slate-100 flex justify-between items-center">
                <div>
                   <h2 className="text-xl font-black text-slate-800 uppercase tracking-tight">লেনদেনের ইতিহাস</h2>
                   <p className="text-xs text-slate-500 font-bold uppercase tracking-widest mt-1">পুরনো মাসের হিসাবগুলো</p>
                </div>
                <button onClick={() => setShowHistory(false)} className="w-10 h-10 bg-slate-100 text-slate-500 rounded-full flex items-center justify-center">
                  <i className="fas fa-times"></i>
                </button>
             </div>
             <div className="flex-1 overflow-y-auto p-6 space-y-6 custom-scrollbar">
                {archives.length > 0 ? archives.map(arc => (
                  <div key={arc.id} className="bg-slate-50 rounded-3xl p-5 border border-slate-100">
                     <div className="flex justify-between items-center mb-4 pb-4 border-b border-slate-200 border-dashed">
                        <div>
                           <h4 className="font-black text-slate-800">{arc.monthName} {arc.year}</h4>
                           <p className="text-[10px] text-slate-400 font-bold">{arc.transactions.length}টি লেনদেন</p>
                        </div>
                        <div className="text-right">
                           <p className="text-xs font-bold text-green-600">আয়: ৳{arc.totalIncome}</p>
                           <p className="text-xs font-bold text-rose-600">ব্যয়: ৳{arc.totalExpense}</p>
                        </div>
                     </div>
                     <div className="space-y-2 opacity-60">
                        {arc.transactions.slice(0, 3).map(tx => (
                          <div key={tx.id} className="flex justify-between text-[11px] font-bold">
                             <span className="text-slate-600">{tx.category}</span>
                             <span className={tx.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-600'}>
                                {tx.type === TransactionType.INCOME ? '+' : '-'}{tx.amount}
                             </span>
                          </div>
                        ))}
                        {arc.transactions.length > 3 && <p className="text-[10px] text-center pt-2 font-bold text-slate-400 italic">...আরো লেনদেন আছে</p>}
                     </div>
                  </div>
                )) : (
                  <div className="text-center py-20">
                     <i className="fas fa-box-open text-slate-200 text-5xl mb-4"></i>
                     <p className="text-slate-400 font-bold uppercase text-xs">ইতিহাস খালি</p>
                  </div>
                )}
             </div>
          </div>
        </div>
      )}

      {/* Add Transaction Modal */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm flex items-end sm:items-center justify-center p-0 sm:p-4 z-[100] animate-fadeIn">
          <div className="bg-white w-full max-w-md rounded-t-[32px] sm:rounded-[32px] overflow-hidden animate-slideUp">
            <div className={`p-6 ${modalType === TransactionType.INCOME ? 'bg-green-600' : 'bg-rose-600'} text-white flex justify-between items-center`}>
              <h2 className="text-lg font-black uppercase">{modalType === TransactionType.INCOME ? 'আয় যোগ' : 'ব্যয় যোগ'}</h2>
              <button onClick={() => setModalType(null)} className="w-8 h-8 flex items-center justify-center hover:bg-black/10 rounded-full"><i className="fas fa-times"></i></button>
            </div>
            <form onSubmit={addTransaction} className="p-8 space-y-6">
              <div className="space-y-2">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest">টাকার পরিমাণ</label>
                <input type="number" name="amount" required placeholder="0.00" autoFocus className="w-full py-4 bg-slate-50 border-2 border-slate-100 rounded-2xl focus:border-blue-500 outline-none font-black text-2xl text-slate-800 text-center" />
              </div>
              <div className="space-y-2">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest">ক্যাটাগরি</label>
                <select name="category" className="w-full px-4 py-4 bg-slate-50 border-2 border-slate-100 rounded-2xl outline-none font-bold text-slate-700">
                  {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => (
                    <option key={c} value={c}>{c}</option>
                  ))}
                </select>
              </div>
              <div className="space-y-2">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest">নোট</label>
                <input type="text" name="note" placeholder="কি কারণে?" className="w-full px-4 py-4 bg-slate-50 border-2 border-slate-100 rounded-2xl outline-none font-bold text-slate-700" />
              </div>
              <button type="submit" className={`w-full py-5 rounded-2xl text-white font-black text-lg shadow-xl active:scale-95 transition-transform ${modalType === TransactionType.INCOME ? 'bg-green-600' : 'bg-rose-600'}`}>
                সংরক্ষণ করুন
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

