  previousMonthSavings: number;
  categoryBreakdown: Record<string, number>;
  monthlyHistory: { month: string; income: number; expense: number }[];
}

export interface Insight {
  title: string;
  description: string;
  type: 'success' | 'warning' | 'info';
}

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  // Current Month Transactions
  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  // Past Monthly Archives
  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  // Budgets State
  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);
  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingInsights, setLoadingInsights] = useState(false);

  // Persistence
  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
  }, [transactions]);

  useEffect(() => {
    localStorage.setItem('spark_archives', JSON.stringify(archives));
  }, [archives]);

  useEffect(() => {
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
  }, [budgets]);

  // Current Summary & Budget Analysis
  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);

    // Category wise expense breakdown
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });

    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  // AI Insights
  useEffect(() => {
    const fetchInsights = async () => {
      if (transactions.length < 3) return;
      setLoadingInsights(true);
      try {
        const res = await getFinancialInsights(transactions, {
          totalIncome: summary.income,
          totalExpenses: summary.expense,
          balance: summary.balance,
          savings: summary.balance,
          previousMonthSavings: 0,
          categoryBreakdown: summary.breakdown,
          monthlyHistory: []
        });
        setInsights(res);
      } catch (e) { console.error(e); }
      finally { setLoadingInsights(false); }
    };
    fetchInsights();
  }, [transactions.length]);

  const addTransaction = (e: any) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount) return;

    const newTx: Transaction = {
      id: Date.now().toString(),
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: formData.get('note') as string,
      date: new Date().toISOString()
    };
    setTransactions([newTx, ...transactions]);
    setModalType(null);
  };

  const updateBudget = (category: string, amount: string) => {
    const val = parseFloat(amount) || 0;
    setBudgets({ ...budgets, [category]: val });
  };

  const deleteTx = (id: string) => {
    if (window.confirm('মুছে ফেলতে চান?')) {
      setTransactions(transactions.filter(t => t.id !== id));
    }
  };

  const startNewMonth = () => {
    if (transactions.length === 0) {
      alert("আর্কাইভ করার মতো কোনো লেনদেন নেই।");
      return;
    }
    if (window.confirm('চলতি মাসের হিসাব কি ইতিহাসে জমা করে ড্যাশবোর্ড পরিষ্কার করতে চান?')) {
      const now = new Date();
      const monthName = now.toLocaleString('bn-BD', { month: 'long' });
      const newArchive: MonthlyArchive = {
        id: Date.now().toString(),
        monthName,
        year: now.getFullYear(),
        totalIncome: summary.income,
        totalExpense: summary.expense,
        transactions: [...transactions]
      };
      setArchives([newArchive, ...archives]);
      setTransactions([]);
      setInsights([]);
      alert("হিসাব ইতিহাসে জমা করা হয়েছে।");
    }
  };

  const clearEverything = () => {
    if (window.confirm('সতর্কতা: আপনার বর্তমান এবং ইতিহাস সব মুছে যাবে। আপনি কি নিশ্চিত?')) {
      setTransactions([]);
      setArchives([]);
      setInsights([]);
      setBudgets({});
    }
  };

  return (
    <div className="max-w-md mx-auto min-h-screen bg-white shadow-2xl flex flex-col font-sans relative">
      {/* Top Header */}
      <header className="bg-gradient-to-br from-blue-700 to-indigo-800 p-8 text-white rounded-b-[40px] shadow-xl relative z-20">
        <div className="flex justify-between items-center mb-6">
          <div className="flex items-center gap-2">
             <div className="w-8 h-8 bg-white/20 rounded-lg flex items-center justify-center">
                <i className="fas fa-sparkles text-yellow-300"></i>
             </div>
             <h1 className="text-lg font-black tracking-tight uppercase">স্পার্ক</h1>
          </div>
          <div className="flex gap-4">
            <button onClick={() => setShowBudgetSettings(true)} className="w-10 h-10 bg-white/10 rounded-full flex items-center justify-center hover:bg-white/20 transition-all" title="বাজেট সেট করুন">
              <i className="fas fa-bullseye"></i>
            </button>
            <button onClick={() => setShowHistory(true)} className="w-10 h-10 bg-white/10 rounded-full flex items-center justify-center hover:bg-white/20 transition-all" title="ইতিহাস">
              <i className="fas fa-history"></i>
            </button>
            <button onClick={clearEverything} className="w-10 h-10 bg-white/10 rounded-full flex items-center justify-center hover:bg-rose-500/30 transition-all" title="রিসেট">
              <i className="fas fa-sync-alt"></i>
            </button>
          </div>
        </div>

        <div className="text-center py-4">
          <p className="text-blue-100 text-xs font-bold uppercase tracking-widest mb-1 opacity-70">বর্তমান ব্যালেন্স</p>
          <h2 className="text-4xl font-black mb-6">{formatCurrency(summary.balance)}</h2>

          <div className="flex gap-3">
            <div className="flex-1 bg-white/10 backdrop-blur-md p-3 rounded-2xl border border-white/5">
              <p className="text-[10px] uppercase font-black text-blue-200 mb-1">আয়</p>
              <p className="text-sm font-bold">৳{summary.income}</p>
            </div>
            <div className="flex-1 bg-white/10 backdrop-blur-md p-3 rounded-2xl border border-white/5">
              <p className="text-[10px] uppercase font-black text-blue-200 mb-1">ব্যয়</p>
              <p className="text-sm font-bold">৳{summary.expense}</p>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-1 px-6 pt-6 pb-24 overflow-y-auto custom-scrollbar bg-slate-50/50">
        {/* Action Buttons */}
        <div className="grid grid-cols-2 gap-4 mb-6">
          <button onClick={() => setModalType(TransactionType.INCOME)} className="p-4 bg-white border border-slate-100 rounded-3xl shadow-sm hover:shadow-md transition-all flex flex-col items-center gap-2 group">
            <div className="w-10 h-10 bg-green-500 text-white rounded-full flex items-center justify-center group-active:scale-90 transition-transform">
              <i className="fas fa-plus"></i>
            </div>
            <span className="text-xs font-black text-slate-600 uppercase">আয় যোগ</span>
          </button>
          <button onClick={() => setModalType(TransactionType.EXPENSE)} className="p-4 bg-white border border-slate-100 rounded-3xl shadow-sm hover:shadow-md transition-all flex flex-col items-center gap-2 group">
            <div className="w-10 h-10 bg-rose-500 text-white rounded-full flex items-center justify-center group-active:scale-90 transition-transform">
              <i className="fas fa-minus"></i>
            </div>
            <span className="text-xs font-black text-slate-600 uppercase">ব্যয় যোগ</span>
          </button>
        </div>

        {/* Budget Overview Section */}
        {Object.keys(budgets).length > 0 && (
          <div className="mb-8 space-y-4">
            <div className="flex justify-between items-center px-1">
              <h3 className="font-black text-slate-800 text-xs uppercase tracking-widest">বাজেট প্রগতি</h3>
              <span className="text-[10px] font-bold text-slate-400">এই মাস</span>
            </div>
            <div className="grid grid-cols-1 gap-3">
              {EXPENSE_CATEGORIES.map(cat => {
                const budget = budgets[cat] || 0;
                if (budget === 0) return null;
                const spent = summary.breakdown[cat] || 0;
                const percent = Math.min((spent / budget) * 100, 100);
                const isOver = spent > budget;

                return (
                  <div key={cat} className="bg-white p-4 rounded-2xl border border-slate-100 shadow-sm">
                    <div className="flex justify-between items-center mb-2">
                      <div className="flex items-center gap-2">
                        <i className={`fas ${CATEGORY_ICONS[cat]} text-slate-400 text-xs`}></i>
                        <span className="text-xs font-bold text-slate-700">{cat}</span>
                      </div>
                      <span className={`text-[10px] font-black ${isOver ? 'text-rose-600' : 'text-slate-500'}`}>
                        {formatCurrency(spent)} / {formatCurrency(budget)}
                      </span>
                    </div>
                    <div className="w-full h-2 bg-slate-100 rounded-full overflow-hidden">
                      <div 
                        className={`h-full transition-all duration-700 rounded-full ${isOver ? 'bg-rose-500' : percent > 80 ? 'bg-amber-500' : 'bg-blue-500'}`}
                        style={{ width: `${percent}%` }}
                      ></div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        )}

        {/* AI Insight */}
        {loadingInsights ? (
          <div className="mb-8 h-20 bg-blue-50 animate-pulse rounded-3xl"></div>
        ) : insights.length > 0 && (
          <div className="mb-8 p-5 bg-blue-600 text-white rounded-3xl shadow-lg relative overflow-hidden">
            <div className="relative z-10">
              <div className="flex items-center gap-2 mb-2">
                <i className="fas fa-lightbulb text-yellow-300"></i>
                <h3 className="text-[10px] font-black uppercase tracking-widest opacity-80">এআই টিপস</h3>
              </div>
              <p className="text-xs font-medium leading-relaxed">{insights[0].description}</p>
            </div>
            <i className="fas fa-brain absolute -bottom-4 -right-4 text-7xl opacity-10"></i>
          </div>
        )}

        {/* Transactions List */}
        <div className="flex justify-between items-center mb-4 px-1">
          <h3 className="font-black text-slate-800 text-sm uppercase tracking-tighter">সাম্প্রতিক লেনদেন</h3>
          <button onClick={startNewMonth} className="text-[10px] font-black text-blue-600 bg-blue-50 px-3 py-1.5 rounded-full hover:bg-blue-100 transition-colors uppercase">
             নতুন মাস শুরু
          </button>
        </div>

        {transactions.length > 0 ? (
          <div className="space-y-3">
            {transactions.map(t => {
              const budget = budgets[t.category] || 0;
              const spent = summary.breakdown[t.category] || 0;
              const isOver = t.type === TransactionType.EXPENSE && budget > 0 && spent > budget;

              return (
                <div key={t.id} className={`flex items-center justify-between p-4 bg-white border rounded-2xl group transition-all hover:shadow-md ${isOver ? 'border-rose-200' : 'border-slate-100'}`}>
                  <div className="flex items-center gap-4">
                    <div className={`w-10 h-10 rounded-xl flex items-center justify-center text-lg ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-500' : 'bg-rose-50 text-rose-500'}`}>
                      <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                    </div>
                    <div>
                      <p className="font-bold text-slate-800 text-sm flex items-center gap-2">
                        {t.category}
                        {isOver && <span className="bg-rose-500 w-1.5 h-1.5 rounded-full animate-pulse"></span>}
                      </p>
                      <p className="text-[10px] text-slate-400 font-bold uppercase">{t.note || 'নোট নেই'}</p>
                    </div>
                  </div>
                  <div className="flex items-center gap-3">
                    <p className={`font-black text-sm ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                      {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                    </p>
                    <button onClick={() => deleteTx(t.id)} className="text-slate-200 hover:text-rose-500 transition-colors"><i className="fas fa-times"></i></button>
                  </div>
                </div>
              );
            })}
          </div>
        ) : (
          <div className="text-center py-12 bg-white rounded-3xl border border-dashed border-slate-200">
            <i className="fas fa-receipt text-slate-200 text-3xl mb-2"></i>
            <p className="text-slate-400 text-xs font-bold uppercase">কোনো লেনদেন নেই</p>
          </div>
        )}
      </main>

      {/* Budget Settings Modal */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-md z-[110] animate-fadeIn flex items-center justify-center p-4">
          <div className="bg-white w-full max-w-md rounded-[32px] overflow-hidden shadow-2xl animate-slideUp">
            <div className="p-6 bg-blue-600 text-white flex justify-between items-center">
              <div>
                <h2 className="text-lg font-black uppercase">বাজেট সেটিংস</h2>
                <p className="text-[10px] font-bold opacity-80 uppercase">মাসিক খরচের লক্ষ্যমাত্রা সেট করুন</p>
              </div>
              <button onClick={() => setShowBudgetSettings(false)} className="w-8 h-8 flex items-center justify-center hover:bg-black/10 rounded-full">
                <i className="fas fa-times"></i>
              </button>
            </div>
            <div className="p-6 space-y-4 max-h-[60vh] overflow-y-auto custom-scrollbar">
              {EXPENSE_CATEGORIES.map(cat => (
                <div key={cat} className="space-y-1">
                  <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest flex items-center gap-2">
                    <i className={`fas ${CATEGORY_ICONS[cat]}`}></i> {cat}
                  </label>
                  <div className="relative">
                    <span className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 font-bold">৳</span>
                    <input 
                      type="number" 
                      value={budgets[cat] || ''}
                      onChange={(e) => updateBudget(cat, e.target.value)}
                      placeholder="বাজেট নেই"
                      className="w-full pl-9 pr-4 py-3 bg-slate-50 border border-slate-200 rounded-xl focus:border-blue-500 outline-none font-bold text-slate-700"
                    />
                  </div>
                </div>
              ))}
            </div>
            <div className="p-6 border-t border-slate-50">
              <button onClick={() => setShowBudgetSettings(false)} className="w-full py-4 bg-slate-900 text-white rounded-2xl font-black uppercase shadow-lg active:scale-95 transition-transform">
                ঠিক আছে
              </button>
            </div>
          </div>
        </div>
      )}

      {/* History Modal */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-md z-[100] animate-fadeIn flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[40px] shadow-2xl flex flex-col overflow-hidden animate-slideUp">
             <div className="p-8 border-b border-slate-100 flex justify-between items-center">
                <div>
                   <h2 className="text-xl font-black text-slate-800 uppercase tracking-tight">লেনদেনের ইতিহাস</h2>
                   <p className="text-xs text-slate-500 font-bold uppercase tracking-widest mt-1">পুরনো মাসের হিসাবগুলো</p>
                </div>
                <button onClick={() => setShowHistory(false)} className="w-10 h-10 bg-slate-100 text-slate-500 rounded-full flex items-center justify-center">
                  <i className="fas fa-times"></i>
                </button>
             </div>
             <div className="flex-1 overflow-y-auto p-6 space-y-6 custom-scrollbar">
                {archives.length > 0 ? archives.map(arc => (
                  <div key={arc.id} className="bg-slate-50 rounded-3xl p-5 border border-slate-100">
                     <div className="flex justify-between items-center mb-4 pb-4 border-b border-slate-200 border-dashed">
                        <div>
                           <h4 className="font-black text-slate-800">{arc.monthName} {arc.year}</h4>
                           <p className="text-[10px] text-slate-400 font-bold">{arc.transactions.length}টি লেনদেন</p>
                        </div>
                        <div className="text-right">
                           <p className="text-xs font-bold text-green-600">আয়: ৳{arc.totalIncome}</p>
                           <p className="text-xs font-bold text-rose-600">ব্যয়: ৳{arc.totalExpense}</p>
                        </div>
                     </div>
                     <div className="space-y-2 opacity-60">
                        {arc.transactions.slice(0, 3).map(tx => (
                          <div key={tx.id} className="flex justify-between text-[11px] font-bold">
                             <span className="text-slate-600">{tx.category}</span>
                             <span className={tx.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-600'}>
                                {tx.type === TransactionType.INCOME ? '+' : '-'}{tx.amount}
                             </span>
                          </div>
                        ))}
                     </div>
                  </div>
                )) : (
                  <div className="text-center py-20">
                     <i className="fas fa-box-open text-slate-200 text-5xl mb-4"></i>
                     <p className="text-slate-400 font-bold uppercase text-xs">ইতিহাস খালি</p>
                  </div>
                )}
             </div>
          </div>
        </div>
      )}

      {/* Add Transaction Modal */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm flex items-end sm:items-center justify-center p-0 sm:p-4 z-[100] animate-fadeIn">
          <div className="bg-white w-full max-w-md rounded-t-[32px] sm:rounded-[32px] overflow-hidden animate-slideUp">
            <div className={`p-6 ${modalType === TransactionType.INCOME ? 'bg-green-600' : 'bg-rose-600'} text-white flex justify-between items-center`}>
              <h2 className="text-lg font-black uppercase">{modalType === TransactionType.INCOME ? 'আয় যোগ' : 'ব্যয় যোগ'}</h2>
              <button onClick={() => setModalType(null)} className="w-8 h-8 flex items-center justify-center hover:bg-black/10 rounded-full"><i className="fas fa-times"></i></button>
            </div>
            <form onSubmit={addTransaction} className="p-8 space-y-6">
              <div className="space-y-2">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest">টাকার পরিমাণ</label>
                <input type="number" name="amount" required placeholder="0.00" autoFocus className="w-full py-4 bg-slate-50 border-2 border-slate-100 rounded-2xl focus:border-blue-500 outline-none font-black text-2xl text-slate-800 text-center" />
              </div>
              <div className="space-y-2">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest">ক্যাটাগরি</label>
                <select name="category" className="w-full px-4 py-4 bg-slate-50 border-2 border-slate-100 rounded-2xl outline-none font-bold text-slate-700">
                  {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => (
                    <option key={c} value={c}>{c}</option>
                  ))}
                </select>
              </div>
              <div className="space-y-2">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest">নোট</label>
                <input type="text" name="note" placeholder="কি কারণে?" className="w-full px-4 py-4 bg-slate-50 border-2 border-slate-100 rounded-2xl outline-none font-bold text-slate-700" />
              </div>
              <button type="submit" className={`w-full py-5 rounded-2xl text-white font-black text-lg shadow-xl active:scale-95 transition-transform ${modalType === TransactionType.INCOME ? 'bg-green-600' : 'bg-rose-600'}`}>
                সংরক্ষণ করুন
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  // Current Tab
  const [activeTab, setActiveTab] = useState<'home' | 'tools'>('home');

  // Profile Image
  const [profileImage, setProfileImage] = useState<string | null>(() => {
    return localStorage.getItem('spark_profile') || null;
  });

  // Current Month Transactions
  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  // Past Monthly Archives
  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  // Budgets State
  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);
  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingInsights, setLoadingInsights] = useState(false);

  // Bill Splitter State
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  // Persistence
  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
  }, [transactions]);

  useEffect(() => {
    localStorage.setItem('spark_archives', JSON.stringify(archives));
  }, [archives]);

  useEffect(() => {
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
  }, [budgets]);

  useEffect(() => {
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [profileImage]);

  // Current Summary
  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  // Financial Health Score
  const healthScore = useMemo(() => {
    if (summary.income === 0) return 0;
    const ratio = (summary.balance / summary.income) * 100;
    return Math.max(0, Math.min(100, Math.round(ratio)));
  }, [summary]);

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setProfileImage(reader.result as string);
      };
      reader.readAsDataURL(file);
    }
  };

  const addTransaction = (e: any) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount) return;
    const newTx: Transaction = {
      id: Date.now().toString(),
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: formData.get('note') as string,
      date: new Date().toISOString()
    };
    setTransactions([newTx, ...transactions]);
    setModalType(null);
  };

  const deleteTx = (id: string) => {
    if (window.confirm('মুছে ফেলতে চান?')) {
      setTransactions(transactions.filter(t => t.id !== id));
    }
  };

  const startNewMonth = () => {
    if (transactions.length === 0) {
      alert("আর্কাইভ করার মতো কোনো লেনদেন নেই।");
      return;
    }
    if (window.confirm('চলতি মাসের হিসাব কি ইতিহাসে জমা করে ড্যাশবোর্ড পরিষ্কার করতে চান?')) {
      const now = new Date();
      const monthName = now.toLocaleString('bn-BD', { month: 'long' });
      const newArchive: MonthlyArchive = {
        id: Date.now().toString(),
        monthName,
        year: now.getFullYear(),
        totalIncome: summary.income,
        totalExpense: summary.expense,
        transactions: [...transactions]
      };
      setArchives([newArchive, ...archives]);
      setTransactions([]);
      alert("হিসাব ইতিহাসে জমা করা হয়েছে।");
    }
  };

  return (
    <div className="max-w-md mx-auto min-h-screen bg-slate-50 shadow-2xl flex flex-col font-sans relative pb-20">
      {/* Header */}
      <header className="bg-gradient-to-br from-indigo-700 via-blue-600 to-blue-700 p-8 text-white rounded-b-[40px] shadow-xl relative z-20">
        <div className="flex justify-between items-center mb-8">
          <div className="flex items-center gap-3">
             <div className="w-10 h-10 bg-white/20 rounded-xl flex items-center justify-center relative overflow-hidden group border border-white/30 cursor-pointer">
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
                ) : (
                  <i className="fas fa-camera text-blue-100 opacity-50"></i>
                )}
                <input 
                  type="file" 
                  accept="image/*" 
                  onChange={handleImageUpload} 
                  className="absolute inset-0 opacity-0 cursor-pointer" 
                  title="ছবি আপলোড করুন"
                />
             </div>
             <div>
                <h1 className="text-lg font-black tracking-tight uppercase leading-none">স্পার্ক</h1>
                <p className="text-[10px] font-bold text-blue-200 uppercase mt-0.5 tracking-widest">স্মার্ট ফিন্যান্স</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-9 h-9 bg-white/10 rounded-full flex items-center justify-center hover:bg-white/20 transition-all border border-white/5">
              <i className="fas fa-bullseye text-sm"></i>
            </button>
            <button onClick={() => setShowHistory(true)} className="w-9 h-9 bg-white/10 rounded-full flex items-center justify-center hover:bg-white/20 transition-all border border-white/5">
              <i className="fas fa-history text-sm"></i>
            </button>
          </div>
        </div>

        <div className="text-center py-2">
          <p className="text-blue-100 text-[10px] font-black uppercase tracking-[0.2em] mb-2 opacity-70">মোট ব্যালেন্স</p>
          <h2 className="text-4xl font-black mb-8 tracking-tight">{formatCurrency(summary.balance)}</h2>

          <div className="grid grid-cols-2 gap-4">
            <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10">
              <div className="flex items-center justify-center gap-2 mb-1">
                <i className="fas fa-arrow-down text-[10px] text-green-300"></i>
                <p className="text-[10px] uppercase font-black text-blue-200">মোট আয়</p>
              </div>
              <p className="text-lg font-black">৳{summary.income}</p>
            </div>
            <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10">
              <div className="flex items-center justify-center gap-2 mb-1">
                <i className="fas fa-arrow-up text-[10px] text-rose-300"></i>
                <p className="text-[10px] uppercase font-black text-blue-200">মোট ব্যয়</p>
              </div>
              <p className="text-lg font-black">৳{summary.expense}</p>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content Area */}
      <main className="flex-1 overflow-y-auto custom-scrollbar">
        {activeTab === 'home' ? (
          <div className="px-6 py-6 space-y-8 animate-fadeIn">
            {/* Quick Actions */}
            <div className="grid grid-cols-2 gap-4">
              <button onClick={() => setModalType(TransactionType.INCOME)} className="p-5 bg-white border border-slate-100 rounded-[32px] shadow-sm hover:shadow-md transition-all flex flex-col items-center gap-3">
                <div className="w-12 h-12 bg-green-500 text-white rounded-2xl flex items-center justify-center text-xl shadow-lg shadow-green-200">
                  <i className="fas fa-plus"></i>
                </div>
                <span className="text-[11px] font-black text-slate-500 uppercase">আয় যোগ</span>
              </button>
              <button onClick={() => setModalType(TransactionType.EXPENSE)} className="p-5 bg-white border border-slate-100 rounded-[32px] shadow-sm hover:shadow-md transition-all flex flex-col items-center gap-3">
                <div className="w-12 h-12 bg-rose-500 text-white rounded-2xl flex items-center justify-center text-xl shadow-lg shadow-rose-200">
                  <i className="fas fa-minus"></i>
                </div>
                <span className="text-[11px] font-black text-slate-500 uppercase">ব্যয় যোগ</span>
              </button>
            </div>

            {/* Budget Progress */}
            {Object.keys(budgets).length > 0 && (
              <div className="space-y-4">
                <h3 className="font-black text-slate-800 text-xs uppercase tracking-widest px-1">বাজেট স্ট্যাটাস</h3>
                <div className="grid grid-cols-1 gap-3">
                  {EXPENSE_CATEGORIES.map(cat => {
                    const budget = budgets[cat] || 0;
                    if (budget === 0) return null;
                    const spent = summary.breakdown[cat] || 0;
                    const percent = Math.min((spent / budget) * 100, 100);
                    return (
                      <div key={cat} className="bg-white p-4 rounded-2xl border border-slate-100 shadow-sm">
                        <div className="flex justify-between items-center mb-2">
                          <span className="text-xs font-bold text-slate-700">{cat}</span>
                          <span className="text-[10px] font-black text-slate-500">{percent.toFixed(0)}%</span>
                        </div>
                        <div className="w-full h-1.5 bg-slate-100 rounded-full overflow-hidden">
                          <div className={`h-full transition-all duration-1000 ${spent > budget ? 'bg-rose-500' : 'bg-blue-500'}`} style={{ width: `${percent}%` }}></div>
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            )}

            {/* Transactions */}
            <div className="space-y-4">
              <div className="flex justify-between items-center px-1">
                <h3 className="font-black text-slate-800 text-xs uppercase tracking-widest">সাম্প্রতিক লেনদেন</h3>
                <button onClick={startNewMonth} className="text-[9px] font-black text-blue-600 bg-blue-50 px-3 py-1.5 rounded-full uppercase">নতুন মাস শুরু</button>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3">
                  {transactions.map(t => (
                    <div key={t.id} className="flex items-center justify-between p-4 bg-white border border-slate-100 rounded-2xl group transition-all">
                      <div className="flex items-center gap-4">
                        <div className={`w-10 h-10 rounded-xl flex items-center justify-center text-lg ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-500' : 'bg-rose-50 text-rose-500'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-bold text-slate-800 text-sm">{t.category}</p>
                          <p className="text-[10px] text-slate-400 font-bold uppercase">{t.note || 'নোট নেই'}</p>
                        </div>
                      </div>
                      <div className="flex items-center gap-3">
                        <p className={`font-black text-sm ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                          {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                        </p>
                        <button onClick={() => deleteTx(t.id)} className="text-slate-200 hover:text-rose-500"><i className="fas fa-times"></i></button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-10 bg-white rounded-3xl border border-dashed border-slate-200">
                  <p className="text-slate-400 text-[10px] font-black uppercase">খালি</p>
                </div>
              )}
            </div>
          </div>
        ) : (
          <div className="px-6 py-6 space-y-8 animate-fadeIn">
            {/* Tools Tab Content */}
            <div className="p-8 bg-slate-900 rounded-[40px] text-white relative overflow-hidden shadow-2xl">
              <div className="relative z-10">
                <h3 className="text-xl font-black mb-1 uppercase tracking-tight">আর্থিক স্কোর</h3>
                <p className="text-xs text-slate-400 font-bold mb-6">আপনার সঞ্চয় ও আয়ের অনুপাত</p>
                <div className="flex items-end gap-4">
                  <div className="text-6xl font-black text-blue-400">{healthScore}</div>
                  <div className="pb-2">
                    <div className="text-xs font-black uppercase text-slate-500 mb-1">অবস্থা</div>
                    <div className={`text-xs font-black px-3 py-1 rounded-full uppercase ${healthScore > 50 ? 'bg-green-500/20 text-green-400' : 'bg-amber-500/20 text-amber-400'}`}>
                      {healthScore > 70 ? 'চমৎকার' : healthScore > 40 ? 'মোটামুটি' : 'উন্নতি প্রয়োজন'}
                    </div>
                  </div>
                </div>
              </div>
              <div className="absolute top-0 right-0 w-48 h-48 bg-blue-500/10 rounded-full blur-3xl"></div>
            </div>

            {/* Bill Splitter Tool */}
            <div className="bg-white p-6 rounded-[32px] border border-slate-100 shadow-sm space-y-6">
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 bg-indigo-100 text-indigo-600 rounded-xl flex items-center justify-center">
                  <i className="fas fa-users"></i>
                </div>
                <h3 className="font-black text-slate-800 uppercase text-xs tracking-widest">বিল স্প্লিটার</h3>
              </div>

              <div className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-1">
                    <label className="text-[10px] font-black text-slate-400 uppercase">মোট বিল</label>
                    <input 
                      type="number" 
                      value={splitAmount}
                      onChange={(e) => setSplitAmount(e.target.value)}
                      placeholder="৳ ০"
                      className="w-full p-3 bg-slate-50 border-2 border-slate-100 rounded-xl font-black text-slate-800 outline-none focus:border-indigo-400"
                    />
                  </div>
                  <div className="space-y-1">
                    <label className="text-[10px] font-black text-slate-400 uppercase">জনসংখ্যা</label>
                    <input 
                      type="number" 
                      value={splitPeople}
                      onChange={(e) => setSplitPeople(e.target.value)}
                      className="w-full p-3 bg-slate-50 border-2 border-slate-100 rounded-xl font-black text-slate-800 outline-none focus:border-indigo-400"
                    />
                  </div>
                </div>

                <div className="p-4 bg-indigo-50 rounded-2xl text-center">
                  <p className="text-[10px] font-black text-indigo-400 uppercase mb-1">প্রতি জনের ভাগ</p>
                  <p className="text-2xl font-black text-indigo-600">
                    ৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}
                  </p>
                </div>
              </div>
            </div>

            {/* Savings Goal Fun Widget */}
            <div className="bg-gradient-to-br from-amber-400 to-orange-500 p-6 rounded-[32px] text-white shadow-xl shadow-orange-100">
              <div className="flex justify-between items-start mb-4">
                <h4 className="font-black uppercase text-xs tracking-widest">স্মার্ট টিপস</h4>
                <i className="fas fa-bolt opacity-50"></i>
              </div>
              <p className="text-sm font-bold leading-relaxed">
                আপনার আয়ের ২০% আলাদা করে সরিয়ে রাখার চেষ্টা করুন। এটি আপনার দীর্ঘমেয়াদী আর্থিক নিরাপত্তার জন্য জরুরি।
              </p>
            </div>
          </div>
        )}
      </main>

      {/* Bottom Navigation */}
      <nav className="fixed bottom-0 left-1/2 -translate-x-1/2 w-full max-w-md bg-white border-t border-slate-100 px-10 py-4 flex justify-between items-center z-[100] shadow-[0_-10px_30px_-15px_rgba(0,0,0,0.1)]">
        <button 
          onClick={() => setActiveTab('home')}
          className={`flex flex-col items-center gap-1 transition-all ${activeTab === 'home' ? 'text-blue-600 scale-110' : 'text-slate-300'}`}
        >
          <i className="fas fa-home text-xl"></i>
          <span className="text-[10px] font-black uppercase">হোম</span>
        </button>
        <button 
          onClick={() => setActiveTab('tools')}
          className={`flex flex-col items-center gap-1 transition-all ${activeTab === 'tools' ? 'text-blue-600 scale-110' : 'text-slate-300'}`}
        >
          <i className="fas fa-shapes text-xl"></i>
          <span className="text-[10px] font-black uppercase">টুলস</span>
        </button>
      </nav>

      {/* Modals and Other Overlays (Hidden by default) */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-md z-[200] flex items-center justify-center p-4">
          <div className="bg-white w-full max-w-sm rounded-[40px] overflow-hidden animate-slideUp">
            <div className="p-6 bg-blue-600 text-white flex justify-between items-center">
               <h2 className="text-sm font-black uppercase">বাজেট সেট করুন</h2>
               <button onClick={() => setShowBudgetSettings(false)} className="w-8 h-8 flex items-center justify-center"><i className="fas fa-times"></i></button>
            </div>
            <div className="p-6 space-y-4 max-h-[60vh] overflow-y-auto custom-scrollbar">
              {EXPENSE_CATEGORIES.map(cat => (
                <div key={cat} className="space-y-1">
                  <label className="text-[10px] font-black text-slate-400 uppercase">{cat}</label>
                  <input 
                    type="number" 
                    value={budgets[cat] || ''}
                    onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })}
                    placeholder="৳ ০"
                    className="w-full p-3 bg-slate-50 border-2 border-slate-100 rounded-xl font-bold"
                  />
                </div>
              ))}
            </div>
            <div className="p-6 pt-0">
               <button onClick={() => setShowBudgetSettings(false)} className="w-full py-4 bg-slate-900 text-white rounded-2xl font-black uppercase text-xs">বন্ধ করুন</button>
            </div>
          </div>
        </div>
      )}

      {/* History Modal */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-md z-[200] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[40px] p-8 overflow-y-auto custom-scrollbar animate-slideUp">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-xl font-black uppercase">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-10 h-10 bg-slate-100 rounded-full flex items-center justify-center"><i className="fas fa-times"></i></button>
             </div>
             {archives.length > 0 ? archives.map(arc => (
               <div key={arc.id} className="mb-6 p-5 bg-slate-50 rounded-3xl border border-slate-100">
                  <p className="text-[10px] font-black text-slate-400 uppercase mb-2">{arc.monthName} {arc.year}</p>
                  <div className="flex justify-between font-black text-slate-800">
                    <span className="text-green-600">আয়: ৳{arc.totalIncome}</span>
                    <span className="text-rose-600">ব্যয়: ৳{arc.totalExpense}</span>
                  </div>
               </div>
             )) : <p className="text-center text-slate-400 py-20 font-bold uppercase text-xs">কোনো ইতিহাস নেই</p>}
          </div>
        </div>
      )}

      {/* Add Transaction Modal */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[200] flex items-end justify-center animate-fadeIn">
          <div className="bg-white w-full max-w-md rounded-t-[40px] p-8 animate-slideUp">
            <div className="flex justify-between items-center mb-8">
              <h2 className="text-lg font-black uppercase">{modalType === TransactionType.INCOME ? 'আয় যোগ' : 'ব্যয় যোগ'}</h2>
              <button onClick={() => setModalType(null)} className="w-10 h-10 bg-slate-100 rounded-full flex items-center justify-center"><i className="fas fa-times"></i></button>
            </div>
            <form onSubmit={addTransaction} className="space-y-6">
              <input type="number" name="amount" required placeholder="৳ ০.০০" autoFocus className="w-full py-6 text-4xl font-black text-center text-slate-800 outline-none border-b-4 border-slate-100 focus:border-blue-500 transition-all" />
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-1">
                  <label className="text-[10px] font-black text-slate-400 uppercase ml-1">ক্যাটাগরি</label>
                  <select name="category" className="w-full p-4 bg-slate-50 border-2 border-slate-100 rounded-2xl font-bold text-slate-700 outline-none">
                    {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => (
                      <option key={c} value={c}>{c}</option>
                    ))}
                  </select>
                </div>
                <div className="space-y-1">
                  <label className="text-[10px] font-black text-slate-400 uppercase ml-1">নোট</label>
                  <input type="text" name="note" placeholder="কি কারণে?" className="w-full p-4 bg-slate-50 border-2 border-slate-100 rounded-2xl font-bold text-slate-700 outline-none" />
                </div>
              </div>
              <button type="submit" className={`w-full py-5 rounded-3xl text-white font-black text-lg shadow-xl ${modalType === TransactionType.INCOME ? 'bg-green-600 shadow-green-100' : 'bg-rose-600 shadow-rose-100'}`}>সংরক্ষণ করুন</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

interface Goal {
  id: string;
  name: string;
  target: number;
  current: number;
  icon: string;
}

const App: React.FC = () => {
  // Navigation Tabs
  const [activeTab, setActiveTab] = useState<'home' | 'goals' | 'tools'>('home');

  // Profile Image State
  const [profileImage, setProfileImage] = useState<string | null>(() => {
    return localStorage.getItem('spark_profile') || null;
  });

  // Current Month Transactions
  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  // Goals State
  const [goals, setGoals] = useState<Goal[]>(() => {
    const saved = localStorage.getItem('spark_goals');
    return saved ? JSON.parse(saved) : [
      { id: '1', name: 'জরুরি তহবিল', target: 50000, current: 12000, icon: 'fa-shield-halved' },
      { id: '2', name: 'নতুন ল্যাপটপ', target: 80000, current: 5000, icon: 'fa-laptop' }
    ];
  });

  // Past Monthly Archives
  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  // Budgets State
  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showGoalModal, setShowGoalModal] = useState(false);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);
  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingInsights, setLoadingInsights] = useState(false);

  // Bill Splitter State
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  // Persistence
  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    localStorage.setItem('spark_goals', JSON.stringify(goals));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, goals, profileImage]);

  // Current Summary
  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  // Financial Health Score
  const healthScore = useMemo(() => {
    if (summary.income === 0) return 0;
    const ratio = (summary.balance / summary.income) * 100;
    return Math.max(0, Math.min(100, Math.round(ratio)));
  }, [summary]);

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setProfileImage(reader.result as string);
      reader.readAsDataURL(file);
    }
  };

  const addTransaction = (e: any) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount) return;
    const newTx: Transaction = {
      id: Date.now().toString(),
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: formData.get('note') as string,
      date: new Date().toISOString()
    };
    setTransactions([newTx, ...transactions]);
    setModalType(null);
  };

  const addGoal = (e: any) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const target = parseFloat(formData.get('target') as string);
    if (!target) return;
    const newGoal: Goal = {
      id: Date.now().toString(),
      name: formData.get('name') as string,
      target,
      current: 0,
      icon: 'fa-star'
    };
    setGoals([...goals, newGoal]);
    setShowGoalModal(false);
  };

  const addToGoal = (id: string) => {
    const amountStr = prompt("এই লক্ষ্যে কত টাকা জমা করতে চান?");
    const amount = parseFloat(amountStr || '0');
    if (amount > 0) {
      setGoals(goals.map(g => g.id === id ? { ...g, current: g.current + amount } : g));
    }
  };

  const startNewMonth = () => {
    if (transactions.length === 0) return alert("লেনদেন নেই।");
    if (window.confirm('ইতিহাসে জমা করবেন?')) {
      const now = new Date();
      const newArchive: MonthlyArchive = {
        id: Date.now().toString(),
        monthName: now.toLocaleString('bn-BD', { month: 'long' }),
        year: now.getFullYear(),
        totalIncome: summary.income,
        totalExpense: summary.expense,
        transactions: [...transactions]
      };
      setArchives([newArchive, ...archives]);
      setTransactions([]);
    }
  };

  return (
    <div className="max-w-md mx-auto min-h-screen bg-slate-50 shadow-2xl flex flex-col font-sans relative pb-20 overflow-hidden">
      {/* Header */}
      <header className="bg-gradient-to-br from-indigo-700 via-blue-600 to-blue-700 p-8 text-white rounded-b-[40px] shadow-xl relative z-20">
        <div className="flex justify-between items-center mb-8">
          <div className="flex items-center gap-3">
             <div className="w-10 h-10 bg-white/20 rounded-xl flex items-center justify-center relative overflow-hidden group border border-white/30 cursor-pointer">
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
                ) : (
                  <i className="fas fa-camera text-blue-100 opacity-50"></i>
                )}
                <input type="file" accept="image/*" onChange={handleImageUpload} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div>
                <h1 className="text-lg font-black tracking-tight uppercase leading-none">স্পার্ক</h1>
                <p className="text-[10px] font-bold text-blue-200 uppercase mt-0.5 tracking-widest">স্মার্ট ফিন্যান্স</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-9 h-9 bg-white/10 rounded-full flex items-center justify-center"><i className="fas fa-bullseye text-sm"></i></button>
            <button onClick={() => setShowHistory(true)} className="w-9 h-9 bg-white/10 rounded-full flex items-center justify-center"><i className="fas fa-history text-sm"></i></button>
          </div>
        </div>
        <div className="text-center py-2">
          <p className="text-blue-100 text-[10px] font-black uppercase tracking-[0.2em] mb-2 opacity-70">বর্তমান ব্যালেন্স</p>
          <h2 className="text-4xl font-black mb-8 tracking-tight">{formatCurrency(summary.balance)}</h2>
          <div className="grid grid-cols-2 gap-4">
            <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10">
              <p className="text-[10px] uppercase font-black text-blue-200 mb-1">মোট আয়</p>
              <p className="text-lg font-black">৳{summary.income}</p>
            </div>
            <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10">
              <p className="text-[10px] uppercase font-black text-blue-200 mb-1">মোট ব্যয়</p>
              <p className="text-lg font-black">৳{summary.expense}</p>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content Area */}
      <main className="flex-1 overflow-y-auto custom-scrollbar">
        {activeTab === 'home' ? (
          <div className="px-6 py-6 space-y-8 animate-fadeIn">
            <div className="grid grid-cols-2 gap-4">
              <button onClick={() => setModalType(TransactionType.INCOME)} className="p-5 bg-white border border-slate-100 rounded-[32px] shadow-sm flex flex-col items-center gap-3">
                <div className="w-12 h-12 bg-green-500 text-white rounded-2xl flex items-center justify-center text-xl shadow-lg shadow-green-200"><i className="fas fa-plus"></i></div>
                <span className="text-[11px] font-black text-slate-500 uppercase">আয় যোগ</span>
              </button>
              <button onClick={() => setModalType(TransactionType.EXPENSE)} className="p-5 bg-white border border-slate-100 rounded-[32px] shadow-sm flex flex-col items-center gap-3">
                <div className="w-12 h-12 bg-rose-500 text-white rounded-2xl flex items-center justify-center text-xl shadow-lg shadow-rose-200"><i className="fas fa-minus"></i></div>
                <span className="text-[11px] font-black text-slate-500 uppercase">ব্যয় যোগ</span>
              </button>
            </div>

            <div className="space-y-4">
              <div className="flex justify-between items-center px-1">
                <h3 className="font-black text-slate-800 text-xs uppercase tracking-widest">সাম্প্রতিক লেনদেন</h3>
                <button onClick={startNewMonth} className="text-[9px] font-black text-blue-600 bg-blue-50 px-3 py-1.5 rounded-full uppercase">নতুন মাস শুরু</button>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3">
                  {transactions.map(t => (
                    <div key={t.id} className="flex items-center justify-between p-4 bg-white border border-slate-100 rounded-2xl">
                      <div className="flex items-center gap-4">
                        <div className={`w-10 h-10 rounded-xl flex items-center justify-center ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-500' : 'bg-rose-50 text-rose-500'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-bold text-slate-800 text-sm">{t.category}</p>
                          <p className="text-[10px] text-slate-400 font-bold uppercase">{t.note || 'নোট নেই'}</p>
                        </div>
                      </div>
                      <p className={`font-black text-sm ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                        {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                      </p>
                    </div>
                  ))}
                </div>
              ) : <div className="text-center py-10 bg-white rounded-3xl border border-dashed border-slate-200 font-bold text-slate-300 text-[10px] uppercase tracking-widest">খালি</div>}
            </div>
          </div>
        ) : activeTab === 'goals' ? (
          <div className="px-6 py-6 space-y-8 animate-fadeIn">
            <div className="flex justify-between items-center px-1">
              <h3 className="font-black text-slate-800 text-xs uppercase tracking-widest">আমার সঞ্চয় লক্ষ্য</h3>
              <button onClick={() => setShowGoalModal(true)} className="w-8 h-8 bg-indigo-600 text-white rounded-full flex items-center justify-center shadow-lg shadow-indigo-200"><i className="fas fa-plus"></i></button>
            </div>
            <div className="grid gap-4">
              {goals.map(goal => {
                const percent = Math.min((goal.current / goal.target) * 100, 100);
                return (
                  <div key={goal.id} className="bg-white p-6 rounded-[32px] border border-slate-100 shadow-sm space-y-4">
                    <div className="flex justify-between items-start">
                      <div className="flex items-center gap-4">
                        <div className="w-12 h-12 bg-indigo-50 text-indigo-600 rounded-2xl flex items-center justify-center text-xl"><i className={`fas ${goal.icon}`}></i></div>
                        <div>
                          <h4 className="font-black text-slate-800 text-sm uppercase">{goal.name}</h4>
                          <p className="text-[10px] font-bold text-slate-400">লক্ষ্য: ৳{goal.target}</p>
                        </div>
                      </div>
                      <div className="text-right">
                         <p className="text-lg font-black text-indigo-600 leading-none">৳{goal.current}</p>
                         <p className="text-[9px] font-black text-slate-300 uppercase mt-1">জমানো হয়েছে</p>
                      </div>
                    </div>
                    <div className="space-y-2">
                       <div className="w-full h-2 bg-slate-100 rounded-full overflow-hidden">
                          <div className="h-full bg-indigo-600 transition-all duration-1000" style={{ width: `${percent}%` }}></div>
                       </div>
                       <div className="flex justify-between text-[9px] font-black uppercase text-slate-400">
                          <span>প্রগতি</span>
                          <span className="text-indigo-600">{percent.toFixed(0)}%</span>
                       </div>
                    </div>
                    <button onClick={() => addToGoal(goal.id)} className="w-full py-3 bg-indigo-50 text-indigo-600 rounded-2xl text-[10px] font-black uppercase hover:bg-indigo-100 transition-colors">টাকা যোগ করুন</button>
                  </div>
                );
              })}
            </div>
            {goals.length === 0 && <div className="text-center py-20 text-slate-300 font-bold text-[10px] uppercase">কোনো লক্ষ্য নেই</div>}
          </div>
        ) : (
          <div className="px-6 py-6 space-y-8 animate-fadeIn">
            <div className="p-8 bg-slate-900 rounded-[40px] text-white relative overflow-hidden shadow-2xl">
              <h3 className="text-xl font-black mb-1 uppercase tracking-tight">আর্থিক স্কোর</h3>
              <p className="text-xs text-slate-400 font-bold mb-6">আপনার সঞ্চয় ও আয়ের অনুপাত</p>
              <div className="text-6xl font-black text-blue-400">{healthScore}</div>
            </div>
            <div className="bg-white p-6 rounded-[32px] border border-slate-100 shadow-sm space-y-6">
              <h3 className="font-black text-slate-800 uppercase text-xs tracking-widest">বিল স্প্লিটার</h3>
              <div className="grid grid-cols-2 gap-4">
                <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ ০" className="w-full p-3 bg-slate-50 border-2 border-slate-100 rounded-xl font-black outline-none focus:border-indigo-400" />
                <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} className="w-full p-3 bg-slate-50 border-2 border-slate-100 rounded-xl font-black outline-none focus:border-indigo-400" />
              </div>
              <div className="p-4 bg-indigo-50 rounded-2xl text-center">
                <p className="text-[10px] font-black text-indigo-400 uppercase mb-1">প্রতি জনের ভাগ</p>
                <p className="text-2xl font-black text-indigo-600">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
              </div>
            </div>
          </div>
        )}
      </main>

      {/* Bottom Navigation */}
      <nav className="fixed bottom-0 left-1/2 -translate-x-1/2 w-full max-w-md bg-white border-t border-slate-100 px-8 py-4 flex justify-between items-center z-[100] shadow-[0_-10px_30px_-15px_rgba(0,0,0,0.1)]">
        <button onClick={() => setActiveTab('home')} className={`flex flex-col items-center gap-1 transition-all ${activeTab === 'home' ? 'text-blue-600 scale-110' : 'text-slate-300'}`}>
          <i className="fas fa-home text-xl"></i><span className="text-[9px] font-black uppercase">হোম</span>
        </button>
        <button onClick={() => setActiveTab('goals')} className={`flex flex-col items-center gap-1 transition-all ${activeTab === 'goals' ? 'text-indigo-600 scale-110' : 'text-slate-300'}`}>
          <i className="fas fa-bullseye text-xl"></i><span className="text-[9px] font-black uppercase">লক্ষ্য</span>
        </button>
        <button onClick={() => setActiveTab('tools')} className={`flex flex-col items-center gap-1 transition-all ${activeTab === 'tools' ? 'text-blue-600 scale-110' : 'text-slate-300'}`}>
          <i className="fas fa-shapes text-xl"></i><span className="text-[9px] font-black uppercase">টুলস</span>
        </button>
      </nav>

      {/* Goal Modal */}
      {showGoalModal && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[40px] p-8 animate-slideUp">
            <div className="flex justify-between items-center mb-8">
              <h2 className="text-lg font-black uppercase">নতুন লক্ষ্য যোগ</h2>
              <button onClick={() => setShowGoalModal(false)} className="w-10 h-10 bg-slate-100 rounded-full"><i className="fas fa-times"></i></button>
            </div>
            <form onSubmit={addGoal} className="space-y-6">
              <input type="text" name="name" required placeholder="লক্ষ্যের নাম (যেমন: ঘুরতে যাওয়া)" className="w-full p-4 bg-slate-50 border-2 border-slate-100 rounded-2xl font-bold outline-none" />
              <input type="number" name="target" required placeholder="কত টাকা লাগবে?" className="w-full p-4 bg-slate-50 border-2 border-slate-100 rounded-2xl font-bold outline-none" />
              <button type="submit" className="w-full py-5 bg-indigo-600 rounded-3xl text-white font-black text-lg">সংরক্ষণ করুন</button>
            </form>
          </div>
        </div>
      )}

      {/* Other Modals (Transaction/History/Budget) - Condensed for brevity */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[40px] p-8 animate-slideUp">
            <div className="flex justify-between items-center mb-8">
              <h2 className="text-lg font-black uppercase">{modalType === TransactionType.INCOME ? 'আয়' : 'ব্যয়'} যোগ</h2>
              <button onClick={() => setModalType(null)} className="w-10 h-10 bg-slate-100 rounded-full"><i className="fas fa-times"></i></button>
            </div>
            <form onSubmit={addTransaction} className="space-y-6">
              <input type="number" name="amount" required placeholder="৳ ০.০০" autoFocus className="w-full py-6 text-4xl font-black text-center outline-none border-b-4 border-slate-100 focus:border-blue-500" />
              <select name="category" className="w-full p-4 bg-slate-50 border-2 border-slate-100 rounded-2xl font-bold outline-none">
                {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
              </select>
              <button type="submit" className={`w-full py-5 rounded-3xl text-white font-black text-lg ${modalType === TransactionType.INCOME ? 'bg-green-600' : 'bg-rose-600'}`}>ঠিক আছে</button>
            </form>
          </div>
        </div>
      )}

      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-md z-[200] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[40px] p-8 overflow-y-auto animate-slideUp">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-xl font-black uppercase">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-10 h-10 bg-slate-100 rounded-full"><i className="fas fa-times"></i></button>
             </div>
             {archives.map(arc => (
               <div key={arc.id} className="mb-4 p-5 bg-slate-50 rounded-3xl border border-slate-100 flex justify-between items-center font-black">
                  <div>
                     <p className="text-[10px] text-slate-400 uppercase">{arc.monthName}</p>
                     <p className="text-slate-800">৳{arc.totalIncome - arc.totalExpense}</p>
                  </div>
                  <i className="fas fa-chevron-right text-slate-200"></i>
               </div>
             ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

interface Goal {
  id: string;
  name: string;
  target: number;
  current: number;
  icon: string;
}

const App: React.FC = () => {
  // Navigation Tabs
  const [activeTab, setActiveTab] = useState<'home' | 'goals' | 'tools'>('home');

  // Profile Image State
  const [profileImage, setProfileImage] = useState<string | null>(() => {
    return localStorage.getItem('spark_profile') || null;
  });

  // Current Month Transactions
  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  // Goals State
  const [goals, setGoals] = useState<Goal[]>(() => {
    const saved = localStorage.getItem('spark_goals');
    return saved ? JSON.parse(saved) : [
      { id: '1', name: 'জরুরি তহবিল', target: 50000, current: 12000, icon: 'fa-shield-halved' },
      { id: '2', name: 'নতুন ল্যাপটপ', target: 80000, current: 5000, icon: 'fa-laptop' }
    ];
  });

  // Past Monthly Archives
  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  // Budgets State
  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showGoalModal, setShowGoalModal] = useState(false);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);
  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingInsights, setLoadingInsights] = useState(false);

  // Bill Splitter State
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  // Persistence
  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    localStorage.setItem('spark_goals', JSON.stringify(goals));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, goals, profileImage]);

  // Current Summary
  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  // Financial Health Score
  const healthScore = useMemo(() => {
    if (summary.income === 0) return 0;
    const ratio = (summary.balance / summary.income) * 100;
    return Math.max(0, Math.min(100, Math.round(ratio)));
  }, [summary]);

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setProfileImage(reader.result as string);
      reader.readAsDataURL(file);
    }
  };

  const addTransaction = (e: any) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount) return;
    const newTx: Transaction = {
      id: Date.now().toString(),
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: formData.get('note') as string,
      date: new Date().toISOString()
    };
    setTransactions([newTx, ...transactions]);
    setModalType(null);
  };

  const addGoal = (e: any) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const target = parseFloat(formData.get('target') as string);
    if (!target) return;
    const newGoal: Goal = {
      id: Date.now().toString(),
      name: formData.get('name') as string,
      target,
      current: 0,
      icon: 'fa-star'
    };
    setGoals([...goals, newGoal]);
    setShowGoalModal(false);
  };

  const deleteGoal = (id: string) => {
    if (window.confirm("আপনি কি নিশ্চিতভাবে এই লক্ষ্যটি মুছে ফেলতে চান?")) {
      setGoals(goals.filter(g => g.id !== id));
    }
  };

  const addToGoal = (id: string) => {
    const amountStr = prompt("এই লক্ষ্যে কত টাকা জমা করতে চান?");
    const amount = parseFloat(amountStr || '0');
    if (amount > 0) {
      setGoals(goals.map(g => g.id === id ? { ...g, current: g.current + amount } : g));
    }
  };

  const startNewMonth = () => {
    if (transactions.length === 0) return alert("লেনদেন নেই।");
    if (window.confirm('ইতিহাসে জমা করবেন?')) {
      const now = new Date();
      const newArchive: MonthlyArchive = {
        id: Date.now().toString(),
        monthName: now.toLocaleString('bn-BD', { month: 'long' }),
        year: now.getFullYear(),
        totalIncome: summary.income,
        totalExpense: summary.expense,
        transactions: [...transactions]
      };
      setArchives([newArchive, ...archives]);
      setTransactions([]);
    }
  };

  return (
    <div className="max-w-md mx-auto min-h-screen bg-slate-50 shadow-2xl flex flex-col font-sans relative pb-20 overflow-hidden">
      {/* Header */}
      <header className="bg-gradient-to-br from-indigo-700 via-blue-600 to-blue-700 p-8 text-white rounded-b-[40px] shadow-xl relative z-20">
        <div className="flex justify-between items-center mb-8">
          <div className="flex items-center gap-3">
             <div className="w-10 h-10 bg-white/20 rounded-xl flex items-center justify-center relative overflow-hidden group border border-white/30 cursor-pointer">
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
