                <input type="file" accept="image/*" onChange={handleImageUpload} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div>
                <h1 className="text-lg font-black tracking-tight uppercase leading-none">স্পার্ক</h1>
                <p className="text-[10px] font-bold text-blue-200 uppercase mt-0.5 tracking-widest">স্মার্ট ফিন্যান্স</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-9 h-9 bg-white/10 rounded-full flex items-center justify-center transition-all hover:bg-white/20"><i className="fas fa-bullseye text-sm"></i></button>
            <button onClick={() => setShowHistory(true)} className="w-9 h-9 bg-white/10 rounded-full flex items-center justify-center transition-all hover:bg-white/20"><i className="fas fa-history text-sm"></i></button>
          </div>
        </div>
        <div className="text-center py-2">
          <p className="text-blue-100 text-[10px] font-black uppercase tracking-[0.2em] mb-2 opacity-70">বর্তমান ব্যালেন্স</p>
          <h2 className="text-4xl font-black mb-8 tracking-tight">{formatCurrency(summary.balance)}</h2>
          <div className="grid grid-cols-2 gap-4">
            <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10">
              <p className="text-[10px] uppercase font-black text-blue-200 mb-1">মোট আয়</p>
              <p className="text-lg font-black">৳{summary.income}</p>
            </div>
            <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10">
              <p className="text-[10px] uppercase font-black text-blue-200 mb-1">মোট ব্যয়</p>
              <p className="text-lg font-black">৳{summary.expense}</p>
            </div>
          </div>
