      {/* Transaction Modal */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[40px] p-8 animate-slideUp shadow-2xl">
            <div className="flex justify-between items-center mb-8">
              <h2 className="text-lg font-black uppercase text-slate-900">{modalType === TransactionType.INCOME ? 'আয়' : 'ব্যয়'} যোগ</h2>
              <button onClick={() => setModalType(null)} className="w-10 h-10 bg-slate-100 rounded-full flex items-center justify-center text-slate-900"><i className="fas fa-times"></i></button>
            </div>
            <form onSubmit={addTransaction} className="space-y-6">
              <input type="number" name="amount" required placeholder="৳ ০.০০" autoFocus className="w-full py-6 text-4xl font-black text-center outline-none border-b-4 border-slate-100 focus:border-blue-500 text-slate-900" />
              <select name="category" className="w-full p-4 bg-slate-50 border-2 border-slate-100 rounded-2xl font-bold outline-none text-slate-900">
                {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
              </select>
              <button type="submit" className={`w-full py-5 rounded-3xl text-white font-black text-lg shadow-lg active:scale-95 transition-transform ${modalType === TransactionType.INCOME ? 'bg-green-600' : 'bg-rose-600'}`}>ঠিক আছে</button>
            </form>
          </div>
        </div>
      )}

      {/* History Modal - Updated with Black Text */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-md z-[200] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[40px] p-8 overflow-y-auto animate-slideUp shadow-2xl">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-xl font-black uppercase text-slate-900">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-10 h-10 bg-slate-100 rounded-full flex items-center justify-center text-slate-900 transition-all hover:bg-slate-200"><i className="fas fa-times"></i></button>
             </div>
             {archives.length > 0 ? archives.map(arc => (
               <div key={arc.id} className="mb-4 p-5 bg-slate-50 rounded-3xl border border-slate-100 flex justify-between items-center font-black shadow-sm">
                  <div className="space-y-1">
                     <p className="text-[10px] text-slate-600 uppercase font-bold tracking-wider">{arc.monthName} {arc.year}</p>
                     <p className="text-slate-900 text-lg">৳{arc.totalIncome - arc.totalExpense}</p>
                     <div className="flex gap-4 text-[10px] font-bold uppercase">
                        <span className="text-green-600">আয়: ৳{arc.totalIncome}</span>
                        <span className="text-rose-600">ব্যয়: ৳{arc.totalExpense}</span>
                     </div>
