              <button onClick={() => setShowGoalModal(true)} className="w-8 h-8 bg-indigo-600 text-white rounded-full flex items-center justify-center shadow-lg shadow-indigo-200 active:scale-90 transition-transform"><i className="fas fa-plus"></i></button>
            </div>
            <div className="grid gap-4">
              {goals.map(goal => {
                const percent = Math.min((goal.current / goal.target) * 100, 100);
