        ) : (
          <div className="px-6 py-6 space-y-8 animate-fadeIn">
            <div className="p-8 bg-slate-900 rounded-[40px] text-white relative overflow-hidden shadow-2xl">
              <h3 className="text-xl font-black mb-1 uppercase tracking-tight">আর্থিক স্কোর</h3>
              <p className="text-xs text-slate-300 font-bold mb-6">সঞ্চয় বনাম আয়ের অনুপাত</p>
              <div className="text-7xl font-black text-blue-400">{healthScore}</div>
              <div className="absolute top-0 right-0 w-32 h-32 bg-blue-500/10 rounded-full blur-2xl"></div>
            </div>

            <div className="bg-white p-6 rounded-[32px] border border-slate-100 shadow-sm space-y-6">
              <div className="flex items-center gap-2 mb-2">
                <i className="fas fa-calculator text-indigo-600"></i>
                <h3 className="font-black text-slate-900 uppercase text-xs tracking-widest">বিল স্প্লিটার (Bill Splitter)</h3>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-1">
                  <label className="text-[10px] font-black text-slate-900 uppercase ml-1">মোট বিল</label>
                  <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ ০" className="w-full p-4 bg-slate-50 border-2 border-slate-100 rounded-2xl font-black text-slate-900 outline-none focus:border-indigo-400" />
                </div>
                <div className="space-y-1">
                  <label className="text-[10px] font-black text-slate-900 uppercase ml-1">জনসংখ্যা</label>
                  <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} className="w-full p-4 bg-slate-50 border-2 border-slate-100 rounded-2xl font-black text-slate-900 outline-none focus:border-indigo-400" />
                </div>
              </div>
              <div className="p-6 bg-indigo-50 rounded-[32px] text-center">
                <p className="text-[10px] font-black text-indigo-400 uppercase mb-1">প্রতি জনের ভাগ</p>
                <p className="text-3xl font-black text-indigo-600">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
              </div>
            </div>
          </div>
        )}
      </main>

      {/* Bottom Navigation */}
      <nav className="fixed bottom-0 left-1/2 -translate-x-1/2 w-full max-w-md bg-white border-t border-slate-100 px-8 py-4 flex justify-between items-center z-[100] shadow-[0_-10px_30px_-15px_rgba(0,0,0,0.1)]">
        <button onClick={() => setActiveTab('home')} className={`flex flex-col items-center gap-1 transition-all ${activeTab === 'home' ? 'text-blue-600 scale-110' : 'text-slate-400 hover:text-slate-600'}`}>
          <i className="fas fa-home text-xl"></i><span className="text-[9px] font-black uppercase">হোম</span>
        </button>
        <button onClick={() => setActiveTab('goals')} className={`flex flex-col items-center gap-1 transition-all ${activeTab === 'goals' ? 'text-indigo-600 scale-110' : 'text-slate-400 hover:text-slate-600'}`}>
          <i className="fas fa-bullseye text-xl"></i><span className="text-[9px] font-black uppercase">লক্ষ্য</span>
        </button>
        <button onClick={() => setActiveTab('tools')} className={`flex flex-col items-center gap-1 transition-all ${activeTab === 'tools' ? 'text-blue-600 scale-110' : 'text-slate-400 hover:text-slate-600'}`}>
          <i className="fas fa-shapes text-xl"></i><span className="text-[9px] font-black uppercase">টুলস</span>
        </button>
      </nav>

      {/* Goal Modal */}
      {showGoalModal && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[40px] p-8 animate-slideUp shadow-2xl">
            <div className="flex justify-between items-center mb-8">
              <h2 className="text-lg font-black uppercase text-slate-900">নতুন লক্ষ্য যোগ</h2>
              <button onClick={() => setShowGoalModal(false)} className="w-10 h-10 bg-slate-100 rounded-full flex items-center justify-center text-slate-900"><i className="fas fa-times"></i></button>
            </div>
            <form onSubmit={addGoal} className="space-y-6">
              <input type="text" name="name" required placeholder="লক্ষ্যের নাম (যেমন: ঘুরতে যাওয়া)" className="w-full p-4 bg-slate-50 border-2 border-slate-100 rounded-2xl font-bold outline-none text-slate-900 focus:border-indigo-500" />
              <input type="number" name="target" required placeholder="কত টাকা লাগবে?" className="w-full p-4 bg-slate-50 border-2 border-slate-100 rounded-2xl font-bold outline-none text-slate-900 focus:border-indigo-500" />
              <button type="submit" className="w-full py-5 bg-indigo-600 rounded-3xl text-white font-black text-lg shadow-lg active:scale-95 transition-transform">সংরক্ষণ করুন</button>
            </form>
          </div>
        </div>
      )}

      {/* Transaction Modal */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[40px] p-8 animate-slideUp shadow-2xl">
            <div className="flex justify-between items-center mb-8">
              <h2 className="text-lg font-black uppercase text-slate-900">{modalType === TransactionType.INCOME ? 'আয়' : 'ব্যয়'} যোগ</h2>
              <button onClick={() => setModalType(null)} className="w-10 h-10 bg-slate-100 rounded-full flex items-center justify-center text-slate-900"><i className="fas fa-times"></i></button>
            </div>
            <form onSubmit={addTransaction} className="space-y-6">
              <input type="number" name="amount" required placeholder="৳ ০.০০" autoFocus className="w-full py-6 text-4xl font-black text-center outline-none border-b-4 border-slate-100 focus:border-blue-500 text-slate-900" />
              <select name="category" className="w-full p-4 bg-slate-50 border-2 border-slate-100 rounded-2xl font-bold outline-none text-slate-900">
                {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
              </select>
              <button type="submit" className={`w-full py-5 rounded-3xl text-white font-black text-lg shadow-lg active:scale-95 transition-transform ${modalType === TransactionType.INCOME ? 'bg-green-600' : 'bg-rose-600'}`}>ঠিক আছে</button>
            </form>
          </div>
        </div>
      )}

      {/* History Modal - Updated with Black Text */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-md z-[200] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[40px] p-8 overflow-y-auto animate-slideUp shadow-2xl">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-xl font-black uppercase text-slate-900">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-10 h-10 bg-slate-100 rounded-full flex items-center justify-center text-slate-900 transition-all hover:bg-slate-200"><i className="fas fa-times"></i></button>
             </div>
             {archives.length > 0 ? archives.map(arc => (
               <div key={arc.id} className="mb-4 p-5 bg-slate-50 rounded-3xl border border-slate-100 flex justify-between items-center font-black shadow-sm">
                  <div className="space-y-1">
                     <p className="text-[11px] text-slate-900 uppercase font-black tracking-wider">{arc.monthName} {arc.year}</p>
                     <p className="text-slate-900 text-xl font-black">৳{arc.totalIncome - arc.totalExpense}</p>
                     <div className="flex gap-4 text-[10px] font-black uppercase mt-1">
                        <span className="text-green-600">আয়: ৳{arc.totalIncome}</span>
                        <span className="text-rose-600">ব্যয়: ৳{arc.totalExpense}</span>
                     </div>
                  </div>
                  <div className="w-10 h-10 bg-slate-100 rounded-full flex items-center justify-center">
                    <i className="fas fa-chevron-right text-slate-900"></i>
                  </div>
               </div>
             )) : (
               <div className="text-center py-20">
                  <i className="fas fa-folder-open text-slate-200 text-5xl mb-4"></i>
                  <p className="text-slate-900 font-black uppercase text-xs">কোনো ইতিহাস পাওয়া যায়নি</p>
               </div>
             )}
          </div>
        </div>
      )}

      {/* Budget Settings Modal */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-md z-[200] flex items-center justify-center p-4">
          <div className="bg-white w-full max-w-md rounded-[40px] overflow-hidden animate-slideUp shadow-2xl">
            <div className="p-6 bg-blue-600 text-white flex justify-between items-center">
               <h2 className="text-sm font-black uppercase">বাজেট সেটিংস</h2>
               <button onClick={() => setShowBudgetSettings(false)} className="w-8 h-8 flex items-center justify-center bg-white/10 rounded-full"><i className="fas fa-times"></i></button>
            </div>
            <div className="p-6 space-y-4 max-h-[60vh] overflow-y-auto custom-scrollbar">
              {EXPENSE_CATEGORIES.map(cat => (
                <div key={cat} className="space-y-1">
                  <label className="text-[10px] font-black text-slate-900 uppercase tracking-widest ml-1">{cat}</label>
                  <input 
                    type="number" 
                    value={budgets[cat] || ''}
                    onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })}
                    placeholder="৳ ০"
                    className="w-full p-4 bg-slate-50 border-2 border-slate-100 rounded-2xl font-black text-slate-900 outline-none focus:border-blue-500"
                  />
                </div>
              ))}
            </div>
            <div className="p-6 pt-0">
               <button onClick={() => setShowBudgetSettings(false)} className="w-full py-4 bg-slate-900 text-white rounded-2xl font-black uppercase text-xs shadow-lg active:scale-95 transition-transform">সংরক্ষণ করুন</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

interface Goal {
  id: string;
  name: string;
  target: number;
  current: number;
  icon: string;
}

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'goals' | 'tools'>('home');
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [goals, setGoals] = useState<Goal[]>(() => {
    const saved = localStorage.getItem('spark_goals');
    return saved ? JSON.parse(saved) : [
      { id: '1', name: 'জরুরি তহবিল', target: 50000, current: 12000, icon: 'fa-shield-halved' },
      { id: '2', name: 'নতুন ল্যাপটপ', target: 80000, current: 5000, icon: 'fa-laptop' }
    ];
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showGoalModal, setShowGoalModal] = useState(false);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);

  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    localStorage.setItem('spark_goals', JSON.stringify(goals));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, goals, profileImage]);

  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  const healthScore = useMemo(() => {
    if (summary.income === 0) return 0;
    const ratio = (summary.balance / summary.income) * 100;
    return Math.max(0, Math.min(100, Math.round(ratio)));
  }, [summary]);

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount) return;
    const newTx: Transaction = {
      id: Date.now().toString(),
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: formData.get('note') as string,
      date: new Date().toISOString()
    };
    setTransactions([newTx, ...transactions]);
    setModalType(null);
  };

  const addGoal = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const target = parseFloat(formData.get('target') as string);
    if (!target) return;
    const newGoal: Goal = {
      id: Date.now().toString(),
      name: formData.get('name') as string,
      target,
      current: 0,
      icon: 'fa-star'
    };
    setGoals([...goals, newGoal]);
    setShowGoalModal(false);
  };

  const deleteGoal = (id: string) => {
    const confirmDelete = window.confirm("আপনি কি নিশ্চিতভাবে এই লক্ষ্যটি মুছে ফেলতে চান?");
    if (confirmDelete) {
      const updatedGoals = goals.filter(g => g.id !== id);
      setGoals(updatedGoals);
    }
  };

  const addToGoal = (id: string) => {
    const amountStr = window.prompt("এই লক্ষ্যে কত টাকা জমা করতে চান?");
    if (amountStr === null) return;
    const amount = parseFloat(amountStr);
    if (isNaN(amount) || amount <= 0) {
      alert("দয়া করে সঠিক অংক লিখুন।");
      return;
    }
    const updatedGoals = goals.map(g => 
      g.id === id ? { ...g, current: g.current + amount } : g
    );
    setGoals(updatedGoals);
  };

  return (
    <div className="max-w-md mx-auto min-h-screen bg-slate-50 shadow-2xl flex flex-col font-sans relative pb-20 overflow-hidden">
      {/* Header */}
      <header className="bg-gradient-to-br from-indigo-700 via-blue-600 to-blue-700 p-8 text-white rounded-b-[40px] shadow-xl relative z-20">
        <div className="flex justify-between items-center mb-8">
          <div className="flex items-center gap-3">
             <div className="w-10 h-10 bg-white/20 rounded-xl flex items-center justify-center relative overflow-hidden border border-white/30 cursor-pointer">
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
                ) : (
                  <i className="fas fa-camera text-blue-100 opacity-50"></i>
                )}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div>
                <h1 className="text-lg font-black tracking-tight uppercase leading-none">স্পার্ক</h1>
                <p className="text-[10px] font-bold text-blue-200 mt-1 uppercase tracking-widest">স্মার্ট ফিন্যান্স</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-9 h-9 bg-white/10 rounded-full flex items-center justify-center hover:bg-white/20 transition-all"><i className="fas fa-bullseye"></i></button>
            <button onClick={() => setShowHistory(true)} className="w-9 h-9 bg-white/10 rounded-full flex items-center justify-center hover:bg-white/20 transition-all"><i className="fas fa-history"></i></button>
          </div>
        </div>
        <div className="text-center">
          <p className="text-blue-100 text-[10px] font-black uppercase tracking-[0.2em] mb-2 opacity-70">ব্যালেন্স</p>
          <h2 className="text-4xl font-black mb-8 tracking-tight">{formatCurrency(summary.balance)}</h2>
          <div className="grid grid-cols-2 gap-4">
            <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10">
              <p className="text-[10px] uppercase font-black text-blue-200 mb-1">মোট আয়</p>
              <p className="text-lg font-black">৳{summary.income}</p>
            </div>
            <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10">
              <p className="text-[10px] uppercase font-black text-blue-200 mb-1">মোট ব্যয়</p>
              <p className="text-lg font-black">৳{summary.expense}</p>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-1 overflow-y-auto custom-scrollbar">
        {activeTab === 'home' ? (
          <div className="px-6 py-6 space-y-8 animate-fadeIn">
            <div className="grid grid-cols-2 gap-4">
              <button onClick={() => setModalType(TransactionType.INCOME)} className="p-5 bg-white border border-slate-100 rounded-[32px] shadow-sm flex flex-col items-center gap-3 active:scale-95 transition-transform">
                <div className="w-12 h-12 bg-green-500 text-white rounded-2xl flex items-center justify-center text-xl shadow-lg shadow-green-200"><i className="fas fa-plus"></i></div>
                <span className="text-[11px] font-black text-slate-900 uppercase">আয় যোগ</span>
              </button>
              <button onClick={() => setModalType(TransactionType.EXPENSE)} className="p-5 bg-white border border-slate-100 rounded-[32px] shadow-sm flex flex-col items-center gap-3 active:scale-95 transition-transform">
                <div className="w-12 h-12 bg-rose-500 text-white rounded-2xl flex items-center justify-center text-xl shadow-lg shadow-rose-200"><i className="fas fa-minus"></i></div>
                <span className="text-[11px] font-black text-slate-900 uppercase">ব্যয় যোগ</span>
              </button>
            </div>

            <div className="space-y-4">
              <div className="flex justify-between items-center">
                <h3 className="font-black text-slate-900 text-xs uppercase tracking-widest">সাম্প্রতিক লেনদেন</h3>
                <button onClick={() => {
                  if (transactions.length === 0) return alert("লেনদেন নেই।");
                  if (window.confirm('ইতিহাসে জমা করবেন?')) {
                    const now = new Date();
                    const newArchive: MonthlyArchive = {
                      id: Date.now().toString(),
                      monthName: now.toLocaleString('bn-BD', { month: 'long' }),
                      year: now.getFullYear(),
                      totalIncome: summary.income,
                      totalExpense: summary.expense,
                      transactions: [...transactions]
                    };
                    setArchives([newArchive, ...archives]);
                    setTransactions([]);
                  }
                }} className="text-[9px] font-black text-blue-600 bg-blue-50 px-3 py-1.5 rounded-full uppercase">নতুন মাস শুরু</button>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3">
                  {transactions.map(t => (
                    <div key={t.id} className="flex items-center justify-between p-4 bg-white border border-slate-100 rounded-2xl">
                      <div className="flex items-center gap-4">
                        <div className={`w-10 h-10 rounded-xl flex items-center justify-center ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-500' : 'bg-rose-50 text-rose-500'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-bold text-slate-900 text-sm">{t.category}</p>
                          <p className="text-[10px] text-slate-500 font-bold uppercase">{t.note || 'নোট নেই'}</p>
                        </div>
                      </div>
                      <p className={`font-black text-sm ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-900'}`}>
                        {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                      </p>
                    </div>
                  ))}
                </div>
              ) : <div className="text-center py-10 bg-white rounded-3xl border border-dashed border-slate-200 font-bold text-slate-400 text-[10px] uppercase tracking-widest">খালি</div>}
            </div>
          </div>
        ) : activeTab === 'goals' ? (
          <div className="px-6 py-6 space-y-8 animate-fadeIn">
            <div className="flex justify-between items-center">
              <h3 className="font-black text-slate-900 text-xs uppercase tracking-widest">আমার সঞ্চয় লক্ষ্য</h3>
              <button onClick={() => setShowGoalModal(true)} className="w-9 h-9 bg-indigo-600 text-white rounded-full flex items-center justify-center shadow-lg active:scale-90 transition-transform"><i className="fas fa-plus"></i></button>
            </div>
            <div className="grid gap-6">
              {goals.map(goal => {
                const percent = Math.min((goal.current / goal.target) * 100, 100);
                return (
                  <div key={goal.id} className="bg-white p-6 rounded-[32px] border border-slate-100 shadow-sm space-y-4 relative group">
                    <div className="flex justify-between items-start">
                      <div className="flex items-center gap-4 pr-12">
                        <div className="w-12 h-12 bg-indigo-50 text-indigo-600 rounded-2xl flex items-center justify-center text-xl"><i className={`fas ${goal.icon}`}></i></div>
                        <div>
                          <h4 className="font-black text-slate-900 text-sm uppercase">{goal.name}</h4>
                          <p className="text-[10px] font-bold text-slate-600">লক্ষ্য: ৳{goal.target}</p>
                        </div>
                      </div>
                      <button 
                        onClick={(e) => { e.stopPropagation(); deleteGoal(goal.id); }} 
                        className="absolute top-6 right-6 w-8 h-8 bg-rose-50 text-rose-500 rounded-full flex items-center justify-center hover:bg-rose-100 transition-all z-10"
                      >
                        <i className="fas fa-trash-alt text-xs"></i>
                      </button>
                    </div>
                    <div className="text-right">
                       <p className="text-2xl font-black text-indigo-600 leading-none">৳{goal.current}</p>
                       <p className="text-[9px] font-black text-slate-500 uppercase mt-1">জমানো হয়েছে</p>
                    </div>
                    <div className="space-y-2">
                       <div className="w-full h-3 bg-slate-100 rounded-full overflow-hidden">
                          <div className="h-full bg-indigo-600 transition-all duration-1000" style={{ width: `${percent}%` }}></div>
                       </div>
                       <div className="flex justify-between text-[10px] font-black uppercase">
                          <span className="text-slate-900">প্রগতি</span>
                          <span className="text-indigo-600">{percent.toFixed(0)}%</span>
                       </div>
                    </div>
                    <button onClick={() => addToGoal(goal.id)} className="w-full py-4 bg-indigo-600 text-white rounded-2xl text-[11px] font-black uppercase hover:bg-indigo-700 transition-colors shadow-lg active:scale-95">টাকা জমা করুন</button>
                  </div>
                );
              })}
            </div>
            {goals.length === 0 && <div className="text-center py-20 text-slate-400 font-bold text-[10px] uppercase">কোনো লক্ষ্য নেই</div>}
          </div>
        ) : (
          <div className="px-6 py-6 space-y-8 animate-fadeIn">
            <div className="p-8 bg-slate-900 rounded-[40px] text-white relative overflow-hidden shadow-2xl">
              <h3 className="text-xl font-black mb-1 uppercase tracking-tight">আর্থিক স্কোর</h3>
              <p className="text-xs text-slate-300 font-bold mb-6 tracking-wide">সঞ্চয় বনাম আয়ের অনুপাত</p>
              <div className="text-7xl font-black text-blue-400">{healthScore}</div>
              <div className="absolute top-0 right-0 w-32 h-32 bg-blue-500/10 rounded-full blur-2xl"></div>
            </div>

            <div className="bg-white p-7 rounded-[32px] border border-slate-100 shadow-sm space-y-6">
              <div className="flex items-center gap-2">
                <i className="fas fa-calculator text-indigo-600"></i>
                <h3 className="font-black text-slate-900 uppercase text-xs tracking-widest">বিল স্প্লিটার (Bill Splitter)</h3>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-1">
                  <label className="text-[10px] font-black text-slate-900 uppercase ml-1">মোট বিল</label>
                  <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ ০" className="w-full p-4 bg-slate-50 border-2 border-slate-100 rounded-2xl font-black text-slate-900 outline-none focus:border-indigo-500" />
                </div>
                <div className="space-y-1">
                  <label className="text-[10px] font-black text-slate-900 uppercase ml-1">জনসংখ্যা</label>
                  <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} className="w-full p-4 bg-slate-50 border-2 border-slate-100 rounded-2xl font-black text-slate-900 outline-none focus:border-indigo-500" />
                </div>
              </div>
              <div className="p-6 bg-indigo-50 rounded-[32px] text-center border border-indigo-100">
                <p className="text-[10px] font-black text-indigo-400 uppercase mb-1">প্রতি জনের ভাগ</p>
                <p className="text-3xl font-black text-indigo-600">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
              </div>
            </div>
          </div>
        )}
      </main>

      {/* Bottom Nav */}
      <nav className="fixed bottom-0 left-1/2 -translate-x-1/2 w-full max-w-md bg-white border-t border-slate-100 px-8 py-4 flex justify-between items-center z-[100] shadow-[0_-10px_30px_-15px_rgba(0,0,0,0.1)]">
        <button onClick={() => setActiveTab('home')} className={`flex flex-col items-center gap-1 transition-all ${activeTab === 'home' ? 'text-blue-600 scale-110' : 'text-slate-400'}`}>
          <i className="fas fa-home text-xl"></i><span className="text-[9px] font-black uppercase">হোম</span>
        </button>
        <button onClick={() => setActiveTab('goals')} className={`flex flex-col items-center gap-1 transition-all ${activeTab === 'goals' ? 'text-indigo-600 scale-110' : 'text-slate-400'}`}>
          <i className="fas fa-bullseye text-xl"></i><span className="text-[9px] font-black uppercase">লক্ষ্য</span>
        </button>
        <button onClick={() => setActiveTab('tools')} className={`flex flex-col items-center gap-1 transition-all ${activeTab === 'tools' ? 'text-blue-600 scale-110' : 'text-slate-400'}`}>
          <i className="fas fa-shapes text-xl"></i><span className="text-[9px] font-black uppercase">টুলস</span>
        </button>
      </nav>

      {/* Goal Modal */}
      {showGoalModal && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[40px] p-8 animate-slideUp shadow-2xl">
            <div className="flex justify-between items-center mb-8">
              <h2 className="text-lg font-black uppercase text-slate-900">নতুন সঞ্চয় লক্ষ্য</h2>
              <button onClick={() => setShowGoalModal(false)} className="w-10 h-10 bg-slate-100 rounded-full flex items-center justify-center text-slate-900"><i className="fas fa-times"></i></button>
            </div>
            <form onSubmit={addGoal} className="space-y-6">
              <input type="text" name="name" required placeholder="লক্ষ্যের নাম (যেমন: বাড়ি কেনা)" className="w-full p-4 bg-slate-50 border-2 border-slate-100 rounded-2xl font-bold outline-none text-slate-900 focus:border-indigo-500" />
              <input type="number" name="target" required placeholder="কত টাকা লাগবে?" className="w-full p-4 bg-slate-50 border-2 border-slate-100 rounded-2xl font-bold outline-none text-slate-900 focus:border-indigo-500" />
              <button type="submit" className="w-full py-5 bg-indigo-600 rounded-3xl text-white font-black text-lg shadow-lg active:scale-95 transition-transform">সেভ করুন</button>
            </form>
          </div>
        </div>
      )}

      {/* Transaction Modal - Improved Input Styles */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[40px] p-8 animate-slideUp shadow-2xl">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-lg font-black uppercase text-slate-900">{modalType === TransactionType.INCOME ? 'আয়' : 'ব্যয়'} যোগ করুন</h2>
              <button onClick={() => setModalType(null)} className="w-10 h-10 bg-slate-100 rounded-full flex items-center justify-center text-slate-900"><i className="fas fa-times"></i></button>
            </div>
            <form onSubmit={addTransaction} className="space-y-6">
              <div className="bg-white rounded-3xl p-6 border-2 border-slate-100 focus-within:border-blue-500 transition-all">
                <p className="text-center text-[11px] font-black text-slate-400 uppercase tracking-[0.2em] mb-2">পরিমাণ (৳)</p>
                <input 
                  type="number" 
                  name="amount" 
                  required 
                  placeholder="০" 
                  autoFocus 
                  className="w-full text-5xl font-black text-center outline-none text-slate-900 bg-transparent"
                />
              </div>
              <div className="space-y-4">
                <select name="category" className="w-full p-4 bg-slate-50 border-2 border-slate-100 rounded-2xl font-bold outline-none text-slate-900 focus:border-blue-500">
                  {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                </select>
                <input type="text" name="note" placeholder="নোট (যেমন: শপিং)" className="w-full p-4 bg-slate-50 border-2 border-slate-100 rounded-2xl font-bold outline-none text-slate-900 focus:border-blue-500" />
              </div>
              <button type="submit" className={`w-full py-5 rounded-3xl text-white font-black text-lg shadow-lg active:scale-95 transition-transform ${modalType === TransactionType.INCOME ? 'bg-green-600 hover:bg-green-700' : 'bg-rose-600 hover:bg-rose-700'}`}>নিশ্চিত করুন</button>
            </form>
          </div>
        </div>
      )}

      {/* History Modal - High Contrast Text */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-md z-[200] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[40px] p-8 overflow-y-auto animate-slideUp shadow-2xl">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-xl font-black uppercase text-slate-900">লেনদেনের ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-10 h-10 bg-slate-100 rounded-full flex items-center justify-center text-slate-900 transition-all hover:bg-slate-200"><i className="fas fa-times"></i></button>
             </div>
             {archives.length > 0 ? archives.map(arc => (
               <div key={arc.id} className="mb-6 p-6 bg-slate-50 rounded-[32px] border border-slate-200 flex justify-between items-center shadow-sm">
                  <div className="space-y-1">
                     <p className="text-[12px] text-slate-900 uppercase font-black tracking-widest">{arc.monthName} {arc.year}</p>
                     <p className="text-slate-900 text-2xl font-black">৳{arc.totalIncome - arc.totalExpense}</p>
                     <div className="flex gap-4 text-[10px] font-black uppercase mt-2">
                        <span className="text-green-600 bg-green-50 px-2 py-1 rounded-lg">আয়: ৳{arc.totalIncome}</span>
                        <span className="text-rose-600 bg-rose-50 px-2 py-1 rounded-lg">ব্যয়: ৳{arc.totalExpense}</span>
                     </div>
                  </div>
                  <div className="w-12 h-12 bg-white rounded-2xl flex items-center justify-center shadow-sm border border-slate-100">
                    <i className="fas fa-chevron-right text-slate-900"></i>
                  </div>
               </div>
             )) : (
               <div className="text-center py-20">
                  <i className="fas fa-folder-open text-slate-200 text-6xl mb-4"></i>
                  <p className="text-slate-900 font-black uppercase text-sm tracking-widest">এখনো কোনো ইতিহাস নেই</p>
               </div>
             )}
          </div>
        </div>
      )}

      {/* Budget Settings Modal */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-md z-[200] flex items-center justify-center p-4">
          <div className="bg-white w-full max-w-md rounded-[40px] overflow-hidden animate-slideUp shadow-2xl">
            <div className="p-7 bg-blue-600 text-white flex justify-between items-center">
               <h2 className="text-sm font-black uppercase tracking-widest">মাসিক বাজেট লক্ষ্য</h2>
               <button onClick={() => setShowBudgetSettings(false)} className="w-10 h-10 flex items-center justify-center bg-white/10 rounded-full hover:bg-white/20 transition-all"><i className="fas fa-times"></i></button>
            </div>
            <div className="p-7 space-y-5 max-h-[60vh] overflow-y-auto custom-scrollbar">
              {EXPENSE_CATEGORIES.map(cat => (
                <div key={cat} className="space-y-2">
                  <div className="flex items-center gap-2">
                    <i className={`fas ${CATEGORY_ICONS[cat] || 'fa-tag'} text-blue-600`}></i>
                    <label className="text-[11px] font-black text-slate-900 uppercase tracking-widest">{cat}</label>
                  </div>
                  <input 
                    type="number" 
                    value={budgets[cat] || ''}
                    onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })}
                    placeholder="৳ ০"
                    className="w-full p-4 bg-slate-50 border-2 border-slate-100 rounded-2xl font-black text-slate-900 outline-none focus:border-blue-600"
                  />
                </div>
              ))}
            </div>
            <div className="p-7 pt-0">
               <button onClick={() => setShowBudgetSettings(false)} className="w-full py-5 bg-slate-900 text-white rounded-2xl font-black uppercase text-xs shadow-lg active:scale-95 transition-transform tracking-widest">বাজেট আপডেট করুন</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  // Navigation Tabs: Home, Future, Tools
  const [activeTab, setActiveTab] = useState<'home' | 'future' | 'tools'>('home');
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));

  // Transactions State
  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  // History State
  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  // AI Insights State
  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);

  // Bill Splitter State
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  // Modals
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);

  // Persistence
  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, profileImage]);

  // Financial Summary
  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  // AI Logic for Future Tab
  const fetchAIAdvice = async () => {
    if (transactions.length === 0) {
      alert("পরামর্শ পেতে প্রথমে কিছু লেনদেন যোগ করুন।");
      return;
    }
    setLoadingAI(true);
    const finSummary: FinancialSummary = {
      totalIncome: summary.income,
      totalExpenses: summary.expense,
      balance: summary.balance,
      savings: summary.balance,
      previousMonthSavings: archives[0]?.totalIncome - archives[0]?.totalExpense || 0,
      categoryBreakdown: summary.breakdown,
      monthlyHistory: archives.slice(0, 5).map(a => ({
        month: a.monthName,
        income: a.totalIncome,
        expense: a.totalExpense
      }))
    };
    const results = await getFinancialInsights(transactions, finSummary);
    setInsights(results);
    setLoadingAI(false);
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount) return;
    const newTx: Transaction = {
      id: Date.now().toString(),
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions([newTx, ...transactions]);
    setModalType(null);
  };

  return (
    <div className="max-w-md mx-auto min-h-screen bg-white shadow-2xl flex flex-col font-sans relative pb-24 overflow-hidden">
      {/* Header */}
      <header className="bg-gradient-to-br from-blue-700 via-indigo-600 to-indigo-800 p-8 text-white rounded-b-[40px] shadow-xl relative z-20">
        <div className="flex justify-between items-center mb-8">
          <div className="flex items-center gap-3">
             <div className="w-12 h-12 bg-white/20 rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/30">
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
                ) : (
                  <i className="fas fa-user text-blue-100"></i>
                )}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" title="ছবি পরিবর্তন করুন" />
             </div>
             <div>
                <h1 className="text-xl font-black uppercase leading-none tracking-tight">স্পার্ক</h1>
                <p className="text-[10px] font-black text-blue-200 mt-1 uppercase tracking-widest">স্মার্ট ফিন্যান্স কোচ</p>
             </div>
          </div>
          <button onClick={() => setShowHistory(true)} className="w-10 h-10 bg-white/10 rounded-full flex items-center justify-center hover:bg-white/20 transition-all border border-white/10">
            <i className="fas fa-clock-rotate-left"></i>
          </button>
        </div>
        <div className="text-center">
          <p className="text-blue-100 text-[10px] font-black uppercase tracking-[0.2em] mb-2 opacity-80">বর্তমান ব্যালেন্স</p>
          <h2 className="text-4xl font-black mb-8 tracking-tighter">{formatCurrency(summary.balance)}</h2>
          <div className="grid grid-cols-2 gap-4">
            <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/20">
              <p className="text-[9px] uppercase font-black text-blue-200 mb-1">মোট আয়</p>
              <p className="text-lg font-black tracking-tight">৳{summary.income}</p>
            </div>
            <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/20">
              <p className="text-[9px] uppercase font-black text-blue-200 mb-1">মোট ব্যয়</p>
              <p className="text-lg font-black tracking-tight">৳{summary.expense}</p>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-6">
        {activeTab === 'home' ? (
          <div className="space-y-8 animate-fadeIn">
            <div className="grid grid-cols-2 gap-4">
              <button onClick={() => setModalType(TransactionType.INCOME)} className="p-6 bg-slate-50 border border-slate-100 rounded-[32px] flex flex-col items-center gap-3 active:scale-95 transition-all shadow-sm group hover:bg-white">
                <div className="w-14 h-14 bg-green-500 text-white rounded-3xl flex items-center justify-center text-2xl shadow-lg shadow-green-100 group-hover:scale-110 transition-transform"><i className="fas fa-plus"></i></div>
                <span className="text-[12px] font-black text-slate-900 uppercase tracking-widest">আয় যোগ</span>
              </button>
              <button onClick={() => setModalType(TransactionType.EXPENSE)} className="p-6 bg-slate-50 border border-slate-100 rounded-[32px] flex flex-col items-center gap-3 active:scale-95 transition-all shadow-sm group hover:bg-white">
                <div className="w-14 h-14 bg-rose-500 text-white rounded-3xl flex items-center justify-center text-2xl shadow-lg shadow-rose-100 group-hover:scale-110 transition-transform"><i className="fas fa-minus"></i></div>
                <span className="text-[12px] font-black text-slate-900 uppercase tracking-widest">ব্যয় যোগ</span>
              </button>
            </div>

            <div className="space-y-4">
              <div className="flex justify-between items-center px-1">
                <h3 className="font-black text-slate-900 text-[10px] uppercase tracking-[0.2em]">সাম্প্রতিক লেনদেন</h3>
                <button onClick={() => {
                  if (transactions.length === 0) return alert("লেনদেন নেই।");
                  if (window.confirm('এই মাসের হিসাব ইতিহাসে জমা করবেন?')) {
                    const now = new Date();
                    const newArchive: MonthlyArchive = {
                      id: Date.now().toString(),
                      monthName: now.toLocaleString('bn-BD', { month: 'long' }),
                      year: now.getFullYear(),
                      totalIncome: summary.income,
                      totalExpense: summary.expense,
                      transactions: [...transactions]
                    };
                    setArchives([newArchive, ...archives]);
                    setTransactions([]);
                  }
                }} className="text-[10px] font-black text-blue-600 bg-blue-50 px-4 py-2 rounded-full uppercase transition-colors hover:bg-blue-100">নতুন মাস</button>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3">
                  {transactions.map(t => (
                    <div key={t.id} className="flex items-center justify-between p-5 bg-white border border-slate-100 rounded-[24px] shadow-sm hover:border-blue-100 transition-colors">
                      <div className="flex items-center gap-4">
                        <div className={`w-10 h-10 rounded-2xl flex items-center justify-center text-lg ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-900 text-sm">{t.category}</p>
                          <p className="text-[10px] text-slate-500 font-bold uppercase">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <p className={`font-black text-base tracking-tight ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-900'}`}>
                        {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                      </p>
                    </div>
                  ))}
                </div>
              ) : <div className="text-center py-20 bg-slate-50/50 rounded-[40px] border border-dashed border-slate-200">
                    <i className="fas fa-receipt text-slate-200 text-5xl mb-4"></i>
                    <p className="text-slate-400 font-black uppercase text-[10px] tracking-widest">লেনদেন নেই</p>
                  </div>}
            </div>
          </div>
        ) : activeTab === 'future' ? (
          <div className="space-y-8 animate-fadeIn">
            <div className="p-8 bg-slate-900 rounded-[40px] text-white relative overflow-hidden shadow-2xl">
              <div className="relative z-10">
                <h3 className="text-2xl font-black mb-2 tracking-tight">ভবিষ্যৎ সঞ্চয়</h3>
                <p className="text-xs text-slate-400 font-black uppercase tracking-widest mb-8 opacity-70">এআই এর মাধ্যমে আর্থিক পূর্বাভাস</p>
                <button 
                  onClick={fetchAIAdvice}
                  disabled={loadingAI}
                  className="w-full py-4 bg-indigo-600 text-white rounded-2xl font-black uppercase text-xs shadow-xl active:scale-95 transition-all disabled:opacity-50 flex items-center justify-center gap-3"
                >
                  {loadingAI ? (
                    <><i className="fas fa-circle-notch animate-spin"></i> বিশ্লেষণ করা হচ্ছে...</>
                  ) : (
                    <><i className="fas fa-wand-magic-sparkles"></i> এআই পরামর্শ নিন</>
                  )}
                </button>
              </div>
              <div className="absolute -right-10 -bottom-10 w-48 h-48 bg-indigo-500/20 rounded-full blur-3xl"></div>
            </div>

            {insights.length > 0 ? (
              <div className="space-y-4">
                <h3 className="font-black text-slate-900 text-[10px] uppercase tracking-[0.2em] px-1">আপনার জন্য এআই পরামর্শ</h3>
                {insights.map((insight, idx) => (
                  <div key={idx} className={`p-6 rounded-[32px] border ${insight.type === 'success' ? 'bg-green-50 border-green-100' : insight.type === 'warning' ? 'bg-rose-50 border-rose-100' : 'bg-blue-50 border-blue-100'} animate-fadeIn`}>
                    <div className="flex items-start gap-4">
                      <div className={`w-10 h-10 rounded-2xl flex items-center justify-center flex-shrink-0 ${insight.type === 'success' ? 'bg-green-500 text-white' : insight.type === 'warning' ? 'bg-rose-500 text-white' : 'bg-blue-500 text-white'}`}>
                        <i className={`fas ${insight.type === 'success' ? 'fa-check' : insight.type === 'warning' ? 'fa-triangle-exclamation' : 'fa-lightbulb'}`}></i>
                      </div>
                      <div>
                        <h4 className="font-black text-slate-900 text-sm mb-1">{insight.title}</h4>
                        <p className="text-slate-700 text-xs font-bold leading-relaxed">{insight.description}</p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : !loadingAI && (
              <div className="text-center py-10 px-6 border-2 border-dashed border-slate-100 rounded-[40px]">
                <p className="text-slate-400 font-bold text-xs leading-relaxed uppercase tracking-wide">আপনার আর্থিক ভবিষ্যৎ দেখতে ওপরের বাটনে ক্লিক করুন।</p>
              </div>
