                        </div>
                        <div className="w-10 h-10 bg-white rounded-2xl flex items-center justify-center shadow-sm border border-slate-100">
                          <i className="fas fa-chevron-right text-slate-400"></i>
                        </div>
                      </div>
                      <div className="flex gap-4 pt-4 border-t border-slate-100">
                        <div className="flex-1 text-center">
                          <p className="text-[9px] font-black text-green-600 uppercase mb-1">মোট আয়</p>
                          <p className="text-sm font-black text-slate-900">৳{arc.totalIncome}</p>
                        </div>
                        <div className="flex-1 text-center border-l border-slate-100">
                          <p className="text-[9px] font-black text-rose-600 uppercase mb-1">মোট ব্যয়</p>
                          <p className="text-sm font-black text-slate-900">৳{arc.totalExpense}</p>
                        </div>
                      </div>
                   </div>
                 ))}
               </div>
             ) : (
               <div className="text-center py-20 bg-slate-50 rounded-[40px] border border-dashed border-slate-200 mt-10">
                  <i className="fas fa-box-open text-slate-200 text-6xl mb-4"></i>
                  <p className="text-slate-900 font-black uppercase text-xs tracking-[0.2em]">এখনো কোনো ইতিহাস নেই</p>
               </div>
             )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  // Navigation Tabs: Home, Future, Tools
  const [activeTab, setActiveTab] = useState<'home' | 'future' | 'tools'>('home');
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));

  // Transactions State
  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  // Budgets State
  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  // History State
  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  // AI Insights State
  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);

  // Bill Splitter State
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  // Modals
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);

  // Persistence
  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  // Financial Summary
  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  // AI Logic for Future Tab
  const fetchAIAdvice = async () => {
    if (transactions.length === 0) {
      alert("পরামর্শ পেতে প্রথমে কিছু লেনদেন যোগ করুন।");
      return;
    }
    setLoadingAI(true);
    const finSummary: FinancialSummary = {
      totalIncome: summary.income,
      totalExpenses: summary.expense,
      balance: summary.balance,
      savings: summary.balance,
      previousMonthSavings: archives[0]?.totalIncome - archives[0]?.totalExpense || 0,
      categoryBreakdown: summary.breakdown,
      monthlyHistory: archives.slice(0, 5).map(a => ({
        month: a.monthName,
        income: a.totalIncome,
        expense: a.totalExpense
      }))
    };
    const results = await getFinancialInsights(transactions, finSummary);
    setInsights(results);
    setLoadingAI(false);
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount) return;
    const newTx: Transaction = {
      id: Date.now().toString(),
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions([newTx, ...transactions]);
    setModalType(null);
  };

  const activeBudgetData = useMemo(() => {
    return Object.entries(budgets).filter(([_, amount]) => amount > 0);
  }, [budgets]);

  return (
    <div className="max-w-md mx-auto min-h-screen bg-white shadow-2xl flex flex-col font-sans relative pb-24 overflow-hidden">
      {/* Header */}
      <header className="bg-gradient-to-br from-blue-700 via-indigo-600 to-indigo-800 p-8 text-white rounded-b-[40px] shadow-xl relative z-20">
        <div className="flex justify-between items-center mb-8">
          <div className="flex items-center gap-3">
             <div className="w-12 h-12 bg-white/20 rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/30">
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
                ) : (
                  <i className="fas fa-user text-blue-100"></i>
                )}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" title="ছবি পরিবর্তন করুন" />
             </div>
             <div>
                <h1 className="text-xl font-black uppercase leading-none tracking-tight">স্পার্ক</h1>
                <p className="text-[10px] font-black text-blue-200 mt-1 uppercase tracking-widest">স্মার্ট ফিন্যান্স কোচ</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-10 h-10 bg-white/10 rounded-full flex items-center justify-center hover:bg-white/20 transition-all border border-white/10" title="বাজেট সেট করুন">
              <i className="fas fa-bullseye"></i>
            </button>
            <button onClick={() => setShowHistory(true)} className="w-10 h-10 bg-white/10 rounded-full flex items-center justify-center hover:bg-white/20 transition-all border border-white/10" title="ইতিহাস">
              <i className="fas fa-clock-rotate-left"></i>
            </button>
          </div>
        </div>
        <div className="text-center">
          <p className="text-blue-100 text-[10px] font-black uppercase tracking-[0.2em] mb-2 opacity-80">বর্তমান ব্যালেন্স</p>
          <h2 className="text-4xl font-black mb-8 tracking-tighter">{formatCurrency(summary.balance)}</h2>
          <div className="grid grid-cols-2 gap-4">
            <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/20">
              <p className="text-[9px] uppercase font-black text-blue-200 mb-1">মোট আয়</p>
              <p className="text-lg font-black tracking-tight">৳{summary.income}</p>
            </div>
            <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/20">
              <p className="text-[9px] uppercase font-black text-blue-200 mb-1">মোট ব্যয়</p>
              <p className="text-lg font-black tracking-tight">৳{summary.expense}</p>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-6">
        {activeTab === 'home' ? (
          <div className="space-y-8 animate-fadeIn">
            {/* Quick Actions */}
            <div className="grid grid-cols-2 gap-4">
              <button onClick={() => setModalType(TransactionType.INCOME)} className="p-6 bg-slate-50 border border-slate-100 rounded-[32px] flex flex-col items-center gap-3 active:scale-95 transition-all shadow-sm group hover:bg-white">
                <div className="w-14 h-14 bg-green-500 text-white rounded-3xl flex items-center justify-center text-2xl shadow-lg shadow-green-100 group-hover:scale-110 transition-transform"><i className="fas fa-plus"></i></div>
                <span className="text-[12px] font-black text-slate-900 uppercase tracking-widest">আয় যোগ</span>
              </button>
              <button onClick={() => setModalType(TransactionType.EXPENSE)} className="p-6 bg-slate-50 border border-slate-100 rounded-[32px] flex flex-col items-center gap-3 active:scale-95 transition-all shadow-sm group hover:bg-white">
                <div className="w-14 h-14 bg-rose-500 text-white rounded-3xl flex items-center justify-center text-2xl shadow-lg shadow-rose-100 group-hover:scale-110 transition-transform"><i className="fas fa-minus"></i></div>
                <span className="text-[12px] font-black text-slate-900 uppercase tracking-widest">ব্যয় যোগ</span>
              </button>
            </div>

            {/* Budget Progress Bars */}
            {activeBudgetData.length > 0 && (
              <div className="space-y-4">
                <h3 className="font-black text-slate-900 text-[10px] uppercase tracking-[0.2em] px-1">বাজেট প্রগতি</h3>
                <div className="space-y-4 bg-slate-50 p-6 rounded-[32px] border border-slate-100">
                  {activeBudgetData.map(([cat, budget]) => {
                    const spent = summary.breakdown[cat] || 0;
                    const percent = Math.min((spent / budget) * 100, 100);
                    const isOver = spent > budget;
                    return (
                      <div key={cat} className="space-y-2">
                        <div className="flex justify-between items-center text-[11px] font-black uppercase">
                          <span className="text-slate-900">{cat}</span>
                          <span className={isOver ? 'text-rose-600' : 'text-slate-500'}>
                            ৳{spent} / ৳{budget}
                          </span>
                        </div>
                        <div className="w-full h-3 bg-slate-200 rounded-full overflow-hidden">
                          <div 
                            className={`h-full transition-all duration-700 ${isOver ? 'bg-rose-500' : 'bg-indigo-600'}`}
                            style={{ width: `${percent}%` }}
                          ></div>
                        </div>
                        {isOver && (
                          <p className="text-[9px] font-bold text-rose-500 uppercase tracking-tight flex items-center gap-1">
                            <i className="fas fa-circle-exclamation"></i> বাজেট অতিক্রম করেছে!
                          </p>
                        )}
                      </div>
                    );
                  })}
                </div>
              </div>
            )}

            {/* Recent Transactions */}
            <div className="space-y-4">
              <div className="flex justify-between items-center px-1">
                <h3 className="font-black text-slate-900 text-[10px] uppercase tracking-[0.2em]">সাম্প্রতিক লেনদেন</h3>
                <button onClick={() => {
                  if (transactions.length === 0) return alert("লেনদেন নেই।");
                  if (window.confirm('এই মাসের হিসাব ইতিহাসে জমা করবেন?')) {
                    const now = new Date();
                    const newArchive: MonthlyArchive = {
                      id: Date.now().toString(),
                      monthName: now.toLocaleString('bn-BD', { month: 'long' }),
                      year: now.getFullYear(),
                      totalIncome: summary.income,
                      totalExpense: summary.expense,
                      transactions: [...transactions]
                    };
                    setArchives([newArchive, ...archives]);
                    setTransactions([]);
                  }
                }} className="text-[10px] font-black text-blue-600 bg-blue-50 px-4 py-2 rounded-full uppercase transition-colors hover:bg-blue-100">নতুন মাস</button>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3">
                  {transactions.map(t => (
                    <div key={t.id} className="flex items-center justify-between p-5 bg-white border border-slate-100 rounded-[24px] shadow-sm hover:border-blue-100 transition-colors">
                      <div className="flex items-center gap-4">
                        <div className={`w-10 h-10 rounded-2xl flex items-center justify-center text-lg ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-900 text-sm">{t.category}</p>
                          <p className="text-[10px] text-slate-500 font-bold uppercase">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <div className="text-right">
                        <p className={`font-black text-base tracking-tight ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-900'}`}>
                          {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                        </p>
                        <button 
                          onClick={() => setTransactions(transactions.filter(item => item.id !== t.id))}
                          className="text-[9px] text-slate-300 hover:text-rose-500 uppercase font-black tracking-widest transition-colors"
                        >
                          মুছুন
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : <div className="text-center py-20 bg-slate-50/50 rounded-[40px] border border-dashed border-slate-200">
                    <i className="fas fa-receipt text-slate-200 text-5xl mb-4"></i>
                    <p className="text-slate-400 font-black uppercase text-[10px] tracking-widest">লেনদেন নেই</p>
                  </div>}
            </div>
          </div>
        ) : activeTab === 'future' ? (
          <div className="space-y-8 animate-fadeIn">
            <div className="p-8 bg-slate-900 rounded-[40px] text-white relative overflow-hidden shadow-2xl">
              <div className="relative z-10">
                <h3 className="text-2xl font-black mb-2 tracking-tight">ভবিষ্যৎ সঞ্চয়</h3>
                <p className="text-xs text-slate-400 font-black uppercase tracking-widest mb-8 opacity-70">এআই এর মাধ্যমে আর্থিক পূর্বাভাস</p>
                <button 
                  onClick={fetchAIAdvice}
                  disabled={loadingAI}
                  className="w-full py-4 bg-indigo-600 text-white rounded-2xl font-black uppercase text-xs shadow-xl active:scale-95 transition-all disabled:opacity-50 flex items-center justify-center gap-3"
                >
                  {loadingAI ? (
                    <><i className="fas fa-circle-notch animate-spin"></i> বিশ্লেষণ করা হচ্ছে...</>
                  ) : (
                    <><i className="fas fa-wand-magic-sparkles"></i> এআই পরামর্শ নিন</>
                  )}
                </button>
              </div>
              <div className="absolute -right-10 -bottom-10 w-48 h-48 bg-indigo-500/20 rounded-full blur-3xl"></div>
            </div>

            {insights.length > 0 ? (
              <div className="space-y-4">
                <h3 className="font-black text-slate-900 text-[10px] uppercase tracking-[0.2em] px-1">আপনার জন্য এআই পরামর্শ</h3>
                {insights.map((insight, idx) => (
                  <div key={idx} className={`p-6 rounded-[32px] border ${insight.type === 'success' ? 'bg-green-50 border-green-100' : insight.type === 'warning' ? 'bg-rose-50 border-rose-100' : 'bg-blue-50 border-blue-100'} animate-fadeIn`}>
                    <div className="flex items-start gap-4">
                      <div className={`w-10 h-10 rounded-2xl flex items-center justify-center flex-shrink-0 ${insight.type === 'success' ? 'bg-green-500 text-white' : insight.type === 'warning' ? 'bg-rose-500 text-white' : 'bg-blue-500 text-white'}`}>
                        <i className={`fas ${insight.type === 'success' ? 'fa-check' : insight.type === 'warning' ? 'fa-triangle-exclamation' : 'fa-lightbulb'}`}></i>
                      </div>
                      <div>
                        <h4 className="font-black text-slate-900 text-sm mb-1">{insight.title}</h4>
                        <p className="text-slate-700 text-xs font-bold leading-relaxed">{insight.description}</p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : !loadingAI && (
              <div className="text-center py-10 px-6 border-2 border-dashed border-slate-100 rounded-[40px]">
                <p className="text-slate-400 font-bold text-xs leading-relaxed uppercase tracking-wide">আপনার আর্থিক ভবিষ্যৎ দেখতে ওপরের বাটনে ক্লিক করুন।</p>
              </div>
            )}
          </div>
        ) : (
          <div className="space-y-8 animate-fadeIn">
            <div className="bg-slate-50 p-8 rounded-[40px] border border-slate-100 shadow-sm space-y-6">
              <div className="flex items-center gap-3 mb-2">
                <div className="w-10 h-10 bg-blue-600 text-white rounded-2xl flex items-center justify-center"><i className="fas fa-calculator"></i></div>
                <h3 className="font-black text-slate-900 uppercase text-xs tracking-widest">বিল স্প্লিটার</h3>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <label className="text-[10px] font-black text-slate-900 uppercase ml-1">মোট বিল (৳)</label>
                  <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ ০" className="w-full p-4 bg-white border border-slate-200 rounded-2xl font-black text-slate-900 outline-none focus:border-blue-600 transition-all shadow-sm" />
                </div>
                <div className="space-y-2">
                  <label className="text-[10px] font-black text-slate-900 uppercase ml-1">জনসংখ্যা</label>
                  <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} className="w-full p-4 bg-white border border-slate-200 rounded-2xl font-black text-slate-900 outline-none focus:border-blue-600 transition-all shadow-sm" />
                </div>
              </div>
              <div className="p-8 bg-blue-600 rounded-[32px] text-center shadow-lg shadow-blue-100">
                <p className="text-[10px] font-black text-blue-100 uppercase mb-2 tracking-widest">প্রতি জনের ভাগ</p>
                <p className="text-4xl font-black text-white">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
              </div>
            </div>

            <div className="p-6 bg-slate-900 rounded-[40px] text-white flex items-center justify-between">
              <div>
                <h4 className="text-sm font-black mb-1">ডেটা সেটিংস</h4>
                <p className="text-[10px] text-slate-400 font-bold uppercase">সব তথ্য আপনার ফোনেই জমা থাকে</p>
              </div>
              <button 
                onClick={() => { if(window.confirm('সব তথ্য মুছে ফেলতে চান?')) { setTransactions([]); setArchives([]); setBudgets({}); alert('ডেটা মুছে ফেলা হয়েছে'); } }}
                className="w-10 h-10 bg-rose-500/20 text-rose-500 rounded-full flex items-center justify-center hover:bg-rose-500 hover:text-white transition-all"
              >
                <i className="fas fa-trash"></i>
              </button>
            </div>
          </div>
        )}
      </main>

      {/* Bottom Navigation */}
      <nav className="fixed bottom-0 left-1/2 -translate-x-1/2 w-full max-w-md bg-white border-t border-slate-100 px-8 py-5 flex justify-between items-center z-[100] shadow-[0_-15px_40px_-20px_rgba(0,0,0,0.2)]">
        <button onClick={() => setActiveTab('home')} className={`flex flex-col items-center gap-1.5 transition-all ${activeTab === 'home' ? 'text-blue-600 scale-110' : 'text-slate-300'}`}>
          <i className="fas fa-house-chimney text-xl"></i><span className="text-[9px] font-black uppercase tracking-wider">হোম</span>
        </button>
        <button onClick={() => setActiveTab('future')} className={`flex flex-col items-center gap-1.5 transition-all ${activeTab === 'future' ? 'text-indigo-600 scale-110' : 'text-slate-300'}`}>
          <i className="fas fa-sparkles text-xl"></i><span className="text-[9px] font-black uppercase tracking-wider">ভবিষ্যৎ</span>
        </button>
        <button onClick={() => setActiveTab('tools')} className={`flex flex-col items-center gap-1.5 transition-all ${activeTab === 'tools' ? 'text-blue-600 scale-110' : 'text-slate-300'}`}>
          <i className="fas fa-shapes text-xl"></i><span className="text-[9px] font-black uppercase tracking-wider">টুলস</span>
        </button>
      </nav>

      {/* Transaction Modal */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/70 backdrop-blur-md z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[48px] p-8 animate-slideUp shadow-2xl border-t border-white/20">
            <div className="flex justify-between items-center mb-8">
              <h2 className="text-xl font-black uppercase text-slate-900 tracking-tight">{modalType === TransactionType.INCOME ? 'আয়' : 'ব্যয়'} যোগ করুন</h2>
              <button onClick={() => setModalType(null)} className="w-10 h-10 bg-slate-100 rounded-full flex items-center justify-center text-slate-900 hover:bg-slate-200 transition-colors"><i className="fas fa-times"></i></button>
            </div>
            <form onSubmit={addTransaction} className="space-y-6">
              <div className="bg-slate-50 rounded-[32px] p-8 border border-slate-100 focus-within:border-blue-500 focus-within:bg-white transition-all shadow-inner">
                <p className="text-center text-[10px] font-black text-slate-400 uppercase tracking-[0.2em] mb-3">টাকার পরিমাণ (৳)</p>
                <input 
                  type="number" 
                  name="amount" 
                  required 
                  placeholder="০" 
                  autoFocus 
                  className="w-full text-5xl font-black text-center outline-none text-slate-900 bg-transparent tracking-tighter"
                />
              </div>
              <div className="space-y-4">
                <div className="space-y-1">
                  <label className="text-[10px] font-black text-slate-400 uppercase ml-2">ক্যাটাগরি</label>
                  <select name="category" className="w-full p-5 bg-white border border-slate-200 rounded-[24px] font-black text-slate-900 outline-none focus:border-blue-600 shadow-sm">
                    {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                  </select>
                </div>
                <div className="space-y-1">
                  <label className="text-[10px] font-black text-slate-400 uppercase ml-2">নোট (ঐচ্ছিক)</label>
                  <input type="text" name="note" placeholder="যেমন: শপিং, খাবার..." className="w-full p-5 bg-white border border-slate-200 rounded-[24px] font-black text-slate-900 outline-none focus:border-blue-600 shadow-sm" />
                </div>
              </div>
              <button type="submit" className={`w-full py-6 rounded-[32px] text-white font-black text-lg shadow-xl active:scale-95 transition-all ${modalType === TransactionType.INCOME ? 'bg-green-600 hover:bg-green-700' : 'bg-rose-600 hover:bg-rose-700'}`}>নিশ্চিত করুন</button>
            </form>
          </div>
        </div>
      )}

      {/* Budget Settings Modal */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/70 backdrop-blur-md z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[48px] p-8 animate-slideUp shadow-2xl overflow-y-auto max-h-[90vh] custom-scrollbar">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-xl font-black uppercase text-slate-900">মাসিক বাজেট লক্ষ্য</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-10 h-10 bg-slate-100 rounded-full flex items-center justify-center text-slate-900 hover:bg-slate-200 transition-all"><i className="fas fa-times"></i></button>
             </div>
             <p className="text-xs font-bold text-slate-500 mb-6 uppercase tracking-widest">প্রতিটি ক্যাটাগরির জন্য ব্যয়ের সীমা নির্ধারণ করুন</p>
             <div className="space-y-6 mb-8">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="space-y-2">
                    <div className="flex items-center gap-2 ml-1">
                      <i className={`fas ${CATEGORY_ICONS[cat] || 'fa-tag'} text-blue-600 text-xs`}></i>
                      <label className="text-[11px] font-black text-slate-900 uppercase tracking-widest">{cat}</label>
                    </div>
                    <input 
                      type="number" 
                      value={budgets[cat] || ''}
                      onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })}
                      placeholder="৳ ০"
                      className="w-full p-5 bg-slate-50 border border-slate-200 rounded-[24px] font-black text-slate-900 outline-none focus:border-blue-600 focus:bg-white transition-all shadow-sm"
                    />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-6 bg-slate-900 text-white rounded-[32px] font-black uppercase tracking-widest text-sm shadow-xl active:scale-95 transition-transform mb-4">বাজেট আপডেট করুন</button>
          </div>
        </div>
      )}

      {/* History Modal */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/70 backdrop-blur-md z-[200] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[48px] p-8 overflow-y-auto animate-slideUp shadow-2xl">
             <div className="flex justify-between items-center mb-10">
                <h2 className="text-2xl font-black uppercase text-slate-900 tracking-tight">লেনদেনের ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-12 h-12 bg-slate-100 rounded-full flex items-center justify-center text-slate-900 transition-all hover:bg-slate-200 shadow-sm"><i className="fas fa-times"></i></button>
             </div>
             {archives.length > 0 ? (
               <div className="space-y-6">
                 {archives.map(arc => (
                   <div key={arc.id} className="p-6 bg-slate-50 rounded-[32px] border border-slate-100 shadow-sm hover:bg-white transition-colors">
                      <div className="flex justify-between items-start mb-4">
                        <div>
                           <p className="text-[12px] text-slate-900 uppercase font-black tracking-widest">{arc.monthName} {arc.year}</p>
                           <p className="text-slate-900 text-3xl font-black tracking-tighter mt-1">৳{arc.totalIncome - arc.totalExpense}</p>
                        </div>
                        <div className="w-10 h-10 bg-white rounded-2xl flex items-center justify-center shadow-sm border border-slate-100">
                          <i className="fas fa-chevron-right text-slate-400"></i>
                        </div>
                      </div>
                      <div className="flex gap-4 pt-4 border-t border-slate-100">
                        <div className="flex-1 text-center">
                          <p className="text-[9px] font-black text-green-600 uppercase mb-1">মোট আয়</p>
                          <p className="text-sm font-black text-slate-900">৳{arc.totalIncome}</p>
                        </div>
                        <div className="flex-1 text-center border-l border-slate-100">
                          <p className="text-[9px] font-black text-rose-600 uppercase mb-1">মোট ব্যয়</p>
                          <p className="text-sm font-black text-slate-900">৳{arc.totalExpense}</p>
                        </div>
                      </div>
                   </div>
                 ))}
               </div>
             ) : (
               <div className="text-center py-20 bg-slate-50 rounded-[40px] border border-dashed border-slate-200 mt-10">
                  <i className="fas fa-box-open text-slate-200 text-6xl mb-4"></i>
                  <p className="text-slate-900 font-black uppercase text-xs tracking-[0.2em]">এখনো কোনো ইতিহাস নেই</p>
               </div>
             )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  // Navigation Tabs: Home, Future, Tools
  const [activeTab, setActiveTab] = useState<'home' | 'future' | 'tools'>('home');
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));

  // Transactions State
  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  // Budgets State
  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  // History State
  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  // AI Insights State
  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);

  // Bill Splitter State
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  // Modals
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);

  // Persistence
  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  // Financial Summary
  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  // AI Logic for Future Tab
  const fetchAIAdvice = async () => {
    if (transactions.length === 0) {
      alert("পরামর্শ পেতে প্রথমে কিছু লেনদেন যোগ করুন।");
      return;
    }
    setLoadingAI(true);
    // Fix: Handle potentially undefined values when calculating previousMonthSavings to avoid TS errors.
    const finSummary: FinancialSummary = {
      totalIncome: summary.income,
      totalExpenses: summary.expense,
      balance: summary.balance,
      savings: summary.balance,
      previousMonthSavings: (archives[0]?.totalIncome ?? 0) - (archives[0]?.totalExpense ?? 0),
      categoryBreakdown: summary.breakdown,
      monthlyHistory: archives.slice(0, 5).map(a => ({
        month: a.monthName,
        income: a.totalIncome,
        expense: a.totalExpense
      }))
    };
    const results = await getFinancialInsights(transactions, finSummary);
    setInsights(results);
    setLoadingAI(false);
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount) return;
    const newTx: Transaction = {
      id: Date.now().toString(),
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions([newTx, ...transactions]);
    setModalType(null);
  };

  // Fix: Explicitly cast to [string, number][] to ensure comparison 'amount > 0' works without 'unknown' errors.
  const activeBudgetData = useMemo(() => {
    return (Object.entries(budgets) as [string, number][]).filter(([_, amount]) => amount > 0);
  }, [budgets]);

  return (
    <div className="max-w-md mx-auto min-h-screen bg-white shadow-2xl flex flex-col font-sans relative pb-24 overflow-hidden">
      {/* Header */}
      <header className="bg-gradient-to-br from-blue-700 via-indigo-600 to-indigo-800 p-8 text-white rounded-b-[40px] shadow-xl relative z-20">
        <div className="flex justify-between items-center mb-8">
          <div className="flex items-center gap-3">
             <div className="w-12 h-12 bg-white/20 rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/30">
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
                ) : (
                  <i className="fas fa-user text-blue-100"></i>
                )}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" title="ছবি পরিবর্তন করুন" />
             </div>
             <div>
                <h1 className="text-xl font-black uppercase leading-none tracking-tight">স্পার্ক</h1>
                <p className="text-[10px] font-black text-blue-200 mt-1 uppercase tracking-widest">স্মার্ট ফিন্যান্স কোচ</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-10 h-10 bg-white/10 rounded-full flex items-center justify-center hover:bg-white/20 transition-all border border-white/10" title="বাজেট সেট করুন">
              <i className="fas fa-bullseye"></i>
            </button>
            <button onClick={() => setShowHistory(true)} className="w-10 h-10 bg-white/10 rounded-full flex items-center justify-center hover:bg-white/20 transition-all border border-white/10" title="ইতিহাস">
              <i className="fas fa-clock-rotate-left"></i>
            </button>
          </div>
        </div>
        <div className="text-center">
          <p className="text-blue-100 text-[10px] font-black uppercase tracking-[0.2em] mb-2 opacity-80">বর্তমান ব্যালেন্স</p>
          <h2 className="text-4xl font-black mb-8 tracking-tighter">{formatCurrency(summary.balance)}</h2>
          <div className="grid grid-cols-2 gap-4">
            <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/20">
              <p className="text-[9px] uppercase font-black text-blue-200 mb-1">মোট আয়</p>
              <p className="text-lg font-black tracking-tight">৳{summary.income}</p>
            </div>
            <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/20">
              <p className="text-[9px] uppercase font-black text-blue-200 mb-1">মোট ব্যয়</p>
              <p className="text-lg font-black tracking-tight">৳{summary.expense}</p>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-6">
        {activeTab === 'home' ? (
          <div className="space-y-8 animate-fadeIn">
            {/* Quick Actions */}
            <div className="grid grid-cols-2 gap-4">
              <button onClick={() => setModalType(TransactionType.INCOME)} className="p-6 bg-slate-50 border border-slate-100 rounded-[32px] flex flex-col items-center gap-3 active:scale-95 transition-all shadow-sm group hover:bg-white">
                <div className="w-14 h-14 bg-green-500 text-white rounded-3xl flex items-center justify-center text-2xl shadow-lg shadow-green-100 group-hover:scale-110 transition-transform"><i className="fas fa-plus"></i></div>
                <span className="text-[12px] font-black text-slate-900 uppercase tracking-widest">আয় যোগ</span>
              </button>
              <button onClick={() => setModalType(TransactionType.EXPENSE)} className="p-6 bg-slate-50 border border-slate-100 rounded-[32px] flex flex-col items-center gap-3 active:scale-95 transition-all shadow-sm group hover:bg-white">
                <div className="w-14 h-14 bg-rose-500 text-white rounded-3xl flex items-center justify-center text-2xl shadow-lg shadow-rose-100 group-hover:scale-110 transition-transform"><i className="fas fa-minus"></i></div>
                <span className="text-[12px] font-black text-slate-900 uppercase tracking-widest">ব্যয় যোগ</span>
              </button>
            </div>

            {/* Budget Progress Bars */}
            {activeBudgetData.length > 0 && (
              <div className="space-y-4">
                <h3 className="font-black text-slate-900 text-[10px] uppercase tracking-[0.2em] px-1">বাজেট প্রগতি</h3>
                <div className="space-y-4 bg-slate-50 p-6 rounded-[32px] border border-slate-100">
                  {activeBudgetData.map(([cat, budget]) => {
                    const spent = summary.breakdown[cat] || 0;
                    const percent = Math.min((spent / budget) * 100, 100);
                    const isOver = spent > budget;
                    return (
                      <div key={cat} className="space-y-2">
                        <div className="flex justify-between items-center text-[11px] font-black uppercase">
                          <span className="text-slate-900">{cat}</span>
                          <span className={isOver ? 'text-rose-600' : 'text-slate-500'}>
                            ৳{spent} / ৳{budget}
                          </span>
                        </div>
                        <div className="w-full h-3 bg-slate-200 rounded-full overflow-hidden">
                          <div 
                            className={`h-full transition-all duration-700 ${isOver ? 'bg-rose-500' : 'bg-indigo-600'}`}
                            style={{ width: `${percent}%` }}
                          ></div>
                        </div>
                        {isOver && (
                          <p className="text-[9px] font-bold text-rose-500 uppercase tracking-tight flex items-center gap-1">
                            <i className="fas fa-circle-exclamation"></i> বাজেট অতিক্রম করেছে!
                          </p>
                        )}
                      </div>
                    );
                  })}
                </div>
              </div>
            )}

            {/* Recent Transactions */}
            <div className="space-y-4">
              <div className="flex justify-between items-center px-1">
                <h3 className="font-black text-slate-900 text-[10px] uppercase tracking-[0.2em]">সাম্প্রতিক লেনদেন</h3>
                <button onClick={() => {
                  if (transactions.length === 0) return alert("লেনদেন নেই।");
                  if (window.confirm('এই মাসের হিসাব ইতিহাসে জমা করবেন?')) {
                    const now = new Date();
                    const newArchive: MonthlyArchive = {
                      id: Date.now().toString(),
                      monthName: now.toLocaleString('bn-BD', { month: 'long' }),
                      year: now.getFullYear(),
                      totalIncome: summary.income,
                      totalExpense: summary.expense,
                      transactions: [...transactions]
                    };
                    setArchives([newArchive, ...archives]);
                    setTransactions([]);
                  }
                }} className="text-[10px] font-black text-blue-600 bg-blue-50 px-4 py-2 rounded-full uppercase transition-colors hover:bg-blue-100">নতুন মাস</button>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3">
                  {transactions.map(t => (
                    <div key={t.id} className="flex items-center justify-between p-5 bg-white border border-slate-100 rounded-[24px] shadow-sm hover:border-blue-100 transition-colors">
                      <div className="flex items-center gap-4">
                        <div className={`w-10 h-10 rounded-2xl flex items-center justify-center text-lg ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-900 text-sm">{t.category}</p>
                          <p className="text-[10px] text-slate-500 font-bold uppercase">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <div className="text-right">
                        <p className={`font-black text-base tracking-tight ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-900'}`}>
                          {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                        </p>
                        <button 
                          onClick={() => setTransactions(transactions.filter(item => item.id !== t.id))}
                          className="text-[9px] text-slate-300 hover:text-rose-500 uppercase font-black tracking-widest transition-colors"
                        >
                          মুছুন
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : <div className="text-center py-20 bg-slate-50/50 rounded-[40px] border border-dashed border-slate-200">
                    <i className="fas fa-receipt text-slate-200 text-5xl mb-4"></i>
                    <p className="text-slate-400 font-black uppercase text-[10px] tracking-widest">লেনদেন নেই</p>
                  </div>}
            </div>
          </div>
        ) : activeTab === 'future' ? (
          <div className="space-y-8 animate-fadeIn">
            <div className="p-8 bg-slate-900 rounded-[40px] text-white relative overflow-hidden shadow-2xl">
              <div className="relative z-10">
                <h3 className="text-2xl font-black mb-2 tracking-tight">ভবিষ্যৎ সঞ্চয়</h3>
                <p className="text-xs text-slate-400 font-black uppercase tracking-widest mb-8 opacity-70">এআই এর মাধ্যমে আর্থিক পূর্বাভাস</p>
                <button 
                  onClick={fetchAIAdvice}
                  disabled={loadingAI}
                  className="w-full py-4 bg-indigo-600 text-white rounded-2xl font-black uppercase text-xs shadow-xl active:scale-95 transition-all disabled:opacity-50 flex items-center justify-center gap-3"
                >
                  {loadingAI ? (
                    <><i className="fas fa-circle-notch animate-spin"></i> বিশ্লেষণ করা হচ্ছে...</>
                  ) : (
                    <><i className="fas fa-wand-magic-sparkles"></i> এআই পরামর্শ নিন</>
                  )}
                </button>
              </div>
              <div className="absolute -right-10 -bottom-10 w-48 h-48 bg-indigo-500/20 rounded-full blur-3xl"></div>
            </div>

            {insights.length > 0 ? (
              <div className="space-y-4">
                <h3 className="font-black text-slate-900 text-[10px] uppercase tracking-[0.2em] px-1">আপনার জন্য এআই পরামর্শ</h3>
                {insights.map((insight, idx) => (
                  <div key={idx} className={`p-6 rounded-[32px] border ${insight.type === 'success' ? 'bg-green-50 border-green-100' : insight.type === 'warning' ? 'bg-rose-50 border-rose-100' : 'bg-blue-50 border-blue-100'} animate-fadeIn`}>
                    <div className="flex items-start gap-4">
                      <div className={`w-10 h-10 rounded-2xl flex items-center justify-center flex-shrink-0 ${insight.type === 'success' ? 'bg-green-500 text-white' : insight.type === 'warning' ? 'bg-rose-500 text-white' : 'bg-blue-500 text-white'}`}>
                        <i className={`fas ${insight.type === 'success' ? 'fa-check' : insight.type === 'warning' ? 'fa-triangle-exclamation' : 'fa-lightbulb'}`}></i>
                      </div>
                      <div>
                        <h4 className="font-black text-slate-900 text-sm mb-1">{insight.title}</h4>
                        <p className="text-slate-700 text-xs font-bold leading-relaxed">{insight.description}</p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : !loadingAI && (
              <div className="text-center py-10 px-6 border-2 border-dashed border-slate-100 rounded-[40px]">
                <p className="text-slate-400 font-bold text-xs leading-relaxed uppercase tracking-wide">আপনার আর্থিক ভবিষ্যৎ দেখতে ওপরের বাটনে ক্লিক করুন।</p>
              </div>
            )}
          </div>
        ) : (
          <div className="space-y-8 animate-fadeIn">
            <div className="bg-slate-50 p-8 rounded-[40px] border border-slate-100 shadow-sm space-y-6">
              <div className="flex items-center gap-3 mb-2">
                <div className="w-10 h-10 bg-blue-600 text-white rounded-2xl flex items-center justify-center"><i className="fas fa-calculator"></i></div>
                <h3 className="font-black text-slate-900 uppercase text-xs tracking-widest">বিল স্প্লিটার</h3>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <label className="text-[10px] font-black text-slate-900 uppercase ml-1">মোট বিল (৳)</label>
                  <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ ০" className="w-full p-4 bg-white border border-slate-200 rounded-2xl font-black text-slate-900 outline-none focus:border-blue-600 transition-all shadow-sm" />
                </div>
                <div className="space-y-2">
                  <label className="text-[10px] font-black text-slate-900 uppercase ml-1">জনসংখ্যা</label>
                  <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} className="w-full p-4 bg-white border border-slate-200 rounded-2xl font-black text-slate-900 outline-none focus:border-blue-600 transition-all shadow-sm" />
                </div>
              </div>
              <div className="p-8 bg-blue-600 rounded-[32px] text-center shadow-lg shadow-blue-100">
                <p className="text-[10px] font-black text-blue-100 uppercase mb-2 tracking-widest">প্রতি জনের ভাগ</p>
                <p className="text-4xl font-black text-white">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
              </div>
            </div>

            <div className="p-6 bg-slate-900 rounded-[40px] text-white flex items-center justify-between">
              <div>
                <h4 className="text-sm font-black mb-1">ডেটা সেটিংস</h4>
                <p className="text-[10px] text-slate-400 font-bold uppercase">সব তথ্য আপনার ফোনেই জমা থাকে</p>
              </div>
              <button 
