            <div className="flex justify-between items-center mb-8">
              <h2 className="text-xl font-black uppercase text-slate-900 tracking-tight">{modalType === TransactionType.INCOME ? 'আয়' : 'ব্যয়'} যোগ করুন</h2>
              <button onClick={() => setModalType(null)} className="w-10 h-10 bg-slate-100 rounded-full flex items-center justify-center text-slate-900 hover:bg-slate-200 transition-colors"><i className="fas fa-times"></i></button>
            </div>
            <form onSubmit={addTransaction} className="space-y-6">
              <div className="bg-slate-50 rounded-[32px] p-8 border border-slate-100 focus-within:border-blue-500 focus-within:bg-white transition-all shadow-inner">
                <p className="text-center text-[10px] font-black text-slate-400 uppercase tracking-[0.2em] mb-3">টাকার পরিমাণ (৳)</p>
                <input 
                  type="number" 
                  name="amount" 
                  required 
                  placeholder="০" 
                  autoFocus 
                  className="w-full text-5xl font-black text-center outline-none text-slate-900 bg-transparent tracking-tighter"
                />
              </div>
              <div className="space-y-4">
                <div className="space-y-1">
                  <label className="text-[10px] font-black text-slate-400 uppercase ml-2">ক্যাটাগরি</label>
                  <select name="category" className="w-full p-5 bg-white border border-slate-200 rounded-[24px] font-black text-slate-900 outline-none focus:border-blue-600 shadow-sm">
                    {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                  </select>
                </div>
                <div className="space-y-1">
                  <label className="text-[10px] font-black text-slate-400 uppercase ml-2">নোট (ঐচ্ছিক)</label>
                  <input type="text" name="note" placeholder="যেমন: শপিং, খাবার..." className="w-full p-5 bg-white border border-slate-200 rounded-[24px] font-black text-slate-900 outline-none focus:border-blue-600 shadow-sm" />
                </div>
              </div>
              <button type="submit" className={`w-full py-6 rounded-[32px] text-white font-black text-lg shadow-xl active:scale-95 transition-all ${modalType === TransactionType.INCOME ? 'bg-green-600 hover:bg-green-700' : 'bg-rose-600 hover:bg-rose-700'}`}>নিশ্চিত করুন</button>
            </form>
          </div>
        </div>
      )}

      {/* Budget Settings Modal */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/70 backdrop-blur-md z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[48px] p-8 animate-slideUp shadow-2xl overflow-y-auto max-h-[90vh] custom-scrollbar">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-xl font-black uppercase text-slate-900">মাসিক বাজেট লক্ষ্য</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-10 h-10 bg-slate-100 rounded-full flex items-center justify-center text-slate-900 hover:bg-slate-200 transition-all"><i className="fas fa-times"></i></button>
             </div>
             <p className="text-xs font-bold text-slate-500 mb-6 uppercase tracking-widest">প্রতিটি ক্যাটাগরির জন্য ব্যয়ের সীমা নির্ধারণ করুন</p>
             <div className="space-y-6 mb-8">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="space-y-2">
                    <div className="flex items-center gap-2 ml-1">
                      <i className={`fas ${CATEGORY_ICONS[cat] || 'fa-tag'} text-blue-600 text-xs`}></i>
                      <label className="text-[11px] font-black text-slate-900 uppercase tracking-widest">{cat}</label>
                    </div>
                    <input 
                      type="number" 
                      value={budgets[cat] || ''}
                      onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })}
                      placeholder="৳ ০"
                      className="w-full p-5 bg-slate-50 border border-slate-200 rounded-[24px] font-black text-slate-900 outline-none focus:border-blue-600 focus:bg-white transition-all shadow-sm"
                    />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-6 bg-slate-900 text-white rounded-[32px] font-black uppercase tracking-widest text-sm shadow-xl active:scale-95 transition-transform mb-4">বাজেট আপডেট করুন</button>
          </div>
        </div>
      )}

      {/* History Modal */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/70 backdrop-blur-md z-[200] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[48px] p-8 overflow-y-auto animate-slideUp shadow-2xl">
             <div className="flex justify-between items-center mb-10">
                <h2 className="text-2xl font-black uppercase text-slate-900 tracking-tight">লেনদেনের ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-12 h-12 bg-slate-100 rounded-full flex items-center justify-center text-slate-900 transition-all hover:bg-slate-200 shadow-sm"><i className="fas fa-times"></i></button>
             </div>
             {archives.length > 0 ? (
               <div className="space-y-6">
                 {archives.map(arc => (
                   <div key={arc.id} className="p-6 bg-slate-50 rounded-[32px] border border-slate-100 shadow-sm hover:bg-white transition-colors">
                      <div className="flex justify-between items-start mb-4">
                        <div>
                           <p className="text-[12px] text-slate-900 uppercase font-black tracking-widest">{arc.monthName} {arc.year}</p>
                           <p className="text-slate-900 text-3xl font-black tracking-tighter mt-1">৳{arc.totalIncome - arc.totalExpense}</p>
                        </div>
                        <div className="w-10 h-10 bg-white rounded-2xl flex items-center justify-center shadow-sm border border-slate-100">
                          <i className="fas fa-chevron-right text-slate-400"></i>
                        </div>
                      </div>
                      <div className="flex gap-4 pt-4 border-t border-slate-100">
                        <div className="flex-1 text-center">
                          <p className="text-[9px] font-black text-green-600 uppercase mb-1">মোট আয়</p>
                          <p className="text-sm font-black text-slate-900">৳{arc.totalIncome}</p>
                        </div>
                        <div className="flex-1 text-center border-l border-slate-100">
                          <p className="text-[9px] font-black text-rose-600 uppercase mb-1">মোট ব্যয়</p>
                          <p className="text-sm font-black text-slate-900">৳{arc.totalExpense}</p>
                        </div>
                      </div>
                   </div>
                 ))}
               </div>
             ) : (
               <div className="text-center py-20 bg-slate-50 rounded-[40px] border border-dashed border-slate-200 mt-10">
                  <i className="fas fa-box-open text-slate-200 text-6xl mb-4"></i>
                  <p className="text-slate-900 font-black uppercase text-xs tracking-[0.2em]">এখনো কোনো ইতিহাস নেই</p>
               </div>
             )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  // Navigation Tabs: Home, Future, Tools
  const [activeTab, setActiveTab] = useState<'home' | 'future' | 'tools'>('home');
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));

  // Transactions State
  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  // Budgets State
  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  // History State
  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  // AI Insights State
  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);

  // Bill Splitter State
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  // Modals
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);

  // Persistence
  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  // Financial Summary
  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  // AI Logic for Future Tab
  const fetchAIAdvice = async () => {
    if (transactions.length === 0) {
      alert("পরামর্শ পেতে প্রথমে কিছু লেনদেন যোগ করুন।");
      return;
    }
    setLoadingAI(true);
    const finSummary: FinancialSummary = {
      totalIncome: summary.income,
      totalExpenses: summary.expense,
      balance: summary.balance,
      savings: summary.balance,
      previousMonthSavings: (archives[0]?.totalIncome ?? 0) - (archives[0]?.totalExpense ?? 0),
      categoryBreakdown: summary.breakdown,
      monthlyHistory: archives.slice(0, 5).map(a => ({
        month: a.monthName,
        income: a.totalIncome,
        expense: a.totalExpense
      }))
    };
    const results = await getFinancialInsights(transactions, finSummary);
    setInsights(results);
    setLoadingAI(false);
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount) return;
    const newTx: Transaction = {
      id: Date.now().toString(),
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions([newTx, ...transactions]);
    setModalType(null);
  };

  const deleteTransaction = (id: string) => {
    if (window.confirm('আপনি কি এই লেনদেনটি মুছে ফেলতে চান?')) {
      setTransactions(transactions.filter(t => t.id !== id));
    }
  };

  const activeBudgetData = useMemo(() => {
    return (Object.entries(budgets) as [string, number][]).filter(([_, amount]) => amount > 0);
  }, [budgets]);

  return (
    <div className="max-w-md mx-auto min-h-screen bg-slate-50 shadow-2xl flex flex-col font-sans relative pb-28 overflow-hidden">
      {/* Header - Attractive Balance Display */}
      <header className="bg-gradient-to-br from-indigo-700 via-blue-600 to-indigo-800 p-8 text-white rounded-b-[50px] shadow-2xl relative z-20">
        <div className="flex justify-between items-center mb-8">
          <div className="flex items-center gap-4">
             <div className="w-14 h-14 bg-white/20 rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/30 shadow-lg backdrop-blur-sm">
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
                ) : (
                  <i className="fas fa-user-astronaut text-2xl text-blue-100"></i>
                )}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" title="ছবি দিন" />
             </div>
             <div>
                <h1 className="text-2xl font-black uppercase leading-none tracking-tight">স্পার্ক</h1>
                <p className="text-[10px] font-black text-blue-200 mt-1 uppercase tracking-[0.2em] opacity-80">স্মার্ট ওয়ালেট</p>
             </div>
          </div>
          <div className="flex gap-3">
            <button onClick={() => setShowBudgetSettings(true)} className="w-11 h-11 bg-white/10 rounded-2xl flex items-center justify-center hover:bg-white/20 transition-all border border-white/10 shadow-md backdrop-blur-md">
              <i className="fas fa-bullseye text-lg"></i>
            </button>
            <button onClick={() => setShowHistory(true)} className="w-11 h-11 bg-white/10 rounded-2xl flex items-center justify-center hover:bg-white/20 transition-all border border-white/10 shadow-md backdrop-blur-md">
              <i className="fas fa-history text-lg"></i>
            </button>
          </div>
        </div>

        <div className="text-center py-4">
          <p className="text-blue-100 text-[11px] font-black uppercase tracking-[0.3em] mb-3 opacity-90">মোট ব্যালেন্স</p>
          <h2 className="text-5xl font-black mb-10 tracking-tighter drop-shadow-xl">{formatCurrency(summary.balance)}</h2>

          <div className="grid grid-cols-2 gap-5">
            <div className="bg-white/10 backdrop-blur-xl p-5 rounded-[30px] border border-white/20 shadow-lg">
              <p className="text-[10px] uppercase font-black text-blue-200 mb-2 tracking-widest">মোট আয়</p>
              <p className="text-xl font-black tracking-tight text-green-300">৳{summary.income}</p>
            </div>
            <div className="bg-white/10 backdrop-blur-xl p-5 rounded-[30px] border border-white/20 shadow-lg">
              <p className="text-[10px] uppercase font-black text-blue-200 mb-2 tracking-widest">মোট ব্যয়</p>
              <p className="text-xl font-black tracking-tight text-rose-300">৳{summary.expense}</p>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content Area */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-8">
        {activeTab === 'home' ? (
          <div className="space-y-10 animate-fadeIn">
            {/* Quick Actions - Highly Attractive */}
            <div className="grid grid-cols-2 gap-5">
              <button 
                onClick={() => setModalType(TransactionType.INCOME)} 
                className="group relative p-7 bg-white rounded-[40px] shadow-sm border border-slate-100 flex flex-col items-center gap-4 transition-all hover:shadow-xl hover:-translate-y-1 active:scale-95 overflow-hidden"
              >
                <div className="absolute top-0 right-0 w-20 h-20 bg-green-50 rounded-full -mr-10 -mt-10 transition-all group-hover:scale-150"></div>
                <div className="w-16 h-16 bg-green-500 text-white rounded-3xl flex items-center justify-center text-3xl shadow-lg shadow-green-100 relative z-10 transition-transform group-hover:rotate-12"><i className="fas fa-wallet"></i></div>
                <span className="text-[13px] font-black text-slate-800 uppercase tracking-widest relative z-10">আয় যোগ</span>
              </button>

              <button 
                onClick={() => setModalType(TransactionType.EXPENSE)} 
                className="group relative p-7 bg-white rounded-[40px] shadow-sm border border-slate-100 flex flex-col items-center gap-4 transition-all hover:shadow-xl hover:-translate-y-1 active:scale-95 overflow-hidden"
              >
                <div className="absolute top-0 right-0 w-20 h-20 bg-rose-50 rounded-full -mr-10 -mt-10 transition-all group-hover:scale-150"></div>
                <div className="w-16 h-16 bg-rose-500 text-white rounded-3xl flex items-center justify-center text-3xl shadow-lg shadow-rose-100 relative z-10 transition-transform group-hover:-rotate-12"><i className="fas fa-shopping-cart"></i></div>
                <span className="text-[13px] font-black text-slate-800 uppercase tracking-widest relative z-10">ব্যয় যোগ</span>
              </button>
            </div>

            {/* Budget Display */}
            {activeBudgetData.length > 0 && (
              <div className="space-y-5">
                <h3 className="font-black text-slate-900 text-[11px] uppercase tracking-[0.25em] px-2 flex items-center gap-2">
                  <span className="w-1.5 h-1.5 bg-indigo-600 rounded-full"></span> বাজেট পর্যবেক্ষণ
                </h3>
                <div className="space-y-5 bg-white p-7 rounded-[40px] border border-slate-100 shadow-sm">
                  {activeBudgetData.map(([cat, budget]) => {
                    const spent = summary.breakdown[cat] || 0;
                    const percent = Math.min((spent / budget) * 100, 100);
                    const isOver = spent > budget;
                    return (
                      <div key={cat} className="space-y-3">
                        <div className="flex justify-between items-center text-[12px] font-black uppercase">
                          <div className="flex items-center gap-2">
                            <i className={`fas ${CATEGORY_ICONS[cat] || 'fa-tag'} text-slate-400`}></i>
                            <span className="text-slate-900">{cat}</span>
                          </div>
                          <span className={isOver ? 'text-rose-600' : 'text-slate-500'}>
                            ৳{spent} / ৳{budget}
                          </span>
                        </div>
                        <div className="w-full h-3 bg-slate-100 rounded-full overflow-hidden shadow-inner">
                          <div 
                            className={`h-full transition-all duration-1000 ease-out rounded-full ${isOver ? 'bg-gradient-to-r from-rose-500 to-orange-500' : 'bg-gradient-to-r from-indigo-500 to-blue-500'}`}
                            style={{ width: `${percent}%` }}
                          ></div>
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            )}

            {/* Enhanced Transaction List with attractive delete */}
            <div className="space-y-5">
              <div className="flex justify-between items-center px-2">
                <h3 className="font-black text-slate-900 text-[11px] uppercase tracking-[0.25em] flex items-center gap-2">
                   <span className="w-1.5 h-1.5 bg-blue-600 rounded-full"></span> সাম্প্রতিক লেনদেন
                </h3>
                <button onClick={() => {
                   if (transactions.length === 0) return alert("লেনদেন নেই।");
                   if (window.confirm('এই মাসের হিসাব ইতিহাসে জমা করবেন?')) {
                     const now = new Date();
                     const newArchive: MonthlyArchive = {
                       id: Date.now().toString(),
                       monthName: now.toLocaleString('bn-BD', { month: 'long' }),
                       year: now.getFullYear(),
                       totalIncome: summary.income,
                       totalExpense: summary.expense,
                       transactions: [...transactions]
                     };
                     setArchives([newArchive, ...archives]);
                     setTransactions([]);
                   }
                }} className="text-[10px] font-black text-indigo-600 bg-indigo-50 px-5 py-2.5 rounded-2xl uppercase transition-all hover:bg-indigo-600 hover:text-white shadow-sm">নতুন মাস</button>
              </div>

              {transactions.length > 0 ? (
                <div className="space-y-4">
                  {transactions.map(t => (
                    <div key={t.id} className="group relative bg-white border border-slate-100 p-5 rounded-[32px] shadow-sm flex items-center justify-between transition-all hover:shadow-md hover:border-indigo-100">
                      <div className="flex items-center gap-5">
                        <div className={`w-14 h-14 rounded-2xl flex items-center justify-center text-xl shadow-inner ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-900 text-[15px]">{t.category}</p>
                          <p className="text-[11px] text-slate-400 font-bold uppercase tracking-wider">{t.note || 'সাধারণ বিবরণ'}</p>
                        </div>
                      </div>
                      <div className="flex items-center gap-4">
                        <p className={`font-black text-lg tracking-tight ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-900'}`}>
                          {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                        </p>
                        <button 
                          onClick={() => deleteTransaction(t.id)}
                          className="w-10 h-10 bg-slate-50 text-slate-300 rounded-2xl flex items-center justify-center transition-all hover:bg-rose-50 hover:text-rose-500"
                        >
                          <i className="fas fa-trash-alt text-sm"></i>
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-24 bg-white rounded-[50px] border-2 border-dashed border-slate-100 shadow-inner">
                  <div className="w-20 h-20 bg-slate-50 rounded-full flex items-center justify-center mx-auto mb-6">
                    <i className="fas fa-ghost text-slate-200 text-4xl"></i>
                  </div>
                  <p className="text-slate-400 font-black uppercase text-[10px] tracking-[0.3em]">কোনো লেনদেন পাওয়া যায়নি</p>
                </div>
              )}
            </div>
          </div>
        ) : activeTab === 'future' ? (
          <div className="space-y-8 animate-fadeIn">
            {/* AI Advisor - Polished */}
            <div className="p-10 bg-slate-900 rounded-[50px] text-white relative overflow-hidden shadow-2xl border border-slate-800">
              <div className="relative z-10">
                <div className="w-16 h-16 bg-indigo-600/30 rounded-3xl flex items-center justify-center mb-6 border border-indigo-500/30">
                  <i className="fas fa-brain text-3xl text-indigo-400"></i>
                </div>
                <h3 className="text-3xl font-black mb-3 tracking-tight">ভবিষ্যৎ সঞ্চয়</h3>
                <p className="text-xs text-slate-400 font-black uppercase tracking-widest mb-10 opacity-70 leading-relaxed">এআই (AI) এর মাধ্যমে আপনার আর্থিক ভবিষ্যৎ বিশ্লেষণ করুন</p>

                <button 
                  onClick={fetchAIAdvice}
                  disabled={loadingAI}
                  className="w-full py-5 bg-gradient-to-r from-indigo-600 to-blue-600 text-white rounded-[24px] font-black uppercase text-xs shadow-2xl active:scale-95 transition-all disabled:opacity-50 flex items-center justify-center gap-4 border border-indigo-500/30"
                >
                  {loadingAI ? (
                    <><i className="fas fa-spinner animate-spin"></i> প্রসেসিং হচ্ছে...</>
                  ) : (
                    <><i className="fas fa-sparkles"></i> এআই পরামর্শ নিন</>
                  )}
                </button>
              </div>
              <div className="absolute -right-20 -bottom-20 w-72 h-72 bg-indigo-500/10 rounded-full blur-[80px]"></div>
              <div className="absolute -left-20 top-0 w-48 h-48 bg-blue-500/5 rounded-full blur-[60px]"></div>
            </div>

            {insights.length > 0 ? (
              <div className="space-y-5">
                <h3 className="font-black text-slate-900 text-[11px] uppercase tracking-[0.25em] px-2">আপনার জন্য এআই স্পেশাল</h3>
                {insights.map((insight, idx) => (
                  <div key={idx} className={`p-7 rounded-[40px] border-2 shadow-sm ${insight.type === 'success' ? 'bg-green-50/50 border-green-100' : insight.type === 'warning' ? 'bg-rose-50/50 border-rose-100' : 'bg-blue-50/50 border-blue-100'} animate-fadeIn`}>
                    <div className="flex items-start gap-5">
                      <div className={`w-14 h-14 rounded-2xl flex items-center justify-center flex-shrink-0 text-2xl shadow-sm ${insight.type === 'success' ? 'bg-green-500 text-white' : insight.type === 'warning' ? 'bg-rose-500 text-white' : 'bg-blue-500 text-white'}`}>
                        <i className={`fas ${insight.type === 'success' ? 'fa-check-circle' : insight.type === 'warning' ? 'fa-exclamation-circle' : 'fa-lightbulb'}`}></i>
                      </div>
                      <div>
                        <h4 className="font-black text-slate-900 text-[16px] mb-2">{insight.title}</h4>
                        <p className="text-slate-600 text-[13px] font-bold leading-relaxed">{insight.description}</p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : !loadingAI && (
              <div className="text-center py-16 px-8 border-2 border-dashed border-slate-200 rounded-[50px] bg-white">
                <p className="text-slate-400 font-bold text-[13px] leading-relaxed uppercase tracking-wider">আর্থিক তথ্য বিশ্লেষণ পেতে ওপরের বাটনে ক্লিক করুন।</p>
              </div>
            )}
          </div>
        ) : (
          <div className="space-y-10 animate-fadeIn">
            {/* Tools Section */}
            <div className="bg-white p-10 rounded-[50px] border border-slate-100 shadow-sm space-y-8">
              <div className="flex items-center gap-4 mb-4">
                <div className="w-14 h-14 bg-blue-600 text-white rounded-2xl flex items-center justify-center text-2xl shadow-lg shadow-blue-100"><i className="fas fa-calculator"></i></div>
                <h3 className="font-black text-slate-900 uppercase text-[15px] tracking-widest">বিল স্প্লিটার</h3>
              </div>

              <div className="grid grid-cols-2 gap-5">
                <div className="space-y-3">
                  <label className="text-[11px] font-black text-slate-500 uppercase ml-2 tracking-widest">মোট বিল</label>
                  <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ ০" className="w-full p-5 bg-slate-50 border-2 border-slate-100 rounded-[28px] font-black text-slate-900 outline-none focus:border-blue-600 focus:bg-white transition-all shadow-inner" />
                </div>
                <div className="space-y-3">
                  <label className="text-[11px] font-black text-slate-500 uppercase ml-2 tracking-widest">জনসংখ্যা</label>
                  <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} className="w-full p-5 bg-slate-50 border-2 border-slate-100 rounded-[28px] font-black text-slate-900 outline-none focus:border-blue-600 focus:bg-white transition-all shadow-inner" />
                </div>
              </div>

              <div className="p-10 bg-gradient-to-br from-blue-600 to-indigo-700 rounded-[40px] text-center shadow-2xl shadow-blue-200">
                <p className="text-[11px] font-black text-blue-100 uppercase mb-3 tracking-[0.3em]">প্রতি জনের ভাগ</p>
                <p className="text-5xl font-black text-white tracking-tighter">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
              </div>
            </div>

            <div className="p-8 bg-slate-900 rounded-[45px] text-white flex items-center justify-between border border-slate-800 shadow-xl">
              <div>
                <h4 className="text-[16px] font-black mb-1">ডেটা সেটিংস</h4>
                <p className="text-[10px] text-slate-500 font-bold uppercase tracking-widest">সব তথ্য লোকাল স্টোরেজে থাকে</p>
              </div>
              <button 
                onClick={() => { if(window.confirm('আপনি কি নিশ্চিত যে সব তথ্য মুছে ফেলতে চান? এটি আর ফিরে পাওয়া যাবে না।')) { setTransactions([]); setArchives([]); setBudgets({}); alert('সব ডেটা মুছে ফেলা হয়েছে'); } }}
                className="w-14 h-14 bg-rose-500/10 text-rose-500 rounded-[22px] flex items-center justify-center hover:bg-rose-500 hover:text-white transition-all border border-rose-500/20"
                title="সব মুছে ফেলুন"
              >
                <i className="fas fa-trash-alt text-xl"></i>
              </button>
            </div>
          </div>
        )}
      </main>

      {/* Improved Bottom Navigation - High Contrast and Clear Tabs */}
      <nav className="fixed bottom-0 left-0 right-0 bg-white/90 backdrop-blur-xl border-t border-slate-100 p-4 pb-8 flex justify-around items-center z-[100] shadow-[0_-20px_50px_-20px_rgba(0,0,0,0.2)] rounded-t-[40px]">
        <button 
          onClick={() => setActiveTab('home')} 
          className={`relative flex flex-col items-center gap-2 p-2 px-6 rounded-3xl transition-all duration-300 ${activeTab === 'home' ? 'text-blue-600 bg-blue-50' : 'text-slate-400 hover:text-slate-600'}`}
        >
          <i className={`fas fa-home-alt text-xl ${activeTab === 'home' ? 'scale-110' : ''}`}></i>
          <span className={`text-[11px] font-black uppercase tracking-widest ${activeTab === 'home' ? 'opacity-100' : 'opacity-60'}`}>হোম</span>
          {activeTab === 'home' && <div className="absolute -bottom-1 w-6 h-1 bg-blue-600 rounded-full"></div>}
        </button>

        <button 
          onClick={() => setActiveTab('future')} 
          className={`relative flex flex-col items-center gap-2 p-2 px-6 rounded-3xl transition-all duration-300 ${activeTab === 'future' ? 'text-indigo-600 bg-indigo-50' : 'text-slate-400 hover:text-slate-600'}`}
        >
          <i className={`fas fa-sparkles text-xl ${activeTab === 'future' ? 'scale-110' : ''}`}></i>
          <span className={`text-[11px] font-black uppercase tracking-widest ${activeTab === 'future' ? 'opacity-100' : 'opacity-60'}`}>ভবিষ্যৎ</span>
          {activeTab === 'future' && <div className="absolute -bottom-1 w-6 h-1 bg-indigo-600 rounded-full"></div>}
        </button>

        <button 
          onClick={() => setActiveTab('tools')} 
          className={`relative flex flex-col items-center gap-2 p-2 px-6 rounded-3xl transition-all duration-300 ${activeTab === 'tools' ? 'text-blue-600 bg-blue-50' : 'text-slate-400 hover:text-slate-600'}`}
        >
          <i className={`fas fa-grid-2 text-xl ${activeTab === 'tools' ? 'scale-110' : ''}`}></i>
          <span className={`text-[11px] font-black uppercase tracking-widest ${activeTab === 'tools' ? 'opacity-100' : 'opacity-60'}`}>টুলস</span>
          {activeTab === 'tools' && <div className="absolute -bottom-1 w-6 h-1 bg-blue-600 rounded-full"></div>}
        </button>
      </nav>

      {/* Transaction Modal - Improved Visibility */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[60px] p-10 animate-slideUp shadow-2xl border-t border-white/20">
            <div className="flex justify-between items-center mb-10">
              <h2 className="text-2xl font-black uppercase text-slate-900 tracking-tight">{modalType === TransactionType.INCOME ? 'আয়' : 'ব্যয়'} যোগ করুন</h2>
              <button onClick={() => setModalType(null)} className="w-14 h-14 bg-slate-100 rounded-[24px] flex items-center justify-center text-slate-900 hover:bg-slate-200 transition-all shadow-sm"><i className="fas fa-times text-xl"></i></button>
            </div>

            <form onSubmit={addTransaction} className="space-y-8">
              <div className="bg-slate-50 rounded-[40px] p-10 border-2 border-slate-100 focus-within:border-blue-500 focus-within:bg-white focus-within:shadow-2xl transition-all shadow-inner relative group">
                <p className="text-center text-[11px] font-black text-slate-400 uppercase tracking-[0.4em] mb-4 group-focus-within:text-blue-500">টাকার পরিমাণ (৳)</p>
                <input 
                  type="number" 
                  name="amount" 
                  required 
                  placeholder="০" 
                  autoFocus 
                  className="w-full text-6xl font-black text-center outline-none text-slate-900 bg-transparent tracking-tighter placeholder:text-slate-200"
                />
              </div>

              <div className="space-y-5">
                <div className="space-y-2">
                  <label className="text-[11px] font-black text-slate-400 uppercase ml-4 tracking-[0.2em]">ক্যাটাগরি বাছাই করুন</label>
                  <select name="category" className="w-full p-6 bg-white border-2 border-slate-100 rounded-[30px] font-black text-slate-900 outline-none focus:border-blue-600 shadow-sm appearance-none cursor-pointer">
                    {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                  </select>
                </div>

                <div className="space-y-2">
                  <label className="text-[11px] font-black text-slate-400 uppercase ml-4 tracking-[0.2em]">নোট (ঐচ্ছিক)</label>
                  <input type="text" name="note" placeholder="যেমন: অফিসের বেতন, বাজার..." className="w-full p-6 bg-white border-2 border-slate-100 rounded-[30px] font-black text-slate-900 outline-none focus:border-blue-600 shadow-sm" />
                </div>
              </div>

              <button type="submit" className={`w-full py-7 rounded-[35px] text-white font-black text-xl shadow-2xl active:scale-95 transition-all mb-4 ${modalType === TransactionType.INCOME ? 'bg-gradient-to-r from-green-600 to-emerald-600 hover:from-green-700 hover:to-emerald-700' : 'bg-gradient-to-r from-rose-600 to-pink-600 hover:from-rose-700 hover:to-pink-700'}`}>নিশ্চিত করুন</button>
            </form>
          </div>
        </div>
      )}

      {/* Budget Settings Modal */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[60px] p-10 animate-slideUp shadow-2xl overflow-y-auto max-h-[90vh] custom-scrollbar">
             <div className="flex justify-between items-center mb-10">
                <h2 className="text-2xl font-black uppercase text-slate-900">বাজেট লক্ষ্যমাত্রা</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-14 h-14 bg-slate-100 rounded-[24px] flex items-center justify-center text-slate-900 hover:bg-slate-200 transition-all shadow-sm"><i className="fas fa-times text-xl"></i></button>
             </div>

             <div className="space-y-7 mb-10">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="space-y-3">
                    <div className="flex items-center gap-3 ml-3">
                      <div className="w-8 h-8 bg-blue-50 text-blue-600 rounded-xl flex items-center justify-center text-sm shadow-sm">
                        <i className={`fas ${CATEGORY_ICONS[cat] || 'fa-tag'}`}>`</i>
                      </div>
                      <label className="text-[12px] font-black text-slate-800 uppercase tracking-widest">{cat}</label>
                    </div>
                    <input 
                      type="number" 
                      value={budgets[cat] || ''}
                      onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })}
                      placeholder="৳ বাজেট নেই"
                      className="w-full p-6 bg-slate-50 border-2 border-slate-100 rounded-[30px] font-black text-slate-900 outline-none focus:border-blue-600 focus:bg-white transition-all shadow-inner"
                    />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-7 bg-slate-900 text-white rounded-[35px] font-black uppercase tracking-[0.3em] text-sm shadow-2xl active:scale-95 transition-all mb-6">বাজেট আপডেট করুন</button>
          </div>
        </div>
      )}

      {/* History Modal */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex flex-col">
          <div className="bg-white flex-1 mt-24 rounded-t-[60px] p-10 overflow-y-auto animate-slideUp shadow-2xl">
             <div className="flex justify-between items-center mb-12">
                <h2 className="text-3xl font-black uppercase text-slate-900 tracking-tight">পূর্ববর্তী ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-16 h-16 bg-slate-100 rounded-[28px] flex items-center justify-center text-slate-900 transition-all hover:bg-slate-200 shadow-sm"><i className="fas fa-times text-xl"></i></button>
             </div>

             {archives.length > 0 ? (
               <div className="space-y-8">
                 {archives.map(arc => (
                   <div key={arc.id} className="group p-8 bg-slate-50 rounded-[45px] border border-slate-100 shadow-sm hover:bg-white hover:shadow-xl transition-all duration-300">
                      <div className="flex justify-between items-start mb-6">
                        <div>
                           <p className="text-[13px] text-indigo-600 uppercase font-black tracking-[0.2em] mb-1">{arc.monthName} {arc.year}</p>
                           <p className="text-slate-900 text-4xl font-black tracking-tighter mt-1">৳{arc.totalIncome - arc.totalExpense}</p>
                        </div>
                        <div className="w-14 h-14 bg-white rounded-3xl flex items-center justify-center shadow-md border border-slate-50 transition-transform group-hover:rotate-12">
                          <i className="fas fa-calendar-check text-blue-600 text-xl"></i>
                        </div>
                      </div>
                      <div className="flex gap-6 pt-6 border-t border-slate-200">
                        <div className="flex-1">
                          <p className="text-[10px] font-black text-green-600 uppercase mb-2 tracking-widest">আয়</p>
                          <p className="text-lg font-black text-slate-900 tracking-tight">৳{arc.totalIncome}</p>
                        </div>
                        <div className="w-px bg-slate-200 h-10 self-center"></div>
                        <div className="flex-1 text-right">
                          <p className="text-[10px] font-black text-rose-600 uppercase mb-2 tracking-widest">ব্যয়</p>
                          <p className="text-lg font-black text-slate-900 tracking-tight">৳{arc.totalExpense}</p>
                        </div>
                      </div>
                   </div>
                 ))}
               </div>
             ) : (
               <div className="text-center py-32 bg-slate-50 rounded-[60px] border-2 border-dashed border-slate-100 mt-10">
                  <div className="w-24 h-24 bg-white rounded-full flex items-center justify-center mx-auto mb-8 shadow-sm">
                    <i className="fas fa-archive text-slate-100 text-5xl"></i>
                  </div>
                  <p className="text-slate-400 font-black uppercase text-[12px] tracking-[0.4em]">ইতিহাস খালি</p>
               </div>
             )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  // Navigation Tabs: Home, Future, Tools
  const [activeTab, setActiveTab] = useState<'home' | 'future' | 'tools'>('home');
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));

  // Transactions State
  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  // Budgets State
  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  // History State
  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  // AI Insights State
  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);

  // Bill Splitter State
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  // Modals
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);

  // Persistence
  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  // Financial Summary
  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  // AI Logic
  const fetchAIAdvice = async () => {
    if (transactions.length === 0) {
      alert("পরামর্শ পেতে প্রথমে কিছু লেনদেন যোগ করুন।");
      return;
    }
    setLoadingAI(true);
    const finSummary: FinancialSummary = {
      totalIncome: summary.income,
      totalExpenses: summary.expense,
      balance: summary.balance,
      savings: summary.balance,
      previousMonthSavings: (archives[0]?.totalIncome ?? 0) - (archives[0]?.totalExpense ?? 0),
      categoryBreakdown: summary.breakdown,
      monthlyHistory: archives.slice(0, 5).map(a => ({
        month: a.monthName,
        income: a.totalIncome,
        expense: a.totalExpense
      }))
    };
    const results = await getFinancialInsights(transactions, finSummary);
    setInsights(results);
    setLoadingAI(false);
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: Date.now().toString(),
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
  };

  const deleteTransaction = (id: string) => {
    if (window.confirm('আপনি কি এই লেনদেনটি মুছে ফেলতে চান?')) {
      setTransactions(prev => prev.filter(t => t.id !== id));
    }
  };

  const activeBudgetData = useMemo(() => {
    return (Object.entries(budgets) as [string, number][]).filter(([_, amount]) => amount > 0);
  }, [budgets]);

  return (
    <div className="max-w-md mx-auto min-h-screen bg-slate-50 shadow-2xl flex flex-col font-sans relative pb-32 overflow-hidden">
      {/* Header */}
      <header className="bg-gradient-to-br from-indigo-700 via-blue-600 to-indigo-800 p-8 text-white rounded-b-[40px] shadow-2xl relative z-20">
        <div className="flex justify-between items-center mb-8">
          <div className="flex items-center gap-4">
             <div className="w-14 h-14 bg-white/20 rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/30 shadow-lg backdrop-blur-sm group">
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
                ) : (
                  <i className="fas fa-user-circle text-3xl text-blue-100"></i>
                )}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
