                    <label className="text-[11px] font-black text-slate-800 uppercase ml-3">{cat}</label>
                    <input 
                      type="number" 
                      value={budgets[cat] || ''}
                      onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })}
                      placeholder="৳ বাজেট দিন"
                      className="w-full p-5 bg-slate-50 border border-slate-200 rounded-[25px] font-black text-slate-900 outline-none focus:border-blue-600"
                    />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-6 bg-slate-900 text-white rounded-[30px] font-black uppercase text-sm shadow-xl mb-6">সংরক্ষণ করুন</button>
          </div>
        </div>
      )}

      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[55px] p-9 overflow-y-auto animate-slideUp shadow-2xl">
             <div className="flex justify-between items-center mb-10">
                <h2 className="text-2xl font-black text-slate-900">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-12 h-12 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900"><i className="fas fa-times"></i></button>
             </div>
             {archives.length > 0 ? (
               <div className="space-y-6">
                 {archives.map(arc => (
                   <div key={arc.id} className="p-7 bg-slate-50 rounded-[40px] border border-slate-100 shadow-sm">
                      <p className="text-[11px] text-indigo-600 uppercase font-black tracking-widest mb-1">{arc.monthName} {arc.year}</p>
                      <p className="text-slate-900 text-3xl font-black tracking-tighter">৳{arc.totalIncome - arc.totalExpense}</p>
                      <div className="flex gap-5 pt-5 mt-5 border-t border-slate-200">
                        <div className="flex-1">
                          <p className="text-[9px] font-black text-green-600 uppercase mb-1">আয়</p>
                          <p className="text-sm font-black text-slate-800">৳{arc.totalIncome}</p>
                        </div>
                        <div className="flex-1 text-right">
                          <p className="text-[9px] font-black text-rose-600 uppercase mb-1">ব্যয়</p>
                          <p className="text-sm font-black text-slate-800">৳{arc.totalExpense}</p>
                        </div>
                      </div>
                   </div>
                 ))}
               </div>
             ) : (
               <div className="text-center py-24 text-slate-400 font-black uppercase text-[11px]">ইতিহাস খালি</div>
             )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'future' | 'tools'>('home');
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  const fetchAIAdvice = async () => {
    if (transactions.length === 0) {
      alert("পরামর্শ পেতে প্রথমে কিছু লেনদেন যোগ করুন।");
      return;
    }
    setLoadingAI(true);
    const finSummary: FinancialSummary = {
      totalIncome: summary.income,
      totalExpenses: summary.expense,
      balance: summary.balance,
      savings: summary.balance,
      previousMonthSavings: (archives[0]?.totalIncome ?? 0) - (archives[0]?.totalExpense ?? 0),
      categoryBreakdown: summary.breakdown,
      monthlyHistory: archives.slice(0, 5).map(a => ({
        month: a.monthName,
        income: a.totalIncome,
        expense: a.totalExpense
      }))
    };
    const results = await getFinancialInsights(transactions, finSummary);
    setInsights(results);
    setLoadingAI(false);
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: Math.random().toString(36).substr(2, 9),
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
  };

  const deleteTransaction = (id: string) => {
    if (window.confirm('লেনদেনটি মুছে ফেলতে চান?')) {
      setTransactions(prev => prev.filter(t => t.id !== id));
    }
  };

  const activeBudgetData = useMemo(() => {
    return (Object.entries(budgets) as [string, number][]).filter(([_, amount]) => amount > 0);
  }, [budgets]);

  return (
    <div className="max-w-md mx-auto min-h-screen bg-[#F8FAFC] shadow-2xl flex flex-col font-sans relative pb-20 overflow-hidden">
      {/* Header - Slimmer & Darker */}
      <header className="bg-[#1E293B] p-6 text-white rounded-b-[35px] shadow-xl relative z-20">
        <div className="flex justify-between items-center mb-6">
          <div className="flex items-center gap-3">
             <div className="w-10 h-10 bg-blue-600 rounded-xl flex items-center justify-center relative overflow-hidden border border-white/10 shadow-lg">
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
                ) : (
                  <i className="fas fa-user text-sm text-white"></i>
                )}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div>
                <h1 className="text-lg font-bold uppercase tracking-tight">স্পার্ক</h1>
                <p className="text-[8px] font-bold text-slate-400 uppercase tracking-widest">পার্সোনাল ওয়ালেট</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-9 h-9 bg-white/5 rounded-lg flex items-center justify-center hover:bg-white/10 transition-all border border-white/5">
              <i className="fas fa-bullseye text-xs text-slate-300"></i>
            </button>
            <button onClick={() => setShowHistory(true)} className="w-9 h-9 bg-white/5 rounded-lg flex items-center justify-center hover:bg-white/10 transition-all border border-white/5">
              <i className="fas fa-history text-xs text-slate-300"></i>
            </button>
          </div>
        </div>

        <div className="text-center py-2">
          <p className="text-slate-400 text-[9px] font-bold uppercase tracking-[0.2em] mb-1">মোট ব্যালেন্স</p>
          <h2 className="text-3xl font-bold mb-6 tracking-tight">{formatCurrency(summary.balance)}</h2>

          <div className="grid grid-cols-2 gap-3">
            <div className="bg-white/5 p-3 rounded-2xl border border-white/5">
              <p className="text-[8px] uppercase font-bold text-slate-500 mb-1">মোট আয়</p>
              <p className="text-sm font-bold text-green-400">৳{summary.income}</p>
            </div>
            <div className="bg-white/5 p-3 rounded-2xl border border-white/5">
              <p className="text-[8px] uppercase font-bold text-slate-500 mb-1">মোট ব্যয়</p>
              <p className="text-sm font-bold text-rose-400">৳{summary.expense}</p>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-1 overflow-y-auto px-5 py-6">
        {activeTab === 'home' ? (
          <div className="space-y-8 animate-fadeIn">
            {/* Quick Actions - Attractive Cards */}
            <div className="grid grid-cols-2 gap-4">
              <button 
                onClick={() => setModalType(TransactionType.INCOME)} 
                className="group p-5 bg-white border border-slate-100 rounded-[28px] shadow-sm flex flex-col items-center gap-2 hover:shadow-md transition-all active:scale-95"
              >
                <div className="w-12 h-12 bg-green-500/10 text-green-600 rounded-2xl flex items-center justify-center text-xl transition-transform group-hover:scale-110">
                  <i className="fas fa-plus"></i>
                </div>
                <span className="text-[10px] font-bold text-slate-700 uppercase tracking-widest">টাকা যোগ</span>
              </button>

              <button 
                onClick={() => setModalType(TransactionType.EXPENSE)} 
                className="group p-5 bg-white border border-slate-100 rounded-[28px] shadow-sm flex flex-col items-center gap-2 hover:shadow-md transition-all active:scale-95"
              >
                <div className="w-12 h-12 bg-rose-500/10 text-rose-600 rounded-2xl flex items-center justify-center text-xl transition-transform group-hover:scale-110">
                  <i className="fas fa-minus"></i>
                </div>
                <span className="text-[10px] font-bold text-slate-700 uppercase tracking-widest">খরচ যোগ</span>
              </button>
            </div>

            {/* Budget Display */}
            {activeBudgetData.length > 0 && (
              <div className="space-y-3">
                <h3 className="font-bold text-slate-400 text-[9px] uppercase tracking-widest px-1">বাজেট প্রগতি</h3>
                <div className="bg-white p-5 rounded-[28px] border border-slate-100 shadow-sm space-y-4">
                  {activeBudgetData.map(([cat, budget]) => {
                    const spent = summary.breakdown[cat] || 0;
                    const percent = Math.min((spent / budget) * 100, 100);
                    const isOver = spent > budget;
                    return (
                      <div key={cat} className="space-y-2">
                        <div className="flex justify-between items-center text-[9px] font-bold uppercase">
                          <span className="text-slate-600">{cat}</span>
                          <span className={isOver ? 'text-rose-600' : 'text-slate-400'}>৳{spent} / ৳{budget}</span>
                        </div>
                        <div className="w-full h-1.5 bg-slate-50 rounded-full overflow-hidden">
                          <div 
                            className={`h-full rounded-full transition-all duration-700 ${isOver ? 'bg-rose-500' : 'bg-blue-600'}`}
                            style={{ width: `${percent}%` }}
                          ></div>
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            )}

            {/* List */}
            <div className="space-y-4">
              <h3 className="font-bold text-slate-400 text-[9px] uppercase tracking-widest px-1">সাম্প্রতিক লেনদেন</h3>
              {transactions.length > 0 ? (
                <div className="space-y-3">
                  {transactions.map(t => (
                    <div key={t.id} className="bg-white border border-slate-100 p-4 rounded-[22px] shadow-sm flex items-center justify-between group">
                      <div className="flex items-center gap-3">
                        <div className={`w-10 h-10 rounded-xl flex items-center justify-center text-md ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-bold text-slate-800 text-xs">{t.category}</p>
                          <p className="text-[8px] text-slate-400 font-bold uppercase tracking-tighter">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <div className="flex items-center gap-3">
                        <p className={`font-bold text-sm ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                          {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                        </p>
                        <button 
                          onClick={() => deleteTransaction(t.id)}
                          className="w-8 h-8 bg-slate-50 text-slate-300 rounded-lg flex items-center justify-center hover:bg-rose-50 hover:text-rose-500 transition-colors"
                        >
                          <i className="fas fa-trash-alt text-[10px]"></i>
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-10 bg-white rounded-[28px] border border-dashed border-slate-200">
                  <p className="text-slate-400 font-bold text-[9px] uppercase tracking-widest">লেনদেন নেই</p>
                </div>
              )}
            </div>
          </div>
        ) : activeTab === 'future' ? (
          <div className="space-y-8 animate-fadeIn">
            <div className="p-8 bg-blue-600 rounded-[30px] text-white shadow-lg relative overflow-hidden">
               <h3 className="text-xl font-bold mb-1">এআই কোচ</h3>
               <p className="text-[9px] uppercase font-bold text-blue-100 mb-8 opacity-70">আপনার আর্থিক ভবিষ্যৎ বিশ্লেষণ</p>
               <button 
                onClick={fetchAIAdvice}
                disabled={loadingAI}
                className="w-full py-4 bg-white text-blue-600 rounded-xl font-bold uppercase text-[10px] shadow-sm active:scale-95 transition-all"
               >
                 {loadingAI ? 'অপেক্ষা করুন...' : 'এআই পরামর্শ নিন'}
               </button>
               <i className="fas fa-robot absolute -right-4 -bottom-4 text-8xl text-white/10"></i>
            </div>
            <div className="space-y-3">
              {insights.map((insight, idx) => (
                <div key={idx} className={`p-5 rounded-2xl border-2 ${insight.type === 'success' ? 'bg-green-50 border-green-100' : 'bg-blue-50 border-blue-100'}`}>
                   <h4 className="font-bold text-slate-800 text-xs mb-1">{insight.title}</h4>
                   <p className="text-slate-600 text-[10px] font-bold leading-relaxed">{insight.description}</p>
                </div>
              ))}
            </div>
          </div>
        ) : (
          <div className="space-y-6 animate-fadeIn">
            <div className="bg-white p-7 rounded-[30px] border border-slate-100 shadow-sm space-y-6">
              <h3 className="font-bold text-slate-800 uppercase text-[10px] tracking-widest">বিল স্প্লিটার</h3>
              <div className="grid grid-cols-2 gap-4">
                <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="মোট বিল" className="p-4 bg-slate-50 border border-slate-100 rounded-xl font-bold text-xs outline-none focus:border-blue-600" />
                <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} placeholder="মানুষ" className="p-4 bg-slate-50 border border-slate-100 rounded-xl font-bold text-xs outline-none focus:border-blue-600" />
              </div>
              <div className="p-6 bg-slate-100 rounded-2xl text-center">
                <p className="text-[8px] font-bold text-slate-400 uppercase mb-1">প্রতি জনের ভাগ</p>
                <p className="text-2xl font-bold text-slate-800">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
              </div>
            </div>

            <button 
              onClick={() => { if(window.confirm('সব তথ্য মুছে ফেলবেন?')) { setTransactions([]); setArchives([]); setBudgets({}); } }}
              className="w-full p-4 bg-rose-50 border border-rose-100 text-rose-600 rounded-xl font-bold uppercase text-[9px] tracking-widest hover:bg-rose-600 hover:text-white transition-all"
            >
              সব তথ্য রিসেট করুন
            </button>
          </div>
        )}
      </main>

      {/* Slim Modern Navigation */}
      <nav className="fixed bottom-3 left-1/2 -translate-x-1/2 w-[90%] max-w-[320px] bg-[#1E293B] px-2 py-2 flex justify-between items-center z-[100] shadow-2xl rounded-2xl border border-white/5">
        <button 
          onClick={() => setActiveTab('home')} 
          className={`flex-1 flex flex-col items-center gap-1 py-2 transition-all duration-300 rounded-xl ${activeTab === 'home' ? 'bg-blue-600 text-white' : 'text-slate-500'}`}
        >
          <i className="fas fa-home text-xs"></i>
          <span className="text-[7px] font-bold uppercase tracking-widest">হোম</span>
        </button>

        <button 
          onClick={() => setActiveTab('future')} 
          className={`flex-1 flex flex-col items-center gap-1 py-2 transition-all duration-300 rounded-xl ${activeTab === 'future' ? 'bg-blue-600 text-white' : 'text-slate-500'}`}
        >
          <i className="fas fa-brain text-xs"></i>
          <span className="text-[7px] font-bold uppercase tracking-widest">এআই</span>
        </button>

        <button 
          onClick={() => setActiveTab('tools')} 
          className={`flex-1 flex flex-col items-center gap-1 py-2 transition-all duration-300 rounded-xl ${activeTab === 'tools' ? 'bg-blue-600 text-white' : 'text-slate-500'}`}
        >
          <i className="fas fa-plus-circle text-xs"></i>
          <span className="text-[7px] font-bold uppercase tracking-widest">টুলস</span>
        </button>
      </nav>

      {/* Transaction Modal - Compact Card */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[200] flex items-center justify-center p-6">
          <div className="bg-white w-full max-w-[320px] rounded-[30px] p-6 animate-fadeIn shadow-2xl border border-slate-100">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-sm font-bold uppercase text-slate-800">{modalType === TransactionType.INCOME ? 'টাকা যোগ' : 'খরচ যোগ'}</h2>
              <button onClick={() => setModalType(null)} className="w-8 h-8 bg-slate-100 rounded-lg flex items-center justify-center text-slate-500 hover:bg-slate-200 transition-all"><i className="fas fa-times text-xs"></i></button>
            </div>

            <form onSubmit={addTransaction} className="space-y-4">
              <div className="bg-slate-50 rounded-2xl p-4 border border-slate-100">
                <p className="text-center text-[8px] font-bold text-slate-400 uppercase tracking-widest mb-2">পরিমাণ (৳)</p>
                <input 
                  type="number" 
                  name="amount" 
                  required 
                  placeholder="০" 
                  autoFocus 
                  className="w-full text-2xl font-bold text-center outline-none text-slate-900 bg-transparent tracking-tight"
                />
              </div>

              <div className="space-y-3">
                <select name="category" className="w-full p-4 bg-white border border-slate-200 rounded-xl font-bold text-xs text-slate-700 outline-none focus:border-blue-600 appearance-none">
                  {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                </select>
                <input type="text" name="note" placeholder="নোট (ঐচ্ছিক)" className="w-full p-4 bg-white border border-slate-200 rounded-xl font-bold text-xs text-slate-700 outline-none focus:border-blue-600" />
              </div>

              <button type="submit" className={`w-full py-4 rounded-xl text-white font-bold text-xs uppercase tracking-widest shadow-lg transition-all ${modalType === TransactionType.INCOME ? 'bg-green-600 hover:bg-green-700' : 'bg-rose-600 hover:bg-rose-700'}`}>নিশ্চিত করুন</button>
            </form>
          </div>
        </div>
      )}

      {/* Budget & History remain in compact drawers/modals */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[35px] p-7 animate-slideUp shadow-2xl overflow-y-auto max-h-[80vh]">
             <div className="flex justify-between items-center mb-6">
                <h2 className="text-sm font-bold text-slate-800 uppercase tracking-widest">বাজেট লক্ষ্য</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-9 h-9 bg-slate-100 rounded-lg flex items-center justify-center"><i className="fas fa-times"></i></button>
             </div>
             <div className="space-y-4 mb-6">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="flex items-center gap-3">
                    <label className="w-20 text-[10px] font-bold text-slate-500 uppercase">{cat}</label>
                    <input 
                      type="number" 
                      value={budgets[cat] || ''}
                      onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })}
                      placeholder="৳ ০"
                      className="flex-1 p-3 bg-slate-50 border border-slate-100 rounded-xl font-bold text-xs outline-none focus:border-blue-600"
                    />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-4 bg-[#1E293B] text-white rounded-xl font-bold uppercase text-[10px] tracking-widest">সংরক্ষণ করুন</button>
          </div>
        </div>
      )}

      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[200] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[35px] p-6 overflow-y-auto animate-slideUp shadow-2xl">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-lg font-bold text-slate-800">আর্কাইভ</h2>
                <button onClick={() => setShowHistory(false)} className="w-10 h-10 bg-slate-100 rounded-xl flex items-center justify-center"><i className="fas fa-times"></i></button>
             </div>
             {archives.length > 0 ? (
               <div className="space-y-4">
                 {archives.map(arc => (
                   <div key={arc.id} className="p-5 bg-slate-50 rounded-2xl border border-slate-100">
                      <p className="text-[10px] text-blue-600 uppercase font-bold mb-1">{arc.monthName} {arc.year}</p>
                      <p className="text-slate-800 text-xl font-bold">৳{arc.totalIncome - arc.totalExpense}</p>
                      <div className="flex gap-4 pt-3 mt-3 border-t border-slate-200 text-[9px] font-bold">
                        <span className="text-green-600">আয়: ৳{arc.totalIncome}</span>
                        <span className="text-rose-600">ব্যয়: ৳{arc.totalExpense}</span>
                      </div>
                   </div>
                 ))}
               </div>
             ) : (
               <p className="text-center py-20 text-slate-400 font-bold text-[10px] uppercase">ইতিহাস খালি</p>
             )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'future' | 'tools'>('home');
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  // Persistence
  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  const fetchAIAdvice = async () => {
    if (transactions.length === 0) {
      alert("পরামর্শ পেতে প্রথমে কিছু লেনদেন যোগ করুন।");
      return;
    }
    setLoadingAI(true);
    const finSummary: FinancialSummary = {
      totalIncome: summary.income,
      totalExpenses: summary.expense,
      balance: summary.balance,
      savings: summary.balance,
      previousMonthSavings: (archives[0]?.totalIncome ?? 0) - (archives[0]?.totalExpense ?? 0),
      categoryBreakdown: summary.breakdown,
      monthlyHistory: archives.slice(0, 5).map(a => ({
        month: a.monthName,
        income: a.totalIncome,
        expense: a.totalExpense
      }))
    };
    const results = await getFinancialInsights(transactions, finSummary);
    setInsights(results);
    setLoadingAI(false);
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };

    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
  };

  const deleteTransaction = (id: string) => {
    if (window.confirm('লেনদেনটি কি মুছে ফেলতে চান?')) {
      setTransactions(prev => prev.filter(t => t.id !== id));
    }
  };

  const activeBudgetData = useMemo(() => {
    return (Object.entries(budgets) as [string, number][]).filter(([_, amount]) => amount > 0);
  }, [budgets]);

  return (
    <div className="max-w-md mx-auto min-h-screen bg-[#FDFDFD] shadow-2xl flex flex-col font-sans relative pb-20 overflow-hidden">
      {/* Dynamic Slim Header */}
      <header className="bg-slate-900 p-6 text-white rounded-b-[40px] shadow-lg relative z-20">
        <div className="flex justify-between items-center mb-6">
          <div className="flex items-center gap-3">
             <div className="w-10 h-10 bg-indigo-600 rounded-xl flex items-center justify-center relative overflow-hidden ring-1 ring-white/20">
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
                ) : (
                  <i className="fas fa-user-circle text-lg"></i>
                )}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
