};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'future' | 'tools'>('home');
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  const fetchAIAdvice = async () => {
    if (transactions.length === 0) {
      alert("পরামর্শ পেতে প্রথমে কিছু লেনদেন যোগ করুন।");
      return;
    }
    setLoadingAI(true);
    const finSummary: FinancialSummary = {
      totalIncome: summary.income,
      totalExpenses: summary.expense,
      balance: summary.balance,
      savings: summary.balance,
      previousMonthSavings: (archives[0]?.totalIncome ?? 0) - (archives[0]?.totalExpense ?? 0),
      categoryBreakdown: summary.breakdown,
      monthlyHistory: archives.slice(0, 5).map(a => ({
        month: a.monthName,
        income: a.totalIncome,
        expense: a.totalExpense
      }))
    };
    const results = await getFinancialInsights(transactions, finSummary);
    setInsights(results);
    setLoadingAI(false);
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
  };

  const deleteTransaction = (id: string) => {
    if (window.confirm('আপনি কি এই লেনদেনটি মুছে ফেলতে চান?')) {
      setTransactions(prev => prev.filter(t => t.id !== id));
    }
  };

  const activeBudgetData = useMemo(() => {
    return (Object.entries(budgets) as [string, number][]).filter(([_, amount]) => amount > 0);
  }, [budgets]);

  return (
    <div className="max-w-md mx-auto min-h-screen bg-[#f8fafc] shadow-2xl flex flex-col font-sans relative pb-24 overflow-hidden">
      {/* Header */}
      <header className="bg-gradient-to-br from-blue-700 via-indigo-600 to-indigo-800 p-7 text-white rounded-b-[45px] shadow-2xl relative z-20">
        <div className="flex justify-between items-center mb-6">
          <div className="flex items-center gap-3">
             <div className="w-12 h-12 bg-white/20 rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/30 shadow-lg backdrop-blur-md">
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
                ) : (
                  <i className="fas fa-user-circle text-2xl text-white"></i>
                )}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div>
                <h1 className="text-xl font-black uppercase tracking-tight">স্পার্ক</h1>
                <p className="text-[9px] font-black text-blue-200 mt-0.5 uppercase tracking-widest opacity-80">স্মার্ট ওয়ালেট</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center hover:bg-white/20 transition-all border border-white/10 backdrop-blur-md">
              <i className="fas fa-bullseye text-sm"></i>
            </button>
            <button onClick={() => setShowHistory(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center hover:bg-white/20 transition-all border border-white/10 backdrop-blur-md">
              <i className="fas fa-history text-sm"></i>
            </button>
          </div>
        </div>

        <div className="text-center py-2">
          <p className="text-blue-100 text-[10px] font-black uppercase tracking-[0.3em] mb-2 opacity-90">বর্তমান ব্যালেন্স</p>
          <h2 className="text-4xl font-black mb-8 tracking-tighter drop-shadow-lg">{formatCurrency(summary.balance)}</h2>

          <div className="grid grid-cols-2 gap-3">
            <div className="bg-white/10 backdrop-blur-xl p-3.5 rounded-[24px] border border-white/20 shadow-sm">
              <p className="text-[9px] uppercase font-black text-blue-200 mb-1">মোট আয়</p>
              <p className="text-lg font-black text-green-300">৳{summary.income}</p>
            </div>
            <div className="bg-white/10 backdrop-blur-xl p-3.5 rounded-[24px] border border-white/20 shadow-sm">
              <p className="text-[9px] uppercase font-black text-blue-200 mb-1">মোট ব্যয়</p>
              <p className="text-lg font-black text-rose-300">৳{summary.expense}</p>
            </div>
          </div>
        </div>
      </header>

      {/* Main Area */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-8">
        {activeTab === 'home' ? (
          <div className="space-y-10 animate-fadeIn">
            {/* Quick Add Balance */}
            <div className="grid grid-cols-2 gap-4">
              <button 
                onClick={() => setModalType(TransactionType.INCOME)} 
                className="relative overflow-hidden p-6 bg-white border border-slate-100 rounded-[35px] flex flex-col items-center gap-3 transition-all hover:shadow-2xl hover:-translate-y-1 active:scale-95 group"
              >
                <div className="absolute top-0 right-0 w-16 h-16 bg-green-50 rounded-full -mr-8 -mt-8 group-hover:scale-150 transition-transform duration-500"></div>
                <div className="w-14 h-14 bg-gradient-to-br from-green-400 to-green-600 text-white rounded-[22px] flex items-center justify-center text-2xl shadow-lg shadow-green-100 relative z-10">
                  <i className="fas fa-plus"></i>
                </div>
                <span className="text-[11px] font-black text-slate-800 uppercase tracking-[0.15em] relative z-10">টাকা যোগ</span>
              </button>

              <button 
                onClick={() => setModalType(TransactionType.EXPENSE)} 
                className="relative overflow-hidden p-6 bg-white border border-slate-100 rounded-[35px] flex flex-col items-center gap-3 transition-all hover:shadow-2xl hover:-translate-y-1 active:scale-95 group"
              >
                <div className="absolute top-0 right-0 w-16 h-16 bg-rose-50 rounded-full -mr-8 -mt-8 group-hover:scale-150 transition-transform duration-500"></div>
                <div className="w-14 h-14 bg-gradient-to-br from-rose-400 to-rose-600 text-white rounded-[22px] flex items-center justify-center text-2xl shadow-lg shadow-rose-100 relative z-10">
                  <i className="fas fa-minus"></i>
                </div>
                <span className="text-[11px] font-black text-slate-800 uppercase tracking-[0.15em] relative z-10">খরচ যোগ</span>
              </button>
            </div>

            {/* Budget Progress */}
            {activeBudgetData.length > 0 && (
              <div className="space-y-4">
                <h3 className="font-black text-slate-900 text-[10px] uppercase tracking-[0.2em] px-2 opacity-60">বাজেট প্রগতি</h3>
                <div className="bg-white p-6 rounded-[35px] border border-slate-100 shadow-sm space-y-4">
                  {activeBudgetData.map(([cat, budget]) => {
                    const spent = summary.breakdown[cat] || 0;
                    const percent = Math.min((spent / budget) * 100, 100);
                    const isOver = spent > budget;
                    return (
                      <div key={cat} className="space-y-2">
                        <div className="flex justify-between items-center text-[10px] font-black uppercase">
                          <span className="text-slate-700">{cat}</span>
                          <span className={isOver ? 'text-rose-600' : 'text-slate-500'}>৳{spent} / ৳{budget}</span>
                        </div>
                        <div className="w-full h-2 bg-slate-100 rounded-full overflow-hidden">
                          <div 
                            className={`h-full rounded-full transition-all duration-1000 ${isOver ? 'bg-rose-500' : 'bg-indigo-600'}`}
                            style={{ width: `${percent}%` }}
                          ></div>
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            )}

            {/* Transactions List */}
            <div className="space-y-4">
              <div className="flex justify-between items-center px-2">
                <h3 className="font-black text-slate-900 text-[10px] uppercase tracking-[0.2em] opacity-60">সাম্প্রতিক লেনদেন</h3>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3">
                  {transactions.map(t => (
                    <div key={t.id} className="bg-white border border-slate-100 p-4 rounded-[28px] shadow-sm flex items-center justify-between group hover:border-indigo-100 transition-colors">
                      <div className="flex items-center gap-4">
                        <div className={`w-11 h-11 rounded-2xl flex items-center justify-center text-lg ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-800 text-[14px]">{t.category}</p>
                          <p className="text-[9px] text-slate-400 font-black uppercase">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <div className="flex items-center gap-3">
                        <p className={`font-black text-sm ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                          {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                        </p>
                        <button 
                          onClick={() => deleteTransaction(t.id)}
                          className="w-9 h-9 bg-slate-50 text-slate-300 rounded-xl flex items-center justify-center hover:bg-rose-50 hover:text-rose-500 transition-all active:scale-90"
                        >
                          <i className="fas fa-trash-alt text-xs"></i>
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-16 bg-white rounded-[40px] border border-dashed border-slate-200">
                  <i className="fas fa-receipt text-slate-200 text-3xl mb-3"></i>
                  <p className="text-slate-400 font-black uppercase text-[9px] tracking-widest">খালি তালিকা</p>
                </div>
              )}
            </div>
          </div>
        ) : activeTab === 'future' ? (
          <div className="space-y-8 animate-fadeIn">
            <div className="p-9 bg-slate-900 rounded-[45px] text-white relative overflow-hidden shadow-2xl">
              <div className="relative z-10">
                <h3 className="text-2xl font-black mb-2">ভবিষ্যৎ সঞ্চয়</h3>
                <p className="text-[10px] text-slate-400 font-black uppercase tracking-widest mb-10 opacity-70">এআই এর মাধ্যমে আর্থিক পূর্বাভাস</p>
                <button 
                  onClick={fetchAIAdvice}
                  disabled={loadingAI}
                  className="w-full py-4.5 bg-gradient-to-r from-indigo-600 to-blue-600 text-white rounded-[20px] font-black uppercase text-[10px] shadow-xl active:scale-95 transition-all flex items-center justify-center gap-3"
                >
                  {loadingAI ? <><i className="fas fa-spinner animate-spin"></i> প্রসেসিং...</> : <><i className="fas fa-wand-magic-sparkles"></i> পরামর্শ নিন</>}
                </button>
              </div>
              <div className="absolute -right-16 -bottom-16 w-56 h-56 bg-indigo-500/10 rounded-full blur-3xl"></div>
            </div>
            {insights.map((insight, idx) => (
              <div key={idx} className={`p-6 rounded-[35px] border ${insight.type === 'success' ? 'bg-green-50 border-green-100' : insight.type === 'warning' ? 'bg-rose-50 border-rose-100' : 'bg-blue-50 border-blue-100'} animate-fadeIn`}>
                <div className="flex gap-4">
                  <div className={`w-11 h-11 rounded-2xl flex items-center justify-center text-xl shadow-sm ${insight.type === 'success' ? 'bg-green-500 text-white' : insight.type === 'warning' ? 'bg-rose-500 text-white' : 'bg-blue-500 text-white'}`}>
                    <i className={`fas ${insight.type === 'success' ? 'fa-check' : insight.type === 'warning' ? 'fa-exclamation' : 'fa-lightbulb'}`}></i>
                  </div>
                  <div>
                    <h4 className="font-black text-slate-900 text-[14px] mb-1">{insight.title}</h4>
                    <p className="text-slate-600 text-[12px] font-bold leading-relaxed">{insight.description}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="space-y-8 animate-fadeIn">
            <div className="bg-white p-9 rounded-[45px] border border-slate-100 shadow-sm space-y-7">
              <div className="flex items-center gap-3 mb-2">
                <div className="w-12 h-12 bg-indigo-600 text-white rounded-2xl flex items-center justify-center text-xl shadow-lg shadow-indigo-100"><i className="fas fa-calculator"></i></div>
                <h3 className="font-black text-slate-900 uppercase text-xs tracking-widest">বিল স্প্লিটার</h3>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ ০" className="p-5 bg-slate-50 border border-slate-200 rounded-[22px] font-black text-slate-900 outline-none focus:border-indigo-600 transition-all shadow-inner" />
                <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} className="p-5 bg-slate-50 border border-slate-200 rounded-[22px] font-black text-slate-900 outline-none focus:border-indigo-600 transition-all shadow-inner" />
              </div>
              <div className="p-9 bg-gradient-to-br from-indigo-600 to-blue-700 rounded-[35px] text-center shadow-lg">
                <p className="text-[10px] font-black text-blue-100 uppercase mb-2 tracking-widest">প্রতি জনের ভাগ</p>
                <p className="text-4xl font-black text-white">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
              </div>
            </div>
            <button 
              onClick={() => { if(window.confirm('সব তথ্য মুছে ফেলবেন?')) { setTransactions([]); setArchives([]); setBudgets({}); } }}
              className="w-full p-6 text-rose-500 font-black uppercase text-[10px] tracking-widest hover:text-rose-700 transition-all"
            >
              সব তথ্য রিসেট করুন
            </button>
          </div>
        )}
      </main>

      {/* Modern Compact Floating Navigation */}
      <nav className="fixed bottom-5 left-1/2 -translate-x-1/2 w-[90%] max-w-[350px] bg-white/90 backdrop-blur-2xl border border-slate-200 px-2 py-2 flex justify-between items-center z-[100] shadow-[0_15px_35px_-10px_rgba(0,0,0,0.15)] rounded-[30px]">
        <button 
          onClick={() => setActiveTab('home')} 
          className={`flex-1 flex flex-col items-center gap-1 py-3 transition-all duration-300 rounded-[22px] ${activeTab === 'home' ? 'text-blue-700 bg-blue-50/80 shadow-inner' : 'text-slate-400 hover:text-slate-600'}`}
        >
          <i className={`fas fa-house-chimney text-lg ${activeTab === 'home' ? 'scale-110' : 'opacity-70'}`}></i>
          <span className={`text-[8px] font-black uppercase tracking-widest ${activeTab === 'home' ? 'opacity-100' : 'opacity-0'}`}>হোম</span>
        </button>

        <button 
          onClick={() => setActiveTab('future')} 
          className={`flex-1 flex flex-col items-center gap-1 py-3 transition-all duration-300 rounded-[22px] ${activeTab === 'future' ? 'text-indigo-700 bg-indigo-50/80 shadow-inner' : 'text-slate-400 hover:text-slate-600'}`}
        >
          <i className={`fas fa-rocket text-lg ${activeTab === 'future' ? 'scale-110' : 'opacity-70'}`}></i>
          <span className={`text-[8px] font-black uppercase tracking-widest ${activeTab === 'future' ? 'opacity-100' : 'opacity-0'}`}>ভবিষ্যৎ</span>
        </button>

        <button 
          onClick={() => setActiveTab('tools')} 
          className={`flex-1 flex flex-col items-center gap-1 py-3 transition-all duration-300 rounded-[22px] ${activeTab === 'tools' ? 'text-blue-700 bg-blue-50/80 shadow-inner' : 'text-slate-400 hover:text-slate-600'}`}
        >
          <i className={`fas fa-layer-group text-lg ${activeTab === 'tools' ? 'scale-110' : 'opacity-70'}`}></i>
          <span className={`text-[8px] font-black uppercase tracking-widest ${activeTab === 'tools' ? 'opacity-100' : 'opacity-0'}`}>টুলস</span>
        </button>
      </nav>

      {/* Transaction Modal */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[50px] p-9 animate-slideUp shadow-2xl">
            <div className="flex justify-between items-center mb-9">
              <h2 className="text-xl font-black uppercase text-slate-900">{modalType === TransactionType.INCOME ? 'টাকা যোগ' : 'খরচ যোগ'}</h2>
              <button onClick={() => setModalType(null)} className="w-12 h-12 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900"><i className="fas fa-times"></i></button>
            </div>
            <form onSubmit={addTransaction} className="space-y-7">
              <div className="bg-slate-50 rounded-[35px] p-10 border border-slate-100 text-center">
                <p className="text-[10px] font-black text-slate-400 uppercase tracking-[0.4em] mb-4">টাকার পরিমাণ (৳)</p>
                <input type="number" name="amount" required placeholder="০" autoFocus className="w-full text-5xl font-black text-center outline-none text-slate-900 bg-transparent tracking-tighter" />
              </div>
              <div className="space-y-4">
                <select name="category" className="w-full p-5 bg-white border-2 border-slate-100 rounded-[28px] font-black text-slate-900 outline-none focus:border-blue-600 appearance-none">
                  {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                </select>
                <input type="text" name="note" placeholder="নোট (যেমন: বাজার, বেতন...)" className="w-full p-5 bg-white border-2 border-slate-100 rounded-[28px] font-black text-slate-900 outline-none focus:border-blue-600 shadow-sm" />
              </div>
              <button type="submit" className={`w-full py-6 rounded-[30px] text-white font-black text-lg shadow-xl active:scale-95 transition-all mb-4 ${modalType === TransactionType.INCOME ? 'bg-gradient-to-r from-green-500 to-emerald-600' : 'bg-gradient-to-r from-rose-500 to-pink-600'}`}>সংরক্ষণ করুন</button>
            </form>
          </div>
        </div>
      )}

      {/* Settings Modal */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[50px] p-9 animate-slideUp shadow-2xl overflow-y-auto max-h-[85vh] custom-scrollbar">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-xl font-black text-slate-900">বাজেট লক্ষ্য</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-12 h-12 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900"><i className="fas fa-times"></i></button>
             </div>
             <div className="space-y-5 mb-8">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="space-y-2">
                    <label className="text-[11px] font-black text-slate-800 uppercase ml-3">{cat}</label>
                    <input 
                      type="number" 
                      value={budgets[cat] || ''}
                      onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })}
                      placeholder="৳ বাজেট দিন"
                      className="w-full p-5 bg-slate-50 border border-slate-200 rounded-[25px] font-black text-slate-900 outline-none focus:border-blue-600"
                    />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-6 bg-slate-900 text-white rounded-[30px] font-black uppercase text-sm shadow-xl mb-6">সংরক্ষণ করুন</button>
          </div>
        </div>
      )}

      {/* History Modal */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[55px] p-9 overflow-y-auto animate-slideUp shadow-2xl">
             <div className="flex justify-between items-center mb-10">
                <h2 className="text-2xl font-black text-slate-900">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-12 h-12 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900"><i className="fas fa-times"></i></button>
             </div>
             {archives.length > 0 ? (
               <div className="space-y-6">
                 {archives.map(arc => (
                   <div key={arc.id} className="p-7 bg-slate-50 rounded-[40px] border border-slate-100 shadow-sm">
                      <p className="text-[11px] text-indigo-600 uppercase font-black tracking-widest mb-1">{arc.monthName} {arc.year}</p>
                      <p className="text-slate-900 text-3xl font-black tracking-tighter">৳{arc.totalIncome - arc.totalExpense}</p>
                      <div className="flex gap-5 pt-5 mt-5 border-t border-slate-200">
                        <div className="flex-1">
                          <p className="text-[9px] font-black text-green-600 uppercase mb-1">আয়</p>
                          <p className="text-sm font-black text-slate-800">৳{arc.totalIncome}</p>
                        </div>
                        <div className="flex-1 text-right">
                          <p className="text-[9px] font-black text-rose-600 uppercase mb-1">ব্যয়</p>
                          <p className="text-sm font-black text-slate-800">৳{arc.totalExpense}</p>
                        </div>
                      </div>
                   </div>
                 ))}
               </div>
             ) : (
               <div className="text-center py-24 text-slate-400 font-black uppercase text-[11px]">ইতিহাস খালি</div>
             )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'future' | 'tools'>('home');
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  const fetchAIAdvice = async () => {
    if (transactions.length === 0) {
      alert("পরামর্শ পেতে প্রথমে কিছু লেনদেন যোগ করুন।");
      return;
    }
    setLoadingAI(true);
    const finSummary: FinancialSummary = {
      totalIncome: summary.income,
      totalExpenses: summary.expense,
      balance: summary.balance,
      savings: summary.balance,
      previousMonthSavings: (archives[0]?.totalIncome ?? 0) - (archives[0]?.totalExpense ?? 0),
      categoryBreakdown: summary.breakdown,
      monthlyHistory: archives.slice(0, 5).map(a => ({
        month: a.monthName,
        income: a.totalIncome,
        expense: a.totalExpense
      }))
    };
    const results = await getFinancialInsights(transactions, finSummary);
    setInsights(results);
    setLoadingAI(false);
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
  };

  const deleteTransaction = (id: string) => {
    if (window.confirm('আপনি কি এই লেনদেনটি মুছে ফেলতে চান?')) {
      setTransactions(prev => prev.filter(t => t.id !== id));
    }
  };

  const activeBudgetData = useMemo(() => {
    return (Object.entries(budgets) as [string, number][]).filter(([_, amount]) => amount > 0);
  }, [budgets]);

  return (
    <div className="max-w-md mx-auto min-h-screen bg-[#f8fafc] shadow-2xl flex flex-col font-sans relative pb-24 overflow-hidden">
      {/* Header */}
      <header className="bg-gradient-to-br from-blue-700 via-indigo-600 to-indigo-800 p-7 text-white rounded-b-[45px] shadow-2xl relative z-20">
        <div className="flex justify-between items-center mb-6">
          <div className="flex items-center gap-3">
             <div className="w-12 h-12 bg-white/20 rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/30 shadow-lg backdrop-blur-md">
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
                ) : (
                  <i className="fas fa-user-circle text-2xl text-white"></i>
                )}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div>
                <h1 className="text-xl font-black uppercase tracking-tight">স্পার্ক</h1>
                <p className="text-[9px] font-black text-blue-200 mt-0.5 uppercase tracking-widest opacity-80">স্মার্ট ওয়ালেট</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center hover:bg-white/20 transition-all border border-white/10 backdrop-blur-md">
              <i className="fas fa-bullseye text-sm"></i>
            </button>
            <button onClick={() => setShowHistory(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center hover:bg-white/20 transition-all border border-white/10 backdrop-blur-md">
              <i className="fas fa-history text-sm"></i>
            </button>
          </div>
        </div>

        <div className="text-center py-2">
          <p className="text-blue-100 text-[10px] font-black uppercase tracking-[0.3em] mb-2 opacity-90">বর্তমান ব্যালেন্স</p>
          <h2 className="text-4xl font-black mb-8 tracking-tighter drop-shadow-lg">{formatCurrency(summary.balance)}</h2>

          <div className="grid grid-cols-2 gap-3">
            <div className="bg-white/10 backdrop-blur-xl p-3.5 rounded-[24px] border border-white/20 shadow-sm">
              <p className="text-[9px] uppercase font-black text-blue-200 mb-1">মোট আয়</p>
              <p className="text-lg font-black text-green-300">৳{summary.income}</p>
            </div>
            <div className="bg-white/10 backdrop-blur-xl p-3.5 rounded-[24px] border border-white/20 shadow-sm">
              <p className="text-[9px] uppercase font-black text-blue-200 mb-1">মোট ব্যয়</p>
              <p className="text-lg font-black text-rose-300">৳{summary.expense}</p>
            </div>
          </div>
        </div>
      </header>

      {/* Main Area */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-8">
        {activeTab === 'home' ? (
          <div className="space-y-10 animate-fadeIn">
            {/* Quick Add Balance */}
            <div className="grid grid-cols-2 gap-4">
              <button 
                onClick={() => setModalType(TransactionType.INCOME)} 
                className="relative overflow-hidden p-6 bg-white border border-slate-100 rounded-[35px] flex flex-col items-center gap-3 transition-all hover:shadow-2xl hover:-translate-y-1 active:scale-95 group"
              >
                <div className="absolute top-0 right-0 w-16 h-16 bg-green-50 rounded-full -mr-8 -mt-8 group-hover:scale-150 transition-transform duration-500"></div>
                <div className="w-14 h-14 bg-gradient-to-br from-green-400 to-green-600 text-white rounded-[22px] flex items-center justify-center text-2xl shadow-lg shadow-green-100 relative z-10">
                  <i className="fas fa-plus"></i>
                </div>
                <span className="text-[11px] font-black text-slate-800 uppercase tracking-[0.15em] relative z-10">টাকা যোগ</span>
              </button>

              <button 
                onClick={() => setModalType(TransactionType.EXPENSE)} 
                className="relative overflow-hidden p-6 bg-white border border-slate-100 rounded-[35px] flex flex-col items-center gap-3 transition-all hover:shadow-2xl hover:-translate-y-1 active:scale-95 group"
              >
                <div className="absolute top-0 right-0 w-16 h-16 bg-rose-50 rounded-full -mr-8 -mt-8 group-hover:scale-150 transition-transform duration-500"></div>
                <div className="w-14 h-14 bg-gradient-to-br from-rose-400 to-rose-600 text-white rounded-[22px] flex items-center justify-center text-2xl shadow-lg shadow-rose-100 relative z-10">
                  <i className="fas fa-minus"></i>
                </div>
                <span className="text-[11px] font-black text-slate-800 uppercase tracking-[0.15em] relative z-10">খরচ যোগ</span>
              </button>
            </div>

            {/* Transactions List */}
            <div className="space-y-4">
              <div className="flex justify-between items-center px-2">
                <h3 className="font-black text-slate-900 text-[10px] uppercase tracking-[0.2em] opacity-60">সাম্প্রতিক লেনদেন</h3>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3">
                  {transactions.map(t => (
                    <div key={t.id} className="bg-white border border-slate-100 p-4 rounded-[28px] shadow-sm flex items-center justify-between group hover:border-indigo-100 transition-colors">
                      <div className="flex items-center gap-4">
                        <div className={`w-11 h-11 rounded-2xl flex items-center justify-center text-lg ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-800 text-[14px]">{t.category}</p>
                          <p className="text-[9px] text-slate-400 font-black uppercase">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <div className="flex items-center gap-3">
                        <p className={`font-black text-sm ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                          {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                        </p>
                        <button 
                          onClick={() => deleteTransaction(t.id)}
                          className="w-9 h-9 bg-slate-50 text-slate-300 rounded-xl flex items-center justify-center hover:bg-rose-50 hover:text-rose-500 transition-all active:scale-90"
                        >
                          <i className="fas fa-trash-alt text-xs"></i>
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-16 bg-white rounded-[40px] border border-dashed border-slate-200">
                  <i className="fas fa-receipt text-slate-200 text-3xl mb-3"></i>
                  <p className="text-slate-400 font-black uppercase text-[9px] tracking-widest">খালি তালিকা</p>
                </div>
              )}
            </div>
          </div>
        ) : activeTab === 'future' ? (
          <div className="space-y-8 animate-fadeIn">
            <div className="p-9 bg-slate-900 rounded-[45px] text-white relative overflow-hidden shadow-2xl">
              <div className="relative z-10">
                <h3 className="text-2xl font-black mb-2">ভবিষ্যৎ সঞ্চয়</h3>
                <p className="text-[10px] text-slate-400 font-black uppercase tracking-widest mb-10 opacity-70">এআই এর মাধ্যমে আর্থিক পূর্বাভাস</p>
                <button 
                  onClick={fetchAIAdvice}
                  disabled={loadingAI}
                  className="w-full py-4.5 bg-gradient-to-r from-indigo-600 to-blue-600 text-white rounded-[20px] font-black uppercase text-[10px] shadow-xl active:scale-95 transition-all flex items-center justify-center gap-3"
                >
                  {loadingAI ? <><i className="fas fa-spinner animate-spin"></i> প্রসেসিং...</> : <><i className="fas fa-wand-magic-sparkles"></i> পরামর্শ নিন</>}
                </button>
              </div>
              <div className="absolute -right-16 -bottom-16 w-56 h-56 bg-indigo-500/10 rounded-full blur-3xl"></div>
            </div>
            {insights.map((insight, idx) => (
              <div key={idx} className={`p-6 rounded-[35px] border ${insight.type === 'success' ? 'bg-green-50 border-green-100' : insight.type === 'warning' ? 'bg-rose-50 border-rose-100' : 'bg-blue-50 border-blue-100'} animate-fadeIn`}>
                <div className="flex gap-4">
                  <div className={`w-11 h-11 rounded-2xl flex items-center justify-center text-xl shadow-sm ${insight.type === 'success' ? 'bg-green-500 text-white' : insight.type === 'warning' ? 'bg-rose-500 text-white' : 'bg-blue-500 text-white'}`}>
                    <i className={`fas ${insight.type === 'success' ? 'fa-check' : insight.type === 'warning' ? 'fa-exclamation' : 'fa-lightbulb'}`}></i>
                  </div>
                  <div>
                    <h4 className="font-black text-slate-900 text-[14px] mb-1">{insight.title}</h4>
                    <p className="text-slate-600 text-[12px] font-bold leading-relaxed">{insight.description}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="space-y-8 animate-fadeIn">
            <div className="bg-white p-9 rounded-[45px] border border-slate-100 shadow-sm space-y-7">
              <div className="flex items-center gap-3 mb-2">
                <div className="w-12 h-12 bg-indigo-600 text-white rounded-2xl flex items-center justify-center text-xl shadow-lg shadow-indigo-100"><i className="fas fa-calculator"></i></div>
                <h3 className="font-black text-slate-900 uppercase text-xs tracking-widest">বিল স্প্লিটার</h3>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ ০" className="p-5 bg-slate-50 border border-slate-200 rounded-[22px] font-black text-slate-900 outline-none focus:border-indigo-600 transition-all shadow-inner" />
                <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} className="p-5 bg-slate-50 border border-slate-200 rounded-[22px] font-black text-slate-900 outline-none focus:border-indigo-600 transition-all shadow-inner" />
              </div>
              <div className="p-9 bg-gradient-to-br from-indigo-600 to-blue-700 rounded-[35px] text-center shadow-lg">
                <p className="text-[10px] font-black text-blue-100 uppercase mb-2 tracking-widest">প্রতি জনের ভাগ</p>
                <p className="text-4xl font-black text-white">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
              </div>
            </div>
          </div>
        )}
      </main>

      {/* Optimized Slim Navigation */}
      <nav className="fixed bottom-5 left-1/2 -translate-x-1/2 w-[85%] max-w-[320px] bg-white/95 backdrop-blur-2xl border border-slate-200 px-1.5 py-1.5 flex justify-between items-center z-[100] shadow-[0_12px_40px_-12px_rgba(0,0,0,0.15)] rounded-[24px]">
        <button 
          onClick={() => setActiveTab('home')} 
          className={`flex-1 flex flex-col items-center gap-0.5 py-2.5 transition-all duration-300 rounded-[18px] ${activeTab === 'home' ? 'text-indigo-700 bg-indigo-50 shadow-sm font-bold' : 'text-slate-400 hover:text-slate-600'}`}
        >
          <i className={`fas fa-home text-base`}></i>
          <span className={`text-[7px] font-black uppercase tracking-widest`}>হোম</span>
        </button>

        <button 
          onClick={() => setActiveTab('future')} 
          className={`flex-1 flex flex-col items-center gap-0.5 py-2.5 transition-all duration-300 rounded-[18px] ${activeTab === 'future' ? 'text-indigo-700 bg-indigo-50 shadow-sm font-bold' : 'text-slate-400 hover:text-slate-600'}`}
        >
          <i className={`fas fa-rocket text-base`}></i>
          <span className={`text-[7px] font-black uppercase tracking-widest`}>এআই</span>
        </button>

        <button 
          onClick={() => setActiveTab('tools')} 
          className={`flex-1 flex flex-col items-center gap-0.5 py-2.5 transition-all duration-300 rounded-[18px] ${activeTab === 'tools' ? 'text-indigo-700 bg-indigo-50 shadow-sm font-bold' : 'text-slate-400 hover:text-slate-600'}`}
        >
          <i className={`fas fa-layer-group text-base`}></i>
          <span className={`text-[7px] font-black uppercase tracking-widest`}>টুলস</span>
        </button>
      </nav>

      {/* Transaction Modal - Smaller and Centered card as requested */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[200] flex items-center justify-center p-6">
          <div className="bg-white w-full max-w-[340px] rounded-[35px] p-7 animate-fadeIn shadow-2xl border border-slate-100">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-sm font-black uppercase tracking-widest text-slate-800">{modalType === TransactionType.INCOME ? 'টাকা যোগ' : 'খরচ যোগ'}</h2>
              <button onClick={() => setModalType(null)} className="w-9 h-9 bg-slate-100 rounded-xl flex items-center justify-center text-slate-600 hover:bg-slate-200 transition-all"><i className="fas fa-times text-xs"></i></button>
            </div>

            <form onSubmit={addTransaction} className="space-y-5">
              <div className="bg-slate-50 rounded-[25px] p-7 border border-slate-100 text-center">
                <p className="text-[8px] font-black text-slate-400 uppercase tracking-[0.3em] mb-2">টাকার পরিমাণ (৳)</p>
                <input type="number" name="amount" required placeholder="০" autoFocus className="w-full text-3xl font-black text-center outline-none text-slate-900 bg-transparent tracking-tighter" />
              </div>

              <div className="space-y-3">
                <div className="relative">
                  <select name="category" className="w-full p-4 bg-white border border-slate-200 rounded-[20px] font-black text-[11px] text-slate-800 outline-none focus:border-indigo-600 appearance-none text-center">
                    {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                  </select>
                  <div className="absolute right-4 top-1/2 -translate-y-1/2 pointer-events-none text-slate-400">
                    <i className="fas fa-chevron-down text-[10px]"></i>
                  </div>
                </div>
                <input type="text" name="note" placeholder="নোট (যেমন: বেতন, বাজার...)" className="w-full p-4 bg-white border border-slate-200 rounded-[20px] font-black text-[11px] text-slate-800 outline-none focus:border-indigo-600 text-center" />
              </div>

              <button type="submit" className={`w-full py-4.5 rounded-[22px] text-white font-black text-xs uppercase tracking-widest shadow-lg active:scale-95 transition-all ${modalType === TransactionType.INCOME ? 'bg-gradient-to-r from-green-500 to-emerald-600' : 'bg-gradient-to-r from-rose-500 to-pink-600'}`}>নিশ্চিত করুন</button>
            </form>
          </div>
        </div>
      )}

      {/* Settings Modal */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[45px] p-8 animate-slideUp shadow-2xl overflow-y-auto max-h-[85vh] custom-scrollbar">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-lg font-black text-slate-900 tracking-widest">বাজেট লক্ষ্য</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-10 h-10 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900"><i className="fas fa-times"></i></button>
             </div>
             <div className="space-y-4 mb-8">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="space-y-1">
                    <label className="text-[9px] font-black text-slate-500 uppercase ml-3 tracking-widest">{cat}</label>
                    <input 
                      type="number" 
                      value={budgets[cat] || ''}
                      onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })}
                      placeholder="৳ বাজেট দিন"
                      className="w-full p-4 bg-slate-50 border border-slate-200 rounded-[20px] font-black text-xs text-slate-900 outline-none focus:border-indigo-600"
                    />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-5 bg-slate-900 text-white rounded-[25px] font-black uppercase text-xs tracking-widest shadow-xl mb-4">সংরক্ষণ করুন</button>
          </div>
        </div>
      )}

      {/* History Modal */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[45px] p-8 overflow-y-auto animate-slideUp shadow-2xl">
             <div className="flex justify-between items-center mb-10">
                <h2 className="text-xl font-black text-slate-900 tracking-widest uppercase">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-10 h-10 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900"><i className="fas fa-times"></i></button>
             </div>
             {archives.length > 0 ? (
               <div className="space-y-6">
                 {archives.map(arc => (
                   <div key={arc.id} className="p-6 bg-slate-50 rounded-[35px] border border-slate-100 shadow-sm">
                      <p className="text-[10px] text-indigo-600 uppercase font-black tracking-widest mb-1">{arc.monthName} {arc.year}</p>
                      <p className="text-slate-900 text-2xl font-black tracking-tighter">৳{arc.totalIncome - arc.totalExpense}</p>
                      <div className="flex gap-4 pt-4 mt-4 border-t border-slate-200">
                        <div className="flex-1">
                          <p className="text-[8px] font-black text-green-600 uppercase mb-0.5">আয়</p>
                          <p className="text-xs font-black text-slate-800">৳{arc.totalIncome}</p>
                        </div>
                        <div className="flex-1 text-right">
                          <p className="text-[8px] font-black text-rose-600 uppercase mb-0.5">ব্যয়</p>
                          <p className="text-xs font-black text-slate-800">৳{arc.totalExpense}</p>
                        </div>
                      </div>
                   </div>
                 ))}
               </div>
             ) : (
               <div className="text-center py-24 text-slate-400 font-black uppercase text-[10px] tracking-widest">ইতিহাস খালি</div>
             )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'future' | 'tools'>('home');
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  const fetchAIAdvice = async () => {
    if (transactions.length === 0) {
      alert("পরামর্শ পেতে প্রথমে কিছু লেনদেন যোগ করুন।");
      return;
    }
    setLoadingAI(true);
    const finSummary: FinancialSummary = {
      totalIncome: summary.income,
      totalExpenses: summary.expense,
      balance: summary.balance,
      savings: summary.balance,
      previousMonthSavings: (archives[0]?.totalIncome ?? 0) - (archives[0]?.totalExpense ?? 0),
      categoryBreakdown: summary.breakdown,
      monthlyHistory: archives.slice(0, 5).map(a => ({
        month: a.monthName,
        income: a.totalIncome,
        expense: a.totalExpense
      }))
    };
    const results = await getFinancialInsights(transactions, finSummary);
    setInsights(results);
    setLoadingAI(false);
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
  };

  const deleteTransaction = (id: string) => {
    if (window.confirm('আপনি কি এই লেনদেনটি মুছে ফেলতে চান?')) {
      setTransactions(prev => prev.filter(t => t.id !== id));
    }
  };

  const activeBudgetData = useMemo(() => {
    return (Object.entries(budgets) as [string, number][]).filter(([_, amount]) => amount > 0);
  }, [budgets]);

  return (
    <div className="max-w-md mx-auto min-h-screen bg-[#f8fafc] shadow-2xl flex flex-col font-sans relative pb-24 overflow-hidden">
      {/* Header */}
      <header className="bg-gradient-to-br from-blue-700 via-indigo-600 to-indigo-800 p-7 text-white rounded-b-[45px] shadow-2xl relative z-20">
        <div className="flex justify-between items-center mb-6">
          <div className="flex items-center gap-3">
             <div className="w-12 h-12 bg-white/20 rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/30 shadow-lg backdrop-blur-md">
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
                ) : (
                  <i className="fas fa-user-circle text-2xl text-white"></i>
                )}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div>
                <h1 className="text-xl font-black uppercase tracking-tight">স্পার্ক</h1>
                <p className="text-[9px] font-black text-blue-200 mt-0.5 uppercase tracking-widest opacity-80">স্মার্ট ওয়ালেট</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center hover:bg-white/20 transition-all border border-white/10 backdrop-blur-md">
              <i className="fas fa-bullseye text-sm"></i>
            </button>
            <button onClick={() => setShowHistory(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center hover:bg-white/20 transition-all border border-white/10 backdrop-blur-md">
              <i className="fas fa-history text-sm"></i>
            </button>
          </div>
        </div>

        <div className="text-center py-2">
          <p className="text-blue-100 text-[10px] font-black uppercase tracking-[0.3em] mb-2 opacity-90">বর্তমান ব্যালেন্স</p>
          <h2 className="text-4xl font-black mb-8 tracking-tighter drop-shadow-lg">{formatCurrency(summary.balance)}</h2>

          <div className="grid grid-cols-2 gap-3">
            <div className="bg-white/10 backdrop-blur-xl p-3.5 rounded-[24px] border border-white/20 shadow-sm">
              <p className="text-[9px] uppercase font-black text-blue-200 mb-1">মোট আয়</p>
              <p className="text-lg font-black text-green-300">৳{summary.income}</p>
            </div>
            <div className="bg-white/10 backdrop-blur-xl p-3.5 rounded-[24px] border border-white/20 shadow-sm">
              <p className="text-[9px] uppercase font-black text-blue-200 mb-1">মোট ব্যয়</p>
              <p className="text-lg font-black text-rose-300">৳{summary.expense}</p>
            </div>
          </div>
        </div>
      </header>

      {/* Main Area */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-8">
        {activeTab === 'home' ? (
          <div className="space-y-10 animate-fadeIn">
            {/* Quick Add Balance */}
            <div className="grid grid-cols-2 gap-4">
              <button 
                onClick={() => setModalType(TransactionType.INCOME)} 
                className="relative overflow-hidden p-6 bg-white border border-slate-100 rounded-[35px] flex flex-col items-center gap-3 transition-all hover:shadow-2xl hover:-translate-y-1 active:scale-95 group"
              >
                <div className="absolute top-0 right-0 w-16 h-16 bg-green-50 rounded-full -mr-8 -mt-8 group-hover:scale-150 transition-transform duration-500"></div>
                <div className="w-14 h-14 bg-gradient-to-br from-green-400 to-green-600 text-white rounded-[22px] flex items-center justify-center text-2xl shadow-lg shadow-green-100 relative z-10">
                  <i className="fas fa-plus"></i>
                </div>
                <span className="text-[11px] font-black text-slate-800 uppercase tracking-[0.15em] relative z-10">টাকা যোগ</span>
              </button>

              <button 
                onClick={() => setModalType(TransactionType.EXPENSE)} 
                className="relative overflow-hidden p-6 bg-white border border-slate-100 rounded-[35px] flex flex-col items-center gap-3 transition-all hover:shadow-2xl hover:-translate-y-1 active:scale-95 group"
              >
                <div className="absolute top-0 right-0 w-16 h-16 bg-rose-50 rounded-full -mr-8 -mt-8 group-hover:scale-150 transition-transform duration-500"></div>
                <div className="w-14 h-14 bg-gradient-to-br from-rose-400 to-rose-600 text-white rounded-[22px] flex items-center justify-center text-2xl shadow-lg shadow-rose-100 relative z-10">
                  <i className="fas fa-minus"></i>
                </div>
                <span className="text-[11px] font-black text-slate-800 uppercase tracking-[0.15em] relative z-10">খরচ যোগ</span>
              </button>
            </div>

            {/* Transactions List */}
            <div className="space-y-4">
              <div className="flex justify-between items-center px-2">
                <h3 className="font-black text-slate-900 text-[10px] uppercase tracking-[0.2em] opacity-60">সাম্প্রতিক লেনদেন</h3>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3">
                  {transactions.map(t => (
                    <div key={t.id} className="bg-white border border-slate-100 p-4 rounded-[28px] shadow-sm flex items-center justify-between group hover:border-indigo-100 transition-colors">
                      <div className="flex items-center gap-4">
                        <div className={`w-11 h-11 rounded-2xl flex items-center justify-center text-lg ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-800 text-[14px]">{t.category}</p>
                          <p className="text-[9px] text-slate-400 font-black uppercase">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <div className="flex items-center gap-3">
                        <p className={`font-black text-sm ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                          {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                        </p>
                        <button 
                          onClick={() => deleteTransaction(t.id)}
                          className="w-9 h-9 bg-slate-50 text-slate-300 rounded-xl flex items-center justify-center hover:bg-rose-50 hover:text-rose-500 transition-all active:scale-90"
                        >
                          <i className="fas fa-trash-alt text-xs"></i>
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-16 bg-white rounded-[40px] border border-dashed border-slate-200">
                  <i className="fas fa-receipt text-slate-200 text-3xl mb-3"></i>
                  <p className="text-slate-400 font-black uppercase text-[9px] tracking-widest">খালি তালিকা</p>
                </div>
              )}
            </div>
          </div>
        ) : activeTab === 'future' ? (
          <div className="space-y-8 animate-fadeIn">
            <div className="p-9 bg-slate-900 rounded-[45px] text-white relative overflow-hidden shadow-2xl">
              <div className="relative z-10">
                <h3 className="text-2xl font-black mb-2">ভবিষ্যৎ সঞ্চয়</h3>
                <p className="text-[10px] text-slate-400 font-black uppercase tracking-widest mb-10 opacity-70">এআই এর মাধ্যমে আর্থিক পূর্বাভাস</p>
                <button 
                  onClick={fetchAIAdvice}
                  disabled={loadingAI}
                  className="w-full py-4 bg-gradient-to-r from-indigo-600 to-blue-600 text-white rounded-[20px] font-black uppercase text-[10px] shadow-xl active:scale-95 transition-all flex items-center justify-center gap-3"
                >
                  {loadingAI ? <><i className="fas fa-spinner animate-spin"></i> প্রসেসিং...</> : <><i className="fas fa-wand-magic-sparkles"></i> পরামর্শ নিন</>}
                </button>
              </div>
              <div className="absolute -right-16 -bottom-16 w-56 h-56 bg-indigo-500/10 rounded-full blur-3xl"></div>
            </div>
            {insights.map((insight, idx) => (
              <div key={idx} className={`p-6 rounded-[35px] border ${insight.type === 'success' ? 'bg-green-50 border-green-100' : insight.type === 'warning' ? 'bg-rose-50 border-rose-100' : 'bg-blue-50 border-blue-100'} animate-fadeIn`}>
                <div className="flex gap-4">
                  <div className={`w-11 h-11 rounded-2xl flex items-center justify-center text-xl shadow-sm ${insight.type === 'success' ? 'bg-green-500 text-white' : insight.type === 'warning' ? 'bg-rose-500 text-white' : 'bg-blue-500 text-white'}`}>
                    <i className={`fas ${insight.type === 'success' ? 'fa-check' : insight.type === 'warning' ? 'fa-exclamation' : 'fa-lightbulb'}`}></i>
                  </div>
                  <div>
                    <h4 className="font-black text-slate-900 text-[14px] mb-1">{insight.title}</h4>
                    <p className="text-slate-600 text-[12px] font-bold leading-relaxed">{insight.description}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="space-y-8 animate-fadeIn">
            <div className="bg-white p-9 rounded-[45px] border border-slate-100 shadow-sm space-y-7">
              <div className="flex items-center gap-3 mb-2">
                <div className="w-12 h-12 bg-indigo-600 text-white rounded-2xl flex items-center justify-center text-xl shadow-lg shadow-indigo-100"><i className="fas fa-calculator"></i></div>
                <h3 className="font-black text-slate-900 uppercase text-xs tracking-widest">বিল স্প্লিটার</h3>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ ০" className="p-5 bg-slate-50 border border-slate-200 rounded-[22px] font-black text-slate-900 outline-none focus:border-indigo-600 transition-all shadow-inner" />
                <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} className="p-5 bg-slate-50 border border-slate-200 rounded-[22px] font-black text-slate-900 outline-none focus:border-indigo-600 transition-all shadow-inner" />
              </div>
              <div className="p-9 bg-gradient-to-br from-indigo-600 to-blue-700 rounded-[35px] text-center shadow-lg">
                <p className="text-[10px] font-black text-blue-100 uppercase mb-2 tracking-widest">প্রতি জনের ভাগ</p>
                <p className="text-4xl font-black text-white">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
              </div>
            </div>
          </div>
        )}
      </main>

      {/* Optimized Slim Navigation */}
      <nav className="fixed bottom-5 left-1/2 -translate-x-1/2 w-[85%] max-w-[320px] bg-white/95 backdrop-blur-2xl border border-slate-200 px-1.5 py-1.5 flex justify-between items-center z-[100] shadow-[0_12px_40px_-12px_rgba(0,0,0,0.15)] rounded-[24px]">
        <button 
          onClick={() => setActiveTab('home')} 
          className={`flex-1 flex flex-col items-center gap-0.5 py-2.5 transition-all duration-300 rounded-[18px] ${activeTab === 'home' ? 'text-indigo-700 bg-indigo-50 shadow-sm font-bold' : 'text-slate-400 hover:text-slate-600'}`}
        >
          <i className={`fas fa-home text-base`}></i>
          <span className={`text-[7px] font-black uppercase tracking-widest`}>হোম</span>
        </button>

        <button 
          onClick={() => setActiveTab('future')} 
          className={`flex-1 flex flex-col items-center gap-0.5 py-2.5 transition-all duration-300 rounded-[18px] ${activeTab === 'future' ? 'text-indigo-700 bg-indigo-50 shadow-sm font-bold' : 'text-slate-400 hover:text-slate-600'}`}
        >
          <i className={`fas fa-rocket text-base`}></i>
          <span className={`text-[7px] font-black uppercase tracking-widest`}>এআই</span>
        </button>

        <button 
          onClick={() => setActiveTab('tools')} 
          className={`flex-1 flex flex-col items-center gap-0.5 py-2.5 transition-all duration-300 rounded-[18px] ${activeTab === 'tools' ? 'text-indigo-700 bg-indigo-50 shadow-sm font-bold' : 'text-slate-400 hover:text-slate-600'}`}
        >
          <i className={`fas fa-layer-group text-base`}></i>
          <span className={`text-[7px] font-black uppercase tracking-widest`}>টুলস</span>
        </button>
      </nav>

      {/* Transaction Modal - Small and Centered card fix */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[200] flex items-center justify-center p-6">
          <div className="bg-white w-full max-w-[340px] rounded-[35px] p-7 pb-10 animate-fadeIn shadow-2xl border border-slate-100 flex flex-col">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-sm font-black uppercase tracking-widest text-slate-800">{modalType === TransactionType.INCOME ? 'টাকা যোগ' : 'খরচ যোগ'}</h2>
              <button onClick={() => setModalType(null)} className="w-9 h-9 bg-slate-100 rounded-xl flex items-center justify-center text-slate-600 hover:bg-slate-200 transition-all"><i className="fas fa-times text-xs"></i></button>
            </div>

            <form onSubmit={addTransaction} className="flex flex-col gap-5">
              <div className="bg-slate-50 rounded-[25px] p-6 border border-slate-100 text-center">
                <p className="text-[8px] font-black text-slate-400 uppercase tracking-[0.3em] mb-2">টাকার পরিমাণ (৳)</p>
                <input type="number" name="amount" required placeholder="০" autoFocus className="w-full text-3xl font-black text-center outline-none text-slate-900 bg-transparent tracking-tighter" />
              </div>

              <div className="flex flex-col gap-3">
                <div className="relative">
                  <select name="category" className="w-full p-4 bg-white border border-slate-200 rounded-[20px] font-black text-[11px] text-slate-800 outline-none focus:border-indigo-600 appearance-none text-center">
                    {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                  </select>
                  <div className="absolute right-4 top-1/2 -translate-y-1/2 pointer-events-none text-slate-400">
                    <i className="fas fa-chevron-down text-[10px]"></i>
                  </div>
                </div>
                <input type="text" name="note" placeholder="নোট (যেমন: বেতন, বাজার...)" className="w-full p-4 bg-white border border-slate-200 rounded-[20px] font-black text-[11px] text-slate-800 outline-none focus:border-indigo-600 text-center" />
              </div>

              <button type="submit" className={`w-full py-4 mt-2 rounded-[22px] text-white font-black text-xs uppercase tracking-widest shadow-lg active:scale-95 transition-all ${modalType === TransactionType.INCOME ? 'bg-gradient-to-r from-green-500 to-emerald-600' : 'bg-gradient-to-r from-rose-500 to-pink-600'}`}>
                নিশ্চিত করুন
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Settings Modal */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[45px] p-8 animate-slideUp shadow-2xl overflow-y-auto max-h-[85vh] custom-scrollbar">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-lg font-black text-slate-900 tracking-widest">বাজেট লক্ষ্য</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-10 h-10 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900"><i className="fas fa-times"></i></button>
             </div>
             <div className="space-y-4 mb-8">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="space-y-1">
                    <label className="text-[9px] font-black text-slate-500 uppercase ml-3 tracking-widest">{cat}</label>
                    <input 
                      type="number" 
                      value={budgets[cat] || ''}
                      onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })}
                      placeholder="৳ বাজেট দিন"
                      className="w-full p-4 bg-slate-50 border border-slate-200 rounded-[20px] font-black text-xs text-slate-900 outline-none focus:border-indigo-600"
                    />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-5 bg-slate-900 text-white rounded-[25px] font-black uppercase text-xs tracking-widest shadow-xl mb-4">সংরক্ষণ করুন</button>
          </div>
        </div>
      )}

      {/* History Modal */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[45px] p-8 overflow-y-auto animate-slideUp shadow-2xl">
             <div className="flex justify-between items-center mb-10">
                <h2 className="text-xl font-black text-slate-900 tracking-widest uppercase">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-10 h-10 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900"><i className="fas fa-times"></i></button>
             </div>
             {archives.length > 0 ? (
               <div className="space-y-6">
                 {archives.map(arc => (
                   <div key={arc.id} className="p-6 bg-slate-50 rounded-[35px] border border-slate-100 shadow-sm">
                      <p className="text-[10px] text-indigo-600 uppercase font-black tracking-widest mb-1">{arc.monthName} {arc.year}</p>
                      <p className="text-slate-900 text-2xl font-black tracking-tighter">৳{arc.totalIncome - arc.totalExpense}</p>
                      <div className="flex gap-4 pt-4 mt-4 border-t border-slate-200">
                        <div className="flex-1">
                          <p className="text-[8px] font-black text-green-600 uppercase mb-0.5">আয়</p>
                          <p className="text-xs font-black text-slate-800">৳{arc.totalIncome}</p>
                        </div>
                        <div className="flex-1 text-right">
                          <p className="text-[8px] font-black text-rose-600 uppercase mb-0.5">ব্যয়</p>
                          <p className="text-xs font-black text-slate-800">৳{arc.totalExpense}</p>
                        </div>
                      </div>
                   </div>
                 ))}
               </div>
             ) : (
               <div className="text-center py-24 text-slate-400 font-black uppercase text-[10px] tracking-widest">ইতিহাস খালি</div>
             )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'future' | 'tools'>('home');
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  const fetchAIAdvice = async () => {
    if (transactions.length === 0) {
      alert("পরামর্শ পেতে প্রথমে কিছু লেনদেন যোগ করুন।");
      return;
    }
    setLoadingAI(true);
    const finSummary: FinancialSummary = {
      totalIncome: summary.income,
      totalExpenses: summary.expense,
      balance: summary.balance,
      savings: summary.balance,
      previousMonthSavings: (archives[0]?.totalIncome ?? 0) - (archives[0]?.totalExpense ?? 0),
      categoryBreakdown: summary.breakdown,
      monthlyHistory: archives.slice(0, 5).map(a => ({
        month: a.monthName,
        income: a.totalIncome,
        expense: a.totalExpense
      }))
    };
    const results = await getFinancialInsights(transactions, finSummary);
    setInsights(results);
    setLoadingAI(false);
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
  };

  const deleteTransaction = (id: string) => {
    if (window.confirm('আপনি কি এই লেনদেনটি মুছে ফেলতে চান?')) {
      setTransactions(prev => prev.filter(t => t.id !== id));
    }
  };

  const activeBudgetData = useMemo(() => {
    return (Object.entries(budgets) as [string, number][]).filter(([_, amount]) => amount > 0);
  }, [budgets]);

  return (
    <div className="max-w-md mx-auto min-h-screen bg-[#f8fafc] shadow-2xl flex flex-col font-sans relative pb-24 overflow-hidden">
      {/* Header */}
      <header className="bg-gradient-to-br from-blue-700 via-indigo-600 to-indigo-800 p-7 text-white rounded-b-[45px] shadow-2xl relative z-20">
        <div className="flex justify-between items-center mb-6">
          <div className="flex items-center gap-3">
             <div className="w-12 h-12 bg-white/20 rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/30 shadow-lg backdrop-blur-md">
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
                ) : (
                  <i className="fas fa-user-circle text-2xl text-white"></i>
                )}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div>
                <h1 className="text-xl font-black uppercase tracking-tight">স্পার্ক</h1>
                <p className="text-[9px] font-black text-blue-200 mt-0.5 uppercase tracking-widest opacity-80">স্মার্ট ওয়ালেট</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center hover:bg-white/20 transition-all border border-white/10 backdrop-blur-md">
              <i className="fas fa-bullseye text-sm"></i>
            </button>
            <button onClick={() => setShowHistory(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center hover:bg-white/20 transition-all border border-white/10 backdrop-blur-md">
              <i className="fas fa-history text-sm"></i>
            </button>
          </div>
        </div>

        <div className="text-center py-2">
          <p className="text-blue-100 text-[10px] font-black uppercase tracking-[0.3em] mb-2 opacity-90">বর্তমান ব্যালেন্স</p>
          <h2 className="text-4xl font-black mb-8 tracking-tighter drop-shadow-lg">{formatCurrency(summary.balance)}</h2>

          <div className="grid grid-cols-2 gap-3">
            <div className="bg-white/10 backdrop-blur-xl p-3.5 rounded-[24px] border border-white/20 shadow-sm">
              <p className="text-[9px] uppercase font-black text-blue-200 mb-1">মোট আয়</p>
              <p className="text-lg font-black text-green-300">৳{summary.income}</p>
            </div>
            <div className="bg-white/10 backdrop-blur-xl p-3.5 rounded-[24px] border border-white/20 shadow-sm">
              <p className="text-[9px] uppercase font-black text-blue-200 mb-1">মোট ব্যয়</p>
              <p className="text-lg font-black text-rose-300">৳{summary.expense}</p>
            </div>
          </div>
        </div>
      </header>

      {/* Main Area */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-8">
        {activeTab === 'home' ? (
          <div className="space-y-10 animate-fadeIn">
            {/* Quick Add Balance */}
            <div className="grid grid-cols-2 gap-4">
              <button 
                onClick={() => setModalType(TransactionType.INCOME)} 
                className="relative overflow-hidden p-6 bg-white border border-slate-100 rounded-[35px] flex flex-col items-center gap-3 transition-all hover:shadow-2xl hover:-translate-y-1 active:scale-95 group"
              >
                <div className="absolute top-0 right-0 w-16 h-16 bg-green-50 rounded-full -mr-8 -mt-8 group-hover:scale-150 transition-transform duration-500"></div>
                <div className="w-14 h-14 bg-gradient-to-br from-green-400 to-green-600 text-white rounded-[22px] flex items-center justify-center text-2xl shadow-lg shadow-green-100 relative z-10">
                  <i className="fas fa-plus"></i>
                </div>
                <span className="text-[11px] font-black text-slate-800 uppercase tracking-[0.15em] relative z-10">টাকা যোগ</span>
              </button>

              <button 
                onClick={() => setModalType(TransactionType.EXPENSE)} 
                className="relative overflow-hidden p-6 bg-white border border-slate-100 rounded-[35px] flex flex-col items-center gap-3 transition-all hover:shadow-2xl hover:-translate-y-1 active:scale-95 group"
              >
                <div className="absolute top-0 right-0 w-16 h-16 bg-rose-50 rounded-full -mr-8 -mt-8 group-hover:scale-150 transition-transform duration-500"></div>
                <div className="w-14 h-14 bg-gradient-to-br from-rose-400 to-rose-600 text-white rounded-[22px] flex items-center justify-center text-2xl shadow-lg shadow-rose-100 relative z-10">
                  <i className="fas fa-minus"></i>
                </div>
                <span className="text-[11px] font-black text-slate-800 uppercase tracking-[0.15em] relative z-10">খরচ যোগ</span>
              </button>
            </div>

            {/* Transactions List */}
            <div className="space-y-4">
              <div className="flex justify-between items-center px-2">
                <h3 className="font-black text-slate-900 text-[10px] uppercase tracking-[0.2em] opacity-60">সাম্প্রতিক লেনদেন</h3>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3">
                  {transactions.map(t => (
                    <div key={t.id} className="bg-white border border-slate-100 p-4 rounded-[28px] shadow-sm flex items-center justify-between group hover:border-indigo-100 transition-colors">
                      <div className="flex items-center gap-4">
                        <div className={`w-11 h-11 rounded-2xl flex items-center justify-center text-lg ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-800 text-[14px]">{t.category}</p>
                          <p className="text-[9px] text-slate-400 font-black uppercase">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <div className="flex items-center gap-3">
                        <p className={`font-black text-sm ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                          {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                        </p>
                        <button 
                          onClick={() => deleteTransaction(t.id)}
                          className="w-9 h-9 bg-slate-50 text-slate-300 rounded-xl flex items-center justify-center hover:bg-rose-50 hover:text-rose-500 transition-all active:scale-90"
                        >
                          <i className="fas fa-trash-alt text-xs"></i>
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-16 bg-white rounded-[40px] border border-dashed border-slate-200">
                  <i className="fas fa-receipt text-slate-200 text-3xl mb-3"></i>
                  <p className="text-slate-400 font-black uppercase text-[9px] tracking-widest">খালি তালিকা</p>
                </div>
              )}
            </div>
          </div>
        ) : activeTab === 'future' ? (
          <div className="space-y-8 animate-fadeIn">
            <div className="p-9 bg-slate-900 rounded-[45px] text-white relative overflow-hidden shadow-2xl">
              <div className="relative z-10">
                <h3 className="text-2xl font-black mb-2">ভবিষ্যৎ সঞ্চয়</h3>
                <p className="text-[10px] text-slate-400 font-black uppercase tracking-widest mb-10 opacity-70">এআই এর মাধ্যমে আর্থিক পূর্বাভাস</p>
                <button 
                  onClick={fetchAIAdvice}
                  disabled={loadingAI}
                  className="w-full py-4 bg-gradient-to-r from-indigo-600 to-blue-600 text-white rounded-[20px] font-black uppercase text-[10px] shadow-xl active:scale-95 transition-all flex items-center justify-center gap-3"
                >
                  {loadingAI ? <><i className="fas fa-spinner animate-spin"></i> প্রসেসিং...</> : <><i className="fas fa-wand-magic-sparkles"></i> পরামর্শ নিন</>}
                </button>
              </div>
              <div className="absolute -right-16 -bottom-16 w-56 h-56 bg-indigo-500/10 rounded-full blur-3xl"></div>
            </div>
            {insights.map((insight, idx) => (
              <div key={idx} className={`p-6 rounded-[35px] border ${insight.type === 'success' ? 'bg-green-50 border-green-100' : insight.type === 'warning' ? 'bg-rose-50 border-rose-100' : 'bg-blue-50 border-blue-100'} animate-fadeIn`}>
                <div className="flex gap-4">
                  <div className={`w-11 h-11 rounded-2xl flex items-center justify-center text-xl shadow-sm ${insight.type === 'success' ? 'bg-green-500 text-white' : insight.type === 'warning' ? 'bg-rose-500 text-white' : 'bg-blue-500 text-white'}`}>
                    <i className={`fas ${insight.type === 'success' ? 'fa-check' : insight.type === 'warning' ? 'fa-exclamation' : 'fa-lightbulb'}`}></i>
                  </div>
                  <div>
                    <h4 className="font-black text-slate-900 text-[14px] mb-1">{insight.title}</h4>
                    <p className="text-slate-600 text-[12px] font-bold leading-relaxed">{insight.description}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="space-y-8 animate-fadeIn px-2">
            <div className="bg-white p-9 rounded-[45px] border border-slate-100 shadow-sm space-y-7">
              <div className="flex items-center gap-3 mb-2">
                <div className="w-12 h-12 bg-indigo-600 text-white rounded-2xl flex items-center justify-center text-xl shadow-lg shadow-indigo-100"><i className="fas fa-calculator"></i></div>
                <h3 className="font-black text-slate-900 uppercase text-xs tracking-widest">বিল স্প্লিটার</h3>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-1.5">
                   <p className="text-[8px] font-black text-slate-400 uppercase tracking-widest ml-2">মোট টাকা</p>
                   <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ ০" className="w-full p-4.5 bg-slate-50 border border-slate-200 rounded-[22px] font-black text-slate-900 outline-none focus:border-indigo-600 transition-all shadow-inner text-center" />
                </div>
                <div className="space-y-1.5">
                   <p className="text-[8px] font-black text-slate-400 uppercase tracking-widest ml-2">মানুষের সংখ্যা</p>
                   <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} className="w-full p-4.5 bg-slate-50 border border-slate-200 rounded-[22px] font-black text-slate-900 outline-none focus:border-indigo-600 transition-all shadow-inner text-center" />
                </div>
              </div>
              <div className="p-9 bg-gradient-to-br from-indigo-600 to-blue-700 rounded-[35px] text-center shadow-lg relative overflow-hidden group">
                <div className="relative z-10">
                  <p className="text-[10px] font-black text-blue-100 uppercase mb-2 tracking-widest">প্রতি জনের ভাগ</p>
                  <p className="text-4xl font-black text-white">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
                </div>
                <div className="absolute inset-0 bg-white/5 opacity-0 group-hover:opacity-100 transition-opacity"></div>
              </div>
            </div>
          </div>
        )}
      </main>

      {/* New Improved Bottom-Attached Navigation Bar */}
      <nav className="fixed bottom-0 left-0 right-0 bg-white/95 backdrop-blur-xl border-t border-slate-100 px-4 pt-3 pb-7 flex justify-between items-center z-[100] shadow-[0_-10px_30px_-15px_rgba(0,0,0,0.1)]">
        <button 
          onClick={() => setActiveTab('home')} 
          className={`flex-1 flex flex-col items-center gap-1.5 py-2 transition-all duration-300 ${activeTab === 'home' ? 'text-indigo-700 font-bold' : 'text-slate-400 opacity-60'}`}
        >
          <div className={`w-12 h-8 flex items-center justify-center rounded-2xl transition-all ${activeTab === 'home' ? 'bg-indigo-50 shadow-sm' : ''}`}>
            <i className={`fas fa-house-chimney text-lg`}></i>
          </div>
          <span className={`text-[9px] font-black uppercase tracking-[0.15em]`}>হোম</span>
        </button>

        <button 
          onClick={() => setActiveTab('future')} 
          className={`flex-1 flex flex-col items-center gap-1.5 py-2 transition-all duration-300 ${activeTab === 'future' ? 'text-indigo-700 font-bold' : 'text-slate-400 opacity-60'}`}
        >
          <div className={`w-12 h-8 flex items-center justify-center rounded-2xl transition-all ${activeTab === 'future' ? 'bg-indigo-50 shadow-sm' : ''}`}>
            <i className={`fas fa-rocket text-lg`}></i>
          </div>
          <span className={`text-[9px] font-black uppercase tracking-[0.15em]`}>এআই</span>
        </button>

        <button 
          onClick={() => setActiveTab('tools')} 
          className={`flex-1 flex flex-col items-center gap-1.5 py-2 transition-all duration-300 ${activeTab === 'tools' ? 'text-indigo-700 font-bold' : 'text-slate-400 opacity-60'}`}
        >
          <div className={`w-12 h-8 flex items-center justify-center rounded-2xl transition-all ${activeTab === 'tools' ? 'bg-indigo-50 shadow-sm' : ''}`}>
            <i className={`fas fa-layer-group text-lg`}></i>
          </div>
          <span className={`text-[9px] font-black uppercase tracking-[0.15em]`}>টুলস</span>
        </button>
      </nav>

      {/* Transaction Modal - Small and Centered card fix */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[200] flex items-center justify-center p-6">
          <div className="bg-white w-full max-w-[340px] rounded-[35px] p-7 pb-10 animate-fadeIn shadow-2xl border border-slate-100 flex flex-col">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-sm font-black uppercase tracking-widest text-slate-800">{modalType === TransactionType.INCOME ? 'টাকা যোগ' : 'খরচ যোগ'}</h2>
              <button onClick={() => setModalType(null)} className="w-9 h-9 bg-slate-100 rounded-xl flex items-center justify-center text-slate-600 hover:bg-slate-200 transition-all"><i className="fas fa-times text-xs"></i></button>
            </div>

            <form onSubmit={addTransaction} className="flex flex-col gap-5">
              <div className="bg-slate-50 rounded-[25px] p-6 border border-slate-100 text-center">
                <p className="text-[8px] font-black text-slate-400 uppercase tracking-[0.3em] mb-2">টাকার পরিমাণ (৳)</p>
                <input type="number" name="amount" required placeholder="০" autoFocus className="w-full text-3xl font-black text-center outline-none text-slate-900 bg-transparent tracking-tighter" />
              </div>

              <div className="flex flex-col gap-3">
                <div className="relative">
                  <select name="category" className="w-full p-4 bg-white border border-slate-200 rounded-[20px] font-black text-[11px] text-slate-800 outline-none focus:border-indigo-600 appearance-none text-center">
                    {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                  </select>
                  <div className="absolute right-4 top-1/2 -translate-y-1/2 pointer-events-none text-slate-400">
                    <i className="fas fa-chevron-down text-[10px]"></i>
                  </div>
                </div>
                <input type="text" name="note" placeholder="নোট (যেমন: বেতন, বাজার...)" className="w-full p-4 bg-white border border-slate-200 rounded-[20px] font-black text-[11px] text-slate-800 outline-none focus:border-indigo-600 text-center" />
              </div>

              <button type="submit" className={`w-full py-4.5 mt-2 rounded-[22px] text-white font-black text-xs uppercase tracking-widest shadow-lg active:scale-95 transition-all ${modalType === TransactionType.INCOME ? 'bg-gradient-to-r from-green-500 to-emerald-600' : 'bg-gradient-to-r from-rose-500 to-pink-600'}`}>
                নিশ্চিত করুন
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Settings Modal */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[45px] p-8 animate-slideUp shadow-2xl overflow-y-auto max-h-[85vh] custom-scrollbar">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-lg font-black text-slate-900 tracking-widest">বাজেট লক্ষ্য</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-10 h-10 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900"><i className="fas fa-times"></i></button>
             </div>
             <div className="space-y-4 mb-8">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="space-y-1">
                    <label className="text-[9px] font-black text-slate-500 uppercase ml-3 tracking-widest">{cat}</label>
                    <input 
                      type="number" 
                      value={budgets[cat] || ''}
                      onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })}
                      placeholder="৳ বাজেট দিন"
                      className="w-full p-4 bg-slate-50 border border-slate-200 rounded-[20px] font-black text-xs text-slate-900 outline-none focus:border-indigo-600"
                    />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-5 bg-slate-900 text-white rounded-[25px] font-black uppercase text-xs tracking-widest shadow-xl mb-4">সংরক্ষণ করুন</button>
          </div>
        </div>
      )}

      {/* History Modal */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[45px] p-8 overflow-y-auto animate-slideUp shadow-2xl">
             <div className="flex justify-between items-center mb-10">
                <h2 className="text-xl font-black text-slate-900 tracking-widest uppercase">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-10 h-10 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900"><i className="fas fa-times"></i></button>
             </div>
             {archives.length > 0 ? (
               <div className="space-y-6">
                 {archives.map(arc => (
                   <div key={arc.id} className="p-6 bg-slate-50 rounded-[35px] border border-slate-100 shadow-sm">
                      <p className="text-[10px] text-indigo-600 uppercase font-black tracking-widest mb-1">{arc.monthName} {arc.year}</p>
                      <p className="text-slate-900 text-2xl font-black tracking-tighter">৳{arc.totalIncome - arc.totalExpense}</p>
                      <div className="flex gap-4 pt-4 mt-4 border-t border-slate-200">
                        <div className="flex-1">
                          <p className="text-[8px] font-black text-green-600 uppercase mb-0.5">আয়</p>
                          <p className="text-xs font-black text-slate-800">৳{arc.totalIncome}</p>
                        </div>
                        <div className="flex-1 text-right">
                          <p className="text-[8px] font-black text-rose-600 uppercase mb-0.5">ব্যয়</p>
                          <p className="text-xs font-black text-slate-800">৳{arc.totalExpense}</p>
                        </div>
                      </div>
                   </div>
                 ))}
               </div>
             ) : (
               <div className="text-center py-24 text-slate-400 font-black uppercase text-[10px] tracking-widest">ইতিহাস খালি</div>
             )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'future' | 'tools'>('home');
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  const fetchAIAdvice = async () => {
    if (transactions.length === 0) {
      alert("পরামর্শ পেতে প্রথমে কিছু লেনদেন যোগ করুন।");
      return;
    }
    setLoadingAI(true);
    const finSummary: FinancialSummary = {
      totalIncome: summary.income,
      totalExpenses: summary.expense,
      balance: summary.balance,
      savings: summary.balance,
      previousMonthSavings: (archives[0]?.totalIncome ?? 0) - (archives[0]?.totalExpense ?? 0),
      categoryBreakdown: summary.breakdown,
      monthlyHistory: archives.slice(0, 5).map(a => ({
        month: a.monthName,
        income: a.totalIncome,
        expense: a.totalExpense
      }))
    };
    const results = await getFinancialInsights(transactions, finSummary);
    setInsights(results);
    setLoadingAI(false);
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
  };

  const deleteTransaction = (id: string) => {
    if (window.confirm('আপনি কি এই লেনদেনটি মুছে ফেলতে চান?')) {
      setTransactions(prev => prev.filter(t => t.id !== id));
    }
  };

  return (
    <div className="max-w-md mx-auto min-h-screen bg-[#f8fafc] shadow-2xl flex flex-col font-sans relative pb-28 overflow-hidden">
      {/* Header */}
      <header className="bg-gradient-to-br from-blue-700 via-indigo-600 to-indigo-800 p-7 text-white rounded-b-[45px] shadow-2xl relative z-20">
        <div className="flex justify-between items-center mb-6">
          <div className="flex items-center gap-3">
             <div className="w-12 h-12 bg-white/20 rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/30 shadow-lg backdrop-blur-md">
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
                ) : (
                  <i className="fas fa-user-circle text-2xl text-white"></i>
                )}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div>
                <h1 className="text-xl font-black uppercase tracking-tight text-white">স্পার্ক</h1>
                <p className="text-[9px] font-black text-blue-200 mt-0.5 uppercase tracking-widest opacity-80">স্মার্ট ওয়ালেট</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center hover:bg-white/20 transition-all border border-white/10 backdrop-blur-md">
              <i className="fas fa-bullseye text-sm"></i>
            </button>
            <button onClick={() => setShowHistory(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center hover:bg-white/20 transition-all border border-white/10 backdrop-blur-md">
              <i className="fas fa-history text-sm"></i>
            </button>
          </div>
        </div>

        <div className="text-center py-2">
          <p className="text-blue-100 text-[10px] font-black uppercase tracking-[0.3em] mb-2 opacity-90">বর্তমান ব্যালেন্স</p>
          <h2 className="text-4xl font-black mb-8 tracking-tighter drop-shadow-lg">{formatCurrency(summary.balance)}</h2>

          <div className="grid grid-cols-2 gap-3">
            <div className="bg-white/10 backdrop-blur-xl p-3.5 rounded-[24px] border border-white/20 shadow-sm">
              <p className="text-[9px] uppercase font-black text-blue-200 mb-1">মোট আয়</p>
              <p className="text-lg font-black text-green-300">৳{summary.income}</p>
            </div>
            <div className="bg-white/10 backdrop-blur-xl p-3.5 rounded-[24px] border border-white/20 shadow-sm">
              <p className="text-[9px] uppercase font-black text-blue-200 mb-1">মোট ব্যয়</p>
              <p className="text-lg font-black text-rose-300">৳{summary.expense}</p>
            </div>
          </div>
        </div>
      </header>

      {/* Main Area */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-8">
        {activeTab === 'home' ? (
          <div className="space-y-10 animate-fadeIn">
            {/* Quick Add Balance */}
            <div className="grid grid-cols-2 gap-4">
              <button 
                onClick={() => setModalType(TransactionType.INCOME)} 
                className="relative overflow-hidden p-6 bg-white border border-slate-100 rounded-[35px] flex flex-col items-center gap-3 transition-all hover:shadow-2xl hover:-translate-y-1 active:scale-95 group"
              >
                <div className="absolute top-0 right-0 w-16 h-16 bg-green-50 rounded-full -mr-8 -mt-8 group-hover:scale-150 transition-transform duration-500"></div>
                <div className="w-14 h-14 bg-gradient-to-br from-green-400 to-green-600 text-white rounded-[22px] flex items-center justify-center text-2xl shadow-lg shadow-green-100 relative z-10">
                  <i className="fas fa-plus"></i>
                </div>
                <span className="text-[11px] font-black text-slate-800 uppercase tracking-[0.15em] relative z-10">টাকা যোগ</span>
              </button>

              <button 
                onClick={() => setModalType(TransactionType.EXPENSE)} 
                className="relative overflow-hidden p-6 bg-white border border-slate-100 rounded-[35px] flex flex-col items-center gap-3 transition-all hover:shadow-2xl hover:-translate-y-1 active:scale-95 group"
              >
                <div className="absolute top-0 right-0 w-16 h-16 bg-rose-50 rounded-full -mr-8 -mt-8 group-hover:scale-150 transition-transform duration-500"></div>
                <div className="w-14 h-14 bg-gradient-to-br from-rose-400 to-rose-600 text-white rounded-[22px] flex items-center justify-center text-2xl shadow-lg shadow-rose-100 relative z-10">
                  <i className="fas fa-minus"></i>
                </div>
                <span className="text-[11px] font-black text-slate-800 uppercase tracking-[0.15em] relative z-10">খরচ যোগ</span>
              </button>
            </div>

            {/* Transactions List */}
            <div className="space-y-4">
              <div className="flex justify-between items-center px-2">
                <h3 className="font-black text-slate-900 text-[10px] uppercase tracking-[0.2em] opacity-60">সাম্প্রতিক লেনদেন</h3>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3">
                  {transactions.map(t => (
                    <div key={t.id} className="bg-white border border-slate-100 p-4 rounded-[28px] shadow-sm flex items-center justify-between group hover:border-indigo-100 transition-colors">
                      <div className="flex items-center gap-4">
                        <div className={`w-11 h-11 rounded-2xl flex items-center justify-center text-lg ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-800 text-[14px]">{t.category}</p>
                          <p className="text-[9px] text-slate-400 font-black uppercase">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <div className="flex items-center gap-3">
                        <p className={`font-black text-sm ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                          {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                        </p>
                        <button 
                          onClick={() => deleteTransaction(t.id)}
                          className="w-9 h-9 bg-slate-50 text-slate-300 rounded-xl flex items-center justify-center hover:bg-rose-50 hover:text-rose-500 transition-all active:scale-90"
                        >
                          <i className="fas fa-trash-alt text-xs"></i>
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-16 bg-white rounded-[40px] border border-dashed border-slate-200">
                  <i className="fas fa-receipt text-slate-200 text-3xl mb-3"></i>
                  <p className="text-slate-400 font-black uppercase text-[9px] tracking-widest">খালি তালিকা</p>
                </div>
              )}
            </div>
          </div>
        ) : activeTab === 'future' ? (
          <div className="space-y-8 animate-fadeIn">
            <div className="p-9 bg-slate-900 rounded-[45px] text-white relative overflow-hidden shadow-2xl">
              <div className="relative z-10">
                <h3 className="text-2xl font-black mb-2 text-white">ভবিষ্যৎ সঞ্চয়</h3>
                <p className="text-[10px] text-slate-400 font-black uppercase tracking-widest mb-10 opacity-70">এআই এর মাধ্যমে আর্থিক পূর্বাভাস</p>
                <button 
                  onClick={fetchAIAdvice}
                  disabled={loadingAI}
                  className="w-full py-4 bg-gradient-to-r from-indigo-600 to-blue-600 text-white rounded-[20px] font-black uppercase text-[10px] shadow-xl active:scale-95 transition-all flex items-center justify-center gap-3 border-none"
                >
                  {loadingAI ? <><i className="fas fa-spinner animate-spin"></i> প্রসেসিং...</> : <><i className="fas fa-wand-magic-sparkles"></i> পরামর্শ নিন</>}
                </button>
              </div>
              <div className="absolute -right-16 -bottom-16 w-56 h-56 bg-indigo-500/10 rounded-full blur-3xl"></div>
            </div>
            {insights.map((insight, idx) => (
              <div key={idx} className={`p-6 rounded-[35px] border ${insight.type === 'success' ? 'bg-green-50 border-green-100' : insight.type === 'warning' ? 'bg-rose-50 border-rose-100' : 'bg-blue-50 border-blue-100'} animate-fadeIn`}>
                <div className="flex gap-4">
                  <div className={`w-11 h-11 rounded-2xl flex items-center justify-center text-xl shadow-sm ${insight.type === 'success' ? 'bg-green-500 text-white' : insight.type === 'warning' ? 'bg-rose-500 text-white' : 'bg-blue-500 text-white'}`}>
                    <i className={`fas ${insight.type === 'success' ? 'fa-check' : insight.type === 'warning' ? 'fa-exclamation' : 'fa-lightbulb'}`}></i>
                  </div>
                  <div>
                    <h4 className="font-black text-slate-900 text-[14px] mb-1">{insight.title}</h4>
                    <p className="text-slate-600 text-[12px] font-bold leading-relaxed">{insight.description}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="space-y-8 animate-fadeIn">
            <div className="bg-white p-9 rounded-[45px] border border-slate-100 shadow-sm space-y-7">
              <div className="flex items-center gap-3 mb-2">
                <div className="w-12 h-12 bg-indigo-600 text-white rounded-2xl flex items-center justify-center text-xl shadow-lg shadow-indigo-100"><i className="fas fa-calculator text-white"></i></div>
                <h3 className="font-black text-slate-900 uppercase text-xs tracking-widest">বিল স্প্লিটার</h3>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-1.5">
                   <p className="text-[8px] font-black text-slate-400 uppercase tracking-widest ml-2">মোট টাকা</p>
                   <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ ০" className="w-full p-4.5 bg-slate-50 border border-slate-200 rounded-[22px] font-black text-slate-900 outline-none focus:border-indigo-600 transition-all shadow-inner text-center" />
                </div>
                <div className="space-y-1.5">
                   <p className="text-[8px] font-black text-slate-400 uppercase tracking-widest ml-2">মানুষের সংখ্যা</p>
                   <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} className="w-full p-4.5 bg-slate-50 border border-slate-200 rounded-[22px] font-black text-slate-900 outline-none focus:border-indigo-600 transition-all shadow-inner text-center" />
                </div>
              </div>
              <div className="p-9 bg-gradient-to-br from-indigo-600 to-blue-700 rounded-[35px] text-center shadow-lg relative overflow-hidden group">
                <div className="relative z-10">
                  <p className="text-[10px] font-black text-blue-100 uppercase mb-2 tracking-widest">প্রতি জনের ভাগ</p>
                  <p className="text-4xl font-black text-white">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
                </div>
                <div className="absolute inset-0 bg-white/5 opacity-0 group-hover:opacity-100 transition-opacity"></div>
              </div>
            </div>
          </div>
        )}
      </main>

      {/* Modern Button Menu Navigation Bar */}
      <nav className="fixed bottom-0 left-0 right-0 bg-white border-t border-slate-100 px-6 py-4 flex justify-between items-center z-[100] shadow-[0_-15px_40px_-15px_rgba(0,0,0,0.08)]">
        <button 
          onClick={() => setActiveTab('home')} 
          className={`flex-1 flex flex-col items-center justify-center gap-1.5 py-1 transition-all duration-400 border-none bg-transparent group`}
        >
          <div className={`w-14 h-10 flex items-center justify-center rounded-[18px] transition-all duration-400 ${activeTab === 'home' ? 'bg-indigo-600 text-white shadow-xl shadow-indigo-100' : 'bg-transparent text-slate-400'}`}>
            <i className={`fas fa-house-chimney ${activeTab === 'home' ? 'text-lg text-white' : 'text-xl'}`}></i>
          </div>
          <span className={`text-[9px] font-black uppercase tracking-[0.2em] transition-colors duration-400 ${activeTab === 'home' ? 'text-indigo-600' : 'text-slate-400 opacity-60'}`}>হোম</span>
        </button>

        <button 
          onClick={() => setActiveTab('future')} 
          className={`flex-1 flex flex-col items-center justify-center gap-1.5 py-1 transition-all duration-400 border-none bg-transparent group`}
        >
          <div className={`w-14 h-10 flex items-center justify-center rounded-[18px] transition-all duration-400 ${activeTab === 'future' ? 'bg-indigo-600 text-white shadow-xl shadow-indigo-100' : 'bg-transparent text-slate-400'}`}>
            <i className={`fas fa-rocket ${activeTab === 'future' ? 'text-lg text-white' : 'text-xl'}`}></i>
          </div>
          <span className={`text-[9px] font-black uppercase tracking-[0.2em] transition-colors duration-400 ${activeTab === 'future' ? 'text-indigo-600' : 'text-slate-400 opacity-60'}`}>এআই</span>
        </button>

        <button 
          onClick={() => setActiveTab('tools')} 
          className={`flex-1 flex flex-col items-center justify-center gap-1.5 py-1 transition-all duration-400 border-none bg-transparent group`}
        >
          <div className={`w-14 h-10 flex items-center justify-center rounded-[18px] transition-all duration-400 ${activeTab === 'tools' ? 'bg-indigo-600 text-white shadow-xl shadow-indigo-100' : 'bg-transparent text-slate-400'}`}>
            <i className={`fas fa-layer-group ${activeTab === 'tools' ? 'text-lg text-white' : 'text-xl'}`}></i>
          </div>
          <span className={`text-[9px] font-black uppercase tracking-[0.2em] transition-colors duration-400 ${activeTab === 'tools' ? 'text-indigo-600' : 'text-slate-400 opacity-60'}`}>টুলস</span>
        </button>
      </nav>

      {/* Transaction Modal */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[200] flex items-center justify-center p-6">
          <div className="bg-white w-full max-w-[340px] rounded-[35px] p-7 pb-10 animate-fadeIn shadow-2xl border border-slate-100 flex flex-col">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-sm font-black uppercase tracking-widest text-slate-800">{modalType === TransactionType.INCOME ? 'টাকা যোগ' : 'খরচ যোগ'}</h2>
              <button onClick={() => setModalType(null)} className="w-9 h-9 bg-slate-100 rounded-xl flex items-center justify-center text-slate-600 hover:bg-slate-200 transition-all border-none"><i className="fas fa-times text-xs"></i></button>
            </div>

            <form onSubmit={addTransaction} className="flex flex-col gap-5">
              <div className="bg-slate-50 rounded-[25px] p-6 border border-slate-100 text-center">
                <p className="text-[8px] font-black text-slate-400 uppercase tracking-[0.3em] mb-2">টাকার পরিমাণ (৳)</p>
                <input type="number" name="amount" required placeholder="০" autoFocus className="w-full text-3xl font-black text-center outline-none text-slate-900 bg-transparent tracking-tighter border-none" />
              </div>

              <div className="flex flex-col gap-3">
                <div className="relative">
                  <select name="category" className="w-full p-4 bg-white border border-slate-200 rounded-[20px] font-black text-[11px] text-slate-800 outline-none focus:border-indigo-600 appearance-none text-center">
                    {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                  </select>
                  <div className="absolute right-4 top-1/2 -translate-y-1/2 pointer-events-none text-slate-400">
                    <i className="fas fa-chevron-down text-[10px]"></i>
                  </div>
                </div>
                <input type="text" name="note" placeholder="নোট (যেমন: বেতন, বাজার...)" className="w-full p-4 bg-white border border-slate-200 rounded-[20px] font-black text-[11px] text-slate-800 outline-none focus:border-indigo-600 text-center" />
              </div>

              <button type="submit" className={`w-full py-4.5 mt-2 rounded-[22px] text-white font-black text-xs uppercase tracking-widest shadow-lg active:scale-95 transition-all border-none ${modalType === TransactionType.INCOME ? 'bg-gradient-to-r from-green-500 to-emerald-600' : 'bg-gradient-to-r from-rose-500 to-pink-600'}`}>
                নিশ্চিত করুন
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Other Modals (Budget/History) - Simplified for brevity but kept functional */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[45px] p-8 animate-slideUp shadow-2xl overflow-y-auto max-h-[85vh] custom-scrollbar">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-lg font-black text-slate-900 tracking-widest">বাজেট লক্ষ্য</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-10 h-10 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900 border-none"><i className="fas fa-times"></i></button>
             </div>
             <div className="space-y-4 mb-8">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="space-y-1">
                    <label className="text-[9px] font-black text-slate-500 uppercase ml-3 tracking-widest">{cat}</label>
                    <input 
                      type="number" 
                      value={budgets[cat] || ''}
                      onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })}
                      placeholder="৳ বাজেট দিন"
                      className="w-full p-4 bg-slate-50 border border-slate-200 rounded-[20px] font-black text-xs text-slate-900 outline-none focus:border-indigo-600"
                    />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-5 bg-slate-900 text-white rounded-[25px] font-black uppercase text-xs tracking-widest shadow-xl mb-4 border-none">সংরক্ষণ করুন</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'future' | 'tools'>('home');
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  const fetchAIAdvice = async () => {
    if (transactions.length === 0) {
      alert("পরামর্শ পেতে প্রথমে কিছু লেনদেন যোগ করুন।");
      return;
    }
    setLoadingAI(true);
    const finSummary: FinancialSummary = {
      totalIncome: summary.income,
      totalExpenses: summary.expense,
      balance: summary.balance,
      savings: summary.balance,
      previousMonthSavings: (archives[0]?.totalIncome ?? 0) - (archives[0]?.totalExpense ?? 0),
      categoryBreakdown: summary.breakdown,
      monthlyHistory: archives.slice(0, 5).map(a => ({
        month: a.monthName,
        income: a.totalIncome,
        expense: a.totalExpense
      }))
    };
    const results = await getFinancialInsights(transactions, finSummary);
    setInsights(results);
    setLoadingAI(false);
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
  };

  const deleteTransaction = (id: string) => {
    if (window.confirm('আপনি কি এই লেনদেনটি মুছে ফেলতে চান?')) {
      setTransactions(prev => prev.filter(t => t.id !== id));
    }
  };

  return (
    <div className="max-w-md mx-auto min-h-screen bg-[#f8fafc] shadow-2xl flex flex-col font-sans relative pb-24 overflow-hidden text-slate-900">
