             <div className="flex justify-between items-center mb-8">
                <h2 className="text-lg font-black text-slate-900 tracking-widest">বাজেট লক্ষ্য</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-10 h-10 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900 border-none"><i className="fas fa-times"></i></button>
             </div>
             <div className="space-y-4 mb-8">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="space-y-1 text-slate-900">
                    <label className="text-[9px] font-black text-slate-500 uppercase ml-3 tracking-widest">{cat}</label>
                    <input 
                      type="number" 
                      value={budgets[cat] || ''}
                      onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })}
                      placeholder="৳ বাজেট দিন"
                      className="w-full p-4 bg-slate-50 border border-slate-200 rounded-[20px] font-black text-xs text-slate-900 outline-none focus:border-indigo-600 border-none"
                    />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-5 bg-slate-900 text-white rounded-[25px] font-black uppercase text-xs tracking-widest shadow-xl mb-4 border-none">সংরক্ষণ করুন</button>
          </div>
        </div>
      )}

      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[45px] p-8 overflow-y-auto animate-slideUp shadow-2xl">
             <div className="flex justify-between items-center mb-10">
                <h2 className="text-xl font-black text-slate-900 tracking-widest uppercase">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-10 h-10 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900 border-none"><i className="fas fa-times"></i></button>
             </div>
             {archives.length > 0 ? (
               <div className="space-y-6">
                 {archives.map(arc => (
                   <div key={arc.id} className="p-6 bg-slate-50 rounded-[35px] border border-slate-100 shadow-sm border-none">
                      <p className="text-[10px] text-indigo-600 uppercase font-black tracking-widest mb-1">{arc.monthName} {arc.year}</p>
                      <p className="text-slate-900 text-2xl font-black tracking-tighter">৳{arc.totalIncome - arc.totalExpense}</p>
                      <div className="flex gap-4 pt-4 mt-4 border-t border-slate-200">
                        <div className="flex-1">
                          <p className="text-[8px] font-black text-green-600 uppercase mb-0.5">আয়</p>
                          <p className="text-xs font-black text-slate-800">৳{arc.totalIncome}</p>
                        </div>
                        <div className="flex-1 text-right">
                          <p className="text-[8px] font-black text-rose-600 uppercase mb-0.5">ব্যয়</p>
                          <p className="text-xs font-black text-slate-800">৳{arc.totalExpense}</p>
                        </div>
                      </div>
                   </div>
                 ))}
               </div>
             ) : (
               <div className="text-center py-24 text-slate-400 font-black uppercase text-[10px] tracking-widest">ইতিহাস খালি</div>
             )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'future' | 'tools'>('home');
  const [isShrunk, setIsShrunk] = useState(false);
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  const handleScroll = (e: React.UIEvent<HTMLElement>) => {
    const scrollPos = e.currentTarget.scrollTop;
    if (scrollPos > 20 && !isShrunk) setIsShrunk(true);
    if (scrollPos <= 20 && isShrunk) setIsShrunk(false);
  };

  const fetchAIAdvice = async () => {
    if (transactions.length === 0) {
      alert("পরামর্শ পেতে প্রথমে কিছু লেনদেন যোগ করুন।");
      return;
    }
    setLoadingAI(true);
    const finSummary: FinancialSummary = {
      totalIncome: summary.income,
      totalExpenses: summary.expense,
      balance: summary.balance,
      savings: summary.balance,
      previousMonthSavings: (archives[0]?.totalIncome ?? 0) - (archives[0]?.totalExpense ?? 0),
      categoryBreakdown: summary.breakdown,
      monthlyHistory: archives.slice(0, 5).map(a => ({
        month: a.monthName,
        income: a.totalIncome,
        expense: a.totalExpense
      }))
    };
    const results = await getFinancialInsights(transactions, finSummary);
    setInsights(results);
    setLoadingAI(false);
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
  };

  const deleteTransaction = (id: string) => {
    if (window.confirm('আপনি কি এই লেনদেনটি মুছে ফেলতে চান?')) {
      setTransactions(prev => prev.filter(t => t.id !== id));
    }
  };

  return (
    <div className="max-w-md mx-auto h-screen bg-[#f8fafc] shadow-2xl flex flex-col font-sans relative overflow-hidden text-slate-900">
      {/* Dynamic Shrinking Header with Fixed Layout to prevent bumping */}
      <header 
        className={`bg-gradient-to-br from-blue-700 via-indigo-600 to-indigo-800 text-white shadow-2xl relative z-20 transition-[padding,border-radius] duration-300 ease-out will-change-[padding] ${isShrunk ? 'p-4 rounded-b-[25px]' : 'p-7 rounded-b-[45px]'}`}
      >
        <div className={`flex justify-between items-center transition-all duration-300 ${isShrunk ? 'mb-1' : 'mb-6'}`}>
          <div className="flex items-center gap-3">
             <div className={`bg-white/20 rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/30 shadow-lg backdrop-blur-md transition-all duration-300 ${isShrunk ? 'w-8 h-8' : 'w-12 h-12'}`}>
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
                ) : (
                  <i className={`fas fa-user-circle text-white transition-all ${isShrunk ? 'text-base' : 'text-2xl'}`}></i>
                )}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div className={`${isShrunk ? 'flex items-center gap-2' : ''}`}>
                <h1 className={`font-black uppercase tracking-tight text-white transition-all duration-300 ${isShrunk ? 'text-[14.7px]' : 'text-[21px]'}`}>স্পার্ক</h1>
                <p className={`font-black text-blue-200 uppercase tracking-widest opacity-80 transition-all ${isShrunk ? 'hidden' : 'text-[9.5px] mt-0.5'}`}>স্মার্ট ওয়ালেট</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className={`bg-white/10 rounded-xl flex items-center justify-center hover:bg-white/20 transition-all border border-white/10 backdrop-blur-md text-white border-none ${isShrunk ? 'w-8 h-8' : 'w-10 h-10'}`}>
              <i className="fas fa-bullseye text-[12.6px]"></i>
            </button>
            <button onClick={() => setShowHistory(true)} className={`bg-white/10 rounded-xl flex items-center justify-center hover:bg-white/20 transition-all border border-white/10 backdrop-blur-md text-white border-none ${isShrunk ? 'w-8 h-8' : 'w-10 h-10'}`}>
              <i className="fas fa-history text-[12.6px]"></i>
            </button>
          </div>
        </div>

        <div className={`text-center transition-all duration-300 ${isShrunk ? 'flex flex-row items-center justify-between px-1 py-0' : 'py-1'}`}>
          <div className={`${isShrunk ? 'flex items-baseline gap-2' : ''}`}>
            <p className={`text-blue-100 font-black uppercase transition-all duration-300 ${isShrunk ? 'text-[8.4px] tracking-[0.1em]' : 'text-[10.5px] tracking-[0.3em] mb-2 opacity-90'}`}>বর্তমান ব্যালেন্স</p>
            <h2 className={`font-black tracking-tighter drop-shadow-lg text-white transition-all duration-300 ${isShrunk ? 'text-[21px]' : 'text-[37.8px] mb-8'}`}>{formatCurrency(summary.balance)}</h2>
          </div>

          <div className={`transition-all duration-300 overflow-hidden ${isShrunk ? 'max-h-0 opacity-0' : 'max-h-[200px] opacity-100 grid grid-cols-2 gap-3'}`}>
            <div className="bg-white/10 backdrop-blur-xl p-3.5 rounded-[24px] border border-white/20 shadow-sm">
              <p className="text-[9.5px] uppercase font-black text-blue-200 mb-1">মোট আয়</p>
              <p className="text-[19px] font-black text-green-300">৳{summary.income}</p>
            </div>
            <div className="bg-white/10 backdrop-blur-xl p-3.5 rounded-[24px] border border-white/20 shadow-sm">
              <p className="text-[9.5px] uppercase font-black text-blue-200 mb-1">মোট ব্যয়</p>
              <p className="text-[19px] font-black text-rose-300">৳{summary.expense}</p>
            </div>
          </div>
        </div>
      </header>

      {/* Main Area with optimized Scroll Handler */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-8" onScroll={handleScroll}>
        {activeTab === 'home' ? (
          <div className="space-y-10 animate-fadeIn">
            {/* Quick Add Balance */}
            <div className="grid grid-cols-2 gap-4">
              <button 
                onClick={() => setModalType(TransactionType.INCOME)} 
                className="relative overflow-hidden p-6 bg-white border border-slate-100 rounded-[35px] flex flex-col items-center gap-3 transition-all hover:shadow-2xl hover:-translate-y-1 active:scale-95 group border-none"
              >
                <div className="absolute top-0 right-0 w-16 h-16 bg-green-50 rounded-full -mr-8 -mt-8 group-hover:scale-150 transition-transform duration-500"></div>
                <div className="w-14 h-14 bg-gradient-to-br from-green-400 to-green-600 text-white rounded-[22px] flex items-center justify-center text-[25px] shadow-lg shadow-green-100 relative z-10">
                  <i className="fas fa-plus"></i>
                </div>
                <span className="text-[11.5px] font-black text-slate-800 uppercase tracking-[0.15em] relative z-10">টাকা যোগ</span>
              </button>

              <button 
                onClick={() => setModalType(TransactionType.EXPENSE)} 
                className="relative overflow-hidden p-6 bg-white border border-slate-100 rounded-[35px] flex flex-col items-center gap-3 transition-all hover:shadow-2xl hover:-translate-y-1 active:scale-95 group border-none"
              >
                <div className="absolute top-0 right-0 w-16 h-16 bg-rose-50 rounded-full -mr-8 -mt-8 group-hover:scale-150 transition-transform duration-500"></div>
                <div className="w-14 h-14 bg-gradient-to-br from-rose-400 to-rose-600 text-white rounded-[22px] flex items-center justify-center text-[25px] shadow-lg shadow-rose-100 relative z-10">
                  <i className="fas fa-minus"></i>
                </div>
                <span className="text-[11.5px] font-black text-slate-800 uppercase tracking-[0.15em] relative z-10">খরচ যোগ</span>
              </button>
            </div>

            {/* Transactions List */}
            <div className="space-y-4">
              <div className="flex justify-between items-center px-2">
                <h3 className="font-black text-slate-900 text-[10.5px] uppercase tracking-[0.2em] opacity-60">সাম্প্রতিক লেনদেন</h3>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3 pb-24">
                  {transactions.map(t => (
                    <div key={t.id} className="bg-white border border-slate-100 p-4 rounded-[28px] shadow-sm flex items-center justify-between group hover:border-indigo-100 transition-colors border-none">
                      <div className="flex items-center gap-4">
                        <div className={`w-11 h-11 rounded-2xl flex items-center justify-center text-[19px] ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-800 text-[14.7px]">{t.category}</p>
                          <p className="text-[9.5px] text-slate-400 font-black uppercase">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <div className="flex items-center gap-3">
                        <p className={`font-black text-[14.7px] ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                          {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                        </p>
                        <button 
                          onClick={() => deleteTransaction(t.id)}
                          className="w-9 h-9 bg-slate-50 text-slate-300 rounded-xl flex items-center justify-center hover:bg-rose-50 hover:text-rose-500 transition-all active:scale-90 border-none"
                        >
                          <i className="fas fa-trash-alt text-[12.6px]"></i>
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-16 bg-white rounded-[40px] border border-dashed border-slate-200">
                  <i className="fas fa-receipt text-slate-200 text-[31.5px] mb-3"></i>
                  <p className="text-slate-400 font-black uppercase text-[9.5px] tracking-widest">খালি তালিকা</p>
                </div>
              )}
            </div>
          </div>
        ) : activeTab === 'future' ? (
          <div className="space-y-8 animate-fadeIn pb-24">
            <div className="p-9 bg-slate-900 rounded-[45px] text-white relative overflow-hidden shadow-2xl">
              <div className="relative z-10">
                <h3 className="text-[25px] font-black mb-2 text-white">ভবিষ্যৎ সঞ্চয়</h3>
                <p className="text-[10.5px] text-slate-400 font-black uppercase tracking-widest mb-10 opacity-70">এআই এর মাধ্যমে আর্থিক পূর্বাভাস</p>
                <button 
                  onClick={fetchAIAdvice}
                  disabled={loadingAI}
                  className="w-full py-4 bg-gradient-to-r from-indigo-600 to-blue-600 text-white rounded-[20px] font-black uppercase text-[10.5px] shadow-xl active:scale-95 transition-all flex items-center justify-center gap-3 border-none"
                >
                  {loadingAI ? <><i className="fas fa-spinner animate-spin"></i> প্রসেসিং...</> : <><i className="fas fa-wand-magic-sparkles"></i> পরামর্শ নিন</>}
                </button>
              </div>
              <div className="absolute -right-16 -bottom-16 w-56 h-56 bg-indigo-500/10 rounded-full blur-3xl"></div>
            </div>
            {insights.map((insight, idx) => (
              <div key={idx} className={`p-6 rounded-[35px] border ${insight.type === 'success' ? 'bg-green-50 border-green-100' : insight.type === 'warning' ? 'bg-rose-50 border-rose-100' : 'bg-blue-50 border-blue-100'} animate-fadeIn`}>
                <div className="flex gap-4">
                  <div className={`w-11 h-11 rounded-2xl flex items-center justify-center text-[21px] shadow-sm ${insight.type === 'success' ? 'bg-green-500 text-white' : insight.type === 'warning' ? 'bg-rose-500 text-white' : 'bg-blue-500 text-white'}`}>
                    <i className={`fas ${insight.type === 'success' ? 'fa-check' : insight.type === 'warning' ? 'fa-exclamation' : 'fa-lightbulb'}`}></i>
                  </div>
                  <div>
                    <h4 className="font-black text-slate-900 text-[14.7px] mb-1">{insight.title}</h4>
                    <p className="text-slate-600 text-[12.6px] font-bold leading-relaxed">{insight.description}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="space-y-8 animate-fadeIn pb-24">
            <div className="bg-white p-9 rounded-[45px] border border-slate-100 shadow-sm space-y-7 border-none">
              <div className="flex items-center gap-3 mb-2">
                <div className="w-12 h-12 bg-indigo-600 text-white rounded-2xl flex items-center justify-center text-[21px] shadow-lg shadow-indigo-100"><i className="fas fa-calculator text-white"></i></div>
                <h3 className="font-black text-slate-900 uppercase text-[12.6px] tracking-widest">বিল স্প্লিটার</h3>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-1.5">
                   <p className="text-[8.4px] font-black text-slate-400 uppercase tracking-widest ml-2">মোট টাকা</p>
                   <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ ০" className="w-full p-4 bg-slate-50 border border-slate-200 rounded-[22px] font-black text-slate-900 outline-none focus:border-indigo-600 transition-all shadow-inner text-center border-none" />
                </div>
                <div className="space-y-1.5">
                   <p className="text-[8.4px] font-black text-slate-400 uppercase tracking-widest ml-2">মানুষের সংখ্যা</p>
                   <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} className="w-full p-4 bg-slate-50 border border-slate-200 rounded-[22px] font-black text-slate-900 outline-none focus:border-indigo-600 transition-all shadow-inner text-center border-none" />
                </div>
              </div>
              <div className="p-9 bg-gradient-to-br from-indigo-600 to-blue-700 rounded-[35px] text-center shadow-lg relative overflow-hidden group">
                <div className="relative z-10">
                  <p className="text-[10.5px] font-black text-blue-100 uppercase mb-2 tracking-widest">প্রতি জনের ভাগ</p>
                  <p className="text-[42px] font-black text-white">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
                </div>
                <div className="absolute inset-0 bg-white/5 opacity-0 group-hover:opacity-100 transition-opacity"></div>
              </div>
            </div>
          </div>
        )}
      </main>

      {/* Modern Slimmer Button Menu Navigation Bar */}
      <nav className="fixed bottom-0 left-0 right-0 bg-white border-t border-slate-100 px-6 py-2.5 flex justify-between items-center z-[100] shadow-[0_-15px_40px_-15px_rgba(0,0,0,0.08)]">
        <button 
          onClick={() => setActiveTab('home')} 
          className={`flex-1 flex flex-col items-center justify-center gap-1 py-1 transition-all duration-400 border-none bg-transparent group`}
        >
          <div className={`w-14 h-9 flex items-center justify-center rounded-[18px] transition-all duration-400 ${activeTab === 'home' ? 'bg-indigo-600 text-white shadow-xl shadow-indigo-100' : 'bg-transparent text-slate-400'}`}>
            <i className={`fas fa-house-chimney ${activeTab === 'home' ? 'text-[16.8px] text-white' : 'text-[18.9px]'}`}></i>
          </div>
          <span className={`text-[8.4px] font-black uppercase tracking-[0.2em] transition-colors duration-400 ${activeTab === 'home' ? 'text-indigo-600' : 'text-slate-400 opacity-60'}`}>হোম</span>
        </button>

        <button 
          onClick={() => setActiveTab('future')} 
          className={`flex-1 flex flex-col items-center justify-center gap-1 py-1 transition-all duration-400 border-none bg-transparent group`}
        >
          <div className={`w-14 h-9 flex items-center justify-center rounded-[18px] transition-all duration-400 ${activeTab === 'future' ? 'bg-indigo-600 text-white shadow-xl shadow-indigo-100' : 'bg-transparent text-slate-400'}`}>
            <i className={`fas fa-rocket ${activeTab === 'future' ? 'text-[16.8px] text-white' : 'text-[18.9px]'}`}></i>
          </div>
          <span className={`text-[8.4px] font-black uppercase tracking-[0.2em] transition-colors duration-400 ${activeTab === 'future' ? 'text-indigo-600' : 'text-slate-400 opacity-60'}`}>এআই</span>
        </button>

        <button 
          onClick={() => setActiveTab('tools')} 
          className={`flex-1 flex flex-col items-center justify-center gap-1 py-1 transition-all duration-400 border-none bg-transparent group`}
        >
          <div className={`w-14 h-9 flex items-center justify-center rounded-[18px] transition-all duration-400 ${activeTab === 'tools' ? 'bg-indigo-600 text-white shadow-xl shadow-indigo-100' : 'bg-transparent text-slate-400'}`}>
            <i className={`fas fa-layer-group ${activeTab === 'tools' ? 'text-[16.8px] text-white' : 'text-[18.9px]'}`}></i>
          </div>
          <span className={`text-[8.4px] font-black uppercase tracking-[0.2em] transition-colors duration-400 ${activeTab === 'tools' ? 'text-indigo-600' : 'text-slate-400 opacity-60'}`}>টুলস</span>
        </button>
      </nav>

      {/* Modals remain stable */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[200] flex items-center justify-center p-6">
          <div className="bg-white w-full max-w-[340px] rounded-[35px] p-7 pb-10 animate-fadeIn shadow-2xl border border-slate-100 flex flex-col border-none">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-[14.7px] font-black uppercase tracking-widest text-slate-800">{modalType === TransactionType.INCOME ? 'টাকা যোগ' : 'খরচ যোগ'}</h2>
              <button onClick={() => setModalType(null)} className="w-9 h-9 bg-slate-100 rounded-xl flex items-center justify-center text-slate-600 hover:bg-slate-200 transition-all border-none"><i className="fas fa-times text-[12.6px]"></i></button>
            </div>

            <form onSubmit={addTransaction} className="flex flex-col gap-5">
              <div className="bg-slate-50 rounded-[25px] p-6 border border-slate-100 text-center border-none">
                <p className="text-[8.4px] font-black text-slate-400 uppercase tracking-[0.3em] mb-2">টাকার পরিমাণ (৳)</p>
                <input type="number" name="amount" required placeholder="০" autoFocus className="w-full text-[31.5px] font-black text-center outline-none text-slate-900 bg-transparent tracking-tighter border-none" />
              </div>

              <div className="flex flex-col gap-3">
                <div className="relative">
                  <select name="category" className="w-full p-4 bg-white border border-slate-200 rounded-[20px] font-black text-[11.5px] text-slate-800 outline-none focus:border-indigo-600 appearance-none text-center">
                    {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                  </select>
                  <div className="absolute right-4 top-1/2 -translate-y-1/2 pointer-events-none text-slate-400">
                    <i className="fas fa-chevron-down text-[10.5px]"></i>
                  </div>
                </div>
                <input type="text" name="note" placeholder="নোট (যেমন: বেতন, বাজার...)" className="w-full p-4 bg-white border border-slate-200 rounded-[20px] font-black text-[11.5px] text-slate-800 outline-none focus:border-indigo-600 text-center" />
              </div>

              <button type="submit" className={`w-full py-4.5 mt-2 rounded-[22px] text-white font-black text-[12.6px] uppercase tracking-widest shadow-lg active:scale-95 transition-all border-none ${modalType === TransactionType.INCOME ? 'bg-gradient-to-r from-green-500 to-emerald-600' : 'bg-gradient-to-r from-rose-500 to-pink-600'}`}>
                নিশ্চিত করুন
              </button>
            </form>
          </div>
        </div>
      )}

      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[45px] p-8 animate-slideUp shadow-2xl overflow-y-auto max-h-[85vh] custom-scrollbar border-none">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-[18.9px] font-black text-slate-900 tracking-widest">বাজেট লক্ষ্য</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-10 h-10 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900 border-none"><i className="fas fa-times"></i></button>
             </div>
             <div className="space-y-4 mb-8">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="space-y-1 text-slate-900">
                    <label className="text-[9.5px] font-black text-slate-500 uppercase ml-3 tracking-widest">{cat}</label>
                    <input 
                      type="number" 
                      value={budgets[cat] || ''}
                      onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })}
                      placeholder="৳ বাজেট দিন"
                      className="w-full p-4 bg-slate-50 border border-slate-200 rounded-[20px] font-black text-[12.6px] text-slate-900 outline-none focus:border-indigo-600 border-none"
                    />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-5 bg-slate-900 text-white rounded-[25px] font-black uppercase text-[12.6px] tracking-widest shadow-xl mb-4 border-none">সংরক্ষণ করুন</button>
          </div>
        </div>
      )}

      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[45px] p-8 overflow-y-auto animate-slideUp shadow-2xl">
             <div className="flex justify-between items-center mb-10">
                <h2 className="text-[21px] font-black text-slate-900 tracking-widest uppercase">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-10 h-10 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900 border-none"><i className="fas fa-times"></i></button>
             </div>
             {archives.length > 0 ? (
               <div className="space-y-6 pb-24">
                 {archives.map(arc => (
                   <div key={arc.id} className="p-6 bg-slate-50 rounded-[35px] border border-slate-100 shadow-sm border-none">
                      <p className="text-[10.5px] text-indigo-600 uppercase font-black tracking-widest mb-1">{arc.monthName} {arc.year}</p>
                      <p className="text-slate-900 text-[25px] font-black tracking-tighter">৳{arc.totalIncome - arc.totalExpense}</p>
                      <div className="flex gap-4 pt-4 mt-4 border-t border-slate-200">
                        <div className="flex-1">
                          <p className="text-[8.4px] font-black text-green-600 uppercase mb-0.5">আয়</p>
                          <p className="text-[12.6px] font-black text-slate-800">৳{arc.totalIncome}</p>
                        </div>
                        <div className="flex-1 text-right">
                          <p className="text-[8.4px] font-black text-rose-600 uppercase mb-0.5">ব্যয়</p>
                          <p className="text-[12.6px] font-black text-slate-800">৳{arc.totalExpense}</p>
                        </div>
                      </div>
                   </div>
                 ))}
               </div>
             ) : (
               <div className="text-center py-24 text-slate-400 font-black uppercase text-[10.5px] tracking-widest">ইতিহাস খালি</div>
             )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'future' | 'tools'>('home');
  const [isShrunk, setIsShrunk] = useState(false);
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  const handleScroll = (e: React.UIEvent<HTMLElement>) => {
    const scrollPos = e.currentTarget.scrollTop;
    // Set a threshold to prevent jitter
    if (scrollPos > 30 && !isShrunk) setIsShrunk(true);
    if (scrollPos <= 30 && isShrunk) setIsShrunk(false);
  };

  const fetchAIAdvice = async () => {
    if (transactions.length === 0) {
      alert("পরামর্শ পেতে প্রথমে কিছু লেনদেন যোগ করুন।");
      return;
    }
    setLoadingAI(true);
    const finSummary: FinancialSummary = {
      totalIncome: summary.income,
      totalExpenses: summary.expense,
      balance: summary.balance,
      savings: summary.balance,
      previousMonthSavings: (archives[0]?.totalIncome ?? 0) - (archives[0]?.totalExpense ?? 0),
      categoryBreakdown: summary.breakdown,
      monthlyHistory: archives.slice(0, 5).map(a => ({
        month: a.monthName,
        income: a.totalIncome,
        expense: a.totalExpense
      }))
    };
    const results = await getFinancialInsights(transactions, finSummary);
    setInsights(results);
    setLoadingAI(false);
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
  };

  const deleteTransaction = (id: string) => {
    if (window.confirm('আপনি কি এই লেনদেনটি মুছে ফেলতে চান?')) {
      setTransactions(prev => prev.filter(t => t.id !== id));
    }
  };

  return (
    <div className="max-w-md mx-auto h-screen bg-[#f8fafc] shadow-2xl flex flex-col font-sans relative overflow-hidden text-slate-900">
      {/* Header with improved stability to fix scroll bumping */}
      <header 
        className={`bg-gradient-to-br from-blue-700 via-indigo-600 to-indigo-800 text-white shadow-2xl relative z-20 transition-all duration-300 ease-out will-change-[padding,border-radius] ${isShrunk ? 'p-4 rounded-b-[25px]' : 'p-7 rounded-b-[45px]'}`}
      >
        <div className={`flex justify-between items-center transition-all duration-300 ${isShrunk ? 'mb-1' : 'mb-6'}`}>
          <div className="flex items-center gap-3">
             <div className={`bg-white/20 rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/30 shadow-lg backdrop-blur-md transition-all duration-300 ${isShrunk ? 'w-8 h-8' : 'w-12 h-12'}`}>
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
                ) : (
                  <i className={`fas fa-user-circle text-white transition-all ${isShrunk ? 'text-base' : 'text-2xl'}`}></i>
                )}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div className={`${isShrunk ? 'flex items-center gap-2' : ''}`}>
                <h1 className={`font-black uppercase tracking-tight text-white transition-all duration-300 ${isShrunk ? 'text-[14.7px]' : 'text-[21px]'}`}>স্পার্ক</h1>
                <p className={`font-black text-blue-200 uppercase tracking-widest opacity-80 transition-all ${isShrunk ? 'hidden' : 'text-[9.5px] mt-0.5'}`}>স্মার্ট ওয়ালেট</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className={`bg-white/10 rounded-xl flex items-center justify-center hover:bg-white/20 transition-all border border-white/10 backdrop-blur-md text-white border-none ${isShrunk ? 'w-8 h-8' : 'w-10 h-10'}`}>
              <i className="fas fa-bullseye text-[12.6px]"></i>
            </button>
            <button onClick={() => setShowHistory(true)} className={`bg-white/10 rounded-xl flex items-center justify-center hover:bg-white/20 transition-all border border-white/10 backdrop-blur-md text-white border-none ${isShrunk ? 'w-8 h-8' : 'w-10 h-10'}`}>
              <i className="fas fa-history text-[12.6px]"></i>
            </button>
          </div>
        </div>

        <div className={`text-center transition-all duration-300 ${isShrunk ? 'flex flex-row items-center justify-between px-1' : 'py-1'}`}>
          <div className={`${isShrunk ? 'flex items-baseline gap-2' : ''}`}>
            <p className={`text-blue-100 font-black uppercase transition-all duration-300 ${isShrunk ? 'text-[8.4px] tracking-[0.1em]' : 'text-[10.5px] tracking-[0.3em] mb-2 opacity-90'}`}>বর্তমান ব্যালেন্স</p>
            <h2 className={`font-black tracking-tighter drop-shadow-lg text-white transition-all duration-300 ${isShrunk ? 'text-[21px]' : 'text-[37.8px] mb-8'}`}>{formatCurrency(summary.balance)}</h2>
          </div>

          <div className={`transition-all duration-300 overflow-hidden ${isShrunk ? 'max-h-0 opacity-0' : 'max-h-[200px] opacity-100 grid grid-cols-2 gap-3'}`}>
            <div className="bg-white/10 backdrop-blur-xl p-3.5 rounded-[24px] border border-white/20 shadow-sm">
              <p className="text-[9.5px] uppercase font-black text-blue-200 mb-1">মোট আয়</p>
              <p className="text-[19px] font-black text-green-300">৳{summary.income}</p>
            </div>
            <div className="bg-white/10 backdrop-blur-xl p-3.5 rounded-[24px] border border-white/20 shadow-sm">
              <p className="text-[9.5px] uppercase font-black text-blue-200 mb-1">মোট ব্যয়</p>
              <p className="text-[19px] font-black text-rose-300">৳{summary.expense}</p>
            </div>
          </div>
        </div>
      </header>

      {/* Main content area with scroll handling */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-8" onScroll={handleScroll}>
        {activeTab === 'home' ? (
          <div className="space-y-10 animate-fadeIn">
            {/* Quick Actions */}
            <div className="grid grid-cols-2 gap-4">
              <button 
                onClick={() => setModalType(TransactionType.INCOME)} 
                className="relative overflow-hidden p-6 bg-white border border-slate-100 rounded-[35px] flex flex-col items-center gap-3 transition-all hover:shadow-2xl hover:-translate-y-1 active:scale-95 group border-none"
              >
                <div className="absolute top-0 right-0 w-16 h-16 bg-green-50 rounded-full -mr-8 -mt-8 group-hover:scale-150 transition-transform duration-500"></div>
                <div className="w-14 h-14 bg-gradient-to-br from-green-400 to-green-600 text-white rounded-[22px] flex items-center justify-center text-[25px] shadow-lg shadow-green-100 relative z-10">
                  <i className="fas fa-plus"></i>
                </div>
                <span className="text-[11.5px] font-black text-slate-800 uppercase tracking-[0.15em] relative z-10">টাকা যোগ</span>
              </button>

              <button 
                onClick={() => setModalType(TransactionType.EXPENSE)} 
                className="relative overflow-hidden p-6 bg-white border border-slate-100 rounded-[35px] flex flex-col items-center gap-3 transition-all hover:shadow-2xl hover:-translate-y-1 active:scale-95 group border-none"
              >
                <div className="absolute top-0 right-0 w-16 h-16 bg-rose-50 rounded-full -mr-8 -mt-8 group-hover:scale-150 transition-transform duration-500"></div>
                <div className="w-14 h-14 bg-gradient-to-br from-rose-400 to-rose-600 text-white rounded-[22px] flex items-center justify-center text-[25px] shadow-lg shadow-rose-100 relative z-10">
                  <i className="fas fa-minus"></i>
                </div>
                <span className="text-[11.5px] font-black text-slate-800 uppercase tracking-[0.15em] relative z-10">খরচ যোগ</span>
              </button>
            </div>

            {/* Recent Transactions */}
            <div className="space-y-4">
              <div className="flex justify-between items-center px-2">
                <h3 className="font-black text-slate-900 text-[10.5px] uppercase tracking-[0.2em] opacity-60">সাম্প্রতিক লেনদেন</h3>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3 pb-24">
                  {transactions.map(t => (
                    <div key={t.id} className="bg-white border border-slate-100 p-4 rounded-[28px] shadow-sm flex items-center justify-between group hover:border-indigo-100 transition-colors border-none">
                      <div className="flex items-center gap-4">
                        <div className={`w-11 h-11 rounded-2xl flex items-center justify-center text-[19px] ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-800 text-[14.7px]">{t.category}</p>
                          <p className="text-[9.5px] text-slate-400 font-black uppercase">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <div className="flex items-center gap-3">
                        <p className={`font-black text-[14.7px] ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                          {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                        </p>
                        <button 
                          onClick={() => deleteTransaction(t.id)}
                          className="w-9 h-9 bg-slate-50 text-slate-300 rounded-xl flex items-center justify-center hover:bg-rose-50 hover:text-rose-500 transition-all active:scale-90 border-none"
                        >
                          <i className="fas fa-trash-alt text-[12.6px]"></i>
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-16 bg-white rounded-[40px] border border-dashed border-slate-200">
                  <i className="fas fa-receipt text-slate-200 text-[31.5px] mb-3"></i>
                  <p className="text-slate-400 font-black uppercase text-[9.5px] tracking-widest">খালি তালিকা</p>
                </div>
              )}
            </div>
          </div>
        ) : activeTab === 'future' ? (
          <div className="space-y-8 animate-fadeIn pb-24">
            <div className="p-9 bg-slate-900 rounded-[45px] text-white relative overflow-hidden shadow-2xl">
              <div className="relative z-10">
                <h3 className="text-[25px] font-black mb-2 text-white">ভবিষ্যৎ সঞ্চয়</h3>
                <p className="text-[10.5px] text-slate-400 font-black uppercase tracking-widest mb-10 opacity-70">এআই এর মাধ্যমে আর্থিক পূর্বাভাস</p>
                <button 
                  onClick={fetchAIAdvice}
                  disabled={loadingAI}
                  className="w-full py-4 bg-gradient-to-r from-indigo-600 to-blue-600 text-white rounded-[20px] font-black uppercase text-[10.5px] shadow-xl active:scale-95 transition-all flex items-center justify-center gap-3 border-none"
                >
                  {loadingAI ? <><i className="fas fa-spinner animate-spin"></i> প্রসেসিং...</> : <><i className="fas fa-wand-magic-sparkles"></i> পরামর্শ নিন</>}
                </button>
              </div>
              <div className="absolute -right-16 -bottom-16 w-56 h-56 bg-indigo-500/10 rounded-full blur-3xl"></div>
            </div>
            {insights.map((insight, idx) => (
              <div key={idx} className={`p-6 rounded-[35px] border ${insight.type === 'success' ? 'bg-green-50 border-green-100' : insight.type === 'warning' ? 'bg-rose-50 border-rose-100' : 'bg-blue-50 border-blue-100'} animate-fadeIn`}>
                <div className="flex gap-4">
                  <div className={`w-11 h-11 rounded-2xl flex items-center justify-center text-[21px] shadow-sm ${insight.type === 'success' ? 'bg-green-500 text-white' : insight.type === 'warning' ? 'bg-rose-500 text-white' : 'bg-blue-500 text-white'}`}>
                    <i className={`fas ${insight.type === 'success' ? 'fa-check' : insight.type === 'warning' ? 'fa-exclamation' : 'fa-lightbulb'}`}></i>
                  </div>
                  <div>
                    <h4 className="font-black text-slate-900 text-[14.7px] mb-1">{insight.title}</h4>
                    <p className="text-slate-600 text-[12.6px] font-bold leading-relaxed">{insight.description}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="space-y-8 animate-fadeIn pb-24">
            {/* Bill Splitter */}
            <div className="bg-white p-9 rounded-[45px] border border-slate-100 shadow-sm space-y-7 border-none">
              <div className="flex items-center gap-3 mb-2">
                <div className="w-12 h-12 bg-indigo-600 text-white rounded-2xl flex items-center justify-center text-[21px] shadow-lg shadow-indigo-100"><i className="fas fa-calculator text-white"></i></div>
                <h3 className="font-black text-slate-900 uppercase text-[12.6px] tracking-widest">বিল স্প্লিটার</h3>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-1.5">
                   <p className="text-[8.4px] font-black text-slate-400 uppercase tracking-widest ml-2">মোট টাকা</p>
                   <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ ০" className="w-full p-4 bg-slate-50 border border-slate-200 rounded-[22px] font-black text-slate-900 outline-none focus:border-indigo-600 transition-all shadow-inner text-center border-none" />
                </div>
                <div className="space-y-1.5">
                   <p className="text-[8.4px] font-black text-slate-400 uppercase tracking-widest ml-2">মানুষের সংখ্যা</p>
                   <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} className="w-full p-4 bg-slate-50 border border-slate-200 rounded-[22px] font-black text-slate-900 outline-none focus:border-indigo-600 transition-all shadow-inner text-center border-none" />
                </div>
              </div>
              <div className="p-9 bg-gradient-to-br from-indigo-600 to-blue-700 rounded-[35px] text-center shadow-lg relative overflow-hidden group">
                <div className="relative z-10">
                  <p className="text-[10.5px] font-black text-blue-100 uppercase mb-2 tracking-widest">প্রতি জনের ভাগ</p>
                  <p className="text-[42px] font-black text-white">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
                </div>
                <div className="absolute inset-0 bg-white/5 opacity-0 group-hover:opacity-100 transition-opacity"></div>
              </div>
            </div>
          </div>
        )}
      </main>

      {/* Bottom Navigation Bar */}
      <nav className="fixed bottom-0 left-0 right-0 bg-white border-t border-slate-100 px-6 py-2.5 flex justify-between items-center z-[100] shadow-[0_-15px_40px_-15px_rgba(0,0,0,0.08)]">
        <button 
          onClick={() => setActiveTab('home')} 
          className={`flex-1 flex flex-col items-center justify-center gap-1 py-1 transition-all duration-400 border-none bg-transparent group`}
        >
          <div className={`w-14 h-9 flex items-center justify-center rounded-[18px] transition-all duration-400 ${activeTab === 'home' ? 'bg-indigo-600 text-white shadow-xl shadow-indigo-100' : 'bg-transparent text-slate-400'}`}>
            <i className={`fas fa-house-chimney ${activeTab === 'home' ? 'text-[16.8px] text-white' : 'text-[18.9px]'}`}></i>
          </div>
          <span className={`text-[8.4px] font-black uppercase tracking-[0.2em] transition-colors duration-400 ${activeTab === 'home' ? 'text-indigo-600' : 'text-slate-400 opacity-60'}`}>হোম</span>
        </button>

        <button 
          onClick={() => setActiveTab('future')} 
          className={`flex-1 flex flex-col items-center justify-center gap-1 py-1 transition-all duration-400 border-none bg-transparent group`}
        >
          <div className={`w-14 h-9 flex items-center justify-center rounded-[18px] transition-all duration-400 ${activeTab === 'future' ? 'bg-indigo-600 text-white shadow-xl shadow-indigo-100' : 'bg-transparent text-slate-400'}`}>
            <i className={`fas fa-rocket ${activeTab === 'future' ? 'text-[16.8px] text-white' : 'text-[18.9px]'}`}></i>
          </div>
          <span className={`text-[8.4px] font-black uppercase tracking-[0.2em] transition-colors duration-400 ${activeTab === 'future' ? 'text-indigo-600' : 'text-slate-400 opacity-60'}`}>এআই</span>
        </button>

        <button 
          onClick={() => setActiveTab('tools')} 
          className={`flex-1 flex flex-col items-center justify-center gap-1 py-1 transition-all duration-400 border-none bg-transparent group`}
        >
          <div className={`w-14 h-9 flex items-center justify-center rounded-[18px] transition-all duration-400 ${activeTab === 'tools' ? 'bg-indigo-600 text-white shadow-xl shadow-indigo-100' : 'bg-transparent text-slate-400'}`}>
            <i className={`fas fa-layer-group ${activeTab === 'tools' ? 'text-[16.8px] text-white' : 'text-[18.9px]'}`}></i>
          </div>
          <span className={`text-[8.4px] font-black uppercase tracking-[0.2em] transition-colors duration-400 ${activeTab === 'tools' ? 'text-indigo-600' : 'text-slate-400 opacity-60'}`}>টুলস</span>
        </button>
      </nav>

      {/* Transaction Modal with Fixed Button Text */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[200] flex items-center justify-center p-6">
          <div className="bg-white w-full max-w-[340px] rounded-[35px] p-7 pb-10 animate-fadeIn shadow-2xl border border-slate-100 flex flex-col border-none">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-[14.7px] font-black uppercase tracking-widest text-slate-800">{modalType === TransactionType.INCOME ? 'টাকা যোগ' : 'খরচ যোগ'}</h2>
              <button onClick={() => setModalType(null)} className="w-9 h-9 bg-slate-100 rounded-xl flex items-center justify-center text-slate-600 hover:bg-slate-200 transition-all border-none"><i className="fas fa-times text-[12.6px]"></i></button>
            </div>

            <form onSubmit={addTransaction} className="flex flex-col gap-5">
              <div className="bg-slate-50 rounded-[25px] p-6 border border-slate-100 text-center border-none">
                <p className="text-[8.4px] font-black text-slate-400 uppercase tracking-[0.3em] mb-2">টাকার পরিমাণ (৳)</p>
                <input type="number" name="amount" required placeholder="০" autoFocus className="w-full text-[31.5px] font-black text-center outline-none text-slate-900 bg-transparent tracking-tighter border-none" />
              </div>

              <div className="flex flex-col gap-3">
                <div className="relative">
                  <select name="category" className="w-full p-4 bg-white border border-slate-200 rounded-[20px] font-black text-[11.5px] text-slate-800 outline-none focus:border-indigo-600 appearance-none text-center">
                    {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                  </select>
                  <div className="absolute right-4 top-1/2 -translate-y-1/2 pointer-events-none text-slate-400">
                    <i className="fas fa-chevron-down text-[10.5px]"></i>
                  </div>
                </div>
                <input type="text" name="note" placeholder="নোট (যেমন: বেতন, বাজার...)" className="w-full p-4 bg-white border border-slate-200 rounded-[20px] font-black text-[11.5px] text-slate-800 outline-none focus:border-indigo-600 text-center" />
              </div>

              <button type="submit" className={`w-full py-4.5 mt-2 rounded-[22px] text-white font-black text-[13.5px] uppercase tracking-widest shadow-lg active:scale-95 transition-all border-none ${modalType === TransactionType.INCOME ? 'bg-gradient-to-r from-green-500 to-emerald-600' : 'bg-gradient-to-r from-rose-500 to-pink-600'}`}>
                নিশ্চিত করুন
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Settings Modal */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[45px] p-8 animate-slideUp shadow-2xl overflow-y-auto max-h-[85vh] custom-scrollbar border-none">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-[18.9px] font-black text-slate-900 tracking-widest uppercase">বাজেট লক্ষ্য</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-10 h-10 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900 border-none"><i className="fas fa-times"></i></button>
             </div>
             <div className="space-y-4 mb-8">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="space-y-1 text-slate-900">
                    <label className="text-[9.5px] font-black text-slate-500 uppercase ml-3 tracking-widest">{cat}</label>
                    <input 
                      type="number" 
                      value={budgets[cat] || ''}
                      onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })}
                      placeholder="৳ বাজেট দিন"
                      className="w-full p-4 bg-slate-50 border border-slate-200 rounded-[20px] font-black text-[12.6px] text-slate-900 outline-none focus:border-indigo-600 border-none"
                    />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-5 bg-slate-900 text-white rounded-[25px] font-black uppercase text-[12.6px] tracking-widest shadow-xl mb-4 border-none">সংরক্ষণ করুন</button>
          </div>
        </div>
      )}

      {/* History Modal */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[45px] p-8 overflow-y-auto animate-slideUp shadow-2xl">
             <div className="flex justify-between items-center mb-10">
                <h2 className="text-[21px] font-black text-slate-900 tracking-widest uppercase">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-10 h-10 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900 border-none"><i className="fas fa-times"></i></button>
             </div>
             {archives.length > 0 ? (
               <div className="space-y-6 pb-24">
                 {archives.map(arc => (
                   <div key={arc.id} className="p-6 bg-slate-50 rounded-[35px] border border-slate-100 shadow-sm border-none">
                      <p className="text-[10.5px] text-indigo-600 uppercase font-black tracking-widest mb-1">{arc.monthName} {arc.year}</p>
                      <p className="text-slate-900 text-[25px] font-black tracking-tighter">৳{arc.totalIncome - arc.totalExpense}</p>
                      <div className="flex gap-4 pt-4 mt-4 border-t border-slate-200">
                        <div className="flex-1">
                          <p className="text-[8.4px] font-black text-green-600 uppercase mb-0.5">আয়</p>
                          <p className="text-[12.6px] font-black text-slate-800">৳{arc.totalIncome}</p>
                        </div>
                        <div className="flex-1 text-right">
                          <p className="text-[8.4px] font-black text-rose-600 uppercase mb-0.5">ব্যয়</p>
                          <p className="text-[12.6px] font-black text-slate-800">৳{arc.totalExpense}</p>
                        </div>
                      </div>
                   </div>
                 ))}
               </div>
             ) : (
               <div className="text-center py-24 text-slate-400 font-black uppercase text-[10.5px] tracking-widest">ইতিহাস খালি</div>
             )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'future' | 'tools'>('home');
  const [isShrunk, setIsShrunk] = useState(false);
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  const handleScroll = (e: React.UIEvent<HTMLElement>) => {
    const scrollPos = e.currentTarget.scrollTop;
    if (scrollPos > 25 && !isShrunk) setIsShrunk(true);
    if (scrollPos <= 25 && isShrunk) setIsShrunk(false);
  };

  const fetchAIAdvice = async () => {
    if (transactions.length === 0) {
      alert("পরামর্শ পেতে প্রথমে কিছু লেনদেন যোগ করুন।");
      return;
    }
    setLoadingAI(true);
    const finSummary: FinancialSummary = {
      totalIncome: summary.income,
      totalExpenses: summary.expense,
      balance: summary.balance,
      savings: summary.balance,
      previousMonthSavings: (archives[0]?.totalIncome ?? 0) - (archives[0]?.totalExpense ?? 0),
      categoryBreakdown: summary.breakdown,
      monthlyHistory: archives.slice(0, 5).map(a => ({
        month: a.monthName,
        income: a.totalIncome,
        expense: a.totalExpense
      }))
    };
    const results = await getFinancialInsights(transactions, finSummary);
    setInsights(results);
    setLoadingAI(false);
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amountStr = formData.get('amount') as string;
    const amount = parseFloat(amountStr);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
  };

  const deleteTransaction = (id: string) => {
    if (window.confirm('আপনি কি এই লেনদেনটি মুছে ফেলতে চান?')) {
      setTransactions(prev => prev.filter(t => t.id !== id));
    }
  };

  return (
    <div className="max-w-md mx-auto h-screen bg-[#f8fafc] shadow-2xl flex flex-col font-sans relative overflow-hidden text-slate-900">
      {/* Dynamic Header with Transition optimization to prevent bumping */}
      <header 
        className={`bg-gradient-to-br from-blue-700 via-indigo-600 to-indigo-800 text-white shadow-2xl relative z-20 transition-all duration-300 ease-out will-change-[padding,border-radius] ${isShrunk ? 'p-4 rounded-b-[20px]' : 'p-7 rounded-b-[45px]'}`}
      >
        <div className={`flex justify-between items-center transition-all duration-300 ${isShrunk ? 'mb-1' : 'mb-6'}`}>
          <div className="flex items-center gap-3">
             <div className={`bg-white/20 rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/30 shadow-lg backdrop-blur-md transition-all duration-300 ${isShrunk ? 'w-8.5 h-8.5' : 'w-12 h-12'}`}>
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
                ) : (
                  <i className={`fas fa-user-circle text-white transition-all ${isShrunk ? 'text-base' : 'text-2xl'}`}></i>
                )}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div className={`${isShrunk ? 'flex items-center gap-2' : ''}`}>
                <h1 className={`font-black uppercase tracking-tight text-white transition-all duration-300 ${isShrunk ? 'text-[14.7px]' : 'text-[21px]'}`}>স্পার্ক</h1>
                <p className={`font-black text-blue-200 uppercase tracking-widest opacity-80 transition-all ${isShrunk ? 'hidden' : 'text-[9.5px] mt-0.5'}`}>স্মার্ট ওয়ালেট</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className={`bg-white/10 rounded-xl flex items-center justify-center hover:bg-white/20 transition-all border border-white/10 backdrop-blur-md text-white border-none ${isShrunk ? 'w-8 h-8' : 'w-10 h-10'}`}>
              <i className="fas fa-bullseye text-[12.6px]"></i>
            </button>
            <button onClick={() => setShowHistory(true)} className={`bg-white/10 rounded-xl flex items-center justify-center hover:bg-white/20 transition-all border border-white/10 backdrop-blur-md text-white border-none ${isShrunk ? 'w-8 h-8' : 'w-10 h-10'}`}>
              <i className="fas fa-history text-[12.6px]"></i>
            </button>
          </div>
        </div>

        <div className={`text-center transition-all duration-300 ${isShrunk ? 'flex flex-row items-center justify-between px-1 py-0' : 'py-1'}`}>
          <div className={`${isShrunk ? 'flex items-baseline gap-2' : ''}`}>
            <p className={`text-blue-100 font-black uppercase transition-all duration-300 ${isShrunk ? 'text-[8.4px] tracking-[0.1em]' : 'text-[10.5px] tracking-[0.3em] mb-2 opacity-90'}`}>বর্তমান ব্যালেন্স</p>
            <h2 className={`font-black tracking-tighter drop-shadow-lg text-white transition-all duration-300 ${isShrunk ? 'text-[21px]' : 'text-[37.8px] mb-8'}`}>{formatCurrency(summary.balance)}</h2>
          </div>

          <div className={`transition-all duration-300 overflow-hidden ${isShrunk ? 'max-h-0 opacity-0' : 'max-h-[200px] opacity-100 grid grid-cols-2 gap-3'}`}>
            <div className="bg-white/10 backdrop-blur-xl p-3.5 rounded-[24px] border border-white/20 shadow-sm">
              <p className="text-[9.5px] uppercase font-black text-blue-200 mb-1">মোট আয়</p>
              <p className="text-[19px] font-black text-green-300">৳{summary.income}</p>
            </div>
            <div className="bg-white/10 backdrop-blur-xl p-3.5 rounded-[24px] border border-white/20 shadow-sm">
              <p className="text-[9.5px] uppercase font-black text-blue-200 mb-1">মোট ব্যয়</p>
              <p className="text-[19px] font-black text-rose-300">৳{summary.expense}</p>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content with 5% larger text */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-8" onScroll={handleScroll}>
        {activeTab === 'home' ? (
          <div className="space-y-10 animate-fadeIn">
            {/* Quick Actions */}
            <div className="grid grid-cols-2 gap-4">
              <button 
                onClick={() => setModalType(TransactionType.INCOME)} 
                className="relative overflow-hidden p-6 bg-white border border-slate-100 rounded-[35px] flex flex-col items-center gap-3 transition-all hover:shadow-2xl hover:-translate-y-1 active:scale-95 group border-none"
              >
                <div className="absolute top-0 right-0 w-16 h-16 bg-green-50 rounded-full -mr-8 -mt-8 group-hover:scale-150 transition-transform duration-500"></div>
                <div className="w-14 h-14 bg-gradient-to-br from-green-400 to-green-600 text-white rounded-[22px] flex items-center justify-center text-[25.2px] shadow-lg shadow-green-100 relative z-10">
                  <i className="fas fa-plus"></i>
                </div>
                <span className="text-[11.5px] font-black text-slate-800 uppercase tracking-[0.15em] relative z-10">টাকা যোগ</span>
              </button>

              <button 
                onClick={() => setModalType(TransactionType.EXPENSE)} 
                className="relative overflow-hidden p-6 bg-white border border-slate-100 rounded-[35px] flex flex-col items-center gap-3 transition-all hover:shadow-2xl hover:-translate-y-1 active:scale-95 group border-none"
              >
                <div className="absolute top-0 right-0 w-16 h-16 bg-rose-50 rounded-full -mr-8 -mt-8 group-hover:scale-150 transition-transform duration-500"></div>
                <div className="w-14 h-14 bg-gradient-to-br from-rose-400 to-rose-600 text-white rounded-[22px] flex items-center justify-center text-[25.2px] shadow-lg shadow-rose-100 relative z-10">
                  <i className="fas fa-minus"></i>
                </div>
                <span className="text-[11.5px] font-black text-slate-800 uppercase tracking-[0.15em] relative z-10">খরচ যোগ</span>
              </button>
            </div>

            {/* Recent Transactions */}
            <div className="space-y-4">
              <div className="flex justify-between items-center px-2">
                <h3 className="font-black text-slate-900 text-[10.5px] uppercase tracking-[0.2em] opacity-60">সাম্প্রতিক লেনদেন</h3>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3 pb-24">
                  {transactions.map(t => (
                    <div key={t.id} className="bg-white border border-slate-100 p-4 rounded-[28px] shadow-sm flex items-center justify-between group hover:border-indigo-100 transition-colors border-none">
                      <div className="flex items-center gap-4">
                        <div className={`w-11 h-11 rounded-2xl flex items-center justify-center text-[18.9px] ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-800 text-[14.7px]">{t.category}</p>
                          <p className="text-[9.5px] text-slate-400 font-black uppercase">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <div className="flex items-center gap-3">
                        <p className={`font-black text-[14.7px] ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                          {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                        </p>
                        <button 
                          onClick={() => deleteTransaction(t.id)}
                          className="w-9 h-9 bg-slate-50 text-slate-300 rounded-xl flex items-center justify-center hover:bg-rose-50 hover:text-rose-500 transition-all active:scale-90 border-none"
                        >
                          <i className="fas fa-trash-alt text-[12.6px]"></i>
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-16 bg-white rounded-[40px] border border-dashed border-slate-200">
                  <i className="fas fa-receipt text-slate-200 text-[31.5px] mb-3"></i>
                  <p className="text-slate-400 font-black uppercase text-[9.5px] tracking-widest">খালি তালিকা</p>
                </div>
              )}
            </div>
          </div>
        ) : activeTab === 'future' ? (
          <div className="space-y-8 animate-fadeIn pb-24">
            <div className="p-9 bg-slate-900 rounded-[45px] text-white relative overflow-hidden shadow-2xl">
              <div className="relative z-10">
                <h3 className="text-[25.2px] font-black mb-2 text-white">ভবিষ্যৎ সঞ্চয়</h3>
                <p className="text-[10.5px] text-slate-400 font-black uppercase tracking-widest mb-10 opacity-70">এআই এর মাধ্যমে আর্থিক পূর্বাভাস</p>
                <button 
                  onClick={fetchAIAdvice}
                  disabled={loadingAI}
                  className="w-full py-4.5 bg-gradient-to-r from-indigo-600 to-blue-600 text-white rounded-[22px] font-black uppercase text-[10.5px] shadow-xl active:scale-95 transition-all flex items-center justify-center gap-3 border-none"
                >
                  {loadingAI ? <><i className="fas fa-spinner animate-spin"></i> প্রসেসিং...</> : <><i className="fas fa-wand-magic-sparkles"></i> পরামর্শ নিন</>}
                </button>
              </div>
              <div className="absolute -right-16 -bottom-16 w-56 h-56 bg-indigo-500/10 rounded-full blur-3xl"></div>
            </div>
            {insights.map((insight, idx) => (
              <div key={idx} className={`p-6 rounded-[35px] border ${insight.type === 'success' ? 'bg-green-50 border-green-100' : insight.type === 'warning' ? 'bg-rose-50 border-rose-100' : 'bg-blue-50 border-blue-100'} animate-fadeIn`}>
                <div className="flex gap-4">
                  <div className={`w-11 h-11 rounded-2xl flex items-center justify-center text-[21px] shadow-sm ${insight.type === 'success' ? 'bg-green-500 text-white' : insight.type === 'warning' ? 'bg-rose-500 text-white' : 'bg-blue-500 text-white'}`}>
                    <i className={`fas ${insight.type === 'success' ? 'fa-check' : insight.type === 'warning' ? 'fa-exclamation' : 'fa-lightbulb'}`}></i>
                  </div>
                  <div>
                    <h4 className="font-black text-slate-900 text-[14.7px] mb-1">{insight.title}</h4>
                    <p className="text-slate-600 text-[12.6px] font-bold leading-relaxed">{insight.description}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="space-y-8 animate-fadeIn pb-24">
            {/* Bill Splitter */}
            <div className="bg-white p-9 rounded-[45px] border border-slate-100 shadow-sm space-y-7 border-none">
              <div className="flex items-center gap-3 mb-2">
                <div className="w-12 h-12 bg-indigo-600 text-white rounded-2xl flex items-center justify-center text-[21px] shadow-lg shadow-indigo-100"><i className="fas fa-calculator text-white"></i></div>
                <h3 className="font-black text-slate-900 uppercase text-[12.6px] tracking-widest">বিল স্প্লিটার</h3>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-1.5">
                   <p className="text-[8.4px] font-black text-slate-400 uppercase tracking-widest ml-2">মোট টাকা</p>
                   <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ ০" className="w-full p-4.5 bg-slate-50 border border-slate-200 rounded-[22px] font-black text-slate-900 outline-none focus:border-indigo-600 transition-all shadow-inner text-center border-none" />
                </div>
                <div className="space-y-1.5">
                   <p className="text-[8.4px] font-black text-slate-400 uppercase tracking-widest ml-2">মানুষের সংখ্যা</p>
                   <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} className="w-full p-4.5 bg-slate-50 border border-slate-200 rounded-[22px] font-black text-slate-900 outline-none focus:border-indigo-600 transition-all shadow-inner text-center border-none" />
                </div>
              </div>
              <div className="p-9 bg-gradient-to-br from-indigo-600 to-blue-700 rounded-[35px] text-center shadow-lg relative overflow-hidden group">
                <div className="relative z-10">
                  <p className="text-[10.5px] font-black text-blue-100 uppercase mb-2 tracking-widest">প্রতি জনের ভাগ</p>
                  <p className="text-[44.1px] font-black text-white">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
                </div>
                <div className="absolute inset-0 bg-white/5 opacity-0 group-hover:opacity-100 transition-opacity"></div>
              </div>
            </div>
          </div>
        )}
      </main>

      {/* Bottom Navbar */}
      <nav className="fixed bottom-0 left-0 right-0 bg-white border-t border-slate-100 px-6 py-2.5 flex justify-between items-center z-[100] shadow-[0_-15px_40px_-15px_rgba(0,0,0,0.08)]">
        <button onClick={() => setActiveTab('home')} className="flex-1 flex flex-col items-center justify-center gap-1 py-1 border-none bg-transparent group">
          <div className={`w-14 h-9 flex items-center justify-center rounded-[18px] transition-all duration-400 ${activeTab === 'home' ? 'bg-indigo-600 text-white shadow-xl' : 'bg-transparent text-slate-400'}`}>
            <i className={`fas fa-house-chimney ${activeTab === 'home' ? 'text-[16.8px]' : 'text-[18.9px]'}`}></i>
          </div>
          <span className={`text-[8.4px] font-black uppercase tracking-[0.2em] transition-colors ${activeTab === 'home' ? 'text-indigo-600' : 'text-slate-400 opacity-60'}`}>হোম</span>
        </button>
        <button onClick={() => setActiveTab('future')} className="flex-1 flex flex-col items-center justify-center gap-1 py-1 border-none bg-transparent group">
          <div className={`w-14 h-9 flex items-center justify-center rounded-[18px] transition-all duration-400 ${activeTab === 'future' ? 'bg-indigo-600 text-white shadow-xl' : 'bg-transparent text-slate-400'}`}>
            <i className={`fas fa-rocket ${activeTab === 'future' ? 'text-[16.8px]' : 'text-[18.9px]'}`}></i>
          </div>
          <span className={`text-[8.4px] font-black uppercase tracking-[0.2em] transition-colors ${activeTab === 'future' ? 'text-indigo-600' : 'text-slate-400 opacity-60'}`}>এআই</span>
        </button>
        <button onClick={() => setActiveTab('tools')} className="flex-1 flex flex-col items-center justify-center gap-1 py-1 border-none bg-transparent group">
          <div className={`w-14 h-9 flex items-center justify-center rounded-[18px] transition-all duration-400 ${activeTab === 'tools' ? 'bg-indigo-600 text-white shadow-xl' : 'bg-transparent text-slate-400'}`}>
            <i className={`fas fa-layer-group ${activeTab === 'tools' ? 'text-[16.8px]' : 'text-[18.9px]'}`}></i>
          </div>
          <span className={`text-[8.4px] font-black uppercase tracking-[0.2em] transition-colors ${activeTab === 'tools' ? 'text-indigo-600' : 'text-slate-400 opacity-60'}`}>টুলস</span>
        </button>
      </nav>

      {/* Improved Transaction Modal (Applying edits from screenshot) */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-md z-[200] flex items-center justify-center p-6">
          <div className="bg-white w-full max-w-[360px] rounded-[40px] p-8 pb-10 animate-fadeIn shadow-[0_25px_60px_-15px_rgba(0,0,0,0.3)] border-none flex flex-col">
            <div className="flex justify-between items-center mb-8">
              <h2 className="text-[16.8px] font-black uppercase tracking-widest text-slate-800">{modalType === TransactionType.INCOME ? 'টাকা যোগ' : 'খরচ যোগ'}</h2>
              <button onClick={() => setModalType(null)} className="w-10 h-10 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-500 hover:bg-slate-200 transition-all border-none"><i className="fas fa-times text-[14.7px]"></i></button>
            </div>

            <form onSubmit={addTransaction} className="flex flex-col gap-6">
              {/* Refined Amount Input Box */}
              <div className="bg-slate-50 rounded-[30px] p-7 border border-slate-100 text-center border-none shadow-inner">
                <p className="text-[10px] font-black text-slate-400 uppercase tracking-[0.3em] mb-3">টাকার পরিমাণ (৳)</p>
                <input 
                  type="number" 
                  name="amount" 
                  required 
                  placeholder="০" 
                  autoFocus 
                  className="w-full text-[42px] font-black text-center outline-none text-slate-900 bg-transparent tracking-tighter border-none placeholder:text-slate-200" 
                />
              </div>

              <div className="flex flex-col gap-4">
                <div className="relative">
                  <select name="category" className="w-full p-4.5 bg-white border border-slate-200 rounded-[22px] font-black text-[12.6px] text-slate-800 outline-none focus:border-indigo-600 appearance-none text-center shadow-sm">
                    {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                  </select>
                  <div className="absolute right-5 top-1/2 -translate-y-1/2 pointer-events-none text-slate-400">
                    <i className="fas fa-chevron-down text-[11px]"></i>
                  </div>
                </div>
                <input type="text" name="note" placeholder="নোট (যেমন: বেতন, বাজার...)" className="w-full p-4.5 bg-white border border-slate-200 rounded-[22px] font-black text-[12.6px] text-slate-800 outline-none focus:border-indigo-600 text-center shadow-sm" />
              </div>

              {/* Fixed Confirm Button Style */}
              <button 
                type="submit" 
                className={`w-full py-5.5 mt-4 rounded-[25px] text-white font-black text-[15.7px] uppercase tracking-[0.15em] shadow-[0_10px_25px_-5px_rgba(0,0,0,0.1)] active:scale-[0.97] transition-all border-none ${modalType === TransactionType.INCOME ? 'bg-gradient-to-r from-green-500 to-emerald-600 shadow-green-200/50' : 'bg-gradient-to-r from-rose-500 to-pink-600 shadow-rose-200/50'}`}
              >
                নিশ্চিত করুন
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Budget Settings Modal */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[50px] p-9 animate-slideUp shadow-2xl overflow-y-auto max-h-[85vh] custom-scrollbar border-none">
             <div className="flex justify-between items-center mb-10">
                <h2 className="text-[20px] font-black text-slate-900 tracking-widest uppercase">বাজেট লক্ষ্য</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-11 h-11 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900 border-none"><i className="fas fa-times"></i></button>
             </div>
             <div className="space-y-5 mb-10">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="space-y-2 text-slate-900">
                    <label className="text-[10px] font-black text-slate-500 uppercase ml-3 tracking-widest">{cat}</label>
                    <input 
                      type="number" 
                      value={budgets[cat] || ''}
                      onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })}
                      placeholder="৳ বাজেট দিন"
                      className="w-full p-4.5 bg-slate-50 border border-slate-200 rounded-[22px] font-black text-[13.5px] text-slate-900 outline-none focus:border-indigo-600 border-none shadow-inner"
                    />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-6 bg-slate-900 text-white rounded-[28px] font-black uppercase text-[13.5px] tracking-widest shadow-2xl mb-6 border-none">সংরক্ষণ করুন</button>
          </div>
        </div>
      )}

      {/* History Modal */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[50px] p-9 overflow-y-auto animate-slideUp shadow-2xl">
             <div className="flex justify-between items-center mb-12">
                <h2 className="text-[22px] font-black text-slate-900 tracking-widest uppercase">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-11 h-11 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900 border-none"><i className="fas fa-times"></i></button>
             </div>
             {archives.length > 0 ? (
               <div className="space-y-7 pb-24">
                 {archives.map(arc => (
                   <div key={arc.id} className="p-7 bg-slate-50 rounded-[38px] border-none shadow-sm">
                      <p className="text-[11px] text-indigo-600 uppercase font-black tracking-widest mb-2">{arc.monthName} {arc.year}</p>
                      <p className="text-slate-900 text-[26.2px] font-black tracking-tighter">৳{arc.totalIncome - arc.totalExpense}</p>
                      <div className="flex gap-4 pt-5 mt-5 border-t border-slate-200">
                        <div className="flex-1">
                          <p className="text-[8.9px] font-black text-green-600 uppercase mb-1">আয়</p>
                          <p className="text-[13.2px] font-black text-slate-800">৳{arc.totalIncome}</p>
                        </div>
                        <div className="flex-1 text-right">
                          <p className="text-[8.9px] font-black text-rose-600 uppercase mb-1">ব্যয়</p>
                          <p className="text-[13.2px] font-black text-slate-800">৳{arc.totalExpense}</p>
                        </div>
                      </div>
                   </div>
                 ))}
               </div>
             ) : (
               <div className="text-center py-24 text-slate-400 font-black uppercase text-[11px] tracking-widest">ইতিহাস খালি</div>
             )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'future' | 'tools'>('home');
  const [isShrunk, setIsShrunk] = useState(false);
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  // Stable scroll handling to prevent layout bumping
  const handleScroll = (e: React.UIEvent<HTMLElement>) => {
    const scrollPos = e.currentTarget.scrollTop;
    if (scrollPos > 40 && !isShrunk) setIsShrunk(true);
    if (scrollPos <= 40 && isShrunk) setIsShrunk(false);
  };

  const fetchAIAdvice = async () => {
    if (transactions.length === 0) {
      alert("পরামর্শ পেতে প্রথমে কিছু লেনদেন যোগ করুন।");
      return;
    }
    setLoadingAI(true);
    const finSummary: FinancialSummary = {
      totalIncome: summary.income,
      totalExpenses: summary.expense,
      balance: summary.balance,
      savings: summary.balance,
      previousMonthSavings: (archives[0]?.totalIncome ?? 0) - (archives[0]?.totalExpense ?? 0),
      categoryBreakdown: summary.breakdown,
      monthlyHistory: archives.slice(0, 5).map(a => ({
        month: a.monthName,
        income: a.totalIncome,
        expense: a.totalExpense
      }))
    };
    const results = await getFinancialInsights(transactions, finSummary);
    setInsights(results);
    setLoadingAI(false);
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
  };

  const deleteTransaction = (id: string) => {
    if (window.confirm('আপনি কি এই লেনদেনটি মুছে ফেলতে চান?')) {
      setTransactions(prev => prev.filter(t => t.id !== id));
    }
  };

  return (
    <div className="max-w-md mx-auto h-screen bg-[#f8fafc] shadow-2xl flex flex-col font-sans relative overflow-hidden text-slate-900">
      {/* Header Optimized for Stability */}
      <header 
        className={`bg-gradient-to-br from-blue-700 via-indigo-600 to-indigo-800 text-white shadow-2xl relative z-20 transition-all duration-300 ease-[cubic-bezier(0.4,0,0.2,1)] will-change-[padding,border-radius] ${isShrunk ? 'p-4 rounded-b-[24px]' : 'p-7 rounded-b-[45px]'}`}
      >
        <div className={`flex justify-between items-center transition-all duration-300 ${isShrunk ? 'mb-1' : 'mb-6'}`}>
          <div className="flex items-center gap-3">
             <div className={`bg-white/20 rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/30 shadow-lg backdrop-blur-md transition-all duration-300 ${isShrunk ? 'w-8 h-8' : 'w-12 h-12'}`}>
                {profileImage ? (
                  <img src={profileImage} alt="Profile" className="w-full h-full object-cover" />
                ) : (
                  <i className={`fas fa-user-circle text-white transition-all ${isShrunk ? 'text-base' : 'text-2xl'}`}></i>
                )}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div className={`${isShrunk ? 'flex items-center gap-2' : ''}`}>
                <h1 className={`font-black uppercase tracking-tight text-white transition-all duration-300 ${isShrunk ? 'text-[14.7px]' : 'text-[21px]'}`}>স্পার্ক</h1>
                <p className={`font-black text-blue-200 uppercase tracking-widest opacity-80 transition-all ${isShrunk ? 'hidden' : 'text-[9.5px] mt-0.5'}`}>স্মার্ট ওয়ালেট</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className={`bg-white/10 rounded-xl flex items-center justify-center hover:bg-white/20 transition-all border border-white/10 backdrop-blur-md text-white border-none ${isShrunk ? 'w-8 h-8' : 'w-10 h-10'}`}>
              <i className="fas fa-bullseye text-[12.6px]"></i>
            </button>
            <button onClick={() => setShowHistory(true)} className={`bg-white/10 rounded-xl flex items-center justify-center hover:bg-white/20 transition-all border border-white/10 backdrop-blur-md text-white border-none ${isShrunk ? 'w-8 h-8' : 'w-10 h-10'}`}>
              <i className="fas fa-history text-[12.6px]"></i>
            </button>
          </div>
        </div>

        <div className={`text-center transition-all duration-300 ${isShrunk ? 'flex flex-row items-center justify-between px-1' : 'py-1'}`}>
          <div className={`${isShrunk ? 'flex items-baseline gap-2' : ''}`}>
            <p className={`text-blue-100 font-black uppercase transition-all duration-300 ${isShrunk ? 'text-[8.4px] tracking-[0.1em]' : 'text-[10.5px] tracking-[0.3em] mb-2 opacity-90'}`}>বর্তমান ব্যালেন্স</p>
            <h2 className={`font-black tracking-tighter drop-shadow-lg text-white transition-all duration-300 ${isShrunk ? 'text-[21px]' : 'text-[37.8px] mb-8'}`}>{formatCurrency(summary.balance)}</h2>
          </div>

          <div className={`transition-all duration-300 overflow-hidden ${isShrunk ? 'max-h-0 opacity-0' : 'max-h-[200px] opacity-100 grid grid-cols-2 gap-3'}`}>
            <div className="bg-white/10 backdrop-blur-xl p-3.5 rounded-[24px] border border-white/20 shadow-sm">
              <p className="text-[9.5px] uppercase font-black text-blue-200 mb-1">মোট আয়</p>
              <p className="text-[19px] font-black text-green-300">৳{summary.income}</p>
            </div>
            <div className="bg-white/10 backdrop-blur-xl p-3.5 rounded-[24px] border border-white/20 shadow-sm">
              <p className="text-[9.5px] uppercase font-black text-blue-200 mb-1">মোট ব্যয়</p>
              <p className="text-[19px] font-black text-rose-300">৳{summary.expense}</p>
            </div>
          </div>
        </div>
      </header>

      {/* Main Area with 5% Text Increase */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-8" onScroll={handleScroll}>
        {activeTab === 'home' ? (
          <div className="space-y-10 animate-fadeIn">
            <div className="grid grid-cols-2 gap-4">
              <button 
                onClick={() => setModalType(TransactionType.INCOME)} 
                className="relative overflow-hidden p-6 bg-white border border-slate-100 rounded-[35px] flex flex-col items-center gap-3 transition-all hover:shadow-2xl hover:-translate-y-1 active:scale-95 group border-none"
              >
                <div className="absolute top-0 right-0 w-16 h-16 bg-green-50 rounded-full -mr-8 -mt-8 group-hover:scale-150 transition-transform duration-500"></div>
                <div className="w-14 h-14 bg-gradient-to-br from-green-400 to-green-600 text-white rounded-[22px] flex items-center justify-center text-[25.2px] shadow-lg shadow-green-100 relative z-10">
                  <i className="fas fa-plus"></i>
                </div>
                <span className="text-[11.5px] font-black text-slate-800 uppercase tracking-[0.15em] relative z-10">টাকা যোগ</span>
              </button>

              <button 
                onClick={() => setModalType(TransactionType.EXPENSE)} 
                className="relative overflow-hidden p-6 bg-white border border-slate-100 rounded-[35px] flex flex-col items-center gap-3 transition-all hover:shadow-2xl hover:-translate-y-1 active:scale-95 group border-none"
              >
                <div className="absolute top-0 right-0 w-16 h-16 bg-rose-50 rounded-full -mr-8 -mt-8 group-hover:scale-150 transition-transform duration-500"></div>
                <div className="w-14 h-14 bg-gradient-to-br from-rose-400 to-rose-600 text-white rounded-[22px] flex items-center justify-center text-[25.2px] shadow-lg shadow-rose-100 relative z-10">
                  <i className="fas fa-minus"></i>
                </div>
                <span className="text-[11.5px] font-black text-slate-800 uppercase tracking-[0.15em] relative z-10">খরচ যোগ</span>
              </button>
            </div>

            <div className="space-y-4">
              <div className="flex justify-between items-center px-2">
                <h3 className="font-black text-slate-900 text-[10.5px] uppercase tracking-[0.2em] opacity-60">সাম্প্রতিক লেনদেন</h3>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3 pb-24">
                  {transactions.map(t => (
                    <div key={t.id} className="bg-white border border-slate-100 p-4 rounded-[28px] shadow-sm flex items-center justify-between group hover:border-indigo-100 transition-colors border-none">
                      <div className="flex items-center gap-4">
                        <div className={`w-11 h-11 rounded-2xl flex items-center justify-center text-[18.9px] ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-800 text-[14.7px]">{t.category}</p>
                          <p className="text-[9.5px] text-slate-400 font-black uppercase">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <div className="flex items-center gap-3">
                        <p className={`font-black text-[14.7px] ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                          {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                        </p>
                        <button 
                          onClick={() => deleteTransaction(t.id)}
                          className="w-9 h-9 bg-slate-50 text-slate-300 rounded-xl flex items-center justify-center hover:bg-rose-50 hover:text-rose-500 transition-all active:scale-90 border-none"
                        >
                          <i className="fas fa-trash-alt text-[12.6px]"></i>
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-16 bg-white rounded-[40px] border border-dashed border-slate-200">
                  <i className="fas fa-receipt text-slate-200 text-[31.5px] mb-3"></i>
                  <p className="text-slate-400 font-black uppercase text-[9.5px] tracking-widest">খালি তালিকা</p>
                </div>
              )}
            </div>
          </div>
        ) : activeTab === 'future' ? (
          <div className="space-y-8 animate-fadeIn pb-24">
            <div className="p-9 bg-slate-900 rounded-[45px] text-white relative overflow-hidden shadow-2xl">
              <div className="relative z-10">
                <h3 className="text-[25.2px] font-black mb-2 text-white">ভবিষ্যৎ সঞ্চয়</h3>
                <p className="text-[10.5px] text-slate-400 font-black uppercase tracking-widest mb-10 opacity-70">এআই এর মাধ্যমে আর্থিক পূর্বাভাস</p>
                <button 
                  onClick={fetchAIAdvice}
                  disabled={loadingAI}
                  className="w-full py-4.5 bg-gradient-to-r from-indigo-600 to-blue-600 text-white rounded-[22px] font-black uppercase text-[10.5px] shadow-xl active:scale-95 transition-all flex items-center justify-center gap-3 border-none"
                >
                  {loadingAI ? <><i className="fas fa-spinner animate-spin"></i> প্রসেসিং...</> : <><i className="fas fa-wand-magic-sparkles"></i> পরামর্শ নিন</>}
                </button>
              </div>
              <div className="absolute -right-16 -bottom-16 w-56 h-56 bg-indigo-500/10 rounded-full blur-3xl"></div>
            </div>
            {insights.map((insight, idx) => (
              <div key={idx} className={`p-6 rounded-[35px] border ${insight.type === 'success' ? 'bg-green-50 border-green-100' : insight.type === 'warning' ? 'bg-rose-50 border-rose-100' : 'bg-blue-50 border-blue-100'} animate-fadeIn`}>
                <div className="flex gap-4">
                  <div className={`w-11 h-11 rounded-2xl flex items-center justify-center text-[21px] shadow-sm ${insight.type === 'success' ? 'bg-green-500 text-white' : insight.type === 'warning' ? 'bg-rose-500 text-white' : 'bg-blue-500 text-white'}`}>
                    <i className={`fas ${insight.type === 'success' ? 'fa-check' : insight.type === 'warning' ? 'fa-exclamation' : 'fa-lightbulb'}`}></i>
                  </div>
                  <div>
                    <h4 className="font-black text-slate-900 text-[14.7px] mb-1">{insight.title}</h4>
                    <p className="text-slate-600 text-[12.6px] font-bold leading-relaxed">{insight.description}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="space-y-8 animate-fadeIn pb-24">
            <div className="bg-white p-9 rounded-[45px] border border-slate-100 shadow-sm space-y-7 border-none">
              <div className="flex items-center gap-3 mb-2">
                <div className="w-12 h-12 bg-indigo-600 text-white rounded-2xl flex items-center justify-center text-[21px] shadow-lg shadow-indigo-100"><i className="fas fa-calculator text-white"></i></div>
                <h3 className="font-black text-slate-900 uppercase text-[12.6px] tracking-widest">বিল স্প্লিটার</h3>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-1.5">
                   <p className="text-[8.4px] font-black text-slate-400 uppercase tracking-widest ml-2">মোট টাকা</p>
                   <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ ০" className="w-full p-4.5 bg-slate-50 border border-slate-200 rounded-[22px] font-black text-slate-900 outline-none focus:border-indigo-600 transition-all shadow-inner text-center border-none" />
                </div>
                <div className="space-y-1.5">
                   <p className="text-[8.4px] font-black text-slate-400 uppercase tracking-widest ml-2">মানুষের সংখ্যা</p>
                   <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} className="w-full p-4.5 bg-slate-50 border border-slate-200 rounded-[22px] font-black text-slate-900 outline-none focus:border-indigo-600 transition-all shadow-inner text-center border-none" />
                </div>
              </div>
              <div className="p-9 bg-gradient-to-br from-indigo-600 to-blue-700 rounded-[35px] text-center shadow-lg relative overflow-hidden group">
                <div className="relative z-10">
                  <p className="text-[10.5px] font-black text-blue-100 uppercase mb-2 tracking-widest">প্রতি জনের ভাগ</p>
                  <p className="text-[44.1px] font-black text-white">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
                </div>
                <div className="absolute inset-0 bg-white/5 opacity-0 group-hover:opacity-100 transition-opacity"></div>
              </div>
            </div>
          </div>
        )}
      </main>

      {/* Navigation */}
      <nav className="fixed bottom-0 left-0 right-0 bg-white border-t border-slate-100 px-6 py-2.5 flex justify-between items-center z-[100] shadow-[0_-15px_40px_-15px_rgba(0,0,0,0.08)]">
        <button onClick={() => setActiveTab('home')} className="flex-1 flex flex-col items-center justify-center gap-1 py-1 border-none bg-transparent group">
          <div className={`w-14 h-9 flex items-center justify-center rounded-[18px] transition-all duration-400 ${activeTab === 'home' ? 'bg-indigo-600 text-white shadow-xl' : 'bg-transparent text-slate-400'}`}>
            <i className={`fas fa-house-chimney ${activeTab === 'home' ? 'text-[16.8px]' : 'text-[18.9px]'}`}></i>
          </div>
          <span className={`text-[8.4px] font-black uppercase tracking-[0.2em] transition-colors ${activeTab === 'home' ? 'text-indigo-600' : 'text-slate-400 opacity-60'}`}>হোম</span>
        </button>
        <button onClick={() => setActiveTab('future')} className="flex-1 flex flex-col items-center justify-center gap-1 py-1 border-none bg-transparent group">
          <div className={`w-14 h-9 flex items-center justify-center rounded-[18px] transition-all duration-400 ${activeTab === 'future' ? 'bg-indigo-600 text-white shadow-xl' : 'bg-transparent text-slate-400'}`}>
            <i className={`fas fa-rocket ${activeTab === 'future' ? 'text-[16.8px]' : 'text-[18.9px]'}`}></i>
          </div>
          <span className={`text-[8.4px] font-black uppercase tracking-[0.2em] transition-colors ${activeTab === 'future' ? 'text-indigo-600' : 'text-slate-400 opacity-60'}`}>এআই</span>
        </button>
        <button onClick={() => setActiveTab('tools')} className="flex-1 flex flex-col items-center justify-center gap-1 py-1 border-none bg-transparent group">
          <div className={`w-14 h-9 flex items-center justify-center rounded-[18px] transition-all duration-400 ${activeTab === 'tools' ? 'bg-indigo-600 text-white shadow-xl' : 'bg-transparent text-slate-400'}`}>
            <i className={`fas fa-layer-group ${activeTab === 'tools' ? 'text-[16.8px]' : 'text-[18.9px]'}`}></i>
          </div>
          <span className={`text-[8.4px] font-black uppercase tracking-[0.2em] transition-colors ${activeTab === 'tools' ? 'text-indigo-600' : 'text-slate-400 opacity-60'}`}>টুলস</span>
        </button>
      </nav>

      {/* REFINED ADD TRANSACTION MODAL (PREMIUM UI) */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/70 backdrop-blur-lg z-[200] flex items-center justify-center p-6 animate-fadeIn">
          <div className="bg-white w-full max-w-[380px] rounded-[32px] overflow-hidden shadow-[0_25px_60px_-15px_rgba(0,0,0,0.4)] border-none flex flex-col animate-slideUp">

            {/* Modal Header */}
            <div className="flex justify-between items-center px-8 py-6 border-b border-slate-50">
              <h2 className="text-[21px] font-black text-slate-800 tracking-tight">{modalType === TransactionType.INCOME ? 'টাকা যোগ' : 'খরচ যোগ'}</h2>
              <button 
                onClick={() => setModalType(null)} 
                className="w-10 h-10 bg-slate-50 rounded-full flex items-center justify-center text-slate-400 hover:bg-slate-100 hover:text-slate-600 transition-all border-none shadow-sm"
              >
                <i className="fas fa-times text-[14px]"></i>
              </button>
            </div>

            <form onSubmit={addTransaction} className="p-8 space-y-6">
              {/* Amount Display Card */}
              <div className="bg-slate-50 rounded-[24px] p-8 border border-slate-100 text-center shadow-inner group transition-all focus-within:ring-2 focus-within:ring-[#00cec9]/20">
                <p className="text-[10.5px] font-black text-slate-400 uppercase tracking-[0.2em] mb-2">টাকার পরিমাণ</p>
                <div className="flex items-center justify-center gap-1">
                  <span className="text-[25.2px] font-black text-[#00cec9]">৳</span>
                  <input 
                    type="number" 
                    name="amount" 
                    required 
                    placeholder="0" 
                    autoFocus 
                    className="w-full text-[46.2px] font-black text-center outline-none text-slate-900 bg-transparent tracking-tighter border-none placeholder:text-slate-200" 
                  />
                </div>
              </div>

              <div className="space-y-4">
                {/* Category Dropdown */}
                <div className="relative group">
                  <select 
                    name="category" 
                    className="w-full p-4.5 bg-white border border-slate-200 rounded-[20px] font-bold text-[14.7px] text-slate-800 outline-none focus:border-[#00cec9] focus:ring-4 focus:ring-[#00cec9]/5 appearance-none text-center shadow-sm transition-all"
                  >
                    {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                  </select>
                  <div className="absolute right-5 top-1/2 -translate-y-1/2 pointer-events-none text-slate-400 group-focus-within:text-[#00cec9] transition-colors">
                    <i className="fas fa-chevron-down text-[12px]"></i>
                  </div>
                </div>

                {/* Note Field */}
                <input 
                  type="text" 
                  name="note" 
                  placeholder="নোট (যেমন: বেতন, বাজার...)" 
                  className="w-full p-4.5 bg-white border border-slate-200 rounded-[20px] font-bold text-[14.7px] text-slate-800 outline-none focus:border-[#00cec9] focus:ring-4 focus:ring-[#00cec9]/5 text-center shadow-sm placeholder:text-slate-300 transition-all" 
                />
              </div>

              {/* Premium Confirm Button */}
              <button 
                type="submit" 
                className="w-full py-5.5 mt-4 rounded-full text-white font-black text-[16.8px] uppercase tracking-[0.1em] shadow-[0_15px_35px_-5px_rgba(0,184,148,0.3)] hover:shadow-[0_20px_40px_-5px_rgba(0,184,148,0.4)] hover:-translate-y-1 active:scale-[0.97] transition-all border-none bg-gradient-to-r from-[#00b894] to-[#00cec9]"
              >
                নিশ্চিত করুন
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Settings Modal */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[50px] p-9 animate-slideUp shadow-2xl overflow-y-auto max-h-[85vh] custom-scrollbar border-none">
             <div className="flex justify-between items-center mb-10">
                <h2 className="text-[20px] font-black text-slate-900 tracking-widest uppercase">বাজেট লক্ষ্য</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-11 h-11 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900 border-none"><i className="fas fa-times"></i></button>
             </div>
             <div className="space-y-5 mb-10">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="space-y-2 text-slate-900">
                    <label className="text-[10px] font-black text-slate-500 uppercase ml-3 tracking-widest">{cat}</label>
                    <input 
                      type="number" 
                      value={budgets[cat] || ''}
                      onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })}
                      placeholder="৳ বাজেট দিন"
                      className="w-full p-4.5 bg-slate-50 border border-slate-200 rounded-[22px] font-black text-[13.5px] text-slate-900 outline-none focus:border-indigo-600 border-none shadow-inner"
                    />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-6 bg-slate-900 text-white rounded-[28px] font-black uppercase text-[13.5px] tracking-widest shadow-2xl mb-6 border-none">সংরক্ষণ করুন</button>
          </div>
        </div>
      )}

      {/* History Modal */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[200] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[50px] p-9 overflow-y-auto animate-slideUp shadow-2xl">
             <div className="flex justify-between items-center mb-12">
                <h2 className="text-[22px] font-black text-slate-900 tracking-widest uppercase">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-11 h-11 bg-slate-100 rounded-2xl flex items-center justify-center text-slate-900 border-none"><i className="fas fa-times"></i></button>
             </div>
             {archives.length > 0 ? (
               <div className="space-y-7 pb-24">
                 {archives.map(arc => (
                   <div key={arc.id} className="p-7 bg-slate-50 rounded-[38px] border-none shadow-sm">
                      <p className="text-[11px] text-indigo-600 uppercase font-black tracking-widest mb-2">{arc.monthName} {arc.year}</p>
                      <p className="text-slate-900 text-[26.2px] font-black tracking-tighter">৳{arc.totalIncome - arc.totalExpense}</p>
                      <div className="flex gap-4 pt-5 mt-5 border-t border-slate-200">
                        <div className="flex-1">
                          <p className="text-[8.9px] font-black text-green-600 uppercase mb-1">আয়</p>
                          <p className="text-[13.2px] font-black text-slate-800">৳{arc.totalIncome}</p>
                        </div>
                        <div className="flex-1 text-right">
                          <p className="text-[8.9px] font-black text-rose-600 uppercase mb-1">ব্যয়</p>
                          <p className="text-[13.2px] font-black text-slate-800">৳{arc.totalExpense}</p>
                        </div>
                      </div>
                   </div>
                 ))}
               </div>
             ) : (
               <div className="text-center py-24 text-slate-400 font-black uppercase text-[11px] tracking-widest">ইতিহাস খালি</div>
             )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'future' | 'tools'>('home');
  const [isShrunk, setIsShrunk] = useState(false);
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {};
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('2');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown };
  }, [transactions]);

  const handleScroll = (e: React.UIEvent<HTMLElement>) => {
    const scrollPos = e.currentTarget.scrollTop;
    if (scrollPos > 30 && !isShrunk) setIsShrunk(true);
    if (scrollPos <= 30 && isShrunk) setIsShrunk(false);
  };

  const fetchAIAdvice = async () => {
    if (transactions.length === 0) {
      alert("পরামর্শ পেতে প্রথমে কিছু লেনদেন যোগ করুন।");
      return;
    }
    setLoadingAI(true);
    const finSummary: FinancialSummary = {
      totalIncome: summary.income,
      totalExpenses: summary.expense,
      balance: summary.balance,
      savings: summary.balance,
      previousMonthSavings: (archives[0]?.totalIncome ?? 0) - (archives[0]?.totalExpense ?? 0),
      categoryBreakdown: summary.breakdown,
      monthlyHistory: archives.slice(0, 5).map(a => ({
        month: a.monthName,
        income: a.totalIncome,
        expense: a.totalExpense
      }))
    };
    const results = await getFinancialInsights(transactions, finSummary);
    setInsights(results);
    setLoadingAI(false);
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
  };

  const deleteTransaction = (id: string) => {
    if (window.confirm('আপনি কি এই লেনদেনটি মুছে ফেলতে চান?')) {
      setTransactions(prev => prev.filter(t => t.id !== id));
    }
  };

  return (
    <div className="max-w-md mx-auto h-screen bg-[#f8fafc] shadow-2xl flex flex-col font-sans relative overflow-hidden text-slate-900">
      {/* Premium Header */}
      <header 
        className={`bg-gradient-to-br from-indigo-700 via-indigo-600 to-indigo-800 text-white shadow-2xl relative z-20 transition-all duration-300 ease-out will-change-[padding,border-radius] ${isShrunk ? 'p-4 rounded-b-[20px]' : 'p-7 rounded-b-[45px]'}`}
      >
        <div className={`flex justify-between items-center transition-all duration-300 ${isShrunk ? 'mb-1' : 'mb-6'}`}>
          <div className="flex items-center gap-3">
             <div className={`bg-white/20 rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/30 shadow-lg backdrop-blur-md transition-all duration-300 ${isShrunk ? 'w-8 h-8' : 'w-12 h-12'}`}>
