                <div className="absolute top-0 right-0 w-16 h-16 bg-rose-50 rounded-full -mr-8 -mt-8 group-hover:scale-150 transition-transform duration-500"></div>
                <div className="w-14 h-14 bg-gradient-to-br from-rose-400 to-rose-600 text-white rounded-[22px] flex items-center justify-center text-[25.2px] shadow-lg shadow-rose-100 relative z-10">
