      setArchives([]);
      setBudgets({
        'খাবার': 5000,
        'যাতায়াত': 2000,
        'ভাড়া': 10000,
        'বিল': 3000,
        'কেনাকাটা': 4000,
        'স্বাস্থ্য': 2000,
        'অন্যান্য': 2000
      });
      setInsights([]);
      setProfileImage(null);
      localStorage.clear();
      setActiveTab('home');
      alert('সব ডেটা সফলভাবে মুছে ফেলা হয়েছে।');
