            <h2 className={`font-black tracking-tighter text-white transition-all ${isShrunk ? 'text-[30px]' : 'text-[44px]'}`}>{formatCurrency(summary.balance)}</h2>

            {!isShrunk && (
              <div className="mt-6 grid grid-cols-2 gap-4 animate-fadeIn">
