};

export default App;

export enum TransactionType {
  INCOME = 'INCOME',
  EXPENSE = 'EXPENSE'
}

export enum IncomeCategory {
  SALARY = 'বেতন',
  BUSINESS = 'ব্যবসা',
  FREELANCING = 'ফ্রিল্যান্সিং',
  GIFT = 'উপহার',
  OTHER = 'অন্যান্য'
}

export enum ExpenseCategory {
  FOOD = 'খাবার',
  TRANSPORT = 'যাতায়াত',
  RENT = 'ভাড়া',
  BILLS = 'বিল',
  SHOPPING = 'কেনাকাটা',
  HEALTH = 'স্বাস্থ্য',
  OTHER = 'অন্যান্য'
}

export enum Frequency {
  NONE = 'NONE',
  MONTHLY = 'MONTHLY'
}

export interface ChatMessage {
  role: 'user' | 'model';
  text: string;
}

export interface Transaction {
  id: string;
  type: TransactionType;
  amount: number;
  category: string;
  date: string;
  note: string;
  isRecurring?: boolean;
  frequency?: Frequency;
}

export interface MonthlyArchive {
  id: string;
  monthName: string;
  year: number;
  totalIncome: number;
  totalExpense: number;
  transactions: Transaction[];
}

export interface FinancialSummary {
  totalIncome: number;
  totalExpenses: number;
  balance: number;
  savings: number;
  previousMonthSavings: number;
  categoryBreakdown: Record<string, number>;
  monthlyHistory: { month: string; income: number; expense: number }[];
}

export interface Insight {
  title: string;
  description: string;
  type: 'success' | 'warning' | 'info';
}

import { GoogleGenAI, Type } from "@google/genai";
import { Transaction, FinancialSummary, Insight, ChatMessage } from "../types";

const MODEL_NAME = "gemini-3-pro-preview";

export const getFinancialInsights = async (
  transactions: Transaction[],
  summary: FinancialSummary
): Promise<Insight[]> => {
  if (!process.env.API_KEY) return [];
  const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

  const prompt = `
    তুমি একজন দক্ষ আর্থিক উপদেষ্টা। নিচের ডেটা বিশ্লেষণ করে ৩টি পরামর্শ দাও:
    ব্যালেন্স: ৳${summary.balance}, আয়: ৳${summary.totalIncome}, ব্যয়: ৳${summary.totalExpenses}
    ক্যাটাগরি খরচ: ${JSON.stringify(summary.categoryBreakdown)}
    JSON ফরম্যাটে উত্তর দাও (title, description, type: 'success'|'info'). বাংলা ভাষায়।
  `;

  try {
    const response = await ai.models.generateContent({
      model: MODEL_NAME,
      contents: prompt,
      config: {
        responseMimeType: "application/json",
      },
    });
    return JSON.parse(response.text || "[]");
  } catch (error) {
    console.error(error);
    return [];
  }
};

export const chatWithAI = async (
  message: string,
  history: ChatMessage[],
  summary: FinancialSummary,
  transactions: Transaction[]
): Promise<string> => {
  if (!process.env.API_KEY) return "এপিআই কী পাওয়া যায়নি।";
  const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

  const context = `
    ইউজারের আর্থিক অবস্থা:
    - বর্তমান ব্যালেন্স: ৳${summary.balance}
    - এই মাসের মোট আয়: ৳${summary.totalIncome}
    - এই মাসের মোট ব্যয়: ৳${summary.totalExpenses}
    - খরচের খাতসমূহ: ${JSON.stringify(summary.categoryBreakdown)}

    নির্দেশনা:
    ১. তুমি স্পার্ক ফিন্যান্সের অফিশিয়াল এআই অ্যাসিস্ট্যান্ট। 
    ২. সবসময় বাংলা ভাষায় উত্তর দাও। 
    ৩. বন্ধুত্বপূর্ণ এবং সহযোগিতামূলক হও। 
    ৪. ইউজারের ডেটার ওপর ভিত্তি করে উত্তর দাও।
  `;

  try {
    const chat = ai.chats.create({
      model: MODEL_NAME,
      config: {
        systemInstruction: context,
      },
      history: history.map(m => ({
        role: m.role,
        parts: [{ text: m.text }]
      }))
    });

    const response = await chat.sendMessage({ message });
    return response.text || "আমি দুঃখিত, উত্তর তৈরি করতে পারছি না।";
  } catch (error) {
    console.error(error);
    return "সার্ভারে সমস্যা হচ্ছে, দয়া করে পরে চেষ্টা করুন।";
  }
};

import React, { useState, useEffect, useMemo, useRef } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary, ChatMessage } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights, chatWithAI } from './services/geminiService';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'analytics' | 'tools'>('home');
  const [isShrunk, setIsShrunk] = useState(false);
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));
  const amountInputRef = useRef<HTMLInputElement>(null);
  const chatEndRef = useRef<HTMLDivElement>(null);

  // Chat States
  const [showChat, setShowChat] = useState(false);
  const [chatMessages, setChatMessages] = useState<ChatMessage[]>([
    { role: 'model', text: 'স্বাগতম! আমি আপনার স্পার্ক ফিন্যান্স অ্যাসিস্ট্যান্ট। আপনার খরচ বা আয় নিয়ে কোনো প্রশ্ন আছে?' }
  ]);
  const [chatInput, setChatInput] = useState('');
  const [isTyping, setIsTyping] = useState(false);

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {
      'খাবার': 5000,
      'যাতায়াত': 2000,
      'ভাড়া': 10000,
      'বিল': 3000,
      'কেনাকাটা': 4000,
      'স্বাস্থ্য': 2000,
      'অন্যান্য': 2000
    };
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showAddMenu, setShowAddMenu] = useState(false);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);

  // Tools state
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('');
  const [goalAmount, setGoalAmount] = useState('');
  const [goalMonths, setGoalMonths] = useState('');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [chatMessages]);

  const summary = useMemo(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum, t) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum, t) => sum + t.amount, 0);
    const breakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
    });
    return { income, expense, balance: income - expense, breakdown, totalIncome: income, totalExpenses: expense, savings: income - expense, previousMonthSavings: 0, monthlyHistory: [] };
  }, [transactions]);

  const analyticsData = useMemo(() => {
    const total = summary.income + summary.expense;
    const sortedBreakdown = Object.entries(summary.breakdown).sort(([, a], [, b]) => b - a).slice(0, 5);
    const now = new Date();
    const last7Days = Array.from({ length: 7 }, (_, i) => {
      const d = new Date();
      d.setDate(now.getDate() - i);
      const dateStr = d.toISOString().split('T')[0];
      const dayTotal = transactions.filter(t => t.type === TransactionType.EXPENSE && t.date.startsWith(dateStr)).reduce((sum, t) => sum + t.amount, 0);
      return { day: d.toLocaleDateString('bn-BD', { weekday: 'short' }), amount: dayTotal };
    }).reverse();
    const totalBudget = (Object.values(budgets) as number[]).reduce((sum, b) => sum + b, 0);
    const budgetUsage = totalBudget > 0 ? (summary.expense / totalBudget) * 100 : 0;
    return { sortedBreakdown, last7Days, budgetUsage, avgDailySpend: summary.expense / Math.max(now.getDate(), 1) };
  }, [summary, transactions, budgets]);

  const handleSendMessage = async (e?: React.FormEvent) => {
    e?.preventDefault();
    if (!chatInput.trim()) return;

    const userMsg = chatInput;
    setChatInput('');
    setChatMessages(prev => [...prev, { role: 'user', text: userMsg }]);
    setIsTyping(true);

    try {
      const aiResponse = await chatWithAI(userMsg, chatMessages, summary as any, transactions);
      setChatMessages(prev => [...prev, { role: 'model', text: aiResponse }]);
    } catch (error) {
      setChatMessages(prev => [...prev, { role: 'model', text: 'দুঃখিত, আমি এখন উত্তর দিতে পারছি না।' }]);
    } finally {
      setIsTyping(false);
    }
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
    setShowAddMenu(false);
  };

  return (
    <div className="max-w-md mx-auto h-screen bg-slate-50 shadow-2xl flex flex-col font-sans relative overflow-hidden text-slate-900 border-x border-slate-100">

      {/* HEADER */}
      <header className={`header-container bg-gradient-to-br from-indigo-600 to-indigo-800 text-white relative z-[60] shrink-0 transition-all duration-300 ${isShrunk ? 'p-4 rounded-b-[20px] shadow-lg' : 'p-8 rounded-b-[40px] shadow-xl'}`}>
        <div className="flex justify-between items-center mb-4 transition-all">
          <div className="flex items-center gap-3">
             <div className={`bg-white/10 backdrop-blur-md rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/20 transition-all ${isShrunk ? 'w-10 h-10' : 'w-14 h-14'}`}>
                {profileImage ? <img src={profileImage} className="w-full h-full object-cover" /> : <i className={`fas fa-user-circle ${isShrunk ? 'text-lg' : 'text-2xl'}`}></i>}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div className={isShrunk ? 'hidden' : 'block animate-fadeIn'}>
                <h1 className="font-black uppercase tracking-tight text-white text-[24px]">স্পার্ক</h1>
                <p className="text-[12px] font-bold text-indigo-200 uppercase tracking-widest opacity-90">পার্সোনাল ফিন্যান্স</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center border-none active:scale-90 transition-all"><i className="fas fa-bullseye text-[13px]"></i></button>
            <button onClick={() => setShowHistory(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center border-none active:scale-90 transition-all"><i className="fas fa-history text-[13px]"></i></button>
          </div>
        </div>
        <div className="text-center transition-all">
            <p className={`text-indigo-100 font-bold uppercase tracking-widest ${isShrunk ? 'hidden' : 'text-[13px] mb-2 animate-fadeIn'}`}>বর্তমান ব্যালেন্স</p>
            <h2 className={`font-black tracking-tighter text-white transition-all ${isShrunk ? 'text-[28px]' : 'text-[44px]'}`}>{formatCurrency(summary.balance)}</h2>
            {!isShrunk && (
              <div className="mt-6 grid grid-cols-2 gap-4 animate-fadeIn">
                <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10 shadow-sm text-center">
                  <p className="text-[11px] uppercase font-bold text-indigo-200 mb-1 tracking-wider">মোট আয়</p>
                  <p className="text-[18px] font-black text-green-300">৳{summary.income}</p>
                </div>
                <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10 shadow-sm text-center">
                  <p className="text-[11px] uppercase font-bold text-indigo-200 mb-1 tracking-wider">মোট ব্যয়</p>
                  <p className="text-[18px] font-black text-rose-300">৳{summary.expense}</p>
                </div>
              </div>
            )}
        </div>
      </header>

      {/* MAIN CONTENT AREA */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-4 scroll-smooth" onScroll={(e) => {
        const scrollPos = e.currentTarget.scrollTop;
        setIsShrunk(scrollPos > 40);
      }}>
        {activeTab === 'home' ? (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
            <div className="space-y-4">
              <div className="flex justify-between items-center px-2">
                <h3 className="font-black text-slate-400 text-[13px] uppercase tracking-widest">সাম্প্রতিক লেনদেন</h3>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3">
                  {transactions.slice(0, 6).map(t => (
                    <div key={t.id} className="bg-white p-4 rounded-[24px] shadow-sm flex items-center justify-between border border-slate-100">
                      <div className="flex items-center gap-4">
                        <div className={`w-10 h-10 rounded-2xl flex items-center justify-center text-lg ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-800 text-[14px]">{t.category}</p>
                          <p className="text-[11px] text-slate-400 font-bold uppercase">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <p className={`font-black text-[15px] ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                        {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                      </p>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="py-16 text-center border-2 border-dashed border-slate-200 rounded-[30px]">
                   <p className="text-slate-400 font-black text-[12px]">কোনো লেনদেন নেই</p>
                </div>
              )}
            </div>
            {/* BUDGET PREVIEW */}
            <div className="space-y-4">
               <h3 className="font-black text-slate-400 text-[13px] uppercase tracking-widest px-2">বাজেট ট্র্যাকার</h3>
               <div className="space-y-3">
                {EXPENSE_CATEGORIES.slice(0, 3).map(cat => {
                  const spent = summary.breakdown[cat] || 0;
                  const budget = budgets[cat] || 0;
                  if (budget === 0) return null;
                  return (
                    <div key={cat} className="bg-white p-4 rounded-[24px] shadow-sm border border-slate-100">
                      <div className="flex justify-between items-center mb-2 text-[12px] font-black">
                        <p>{cat}</p>
                        <p>৳{spent} / ৳{budget}</p>
                      </div>
                      <div className="w-full h-1.5 bg-slate-100 rounded-full overflow-hidden">
                        <div className={`h-full bg-indigo-500`} style={{ width: `${Math.min((spent / budget) * 100, 100)}%` }}></div>
                      </div>
                    </div>
                  );
                })}
               </div>
            </div>
          </div>
        ) : activeTab === 'analytics' ? (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
             <div className="bg-white p-8 rounded-[40px] border border-slate-100 shadow-sm space-y-6">
                <h3 className="font-black text-slate-800 uppercase text-[13px] tracking-widest">সাপ্তাহিক ট্রেন্ড</h3>
                <div className="flex items-end justify-between h-32 gap-2">
                   {analyticsData.last7Days.map((d, i) => (
                      <div key={i} className="flex-1 flex flex-col items-center gap-2">
                         <div className="w-full bg-indigo-600 rounded-full" style={{ height: `${Math.max((d.amount / (Math.max(...analyticsData.last7Days.map(x => x.amount)) || 1)) * 100, 5)}%` }}></div>
                         <span className="text-[10px] font-bold text-slate-400">{d.day}</span>
                      </div>
                   ))}
                </div>
             </div>
             <div className="grid grid-cols-2 gap-4">
                <div className="bg-white p-6 rounded-[35px] border border-slate-100 shadow-sm text-center">
                   <p className="text-[10px] font-black text-slate-400 uppercase mb-2">বাজেট ব্যবহার</p>
                   <div className="text-[24px] font-black">{analyticsData.budgetUsage.toFixed(0)}%</div>
                </div>
                <div className="bg-white p-6 rounded-[35px] border border-slate-100 shadow-sm text-center">
                   <p className="text-[10px] font-black text-slate-400 uppercase mb-2">দৈনিক গড়</p>
                   <div className="text-[24px] font-black text-indigo-600">৳{analyticsData.avgDailySpend.toFixed(0)}</div>
                </div>
             </div>
          </div>
        ) : (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
             <div className="bg-white p-8 rounded-[40px] border border-slate-100 shadow-sm space-y-6">
               <h3 className="font-black text-slate-800 uppercase text-[13px] tracking-widest">বিল স্প্লিটার</h3>
               <div className="space-y-4">
                 <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ মোট বিল" className="w-full p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
                 <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} placeholder="মানুষের সংখ্যা" className="w-full p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
               </div>
               <div className="p-8 bg-indigo-600 rounded-[30px] text-center text-white">
                 <p className="text-[11px] font-bold uppercase mb-2">মাথা পিছু</p>
                 <p className="text-[32px] font-black">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
               </div>
             </div>
          </div>
        )}
      </main>

      {/* CHAT INTERFACE (NEW) */}
      {showChat && (
        <div className="fixed inset-0 bg-slate-900/90 backdrop-blur-xl z-[500] flex flex-col animate-fadeIn">
          <div className="flex justify-between items-center p-6 text-white border-b border-white/10">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-indigo-600 rounded-2xl flex items-center justify-center text-lg">
                <i className="fas fa-robot"></i>
              </div>
              <div>
                <h2 className="font-black uppercase text-[16px] tracking-widest">স্মার্ট চ্যাট</h2>
                <p className="text-[10px] text-green-400 font-black uppercase">অনলাইন</p>
              </div>
            </div>
            <button onClick={() => setShowChat(false)} className="w-10 h-10 bg-white/10 rounded-full flex items-center justify-center border-none"><i className="fas fa-times"></i></button>
          </div>

          <div className="flex-1 overflow-y-auto p-6 space-y-6 custom-scrollbar">
            {chatMessages.map((msg, i) => (
              <div key={i} className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'} animate-slideUp`}>
                <div className={`max-w-[85%] px-5 py-4 rounded-[28px] text-[14px] font-bold leading-relaxed ${msg.role === 'user' ? 'bg-indigo-600 text-white rounded-br-none' : 'bg-white/10 text-white rounded-bl-none border border-white/10'}`}>
                  {msg.text}
                </div>
              </div>
            ))}
            {isTyping && (
              <div className="flex justify-start animate-pulse">
                <div className="bg-white/5 text-slate-400 px-5 py-3 rounded-[20px] text-[12px] font-black uppercase tracking-widest">এআই টাইপ করছে...</div>
              </div>
            )}
            <div ref={chatEndRef}></div>
          </div>

          <form onSubmit={handleSendMessage} className="p-6 bg-white/5 border-t border-white/10 flex gap-3">
             <input 
              type="text" 
              value={chatInput}
              onChange={(e) => setChatInput(e.target.value)}
              placeholder="আপনার প্রশ্ন লিখুন..." 
              className="flex-1 bg-white/10 border-none outline-none text-white font-bold px-6 py-4 rounded-full"
             />
             <button type="submit" className="w-14 h-14 bg-indigo-600 text-white rounded-full flex items-center justify-center border-none shadow-lg shadow-indigo-500/30 active:scale-95 transition-all"><i className="fas fa-paper-plane"></i></button>
          </form>
        </div>
      )}

      {/* FLOATING ACTION BUTTONS */}
      <div className="fixed bottom-28 right-6 z-[100] flex flex-col items-end gap-4">
        {/* CHAT BUTTON */}
        <button 
          onClick={() => setShowChat(true)}
          className="w-14 h-14 rounded-full flex items-center justify-center text-white text-xl shadow-lg transition-all active:scale-90 border-none bg-indigo-600 shadow-indigo-500/40"
        >
          <i className="fas fa-comment-dots"></i>
        </button>

        {showAddMenu && (
          <div className="flex flex-col gap-3 mb-2 animate-slideUp items-end">
            <button onClick={() => { setModalType(TransactionType.INCOME); setShowAddMenu(false); }} className="bg-green-600 text-white px-5 py-3 rounded-[18px] font-black text-[12px] uppercase tracking-widest shadow-xl border-none transition-all active:scale-95">
              <i className="fas fa-arrow-up mr-2"></i> আয়
            </button>
            <button onClick={() => { setModalType(TransactionType.EXPENSE); setShowAddMenu(false); }} className="bg-rose-600 text-white px-5 py-3 rounded-[18px] font-black text-[12px] uppercase tracking-widest shadow-xl border-none transition-all active:scale-95">
              <i className="fas fa-arrow-down mr-2"></i> ব্যয়
            </button>
          </div>
        )}

        {/* MAIN ADD BUTTON */}
        <button onClick={() => setShowAddMenu(!showAddMenu)} className={`w-14 h-14 rounded-full flex items-center justify-center text-white text-xl shadow-[0_8px_25px_rgba(0,0,0,0.2)] transition-all active:scale-90 border-none ${showAddMenu ? 'bg-slate-800 rotate-45' : 'bg-slate-900'}`}>
          <i className="fas fa-plus"></i>
        </button>
      </div>

      {/* BOTTOM NAVIGATION */}
      <div className="fixed bottom-6 left-6 right-6 z-[150]">
        <nav className="bg-white/90 backdrop-blur-xl border border-slate-200/50 px-6 py-3 flex justify-between items-center rounded-[30px] shadow-xl overflow-hidden">
          <button onClick={() => setActiveTab('home')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'home' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-house-chimney text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">হোম</span>
          </button>
          <button onClick={() => setActiveTab('analytics')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'analytics' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-chart-pie text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">বিশ্লেষণ</span>
          </button>
          <button onClick={() => setActiveTab('tools')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'tools' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-layer-group text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">টুলস</span>
          </button>
        </nav>
      </div>

      {/* TRANSACTION MODAL */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/70 backdrop-blur-sm z-[200] flex items-center justify-center p-6 animate-fadeIn">
          <div className="bg-white w-full max-w-[340px] rounded-[35px] shadow-2xl border-none animate-slideUp overflow-hidden">
            <div className="p-8 space-y-6">
              <h2 className="text-[18px] font-black text-slate-800 text-center">{modalType === TransactionType.INCOME ? 'আয় যোগ' : 'ব্যয় যোগ'}</h2>
              <form onSubmit={addTransaction} className="space-y-6">
                <input ref={amountInputRef} type="number" name="amount" required placeholder="৳ টাকার পরিমাণ" className="w-full text-[24px] font-black text-center outline-none border-none bg-slate-50 py-4 rounded-[20px]" />
                <select name="category" className="w-full py-4 px-6 bg-slate-50 rounded-[20px] font-black text-slate-800 outline-none border-none">
                  {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                </select>
                <input type="text" name="note" placeholder="নোট..." className="w-full py-4 px-6 bg-slate-50 rounded-[20px] font-black outline-none border-none" />
                <button type="submit" className={`w-full py-5 rounded-[22px] text-white font-black uppercase text-[13px] tracking-widest border-none ${modalType === TransactionType.INCOME ? 'bg-green-600' : 'bg-rose-600'}`}>সংরক্ষণ করুন</button>
                <button type="button" onClick={() => setModalType(null)} className="w-full py-2 text-slate-400 font-black uppercase text-[10px] border-none bg-transparent">বাতিল করুন</button>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* BUDGET MODAL (SIMPLIFIED) */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[300] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[40px] p-8 animate-slideUp overflow-y-auto max-h-[85vh] border-none">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-[18px] font-black uppercase">বাজেট সেটিংস</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-8 h-8 bg-slate-100 rounded-xl flex items-center justify-center border-none"><i className="fas fa-times"></i></button>
             </div>
             <div className="space-y-6 mb-8">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="flex flex-col gap-2">
                    <label className="text-[11px] font-black text-slate-500 uppercase">{cat}</label>
                    <input type="number" value={budgets[cat] || ''} onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })} placeholder="৳ মাসিক বাজেট" className="w-full p-4 bg-slate-50 rounded-[18px] font-black outline-none border-none shadow-inner" />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-5 bg-indigo-600 text-white rounded-[22px] font-black uppercase text-[12px] border-none">সেভ করুন</button>
          </div>
        </div>
      )}

      {/* HISTORY MODAL (SIMPLIFIED) */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[300] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[40px] p-8 overflow-y-auto animate-slideUp border-none">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-[18px] font-black uppercase tracking-widest">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-8 h-8 bg-slate-100 rounded-xl flex items-center justify-center border-none"><i className="fas fa-times"></i></button>
             </div>
             <p className="text-center py-24 text-slate-400 font-black uppercase text-[12px]">পুরানো রেকর্ড নেই</p>
          </div>
        </div>
      )}

    </div>
  );
};

export default App;

export enum TransactionType {
  INCOME = 'INCOME',
  EXPENSE = 'EXPENSE'
}

export enum IncomeCategory {
  SALARY = 'বেতন',
  BUSINESS = 'ব্যবসা',
  FREELANCING = 'ফ্রিল্যান্সিং',
  GIFT = 'উপহার',
  OTHER = 'অন্যান্য'
}

export enum ExpenseCategory {
  FOOD = 'খাবার',
  TRANSPORT = 'যাতায়াত',
  RENT = 'ভাড়া',
  BILLS = 'বিল',
  SHOPPING = 'কেনাকাটা',
  HEALTH = 'স্বাস্থ্য',
  OTHER = 'অন্যান্য'
}

export enum Frequency {
  NONE = 'NONE',
  MONTHLY = 'MONTHLY'
}

export interface ChatMessage {
  role: 'user' | 'model';
  text: string;
}

export interface Transaction {
  id: string;
  type: TransactionType;
  amount: number;
  category: string;
  date: string;
  note: string;
  isRecurring?: boolean;
  frequency?: Frequency;
}

export interface UserSettings {
  categoryColors: Record<string, string>;
  budgets: Record<string, number>;
}

export interface MonthlyArchive {
  id: string;
  monthName: string;
  year: number;
  totalIncome: number;
  totalExpense: number;
  transactions: Transaction[];
}

export interface FinancialSummary {
  totalIncome: number;
  totalExpenses: number;
  balance: number;
  savings: number;
  previousMonthSavings: number;
  categoryBreakdown: Record<string, number>;
  monthlyHistory: { month: string; income: number; expense: number }[];
}

export interface Insight {
  title: string;
  description: string;
  type: 'success' | 'warning' | 'info';
}

import React, { useState, useEffect, useMemo, useRef } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary, ChatMessage } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights, chatWithAI } from './services/geminiService';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'analytics' | 'tools'>('home');
  const [isShrunk, setIsShrunk] = useState(false);
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));
  const amountInputRef = useRef<HTMLInputElement>(null);
  const chatEndRef = useRef<HTMLDivElement>(null);

  // Chat States
  const [showChat, setShowChat] = useState(false);
  const [chatMessages, setChatMessages] = useState<ChatMessage[]>([
    { role: 'model', text: 'স্বাগতম! আমি আপনার স্পার্ক ফিন্যান্স অ্যাসিস্ট্যান্ট। আপনার খরচ বা আয় নিয়ে কোনো প্রশ্ন আছে?' }
  ]);
  const [chatInput, setChatInput] = useState('');
  const [isTyping, setIsTyping] = useState(false);

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {
      'খাবার': 5000,
      'যাতায়াত': 2000,
      'ভাড়া': 10000,
      'বিল': 3000,
      'কেনাকাটা': 4000,
      'স্বাস্থ্য': 2000,
      'অন্যান্য': 2000
    };
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showAddMenu, setShowAddMenu] = useState(false);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);

  // Tools state
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('');
  const [goalAmount, setGoalAmount] = useState('');
  const [goalMonths, setGoalMonths] = useState('');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [chatMessages]);

  const summary = useMemo<FinancialSummary>(() => {
    // Explicitly typing sum and t to resolve arithmetic operation error on line 81
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum: number, t: Transaction) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum: number, t: Transaction) => sum + t.amount, 0);
    const categoryBreakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      categoryBreakdown[t.category] = (categoryBreakdown[t.category] || 0) + t.amount;
    });
    return { 
      totalIncome: income, 
      totalExpenses: expense, 
      balance: income - expense, 
      categoryBreakdown, 
      savings: income - expense, 
      previousMonthSavings: 0, 
      monthlyHistory: [] 
    };
  }, [transactions]);

  const analyticsData = useMemo(() => {
    const total = summary.totalIncome + summary.totalExpenses;
    const sortedBreakdown = Object.entries(summary.categoryBreakdown).sort(([, a], [, b]) => b - a).slice(0, 5);
    const now = new Date();
    const last7Days = Array.from({ length: 7 }, (_, i) => {
      const d = new Date();
      d.setDate(now.getDate() - i);
      const dateStr = d.toISOString().split('T')[0];
      const dayTotal = transactions.filter(t => t.type === TransactionType.EXPENSE && t.date.startsWith(dateStr)).reduce((sum, t) => sum + t.amount, 0);
      return { day: d.toLocaleDateString('bn-BD', { weekday: 'short' }), amount: dayTotal };
    }).reverse();
    const totalBudget = (Object.values(budgets) as number[]).reduce((sum: number, b: number) => sum + b, 0);
    const budgetUsage = totalBudget > 0 ? (summary.totalExpenses / totalBudget) * 100 : 0;
    return { sortedBreakdown, last7Days, budgetUsage, avgDailySpend: summary.totalExpenses / Math.max(now.getDate(), 1) };
  }, [summary, transactions, budgets]);

  const handleSendMessage = async (e?: React.FormEvent) => {
    e?.preventDefault();
    if (!chatInput.trim()) return;

    const userMsg = chatInput;
    setChatInput('');
    setChatMessages(prev => [...prev, { role: 'user', text: userMsg }]);
    setIsTyping(true);

    try {
      // summary now matches FinancialSummary, so we can pass it directly
      const aiResponse = await chatWithAI(userMsg, chatMessages, summary, transactions);
      setChatMessages(prev => [...prev, { role: 'model', text: aiResponse }]);
    } catch (error) {
      setChatMessages(prev => [...prev, { role: 'model', text: 'দুঃখিত, আমি এখন উত্তর দিতে পারছি না।' }]);
    } finally {
      setIsTyping(false);
    }
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
    setShowAddMenu(false);
  };

  return (
    <div className="max-w-md mx-auto h-screen bg-slate-50 shadow-2xl flex flex-col font-sans relative overflow-hidden text-slate-900 border-x border-slate-100">

      {/* HEADER */}
      <header className={`header-container bg-gradient-to-br from-indigo-600 to-indigo-800 text-white relative z-[60] shrink-0 transition-all duration-300 ${isShrunk ? 'p-4 rounded-b-[20px] shadow-lg' : 'p-8 rounded-b-[40px] shadow-xl'}`}>
        <div className="flex justify-between items-center mb-4 transition-all">
          <div className="flex items-center gap-3">
             <div className={`bg-white/10 backdrop-blur-md rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/20 transition-all ${isShrunk ? 'w-10 h-10' : 'w-14 h-14'}`}>
                {profileImage ? <img src={profileImage} className="w-full h-full object-cover" /> : <i className={`fas fa-user-circle ${isShrunk ? 'text-lg' : 'text-2xl'}`}></i>}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div className={isShrunk ? 'hidden' : 'block animate-fadeIn'}>
                <h1 className="font-black uppercase tracking-tight text-white text-[24px]">স্পার্ক</h1>
                <p className="text-[12px] font-bold text-indigo-200 uppercase tracking-widest opacity-90">পার্সোনাল ফিন্যান্স</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center border-none active:scale-90 transition-all"><i className="fas fa-bullseye text-[13px]"></i></button>
            <button onClick={() => setShowHistory(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center border-none active:scale-90 transition-all"><i className="fas fa-history text-[13px]"></i></button>
          </div>
        </div>
        <div className="text-center transition-all">
            <p className={`text-indigo-100 font-bold uppercase tracking-widest ${isShrunk ? 'hidden' : 'text-[13px] mb-2 animate-fadeIn'}`}>বর্তমান ব্যালেন্স</p>
            <h2 className={`font-black tracking-tighter text-white transition-all ${isShrunk ? 'text-[28px]' : 'text-[44px]'}`}>{formatCurrency(summary.balance)}</h2>
            {!isShrunk && (
              <div className="mt-6 grid grid-cols-2 gap-4 animate-fadeIn">
                <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10 shadow-sm text-center">
                  <p className="text-[11px] uppercase font-bold text-indigo-200 mb-1 tracking-wider">মোট আয়</p>
                  <p className="text-[18px] font-black text-green-300">৳{summary.totalIncome}</p>
                </div>
                <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10 shadow-sm text-center">
                  <p className="text-[11px] uppercase font-bold text-indigo-200 mb-1 tracking-wider">মোট ব্যয়</p>
                  <p className="text-[18px] font-black text-rose-300">৳{summary.totalExpenses}</p>
                </div>
              </div>
            )}
        </div>
      </header>

      {/* MAIN CONTENT AREA */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-4 scroll-smooth" onScroll={(e) => {
        const scrollPos = e.currentTarget.scrollTop;
        setIsShrunk(scrollPos > 40);
      }}>
        {activeTab === 'home' ? (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
            <div className="space-y-4">
              <div className="flex justify-between items-center px-2">
                <h3 className="font-black text-slate-400 text-[13px] uppercase tracking-widest">সাম্প্রতিক লেনদেন</h3>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3">
                  {transactions.slice(0, 6).map(t => (
                    <div key={t.id} className="bg-white p-4 rounded-[24px] shadow-sm flex items-center justify-between border border-slate-100">
                      <div className="flex items-center gap-4">
                        <div className={`w-10 h-10 rounded-2xl flex items-center justify-center text-lg ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-800 text-[14px]">{t.category}</p>
                          <p className="text-[11px] text-slate-400 font-bold uppercase">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <p className={`font-black text-[15px] ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                        {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                      </p>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="py-16 text-center border-2 border-dashed border-slate-200 rounded-[30px]">
                   <p className="text-slate-400 font-black text-[12px]">কোনো লেনদেন নেই</p>
                </div>
              )}
            </div>
            {/* BUDGET PREVIEW */}
            <div className="space-y-4">
               <h3 className="font-black text-slate-400 text-[13px] uppercase tracking-widest px-2">বাজেট ট্র্যাকার</h3>
               <div className="space-y-3">
                {EXPENSE_CATEGORIES.slice(0, 3).map(cat => {
                  const spent = summary.categoryBreakdown[cat] || 0;
                  const budget = budgets[cat] || 0;
                  if (budget === 0) return null;
                  return (
                    <div key={cat} className="bg-white p-4 rounded-[24px] shadow-sm border border-slate-100">
                      <div className="flex justify-between items-center mb-2 text-[12px] font-black">
                        <p>{cat}</p>
                        <p>৳{spent} / ৳{budget}</p>
                      </div>
                      <div className="w-full h-1.5 bg-slate-100 rounded-full overflow-hidden">
                        <div className={`h-full bg-indigo-500`} style={{ width: `${Math.min((spent / budget) * 100, 100)}%` }}></div>
                      </div>
                    </div>
                  );
                })}
               </div>
            </div>
          </div>
        ) : activeTab === 'analytics' ? (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
             <div className="bg-white p-8 rounded-[40px] border border-slate-100 shadow-sm space-y-6">
                <h3 className="font-black text-slate-800 uppercase text-[13px] tracking-widest">সাপ্তাহিক ট্রেন্ড</h3>
                <div className="flex items-end justify-between h-32 gap-2">
                   {analyticsData.last7Days.map((d, i) => (
                      <div key={i} className="flex-1 flex flex-col items-center gap-2">
                         <div className="w-full bg-indigo-600 rounded-full" style={{ height: `${Math.max((d.amount / (Math.max(...analyticsData.last7Days.map(x => x.amount)) || 1)) * 100, 5)}%` }}></div>
                         <span className="text-[10px] font-bold text-slate-400">{d.day}</span>
                      </div>
                   ))}
                </div>
             </div>
             <div className="grid grid-cols-2 gap-4">
                <div className="bg-white p-6 rounded-[35px] border border-slate-100 shadow-sm text-center">
                   <p className="text-[10px] font-black text-slate-400 uppercase mb-2">বাজেট ব্যবহার</p>
                   <div className="text-[24px] font-black">{analyticsData.budgetUsage.toFixed(0)}%</div>
                </div>
                <div className="bg-white p-6 rounded-[35px] border border-slate-100 shadow-sm text-center">
                   <p className="text-[10px] font-black text-slate-400 uppercase mb-2">দৈনিক গড়</p>
                   <div className="text-[24px] font-black text-indigo-600">৳{analyticsData.avgDailySpend.toFixed(0)}</div>
                </div>
             </div>
          </div>
        ) : (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
             <div className="bg-white p-8 rounded-[40px] border border-slate-100 shadow-sm space-y-6">
               <h3 className="font-black text-slate-800 uppercase text-[13px] tracking-widest">বিল স্প্লিটার</h3>
               <div className="space-y-4">
                 <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ মোট বিল" className="w-full p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
                 <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} placeholder="মানুষের সংখ্যা" className="w-full p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
               </div>
               <div className="p-8 bg-indigo-600 rounded-[30px] text-center text-white">
                 <p className="text-[11px] font-bold uppercase mb-2">মাথা পিছু</p>
                 <p className="text-[32px] font-black">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
               </div>
             </div>
          </div>
        )}
      </main>

      {/* CHAT INTERFACE (NEW) */}
      {showChat && (
        <div className="fixed inset-0 bg-slate-900/90 backdrop-blur-xl z-[500] flex flex-col animate-fadeIn">
          <div className="flex justify-between items-center p-6 text-white border-b border-white/10">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-indigo-600 rounded-2xl flex items-center justify-center text-lg">
                <i className="fas fa-robot"></i>
              </div>
              <div>
                <h2 className="font-black uppercase text-[16px] tracking-widest">স্মার্ট চ্যাট</h2>
                <p className="text-[10px] text-green-400 font-black uppercase">অনলাইন</p>
              </div>
            </div>
            <button onClick={() => setShowChat(false)} className="w-10 h-10 bg-white/10 rounded-full flex items-center justify-center border-none"><i className="fas fa-times"></i></button>
          </div>

          <div className="flex-1 overflow-y-auto p-6 space-y-6 custom-scrollbar">
            {chatMessages.map((msg, i) => (
              <div key={i} className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'} animate-slideUp`}>
                <div className={`max-w-[85%] px-5 py-4 rounded-[28px] text-[14px] font-bold leading-relaxed ${msg.role === 'user' ? 'bg-indigo-600 text-white rounded-br-none' : 'bg-white/10 text-white rounded-bl-none border border-white/10'}`}>
                  {msg.text}
                </div>
              </div>
            ))}
            {isTyping && (
              <div className="flex justify-start animate-pulse">
                <div className="bg-white/5 text-slate-400 px-5 py-3 rounded-[20px] text-[12px] font-black uppercase tracking-widest">এআই টাইপ করছে...</div>
              </div>
            )}
            <div ref={chatEndRef}></div>
          </div>

          <form onSubmit={handleSendMessage} className="p-6 bg-white/5 border-t border-white/10 flex gap-3">
             <input 
              type="text" 
              value={chatInput}
              onChange={(e) => setChatInput(e.target.value)}
              placeholder="আপনার প্রশ্ন লিখুন..." 
              className="flex-1 bg-white/10 border-none outline-none text-white font-bold px-6 py-4 rounded-full"
             />
             <button type="submit" className="w-14 h-14 bg-indigo-600 text-white rounded-full flex items-center justify-center border-none shadow-lg shadow-indigo-500/30 active:scale-95 transition-all"><i className="fas fa-paper-plane"></i></button>
          </form>
        </div>
      )}

      {/* FLOATING ACTION BUTTONS */}
      <div className="fixed bottom-28 right-6 z-[100] flex flex-col items-end gap-4">
        {/* CHAT BUTTON */}
        <button 
          onClick={() => setShowChat(true)}
          className="w-14 h-14 rounded-full flex items-center justify-center text-white text-xl shadow-lg transition-all active:scale-90 border-none bg-indigo-600 shadow-indigo-500/40"
        >
          <i className="fas fa-comment-dots"></i>
        </button>

        {showAddMenu && (
          <div className="flex flex-col gap-3 mb-2 animate-slideUp items-end">
            <button onClick={() => { setModalType(TransactionType.INCOME); setShowAddMenu(false); }} className="bg-green-600 text-white px-5 py-3 rounded-[18px] font-black text-[12px] uppercase tracking-widest shadow-xl border-none transition-all active:scale-95">
              <i className="fas fa-arrow-up mr-2"></i> আয়
            </button>
            <button onClick={() => { setModalType(TransactionType.EXPENSE); setShowAddMenu(false); }} className="bg-rose-600 text-white px-5 py-3 rounded-[18px] font-black text-[12px] uppercase tracking-widest shadow-xl border-none transition-all active:scale-95">
              <i className="fas fa-arrow-down mr-2"></i> ব্যয়
            </button>
          </div>
        )}

        {/* MAIN ADD BUTTON */}
        <button onClick={() => setShowAddMenu(!showAddMenu)} className={`w-14 h-14 rounded-full flex items-center justify-center text-white text-xl shadow-[0_8px_25px_rgba(0,0,0,0.2)] transition-all active:scale-90 border-none ${showAddMenu ? 'bg-slate-800 rotate-45' : 'bg-slate-900'}`}>
          <i className="fas fa-plus"></i>
        </button>
      </div>

      {/* BOTTOM NAVIGATION */}
      <div className="fixed bottom-6 left-6 right-6 z-[150]">
        <nav className="bg-white/90 backdrop-blur-xl border border-slate-200/50 px-6 py-3 flex justify-between items-center rounded-[30px] shadow-xl overflow-hidden">
          <button onClick={() => setActiveTab('home')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'home' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-house-chimney text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">হোম</span>
          </button>
          <button onClick={() => setActiveTab('analytics')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'analytics' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-chart-pie text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">বিশ্লেষণ</span>
          </button>
          <button onClick={() => setActiveTab('tools')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'tools' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-layer-group text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">টুলস</span>
          </button>
        </nav>
      </div>

      {/* TRANSACTION MODAL */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/70 backdrop-blur-sm z-[200] flex items-center justify-center p-6 animate-fadeIn">
          <div className="bg-white w-full max-w-[340px] rounded-[35px] shadow-2xl border-none animate-slideUp overflow-hidden">
            <div className="p-8 space-y-6">
              <h2 className="text-[18px] font-black text-slate-800 text-center">{modalType === TransactionType.INCOME ? 'আয় যোগ' : 'ব্যয় যোগ'}</h2>
              <form onSubmit={addTransaction} className="space-y-6">
                <input ref={amountInputRef} type="number" name="amount" required placeholder="৳ টাকার পরিমাণ" className="w-full text-[24px] font-black text-center outline-none border-none bg-slate-50 py-4 rounded-[20px]" />
                <select name="category" className="w-full py-4 px-6 bg-slate-50 rounded-[20px] font-black text-slate-800 outline-none border-none">
                  {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                </select>
                <input type="text" name="note" placeholder="নোট..." className="w-full py-4 px-6 bg-slate-50 rounded-[20px] font-black outline-none border-none" />
                <button type="submit" className={`w-full py-5 rounded-[22px] text-white font-black uppercase text-[13px] tracking-widest border-none ${modalType === TransactionType.INCOME ? 'bg-green-600' : 'bg-rose-600'}`}>সংরক্ষণ করুন</button>
                <button type="button" onClick={() => setModalType(null)} className="w-full py-2 text-slate-400 font-black uppercase text-[10px] border-none bg-transparent">বাতিল করুন</button>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* BUDGET MODAL (SIMPLIFIED) */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[300] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[40px] p-8 animate-slideUp overflow-y-auto max-h-[85vh] border-none">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-[18px] font-black uppercase">বাজেট সেটিংস</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-8 h-8 bg-slate-100 rounded-xl flex items-center justify-center border-none"><i className="fas fa-times"></i></button>
             </div>
             <div className="space-y-6 mb-8">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="flex flex-col gap-2">
                    <label className="text-[11px] font-black text-slate-500 uppercase">{cat}</label>
                    <input type="number" value={budgets[cat] || ''} onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })} placeholder="৳ মাসিক বাজেট" className="w-full p-4 bg-slate-50 rounded-[18px] font-black outline-none border-none shadow-inner" />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-5 bg-indigo-600 text-white rounded-[22px] font-black uppercase text-[12px] border-none">সেভ করুন</button>
          </div>
        </div>
      )}

      {/* HISTORY MODAL (SIMPLIFIED) */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[300] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[40px] p-8 overflow-y-auto animate-slideUp border-none">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-[18px] font-black uppercase tracking-widest">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-8 h-8 bg-slate-100 rounded-xl flex items-center justify-center border-none"><i className="fas fa-times"></i></button>
             </div>
             <p className="text-center py-24 text-slate-400 font-black uppercase text-[12px]">পুরানো রেকর্ড নেই</p>
          </div>
        </div>
      )}

    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo, useRef } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary, ChatMessage } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights, chatWithAI } from './services/geminiService';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'analytics' | 'tools'>('home');
  const [isShrunk, setIsShrunk] = useState(false);
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));
  const amountInputRef = useRef<HTMLInputElement>(null);

  // Voice & Quick Command States
  const [isListening, setIsListening] = useState(false);
  const [voiceFeedback, setVoiceFeedback] = useState<string | null>(null);
  const [showQuickInput, setShowQuickInput] = useState(false);
  const [quickInputVal, setQuickInputVal] = useState('');

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {
      'খাবার': 5000,
      'যাতায়াত': 2000,
      'ভাড়া': 10000,
      'বিল': 3000,
      'কেনাকাটা': 4000,
      'স্বাস্থ্য': 2000,
      'অন্যান্য': 2000
    };
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showAddMenu, setShowAddMenu] = useState(false);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);

  // Tools state
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('');
  const [goalAmount, setGoalAmount] = useState('');
  const [goalMonths, setGoalMonths] = useState('');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  const summary = useMemo<FinancialSummary>(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum: number, t: Transaction) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum: number, t: Transaction) => sum + t.amount, 0);
    const categoryBreakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      categoryBreakdown[t.category] = (categoryBreakdown[t.category] || 0) + t.amount;
    });
    return { 
      totalIncome: income, 
      totalExpenses: expense, 
      balance: income - expense, 
      categoryBreakdown, 
      savings: income - expense, 
      previousMonthSavings: 0, 
      monthlyHistory: [] 
    };
  }, [transactions]);

  const analyticsData = useMemo(() => {
    const sortedBreakdown = Object.entries(summary.categoryBreakdown).sort(([, a], [, b]) => b - a).slice(0, 5);
    const now = new Date();
    const last7Days = Array.from({ length: 7 }, (_, i) => {
      const d = new Date();
      d.setDate(now.getDate() - i);
      const dateStr = d.toISOString().split('T')[0];
      const dayTotal = transactions.filter(t => t.type === TransactionType.EXPENSE && t.date.startsWith(dateStr)).reduce((sum, t) => sum + t.amount, 0);
      return { day: d.toLocaleDateString('bn-BD', { weekday: 'short' }), amount: dayTotal };
    }).reverse();
    const totalBudget = (Object.values(budgets) as number[]).reduce((sum: number, b: number) => sum + b, 0);
    const budgetUsage = totalBudget > 0 ? (summary.totalExpenses / totalBudget) * 100 : 0;
    return { sortedBreakdown, last7Days, budgetUsage, avgDailySpend: summary.totalExpenses / Math.max(now.getDate(), 1) };
  }, [summary, transactions, budgets]);

  // HELPER: Bengali Digits to English
  const bengaliToEnglishDigits = (str: string) => {
    const bengaliDigits = ['০', '১', '২', '৩', '৪', '৫', '৬', '৭', '৮', '৯'];
    return str.replace(/[০-৯]/g, (w) => bengaliDigits.indexOf(w).toString());
  };

  // VOICE & QUICK COMMAND LOGIC
  const startVoiceInput = () => {
    const SpeechRecognition = (window as any).SpeechRecognition || (window as any).webkitSpeechRecognition;
    if (!SpeechRecognition) {
      setVoiceFeedback("ভয়েস সাপোর্ট নেই, সরাসরি টাইপ করুন।");
      setShowQuickInput(true);
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = 'bn-BD';
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;

    recognition.onstart = () => {
      setIsListening(true);
      setVoiceFeedback("শুনছি... বলুন (যেমন: ১০০ টাকা নাস্তা)");
    };

    recognition.onresult = (event: any) => {
      const transcript = event.results[0][0].transcript.toLowerCase();
      processCommand(transcript);
    };

    recognition.onerror = (event: any) => {
      setIsListening(false);
      if (event.error === 'network' || !navigator.onLine) {
        setVoiceFeedback("অফলাইন! কিবোর্ড আইকনে ক্লিক করে টাইপ করুন।");
      } else {
        setVoiceFeedback("বুঝতে পারিনি, আবার চেষ্টা করুন।");
      }
      setTimeout(() => setVoiceFeedback(null), 3000);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognition.start();
  };

  const processCommand = (text: string) => {
    const convertedText = bengaliToEnglishDigits(text);
    const amountMatch = convertedText.match(/\d+/);
    const amount = amountMatch ? parseFloat(amountMatch[0]) : null;

    if (!amount) {
      setVoiceFeedback("টাকার পরিমাণ খুঁজে পাওয়া যায়নি।");
      setTimeout(() => setVoiceFeedback(null), 3000);
      return;
    }

    let type = TransactionType.EXPENSE;
    if (text.includes("আয়") || text.includes("পেলাম") || text.includes("বেতন") || text.includes("লাভ") || text.includes("যোগ")) {
      type = TransactionType.INCOME;
    }

    let category = "অন্যান্য";
    const categoriesToCheck = type === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES;
    for (const cat of categoriesToCheck) {
      if (text.includes(cat)) {
        category = cat;
        break;
      }
    }

    const newTx: Transaction = {
      id: `tx_${Date.now()}`,
      type,
      amount,
      category,
      note: text,
      date: new Date().toISOString()
    };

    setTransactions(prev => [newTx, ...prev]);
    setVoiceFeedback(`৳${amount} ${category} হিসেবে যোগ হয়েছে।`);
    setShowQuickInput(false);
    setQuickInputVal('');
    setTimeout(() => setVoiceFeedback(null), 4000);
  };

  const handleQuickSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (quickInputVal.trim()) {
      processCommand(quickInputVal);
    }
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
    setShowAddMenu(false);
  };

  return (
    <div className="max-w-md mx-auto h-screen bg-slate-50 shadow-2xl flex flex-col font-sans relative overflow-hidden text-slate-900 border-x border-slate-100">

      {/* HEADER */}
      <header className={`header-container bg-gradient-to-br from-indigo-600 to-indigo-800 text-white relative z-[60] shrink-0 transition-all duration-300 ${isShrunk ? 'p-4 rounded-b-[20px] shadow-lg' : 'p-8 rounded-b-[40px] shadow-xl'}`}>
        <div className="flex justify-between items-center mb-4 transition-all">
          <div className="flex items-center gap-3">
             <div className={`bg-white/10 backdrop-blur-md rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/20 transition-all ${isShrunk ? 'w-10 h-10' : 'w-14 h-14'}`}>
                {profileImage ? <img src={profileImage} className="w-full h-full object-cover" /> : <i className={`fas fa-user-circle ${isShrunk ? 'text-lg' : 'text-2xl'}`}></i>}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div className={isShrunk ? 'hidden' : 'block animate-fadeIn'}>
                <h1 className="font-black uppercase tracking-tight text-white text-[24px]">স্পার্ক</h1>
                <p className="text-[12px] font-bold text-indigo-200 uppercase tracking-widest opacity-90">পার্সোনাল ফিন্যান্স</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center border-none active:scale-90 transition-all"><i className="fas fa-bullseye text-[13px]"></i></button>
            <button onClick={() => setShowHistory(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center border-none active:scale-90 transition-all"><i className="fas fa-history text-[13px]"></i></button>
          </div>
        </div>
        <div className="text-center transition-all">
            <p className={`text-indigo-100 font-bold uppercase tracking-widest ${isShrunk ? 'hidden' : 'text-[13px] mb-2 animate-fadeIn'}`}>বর্তমান ব্যালেন্স</p>
            <h2 className={`font-black tracking-tighter text-white transition-all ${isShrunk ? 'text-[28px]' : 'text-[44px]'}`}>{formatCurrency(summary.balance)}</h2>
            {!isShrunk && (
              <div className="mt-6 grid grid-cols-2 gap-4 animate-fadeIn">
                <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10 shadow-sm text-center">
                  <p className="text-[11px] uppercase font-bold text-indigo-200 mb-1 tracking-wider">মোট আয়</p>
                  <p className="text-[18px] font-black text-green-300">৳{summary.totalIncome}</p>
                </div>
                <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10 shadow-sm text-center">
                  <p className="text-[11px] uppercase font-bold text-indigo-200 mb-1 tracking-wider">মোট ব্যয়</p>
                  <p className="text-[18px] font-black text-rose-300">৳{summary.totalExpenses}</p>
                </div>
              </div>
            )}
        </div>
      </header>

      {/* MAIN CONTENT AREA */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-4 scroll-smooth" onScroll={(e) => {
        const scrollPos = e.currentTarget.scrollTop;
        setIsShrunk(scrollPos > 40);
      }}>
        {activeTab === 'home' ? (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">

            {/* QUICK COMMAND BAR (OFFLINE FALLBACK) */}
            <div className={`transition-all duration-300 overflow-hidden ${showQuickInput ? 'max-h-24 opacity-100' : 'max-h-0 opacity-0'}`}>
               <form onSubmit={handleQuickSubmit} className="bg-white p-4 rounded-[25px] shadow-sm border border-indigo-100 flex gap-2">
                  <input 
                    type="text" 
                    value={quickInputVal}
                    onChange={(e) => setQuickInputVal(e.target.value)}
                    placeholder="যেমন: ৫০০ টাকা নাস্তা..." 
                    className="flex-1 bg-slate-50 border-none outline-none font-bold text-[13px] px-3 rounded-xl"
                  />
                  <button type="submit" className="bg-indigo-600 text-white w-10 h-10 rounded-xl flex items-center justify-center border-none active:scale-95"><i className="fas fa-paper-plane text-[12px]"></i></button>
               </form>
            </div>

            <div className="space-y-4">
              <div className="flex justify-between items-center px-2">
                <h3 className="font-black text-slate-400 text-[13px] uppercase tracking-widest">সাম্প্রতিক লেনদেন</h3>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3">
                  {transactions.slice(0, 6).map(t => (
                    <div key={t.id} className="bg-white p-4 rounded-[24px] shadow-sm flex items-center justify-between border border-slate-100">
                      <div className="flex items-center gap-4">
                        <div className={`w-10 h-10 rounded-2xl flex items-center justify-center text-lg ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-800 text-[14px]">{t.category}</p>
                          <p className="text-[11px] text-slate-400 font-bold uppercase">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <p className={`font-black text-[15px] ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                        {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                      </p>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="py-16 text-center border-2 border-dashed border-slate-200 rounded-[30px]">
                   <p className="text-slate-400 font-black text-[12px]">কোনো লেনদেন নেই</p>
                </div>
              )}
            </div>
            {/* BUDGET PREVIEW */}
            <div className="space-y-4">
               <h3 className="font-black text-slate-400 text-[13px] uppercase tracking-widest px-2">বাজেট ট্র্যাকার</h3>
               <div className="space-y-3">
                {EXPENSE_CATEGORIES.slice(0, 3).map(cat => {
                  const spent = summary.categoryBreakdown[cat] || 0;
                  const budget = budgets[cat] || 0;
                  if (budget === 0) return null;
                  return (
                    <div key={cat} className="bg-white p-4 rounded-[24px] shadow-sm border border-slate-100">
                      <div className="flex justify-between items-center mb-2 text-[12px] font-black">
                        <p>{cat}</p>
                        <p>৳{spent} / ৳{budget}</p>
                      </div>
                      <div className="w-full h-1.5 bg-slate-100 rounded-full overflow-hidden">
                        <div className={`h-full bg-indigo-500`} style={{ width: `${Math.min((spent / budget) * 100, 100)}%` }}></div>
                      </div>
                    </div>
                  );
                })}
               </div>
            </div>
          </div>
        ) : activeTab === 'analytics' ? (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
             <div className="bg-white p-8 rounded-[40px] border border-slate-100 shadow-sm space-y-6">
                <h3 className="font-black text-slate-800 uppercase text-[13px] tracking-widest">সাপ্তাহিক ট্রেন্ড</h3>
                <div className="flex items-end justify-between h-32 gap-2">
                   {analyticsData.last7Days.map((d, i) => (
                      <div key={i} className="flex-1 flex flex-col items-center gap-2">
                         <div className="w-full bg-indigo-600 rounded-full" style={{ height: `${Math.max((d.amount / (Math.max(...analyticsData.last7Days.map(x => x.amount)) || 1)) * 100, 5)}%` }}></div>
                         <span className="text-[10px] font-bold text-slate-400">{d.day}</span>
                      </div>
                   ))}
                </div>
             </div>
             <div className="grid grid-cols-2 gap-4">
                <div className="bg-white p-6 rounded-[35px] border border-slate-100 shadow-sm text-center">
                   <p className="text-[10px] font-black text-slate-400 uppercase mb-2">বাজেট ব্যবহার</p>
                   <div className="text-[24px] font-black">{analyticsData.budgetUsage.toFixed(0)}%</div>
                </div>
                <div className="bg-white p-6 rounded-[35px] border border-slate-100 shadow-sm text-center">
                   <p className="text-[10px] font-black text-slate-400 uppercase mb-2">দৈনিক গড়</p>
                   <div className="text-[24px] font-black text-indigo-600">৳{analyticsData.avgDailySpend.toFixed(0)}</div>
                </div>
             </div>
          </div>
        ) : (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
             <div className="bg-white p-8 rounded-[40px] border border-slate-100 shadow-sm space-y-6">
               <h3 className="font-black text-slate-800 uppercase text-[13px] tracking-widest">বিল স্প্লিটার</h3>
               <div className="space-y-4">
                 <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ মোট বিল" className="w-full p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
                 <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} placeholder="মানুষের সংখ্যা" className="w-full p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
               </div>
               <div className="p-8 bg-indigo-600 rounded-[30px] text-center text-white">
                 <p className="text-[11px] font-bold uppercase mb-2">মাথা পিছু</p>
                 <p className="text-[32px] font-black">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
               </div>
             </div>
          </div>
        )}
      </main>

      {/* VOICE FEEDBACK OVERLAY */}
      {voiceFeedback && (
        <div className="fixed bottom-44 left-6 right-6 z-[200] animate-fadeIn">
           <div className="bg-slate-900/90 backdrop-blur-md text-white px-6 py-4 rounded-[25px] shadow-2xl flex items-center gap-4 border border-white/10">
              <div className={`w-3 h-3 rounded-full ${isListening ? 'bg-rose-500 animate-pulse' : 'bg-green-500'}`}></div>
              <p className="text-[13px] font-black uppercase tracking-wide flex-1">{voiceFeedback}</p>
           </div>
        </div>
      )}

      {/* FLOATING ACTION BUTTONS */}
      <div className="fixed bottom-28 right-6 z-[100] flex flex-col items-end gap-4">

        {/* QUICK TEXT COMMAND BUTTON (BACKUP) */}
        <button 
          onClick={() => setShowQuickInput(!showQuickInput)} 
          className={`w-12 h-12 rounded-full flex items-center justify-center text-white text-lg shadow-lg transition-all active:scale-90 border-none ${showQuickInput ? 'bg-indigo-700' : 'bg-slate-700 shadow-slate-500/30'}`}
        >
          <i className="fas fa-keyboard text-[14px]"></i>
        </button>

        {/* VOICE INPUT BUTTON - PLACED ABOVE THE + BUTTON */}
        <button 
          onClick={startVoiceInput} 
          disabled={isListening}
          className={`w-14 h-14 rounded-full flex items-center justify-center text-white text-xl shadow-lg transition-all active:scale-90 border-none ${isListening ? 'bg-rose-600 animate-pulse' : 'bg-sky-500 shadow-sky-500/30'}`}
        >
          <i className={`fas ${isListening ? 'fa-circle-stop' : 'fa-microphone'}`}></i>
        </button>

        {showAddMenu && (
          <div className="flex flex-col gap-3 mb-2 animate-slideUp items-end">
            <button onClick={() => { setModalType(TransactionType.INCOME); setShowAddMenu(false); }} className="bg-green-600 text-white px-5 py-3 rounded-[18px] font-black text-[12px] uppercase tracking-widest shadow-xl border-none active:scale-95">
              <i className="fas fa-arrow-up mr-2"></i> আয়
            </button>
            <button onClick={() => { setModalType(TransactionType.EXPENSE); setShowAddMenu(false); }} className="bg-rose-600 text-white px-5 py-3 rounded-[18px] font-black text-[12px] uppercase tracking-widest shadow-xl border-none active:scale-95">
              <i className="fas fa-arrow-down mr-2"></i> ব্যয়
            </button>
          </div>
        )}

        {/* MAIN ADD BUTTON (+) */}
        <button onClick={() => setShowAddMenu(!showAddMenu)} className={`w-14 h-14 rounded-full flex items-center justify-center text-white text-xl shadow-[0_8px_25px_rgba(0,0,0,0.2)] transition-all active:scale-90 border-none ${showAddMenu ? 'bg-slate-800 rotate-45' : 'bg-slate-900'}`}>
          <i className="fas fa-plus"></i>
        </button>
      </div>

      {/* BOTTOM NAVIGATION */}
      <div className="fixed bottom-6 left-6 right-6 z-[150]">
        <nav className="bg-white/90 backdrop-blur-xl border border-slate-200/50 px-6 py-3 flex justify-between items-center rounded-[30px] shadow-xl overflow-hidden">
          <button onClick={() => setActiveTab('home')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'home' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-house-chimney text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">হোম</span>
          </button>
          <button onClick={() => setActiveTab('analytics')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'analytics' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-chart-pie text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">বিশ্লেষণ</span>
          </button>
          <button onClick={() => setActiveTab('tools')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'tools' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-layer-group text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">টুলস</span>
          </button>
        </nav>
      </div>

      {/* TRANSACTION MODAL */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/70 backdrop-blur-sm z-[200] flex items-center justify-center p-6 animate-fadeIn">
          <div className="bg-white w-full max-w-[340px] rounded-[35px] shadow-2xl border-none animate-slideUp overflow-hidden">
            <div className="p-8 space-y-6">
              <h2 className="text-[18px] font-black text-slate-800 text-center">{modalType === TransactionType.INCOME ? 'আয় যোগ' : 'ব্যয় যোগ'}</h2>
              <form onSubmit={addTransaction} className="space-y-6">
                <input ref={amountInputRef} type="number" name="amount" required placeholder="৳ টাকার পরিমাণ" className="w-full text-[24px] font-black text-center outline-none border-none bg-slate-50 py-4 rounded-[20px]" />
                <select name="category" className="w-full py-4 px-6 bg-slate-50 rounded-[20px] font-black text-slate-800 outline-none border-none">
                  {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                </select>
                <input type="text" name="note" placeholder="নোট..." className="w-full py-4 px-6 bg-slate-50 rounded-[20px] font-black outline-none border-none" />
                <button type="submit" className={`w-full py-5 rounded-[22px] text-white font-black uppercase text-[13px] tracking-widest border-none ${modalType === TransactionType.INCOME ? 'bg-green-600' : 'bg-rose-600'}`}>সংরক্ষণ করুন</button>
                <button type="button" onClick={() => setModalType(null)} className="w-full py-2 text-slate-400 font-black uppercase text-[10px] border-none bg-transparent">বাতিল করুন</button>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* BUDGET MODAL (SIMPLIFIED) */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[300] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[40px] p-8 animate-slideUp overflow-y-auto max-h-[85vh] border-none">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-[18px] font-black uppercase">বাজেট সেটিংস</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-8 h-8 bg-slate-100 rounded-xl flex items-center justify-center border-none"><i className="fas fa-times"></i></button>
             </div>
             <div className="space-y-6 mb-8">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="flex flex-col gap-2">
                    <label className="text-[11px] font-black text-slate-500 uppercase">{cat}</label>
                    <input type="number" value={budgets[cat] || ''} onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })} placeholder="৳ মাসিক বাজেট" className="w-full p-4 bg-slate-50 rounded-[18px] font-black outline-none border-none shadow-inner" />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-5 bg-indigo-600 text-white rounded-[22px] font-black uppercase text-[12px] border-none">সেভ করুন</button>
          </div>
        </div>
      )}

      {/* HISTORY MODAL (SIMPLIFIED) */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[300] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[40px] p-8 overflow-y-auto animate-slideUp border-none">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-[18px] font-black uppercase tracking-widest">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-8 h-8 bg-slate-100 rounded-xl flex items-center justify-center border-none"><i className="fas fa-times"></i></button>
             </div>
             <p className="text-center py-24 text-slate-400 font-black uppercase text-[12px]">পুরানো রেকর্ড নেই</p>
          </div>
        </div>
      )}

    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo, useRef } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary, ChatMessage } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights, chatWithAI } from './services/geminiService';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'analytics' | 'tools'>('home');
  const [isShrunk, setIsShrunk] = useState(false);
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));
  const amountInputRef = useRef<HTMLInputElement>(null);

  // Voice & Quick Command States
  const [isListening, setIsListening] = useState(false);
  const [voiceFeedback, setVoiceFeedback] = useState<string | null>(null);
  const [showQuickInput, setShowQuickInput] = useState(false);
  const [quickInputVal, setQuickInputVal] = useState('');

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {
      'খাবার': 5000,
      'যাতায়াত': 2000,
      'ভাড়া': 10000,
      'বিল': 3000,
      'কেনাকাটা': 4000,
      'স্বাস্থ্য': 2000,
      'অন্যান্য': 2000
    };
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showAddMenu, setShowAddMenu] = useState(false);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);

  // Tools state
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('');
  const [goalAmount, setGoalAmount] = useState('');
  const [goalMonths, setGoalMonths] = useState('');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  const summary = useMemo<FinancialSummary>(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum: number, t: Transaction) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum: number, t: Transaction) => sum + t.amount, 0);
    const categoryBreakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      categoryBreakdown[t.category] = (categoryBreakdown[t.category] || 0) + t.amount;
    });
    return { 
      totalIncome: income, 
      totalExpenses: expense, 
      balance: income - expense, 
      categoryBreakdown, 
      savings: income - expense, 
      previousMonthSavings: 0, 
      monthlyHistory: [] 
    };
  }, [transactions]);

  const analyticsData = useMemo(() => {
    // FIX: Added explicit cast to [string, number][] to ensure sort callback arguments are typed as numbers for subtraction
    const sortedBreakdown = (Object.entries(summary.categoryBreakdown) as [string, number][]).sort(([, a], [, b]) => b - a).slice(0, 5);
    const now = new Date();
    const last7Days = Array.from({ length: 7 }, (_, i) => {
      const d = new Date();
      d.setDate(now.getDate() - i);
      const dateStr = d.toISOString().split('T')[0];
      const dayTotal = transactions.filter(t => t.type === TransactionType.EXPENSE && t.date.startsWith(dateStr)).reduce((sum, t) => sum + t.amount, 0);
      return { day: d.toLocaleDateString('bn-BD', { weekday: 'short' }), amount: dayTotal };
    }).reverse();
    const totalBudget = (Object.values(budgets) as number[]).reduce((sum: number, b: number) => sum + b, 0);
    const budgetUsage = totalBudget > 0 ? (summary.totalExpenses / totalBudget) * 100 : 0;
    return { sortedBreakdown, last7Days, budgetUsage, avgDailySpend: summary.totalExpenses / Math.max(now.getDate(), 1) };
  }, [summary, transactions, budgets]);

  // HELPER: Bengali Digits to English
  const bengaliToEnglishDigits = (str: string) => {
    const bengaliDigits = ['০', '১', '২', '৩', '৪', '৫', '৬', '৭', '৮', '৯'];
    return str.replace(/[০-৯]/g, (w) => bengaliDigits.indexOf(w).toString());
  };

  // VOICE & QUICK COMMAND LOGIC
  const startVoiceInput = () => {
    const SpeechRecognition = (window as any).SpeechRecognition || (window as any).webkitSpeechRecognition;
    if (!SpeechRecognition) {
      setVoiceFeedback("ভয়েস সাপোর্ট নেই, সরাসরি টাইপ করুন।");
      setShowQuickInput(true);
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = 'bn-BD';
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;

    recognition.onstart = () => {
      setIsListening(true);
      setVoiceFeedback("শুনছি... বলুন (যেমন: ১০০ টাকা নাস্তা)");
    };

    recognition.onresult = (event: any) => {
      const transcript = event.results[0][0].transcript.toLowerCase();
      processCommand(transcript);
    };

    recognition.onerror = (event: any) => {
      setIsListening(false);
      if (event.error === 'network' || !navigator.onLine) {
        setVoiceFeedback("অফলাইন! কিবোর্ড আইকনে ক্লিক করে টাইপ করুন।");
      } else {
        setVoiceFeedback("বুঝতে পারিনি, আবার চেষ্টা করুন।");
      }
      setTimeout(() => setVoiceFeedback(null), 3000);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognition.start();
  };

  const processCommand = (text: string) => {
    const convertedText = bengaliToEnglishDigits(text);
    const amountMatch = convertedText.match(/\d+/);
    const amount = amountMatch ? parseFloat(amountMatch[0]) : null;

    if (!amount) {
      setVoiceFeedback("টাকার পরিমাণ খুঁজে পাওয়া যায়নি।");
      setTimeout(() => setVoiceFeedback(null), 3000);
      return;
    }

    let type = TransactionType.EXPENSE;
    if (text.includes("আয়") || text.includes("পেলাম") || text.includes("বেতন") || text.includes("লাভ") || text.includes("যোগ")) {
      type = TransactionType.INCOME;
    }

    let category = "অন্যান্য";
    const categoriesToCheck = type === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES;
    for (const cat of categoriesToCheck) {
      if (text.includes(cat)) {
        category = cat;
        break;
      }
    }

    const newTx: Transaction = {
      id: `tx_${Date.now()}`,
      type,
      amount,
      category,
      note: text,
      date: new Date().toISOString()
    };

    setTransactions(prev => [newTx, ...prev]);
    setVoiceFeedback(`৳${amount} ${category} হিসেবে যোগ হয়েছে।`);
    setShowQuickInput(false);
    setQuickInputVal('');
    setTimeout(() => setVoiceFeedback(null), 4000);
  };

  const handleQuickSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (quickInputVal.trim()) {
      processCommand(quickInputVal);
    }
  };

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
    setShowAddMenu(false);
  };

  return (
    <div className="max-w-md mx-auto h-screen bg-slate-50 shadow-2xl flex flex-col font-sans relative overflow-hidden text-slate-900 border-x border-slate-100">

      {/* HEADER */}
      <header className={`header-container bg-gradient-to-br from-indigo-600 to-indigo-800 text-white relative z-[60] shrink-0 transition-all duration-300 ${isShrunk ? 'p-4 rounded-b-[20px] shadow-lg' : 'p-8 rounded-b-[40px] shadow-xl'}`}>
        <div className="flex justify-between items-center mb-4 transition-all">
          <div className="flex items-center gap-3">
             <div className={`bg-white/10 backdrop-blur-md rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/20 transition-all ${isShrunk ? 'w-10 h-10' : 'w-14 h-14'}`}>
                {profileImage ? <img src={profileImage} className="w-full h-full object-cover" /> : <i className={`fas fa-user-circle ${isShrunk ? 'text-lg' : 'text-2xl'}`}></i>}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div className={isShrunk ? 'hidden' : 'block animate-fadeIn'}>
                <h1 className="font-black uppercase tracking-tight text-white text-[24px]">স্পার্ক</h1>
                <p className="text-[12px] font-bold text-indigo-200 uppercase tracking-widest opacity-90">পার্সোনাল ফিন্যান্স</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center border-none active:scale-90 transition-all"><i className="fas fa-bullseye text-[13px]"></i></button>
            <button onClick={() => setShowHistory(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center border-none active:scale-90 transition-all"><i className="fas fa-history text-[13px]"></i></button>
          </div>
        </div>
        <div className="text-center transition-all">
            <p className={`text-indigo-100 font-bold uppercase tracking-widest ${isShrunk ? 'hidden' : 'text-[13px] mb-2 animate-fadeIn'}`}>বর্তমান ব্যালেন্স</p>
            <h2 className={`font-black tracking-tighter text-white transition-all ${isShrunk ? 'text-[28px]' : 'text-[44px]'}`}>{formatCurrency(summary.balance)}</h2>
            {!isShrunk && (
              <div className="mt-6 grid grid-cols-2 gap-4 animate-fadeIn">
                <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10 shadow-sm text-center">
                  <p className="text-[11px] uppercase font-bold text-indigo-200 mb-1 tracking-wider">মোট আয়</p>
                  <p className="text-[18px] font-black text-green-300">৳{summary.totalIncome}</p>
                </div>
                <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10 shadow-sm text-center">
                  <p className="text-[11px] uppercase font-bold text-indigo-200 mb-1 tracking-wider">মোট ব্যয়</p>
                  <p className="text-[18px] font-black text-rose-300">৳{summary.totalExpenses}</p>
                </div>
              </div>
            )}
        </div>
      </header>

      {/* MAIN CONTENT AREA */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-4 scroll-smooth" onScroll={(e) => {
        const scrollPos = e.currentTarget.scrollTop;
        setIsShrunk(scrollPos > 40);
      }}>
        {activeTab === 'home' ? (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">

            {/* QUICK COMMAND BAR (OFFLINE FALLBACK) */}
            <div className={`transition-all duration-300 overflow-hidden ${showQuickInput ? 'max-h-24 opacity-100' : 'max-h-0 opacity-0'}`}>
               <form onSubmit={handleQuickSubmit} className="bg-white p-4 rounded-[25px] shadow-sm border border-indigo-100 flex gap-2">
                  <input 
                    type="text" 
                    value={quickInputVal}
                    onChange={(e) => setQuickInputVal(e.target.value)}
                    placeholder="যেমন: ৫০০ টাকা নাস্তা..." 
                    className="flex-1 bg-slate-50 border-none outline-none font-bold text-[13px] px-3 rounded-xl"
                  />
                  <button type="submit" className="bg-indigo-600 text-white w-10 h-10 rounded-xl flex items-center justify-center border-none active:scale-95"><i className="fas fa-paper-plane text-[12px]"></i></button>
               </form>
            </div>

            <div className="space-y-4">
              <div className="flex justify-between items-center px-2">
                <h3 className="font-black text-slate-400 text-[13px] uppercase tracking-widest">সাম্প্রতিক লেনদেন</h3>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3">
                  {transactions.slice(0, 6).map(t => (
                    <div key={t.id} className="bg-white p-4 rounded-[24px] shadow-sm flex items-center justify-between border border-slate-100">
                      <div className="flex items-center gap-4">
                        <div className={`w-10 h-10 rounded-2xl flex items-center justify-center text-lg ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-800 text-[14px]">{t.category}</p>
                          <p className="text-[11px] text-slate-400 font-bold uppercase">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <p className={`font-black text-[15px] ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                        {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                      </p>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="py-16 text-center border-2 border-dashed border-slate-200 rounded-[30px]">
                   <p className="text-slate-400 font-black text-[12px]">কোনো লেনদেন নেই</p>
                </div>
              )}
            </div>
            {/* BUDGET PREVIEW */}
            <div className="space-y-4">
               <h3 className="font-black text-slate-400 text-[13px] uppercase tracking-widest px-2">বাজেট ট্র্যাকার</h3>
               <div className="space-y-3">
                {EXPENSE_CATEGORIES.slice(0, 3).map(cat => {
                  const spent = summary.categoryBreakdown[cat] || 0;
                  const budget = budgets[cat] || 0;
                  if (budget === 0) return null;
                  return (
                    <div key={cat} className="bg-white p-4 rounded-[24px] shadow-sm border border-slate-100">
                      <div className="flex justify-between items-center mb-2 text-[12px] font-black">
                        <p>{cat}</p>
                        <p>৳{spent} / ৳{budget}</p>
                      </div>
                      <div className="w-full h-1.5 bg-slate-100 rounded-full overflow-hidden">
                        <div className={`h-full bg-indigo-500`} style={{ width: `${Math.min((spent / budget) * 100, 100)}%` }}></div>
                      </div>
                    </div>
                  );
                })}
               </div>
            </div>
          </div>
        ) : activeTab === 'analytics' ? (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
             <div className="bg-white p-8 rounded-[40px] border border-slate-100 shadow-sm space-y-6">
                <h3 className="font-black text-slate-800 uppercase text-[13px] tracking-widest">সাপ্তাহিক ট্রেন্ড</h3>
                <div className="flex items-end justify-between h-32 gap-2">
                   {analyticsData.last7Days.map((d, i) => (
                      <div key={i} className="flex-1 flex flex-col items-center gap-2">
                         <div className="w-full bg-indigo-600 rounded-full" style={{ height: `${Math.max((d.amount / (Math.max(...analyticsData.last7Days.map(x => x.amount)) || 1)) * 100, 5)}%` }}></div>
                         <span className="text-[10px] font-bold text-slate-400">{d.day}</span>
                      </div>
                   ))}
                </div>
             </div>
             <div className="grid grid-cols-2 gap-4">
                <div className="bg-white p-6 rounded-[35px] border border-slate-100 shadow-sm text-center">
                   <p className="text-[10px] font-black text-slate-400 uppercase mb-2">বাজেট ব্যবহার</p>
                   <div className="text-[24px] font-black">{analyticsData.budgetUsage.toFixed(0)}%</div>
                </div>
                <div className="bg-white p-6 rounded-[35px] border border-slate-100 shadow-sm text-center">
                   <p className="text-[10px] font-black text-slate-400 uppercase mb-2">দৈনিক গড়</p>
                   <div className="text-[24px] font-black text-indigo-600">৳{analyticsData.avgDailySpend.toFixed(0)}</div>
                </div>
             </div>
          </div>
        ) : (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
             <div className="bg-white p-8 rounded-[40px] border border-slate-100 shadow-sm space-y-6">
               <h3 className="font-black text-slate-800 uppercase text-[13px] tracking-widest">বিল স্প্লিটার</h3>
               <div className="space-y-4">
                 <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ মোট বিল" className="w-full p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
                 <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} placeholder="মানুষের সংখ্যা" className="w-full p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
               </div>
               <div className="p-8 bg-indigo-600 rounded-[30px] text-center text-white">
                 <p className="text-[11px] font-bold uppercase mb-2">মাথা পিছু</p>
                 <p className="text-[32px] font-black">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
               </div>
             </div>
          </div>
        )}
      </main>

      {/* VOICE FEEDBACK OVERLAY */}
      {voiceFeedback && (
        <div className="fixed bottom-44 left-6 right-6 z-[200] animate-fadeIn">
           <div className="bg-slate-900/90 backdrop-blur-md text-white px-6 py-4 rounded-[25px] shadow-2xl flex items-center gap-4 border border-white/10">
              <div className={`w-3 h-3 rounded-full ${isListening ? 'bg-rose-500 animate-pulse' : 'bg-green-500'}`}></div>
              <p className="text-[13px] font-black uppercase tracking-wide flex-1">{voiceFeedback}</p>
           </div>
        </div>
      )}

      {/* FLOATING ACTION BUTTONS */}
      <div className="fixed bottom-28 right-6 z-[100] flex flex-col items-end gap-4">

        {/* QUICK TEXT COMMAND BUTTON (BACKUP) */}
        <button 
          onClick={() => setShowQuickInput(!showQuickInput)} 
          className={`w-12 h-12 rounded-full flex items-center justify-center text-white text-lg shadow-lg transition-all active:scale-90 border-none ${showQuickInput ? 'bg-indigo-700' : 'bg-slate-700 shadow-slate-500/30'}`}
        >
          <i className="fas fa-keyboard text-[14px]"></i>
        </button>

        {/* VOICE INPUT BUTTON - PLACED ABOVE THE + BUTTON */}
        <button 
          onClick={startVoiceInput} 
          disabled={isListening}
          className={`w-14 h-14 rounded-full flex items-center justify-center text-white text-xl shadow-lg transition-all active:scale-90 border-none ${isListening ? 'bg-rose-600 animate-pulse' : 'bg-sky-500 shadow-sky-500/30'}`}
        >
          <i className={`fas ${isListening ? 'fa-circle-stop' : 'fa-microphone'}`}></i>
        </button>

        {showAddMenu && (
          <div className="flex flex-col gap-3 mb-2 animate-slideUp items-end">
            <button onClick={() => { setModalType(TransactionType.INCOME); setShowAddMenu(false); }} className="bg-green-600 text-white px-5 py-3 rounded-[18px] font-black text-[12px] uppercase tracking-widest shadow-xl border-none active:scale-95">
              <i className="fas fa-arrow-up mr-2"></i> আয়
            </button>
            <button onClick={() => { setModalType(TransactionType.EXPENSE); setShowAddMenu(false); }} className="bg-rose-600 text-white px-5 py-3 rounded-[18px] font-black text-[12px] uppercase tracking-widest shadow-xl border-none active:scale-95">
              <i className="fas fa-arrow-down mr-2"></i> ব্যয়
            </button>
          </div>
        )}

        {/* MAIN ADD BUTTON (+) */}
        <button onClick={() => setShowAddMenu(!showAddMenu)} className={`w-14 h-14 rounded-full flex items-center justify-center text-white text-xl shadow-[0_8px_25px_rgba(0,0,0,0.2)] transition-all active:scale-90 border-none ${showAddMenu ? 'bg-slate-800 rotate-45' : 'bg-slate-900'}`}>
          <i className="fas fa-plus"></i>
        </button>
      </div>

      {/* BOTTOM NAVIGATION */}
      <div className="fixed bottom-6 left-6 right-6 z-[150]">
        <nav className="bg-white/90 backdrop-blur-xl border border-slate-200/50 px-6 py-3 flex justify-between items-center rounded-[30px] shadow-xl overflow-hidden">
          <button onClick={() => setActiveTab('home')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'home' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-house-chimney text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">হোম</span>
          </button>
          <button onClick={() => setActiveTab('analytics')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'analytics' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-chart-pie text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">বিশ্লেষণ</span>
          </button>
          <button onClick={() => setActiveTab('tools')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'tools' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-layer-group text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">টুলস</span>
          </button>
        </nav>
      </div>

      {/* TRANSACTION MODAL */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/70 backdrop-blur-sm z-[200] flex items-center justify-center p-6 animate-fadeIn">
          <div className="bg-white w-full max-w-[340px] rounded-[35px] shadow-2xl border-none animate-slideUp overflow-hidden">
            <div className="p-8 space-y-6">
              <h2 className="text-[18px] font-black text-slate-800 text-center">{modalType === TransactionType.INCOME ? 'আয় যোগ' : 'ব্যয় যোগ'}</h2>
              <form onSubmit={addTransaction} className="space-y-6">
                <input ref={amountInputRef} type="number" name="amount" required placeholder="৳ টাকার পরিমাণ" className="w-full text-[24px] font-black text-center outline-none border-none bg-slate-50 py-4 rounded-[20px]" />
                <select name="category" className="w-full py-4 px-6 bg-slate-50 rounded-[20px] font-black text-slate-800 outline-none border-none">
                  {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                </select>
                <input type="text" name="note" placeholder="নোট..." className="w-full py-4 px-6 bg-slate-50 rounded-[20px] font-black outline-none border-none" />
                <button type="submit" className={`w-full py-5 rounded-[22px] text-white font-black uppercase text-[13px] tracking-widest border-none ${modalType === TransactionType.INCOME ? 'bg-green-600' : 'bg-rose-600'}`}>সংরক্ষণ করুন</button>
                <button type="button" onClick={() => setModalType(null)} className="w-full py-2 text-slate-400 font-black uppercase text-[10px] border-none bg-transparent">বাতিল করুন</button>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* BUDGET MODAL (SIMPLIFIED) */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[300] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[40px] p-8 animate-slideUp overflow-y-auto max-h-[85vh] border-none">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-[18px] font-black uppercase">বাজেট সেটিংস</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-8 h-8 bg-slate-100 rounded-xl flex items-center justify-center border-none"><i className="fas fa-times"></i></button>
             </div>
             <div className="space-y-6 mb-8">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="flex flex-col gap-2">
                    <label className="text-[11px] font-black text-slate-500 uppercase">{cat}</label>
                    <input type="number" value={budgets[cat] || ''} onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })} placeholder="৳ মাসিক বাজেট" className="w-full p-4 bg-slate-50 rounded-[18px] font-black outline-none border-none shadow-inner" />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-5 bg-indigo-600 text-white rounded-[22px] font-black uppercase text-[12px] border-none">সেভ করুন</button>
          </div>
        </div>
      )}

      {/* HISTORY MODAL (SIMPLIFIED) */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[300] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[40px] p-8 overflow-y-auto animate-slideUp border-none">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-[18px] font-black uppercase tracking-widest">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-8 h-8 bg-slate-100 rounded-xl flex items-center justify-center border-none"><i className="fas fa-times"></i></button>
             </div>
             <p className="text-center py-24 text-slate-400 font-black uppercase text-[12px]">পুরানো রেকর্ড নেই</p>
          </div>
        </div>
      )}

    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo, useRef } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'analytics' | 'tools'>('home');
  const [isShrunk, setIsShrunk] = useState(false);
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));
  const amountInputRef = useRef<HTMLInputElement>(null);

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {
      'খাবার': 5000,
      'যাতায়াত': 2000,
      'ভাড়া': 10000,
      'বিল': 3000,
      'কেনাকাটা': 4000,
      'স্বাস্থ্য': 2000,
      'অন্যান্য': 2000
    };
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showAddMenu, setShowAddMenu] = useState(false);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);

  // Tools state
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('');
  const [goalAmount, setGoalAmount] = useState('');
  const [goalMonths, setGoalMonths] = useState('');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  const summary = useMemo<FinancialSummary>(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum: number, t: Transaction) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum: number, t: Transaction) => sum + t.amount, 0);
    const categoryBreakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      categoryBreakdown[t.category] = (categoryBreakdown[t.category] || 0) + t.amount;
    });
    return { 
      totalIncome: income, 
      totalExpenses: expense, 
      balance: income - expense, 
      categoryBreakdown, 
      savings: income - expense, 
      previousMonthSavings: 0, 
      monthlyHistory: [] 
    };
  }, [transactions]);

  const analyticsData = useMemo(() => {
    const sortedBreakdown = (Object.entries(summary.categoryBreakdown) as [string, number][]).sort(([, a], [, b]) => b - a).slice(0, 5);
    const now = new Date();
    const last7Days = Array.from({ length: 7 }, (_, i) => {
      const d = new Date();
      d.setDate(now.getDate() - i);
      const dateStr = d.toISOString().split('T')[0];
      const dayTotal = transactions.filter(t => t.type === TransactionType.EXPENSE && t.date.startsWith(dateStr)).reduce((sum, t) => sum + t.amount, 0);
      return { day: d.toLocaleDateString('bn-BD', { weekday: 'short' }), amount: dayTotal };
    }).reverse();
    const totalBudget = (Object.values(budgets) as number[]).reduce((sum: number, b: number) => sum + b, 0);
    const budgetUsage = totalBudget > 0 ? (summary.totalExpenses / totalBudget) * 100 : 0;
    return { sortedBreakdown, last7Days, budgetUsage, avgDailySpend: summary.totalExpenses / Math.max(now.getDate(), 1) };
  }, [summary, transactions, budgets]);

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
    setShowAddMenu(false);
  };

  const fetchAIAdvice = async () => {
    if (transactions.length === 0) return;
    setLoadingAI(true);
    try {
      const results = await getFinancialInsights(transactions, summary);
      setInsights(results);
    } catch (error) {
      console.error("AI Error:", error);
    } finally {
      setLoadingAI(false);
    }
  };

  return (
    <div className="max-w-md mx-auto h-screen bg-slate-50 shadow-2xl flex flex-col font-sans relative overflow-hidden text-slate-900 border-x border-slate-100">

      {/* HEADER */}
      <header className={`header-container bg-gradient-to-br from-indigo-600 to-indigo-800 text-white relative z-[60] shrink-0 transition-all duration-300 ${isShrunk ? 'p-4 rounded-b-[20px] shadow-lg' : 'p-8 rounded-b-[40px] shadow-xl'}`}>
        <div className="flex justify-between items-center mb-4 transition-all">
          <div className="flex items-center gap-3">
             <div className={`bg-white/10 backdrop-blur-md rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/20 transition-all ${isShrunk ? 'w-10 h-10' : 'w-14 h-14'}`}>
                {profileImage ? <img src={profileImage} className="w-full h-full object-cover" /> : <i className={`fas fa-user-circle ${isShrunk ? 'text-lg' : 'text-2xl'}`}></i>}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div className={isShrunk ? 'hidden' : 'block animate-fadeIn'}>
                <h1 className="font-black uppercase tracking-tight text-white text-[24px]">স্পার্ক</h1>
                <p className="text-[12px] font-bold text-indigo-200 uppercase tracking-widest opacity-90">পার্সোনাল ফিন্যান্স</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center border-none active:scale-90 transition-all"><i className="fas fa-bullseye text-[13px]"></i></button>
            <button onClick={() => setShowHistory(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center border-none active:scale-90 transition-all"><i className="fas fa-history text-[13px]"></i></button>
          </div>
        </div>
        <div className="text-center transition-all">
            <p className={`text-indigo-100 font-bold uppercase tracking-widest ${isShrunk ? 'hidden' : 'text-[13px] mb-2 animate-fadeIn'}`}>বর্তমান ব্যালেন্স</p>
            <h2 className={`font-black tracking-tighter text-white transition-all ${isShrunk ? 'text-[28px]' : 'text-[44px]'}`}>{formatCurrency(summary.balance)}</h2>
            {!isShrunk && (
              <div className="mt-6 grid grid-cols-2 gap-4 animate-fadeIn">
                <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10 shadow-sm text-center">
                  <p className="text-[11px] uppercase font-bold text-indigo-200 mb-1 tracking-wider">মোট আয়</p>
                  <p className="text-[18px] font-black text-green-300">৳{summary.totalIncome}</p>
                </div>
                <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10 shadow-sm text-center">
                  <p className="text-[11px] uppercase font-bold text-indigo-200 mb-1 tracking-wider">মোট ব্যয়</p>
                  <p className="text-[18px] font-black text-rose-300">৳{summary.totalExpenses}</p>
                </div>
              </div>
            )}
        </div>
      </header>

      {/* MAIN CONTENT AREA */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-4 scroll-smooth" onScroll={(e) => {
        const scrollPos = e.currentTarget.scrollTop;
        setIsShrunk(scrollPos > 40);
      }}>
        {activeTab === 'home' ? (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
            <div className="space-y-4">
              <div className="flex justify-between items-center px-2">
                <h3 className="font-black text-slate-400 text-[13px] uppercase tracking-widest">সাম্প্রতিক লেনদেন</h3>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3">
                  {transactions.slice(0, 6).map(t => (
                    <div key={t.id} className="bg-white p-4 rounded-[24px] shadow-sm flex items-center justify-between border border-slate-100">
                      <div className="flex items-center gap-4">
                        <div className={`w-10 h-10 rounded-2xl flex items-center justify-center text-lg ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-800 text-[14px]">{t.category}</p>
                          <p className="text-[11px] text-slate-400 font-bold uppercase">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <p className={`font-black text-[15px] ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                        {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                      </p>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="py-16 text-center border-2 border-dashed border-slate-200 rounded-[30px]">
                   <p className="text-slate-400 font-black text-[12px]">কোনো লেনদেন নেই</p>
                </div>
              )}
            </div>
            {/* BUDGET PREVIEW */}
            <div className="space-y-4">
               <h3 className="font-black text-slate-400 text-[13px] uppercase tracking-widest px-2">বাজেট ট্র্যাকার</h3>
               <div className="space-y-3">
                {EXPENSE_CATEGORIES.slice(0, 3).map(cat => {
                  const spent = summary.categoryBreakdown[cat] || 0;
                  const budget = budgets[cat] || 0;
                  if (budget === 0) return null;
                  return (
                    <div key={cat} className="bg-white p-4 rounded-[24px] shadow-sm border border-slate-100">
                      <div className="flex justify-between items-center mb-2 text-[12px] font-black">
                        <p>{cat}</p>
                        <p>৳{spent} / ৳{budget}</p>
                      </div>
                      <div className="w-full h-1.5 bg-slate-100 rounded-full overflow-hidden">
                        <div className={`h-full bg-indigo-500`} style={{ width: `${Math.min((spent / budget) * 100, 100)}%` }}></div>
                      </div>
                    </div>
                  );
                })}
               </div>
            </div>
          </div>
        ) : activeTab === 'analytics' ? (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
             <div className="bg-white p-8 rounded-[40px] border border-slate-100 shadow-sm space-y-6">
                <h3 className="font-black text-slate-800 uppercase text-[13px] tracking-widest">সাপ্তাহিক ট্রেন্ড</h3>
                <div className="flex items-end justify-between h-32 gap-2">
                   {analyticsData.last7Days.map((d, i) => (
                      <div key={i} className="flex-1 flex flex-col items-center gap-2">
                         <div className="w-full bg-indigo-600 rounded-full" style={{ height: `${Math.max((d.amount / (Math.max(...analyticsData.last7Days.map(x => x.amount)) || 1)) * 100, 5)}%` }}></div>
                         <span className="text-[10px] font-bold text-slate-400">{d.day}</span>
                      </div>
                   ))}
                </div>
             </div>
             <div className="grid grid-cols-2 gap-4">
                <div className="bg-white p-6 rounded-[35px] border border-slate-100 shadow-sm text-center">
                   <p className="text-[10px] font-black text-slate-400 uppercase mb-2">বাজেট ব্যবহার</p>
                   <div className="text-[24px] font-black">{analyticsData.budgetUsage.toFixed(0)}%</div>
                </div>
                <div className="bg-white p-6 rounded-[35px] border border-slate-100 shadow-sm text-center">
                   <p className="text-[10px] font-black text-slate-400 uppercase mb-2">দৈনিক গড়</p>
                   <div className="text-[24px] font-black text-indigo-600">৳{analyticsData.avgDailySpend.toFixed(0)}</div>
                </div>
             </div>
             <div className="bg-slate-900 p-8 rounded-[40px] text-white shadow-2xl relative overflow-hidden">
                <h3 className="text-[13px] font-black uppercase tracking-widest text-indigo-400 mb-4">এআই পর্যবেক্ষণ</h3>
                {insights.length > 0 ? (
                  <div className="space-y-4 animate-fadeIn">
                    {insights.slice(0, 1).map((insight, idx) => (
                      <div key={idx} className="space-y-2">
                        <p className="font-black text-[18px] text-white">{insight.title}</p>
                        <p className="text-slate-400 text-[14px] font-bold">{insight.description}</p>
                      </div>
                    ))}
                  </div>
                ) : (
                  <button onClick={fetchAIAdvice} disabled={loadingAI} className="w-full py-4 bg-indigo-600 rounded-2xl font-black uppercase text-[11px] tracking-widest active:scale-95 transition-all">
                    {loadingAI ? 'বিশ্লেষণ হচ্ছে...' : 'এআই পরামর্শ পান'}
                  </button>
                )}
             </div>
          </div>
        ) : (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
             <div className="bg-white p-8 rounded-[40px] border border-slate-100 shadow-sm space-y-6">
               <h3 className="font-black text-slate-800 uppercase text-[13px] tracking-widest">বিল স্প্লিটার</h3>
               <div className="space-y-4">
                 <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ মোট বিল" className="w-full p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
                 <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} placeholder="মানুষের সংখ্যা" className="w-full p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
               </div>
               <div className="p-8 bg-indigo-600 rounded-[30px] text-center text-white">
                 <p className="text-[11px] font-bold uppercase mb-2">মাথা পিছু</p>
                 <p className="text-[32px] font-black">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
               </div>
             </div>
          </div>
        )}
      </main>

      {/* FLOATING ACTION BUTTONS */}
      <div className="fixed bottom-28 right-6 z-[100] flex flex-col items-end gap-4">
        {showAddMenu && (
          <div className="flex flex-col gap-3 mb-2 animate-slideUp items-end">
            <button onClick={() => { setModalType(TransactionType.INCOME); setShowAddMenu(false); }} className="bg-green-600 text-white px-5 py-3 rounded-[18px] font-black text-[12px] uppercase tracking-widest shadow-xl border-none active:scale-95">
              <i className="fas fa-arrow-up mr-2"></i> আয়
            </button>
            <button onClick={() => { setModalType(TransactionType.EXPENSE); setShowAddMenu(false); }} className="bg-rose-600 text-white px-5 py-3 rounded-[18px] font-black text-[12px] uppercase tracking-widest shadow-xl border-none active:scale-95">
              <i className="fas fa-arrow-down mr-2"></i> ব্যয়
            </button>
          </div>
        )}

        {/* MAIN ADD BUTTON (+) */}
        <button onClick={() => setShowAddMenu(!showAddMenu)} className={`w-14 h-14 rounded-full flex items-center justify-center text-white text-xl shadow-[0_8px_25px_rgba(0,0,0,0.2)] transition-all active:scale-90 border-none ${showAddMenu ? 'bg-slate-800 rotate-45' : 'bg-slate-900'}`}>
          <i className="fas fa-plus"></i>
        </button>
      </div>

      {/* BOTTOM NAVIGATION */}
      <div className="fixed bottom-6 left-6 right-6 z-[150]">
        <nav className="bg-white/90 backdrop-blur-xl border border-slate-200/50 px-6 py-3 flex justify-between items-center rounded-[30px] shadow-xl overflow-hidden">
          <button onClick={() => setActiveTab('home')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'home' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-house-chimney text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">হোম</span>
          </button>
          <button onClick={() => setActiveTab('analytics')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'analytics' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-chart-pie text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">বিশ্লেষণ</span>
          </button>
          <button onClick={() => setActiveTab('tools')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'tools' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-layer-group text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">টুলস</span>
          </button>
        </nav>
      </div>

      {/* TRANSACTION MODAL */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/70 backdrop-blur-sm z-[200] flex items-center justify-center p-6 animate-fadeIn">
          <div className="bg-white w-full max-w-[340px] rounded-[35px] shadow-2xl border-none animate-slideUp overflow-hidden">
            <div className="p-8 space-y-6">
              <h2 className="text-[18px] font-black text-slate-800 text-center">{modalType === TransactionType.INCOME ? 'আয় যোগ' : 'ব্যয় যোগ'}</h2>
              <form onSubmit={addTransaction} className="space-y-6">
                <input ref={amountInputRef} type="number" name="amount" required placeholder="৳ টাকার পরিমাণ" className="w-full text-[24px] font-black text-center outline-none border-none bg-slate-50 py-4 rounded-[20px]" />
                <select name="category" className="w-full py-4 px-6 bg-slate-50 rounded-[20px] font-black text-slate-800 outline-none border-none">
                  {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                </select>
                <input type="text" name="note" placeholder="নোট..." className="w-full py-4 px-6 bg-slate-50 rounded-[20px] font-black outline-none border-none" />
                <button type="submit" className={`w-full py-5 rounded-[22px] text-white font-black uppercase text-[13px] tracking-widest border-none ${modalType === TransactionType.INCOME ? 'bg-green-600' : 'bg-rose-600'}`}>সংরক্ষণ করুন</button>
                <button type="button" onClick={() => setModalType(null)} className="w-full py-2 text-slate-400 font-black uppercase text-[10px] border-none bg-transparent">বাতিল করুন</button>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* BUDGET MODAL (SIMPLIFIED) */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[300] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[40px] p-8 animate-slideUp overflow-y-auto max-h-[85vh] border-none">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-[18px] font-black uppercase">বাজেট সেটিংস</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-8 h-8 bg-slate-100 rounded-xl flex items-center justify-center border-none"><i className="fas fa-times"></i></button>
             </div>
             <div className="space-y-6 mb-8">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="flex flex-col gap-2">
                    <label className="text-[11px] font-black text-slate-500 uppercase">{cat}</label>
                    <input type="number" value={budgets[cat] || ''} onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })} placeholder="৳ মাসিক বাজেট" className="w-full p-4 bg-slate-50 rounded-[18px] font-black outline-none border-none shadow-inner" />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-5 bg-indigo-600 text-white rounded-[22px] font-black uppercase text-[12px] border-none">সেভ করুন</button>
          </div>
        </div>
      )}

      {/* HISTORY MODAL (SIMPLIFIED) */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[300] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[40px] p-8 overflow-y-auto animate-slideUp border-none">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-[18px] font-black uppercase tracking-widest">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-8 h-8 bg-slate-100 rounded-xl flex items-center justify-center border-none"><i className="fas fa-times"></i></button>
             </div>
             <p className="text-center py-24 text-slate-400 font-black uppercase text-[12px]">পুরানো রেকর্ড নেই</p>
          </div>
        </div>
      )}

    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo, useRef } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'analytics' | 'tools'>('home');
  const [isShrunk, setIsShrunk] = useState(false);
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));
  const amountInputRef = useRef<HTMLInputElement>(null);

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {
      'খাবার': 5000,
      'যাতায়াত': 2000,
      'ভাড়া': 10000,
      'বিল': 3000,
      'কেনাকাটা': 4000,
      'স্বাস্থ্য': 2000,
      'অন্যান্য': 2000
    };
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showAddMenu, setShowAddMenu] = useState(false);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);

  // Tools state
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  const summary = useMemo<FinancialSummary>(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum: number, t: Transaction) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum: number, t: Transaction) => sum + t.amount, 0);
    const categoryBreakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      categoryBreakdown[t.category] = (categoryBreakdown[t.category] || 0) + t.amount;
    });
    return { 
      totalIncome: income, 
      totalExpenses: expense, 
      balance: income - expense, 
      categoryBreakdown, 
      savings: income - expense, 
      previousMonthSavings: 0, 
      monthlyHistory: [] 
    };
  }, [transactions]);

  const analyticsData = useMemo(() => {
    const sortedBreakdown = (Object.entries(summary.categoryBreakdown) as [string, number][]).sort(([, a], [, b]) => b - a).slice(0, 5);
    const now = new Date();
    const last7Days = Array.from({ length: 7 }, (_, i) => {
      const d = new Date();
      d.setDate(now.getDate() - i);
      const dateStr = d.toISOString().split('T')[0];
      const dayTotal = transactions.filter(t => t.type === TransactionType.EXPENSE && t.date.startsWith(dateStr)).reduce((sum, t) => sum + t.amount, 0);
      return { day: d.toLocaleDateString('bn-BD', { weekday: 'short' }), amount: dayTotal };
    }).reverse();
    const totalBudget = (Object.values(budgets) as number[]).reduce((sum: number, b: number) => sum + b, 0);
    const budgetUsage = totalBudget > 0 ? (summary.totalExpenses / totalBudget) * 100 : 0;
    return { sortedBreakdown, last7Days, budgetUsage, avgDailySpend: summary.totalExpenses / Math.max(now.getDate(), 1) };
  }, [summary, transactions, budgets]);

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
    setShowAddMenu(false);
  };

  const fetchAIAdvice = async () => {
    if (transactions.length === 0) return;
    setLoadingAI(true);
    try {
      const results = await getFinancialInsights(transactions, summary);
      setInsights(results);
    } catch (error) {
      console.error("AI Error:", error);
    } finally {
      setLoadingAI(false);
    }
  };

  return (
    <div className="max-w-md mx-auto h-screen bg-slate-50 shadow-2xl flex flex-col font-sans relative overflow-hidden text-slate-900 border-x border-slate-100">

      {/* HEADER */}
      <header className={`header-container bg-gradient-to-br from-indigo-600 to-indigo-800 text-white relative z-[60] shrink-0 transition-all duration-300 ${isShrunk ? 'p-4 rounded-b-[20px] shadow-lg' : 'p-8 rounded-b-[40px] shadow-xl'}`}>
        <div className="flex justify-between items-center mb-4 transition-all">
          <div className="flex items-center gap-3">
             <div className={`bg-white/10 backdrop-blur-md rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/20 transition-all ${isShrunk ? 'w-10 h-10' : 'w-14 h-14'}`}>
                {profileImage ? <img src={profileImage} className="w-full h-full object-cover" /> : <i className={`fas fa-user-circle ${isShrunk ? 'text-lg' : 'text-2xl'}`}></i>}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div className={isShrunk ? 'hidden' : 'block animate-fadeIn'}>
                <h1 className="font-black uppercase tracking-tight text-white text-[24px]">স্পার্ক</h1>
                <p className="text-[12px] font-bold text-indigo-200 uppercase tracking-widest opacity-90">পার্সোনাল ফিন্যান্স</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center border-none active:scale-90 transition-all"><i className="fas fa-bullseye text-[13px]"></i></button>
            <button onClick={() => setShowHistory(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center border-none active:scale-90 transition-all"><i className="fas fa-history text-[13px]"></i></button>
          </div>
        </div>
        <div className="text-center transition-all">
            <p className={`text-indigo-100 font-bold uppercase tracking-widest ${isShrunk ? 'hidden' : 'text-[13px] mb-2 animate-fadeIn'}`}>বর্তমান ব্যালেন্স</p>
            <h2 className={`font-black tracking-tighter text-white transition-all ${isShrunk ? 'text-[28px]' : 'text-[44px]'}`}>{formatCurrency(summary.balance)}</h2>
            {!isShrunk && (
              <div className="mt-6 grid grid-cols-2 gap-4 animate-fadeIn">
                <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10 shadow-sm text-center">
                  <p className="text-[11px] uppercase font-bold text-indigo-200 mb-1 tracking-wider">মোট আয়</p>
                  <p className="text-[18px] font-black text-green-300">৳{summary.totalIncome}</p>
                </div>
                <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10 shadow-sm text-center">
                  <p className="text-[11px] uppercase font-bold text-indigo-200 mb-1 tracking-wider">মোট ব্যয়</p>
                  <p className="text-[18px] font-black text-rose-300">৳{summary.totalExpenses}</p>
                </div>
              </div>
            )}
        </div>
      </header>

      {/* MAIN CONTENT AREA */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-4 scroll-smooth" onScroll={(e) => {
        const scrollPos = e.currentTarget.scrollTop;
        setIsShrunk(scrollPos > 40);
      }}>
        {activeTab === 'home' ? (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
            <div className="space-y-4">
              <div className="flex justify-between items-center px-2">
                <h3 className="font-black text-slate-400 text-[13px] uppercase tracking-widest">সাম্প্রতিক লেনদেন</h3>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3">
                  {transactions.slice(0, 6).map(t => (
                    <div key={t.id} className="bg-white p-4 rounded-[24px] shadow-sm flex items-center justify-between border border-slate-100">
                      <div className="flex items-center gap-4">
                        <div className={`w-10 h-10 rounded-2xl flex items-center justify-center text-lg ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-800 text-[14px]">{t.category}</p>
                          <p className="text-[11px] text-slate-400 font-bold uppercase">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <p className={`font-black text-[15px] ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                        {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                      </p>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="py-16 text-center border-2 border-dashed border-slate-200 rounded-[30px]">
                   <p className="text-slate-400 font-black text-[12px]">কোনো লেনদেন নেই</p>
                </div>
              )}
            </div>
            {/* BUDGET PREVIEW */}
            <div className="space-y-4">
               <h3 className="font-black text-slate-400 text-[13px] uppercase tracking-widest px-2">বাজেট ট্র্যাকার</h3>
               <div className="space-y-3">
                {EXPENSE_CATEGORIES.slice(0, 3).map(cat => {
                  const spent = summary.categoryBreakdown[cat] || 0;
                  const budget = budgets[cat] || 0;
                  if (budget === 0) return null;
                  return (
                    <div key={cat} className="bg-white p-4 rounded-[24px] shadow-sm border border-slate-100">
                      <div className="flex justify-between items-center mb-2 text-[12px] font-black">
                        <p>{cat}</p>
                        <p>৳{spent} / ৳{budget}</p>
                      </div>
                      <div className="w-full h-1.5 bg-slate-100 rounded-full overflow-hidden">
                        <div className={`h-full bg-indigo-500`} style={{ width: `${Math.min((spent / budget) * 100, 100)}%` }}></div>
                      </div>
                    </div>
                  );
                })}
               </div>
            </div>
          </div>
        ) : activeTab === 'analytics' ? (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
             <div className="bg-white p-8 rounded-[40px] border border-slate-100 shadow-sm space-y-6">
                <h3 className="font-black text-slate-800 uppercase text-[13px] tracking-widest">সাপ্তাহিক ট্রেন্ড</h3>
                <div className="flex items-end justify-between h-32 gap-2">
                   {analyticsData.last7Days.map((d, i) => (
                      <div key={i} className="flex-1 flex flex-col items-center gap-2">
                         <div className="w-full bg-indigo-600 rounded-full" style={{ height: `${Math.max((d.amount / (Math.max(...analyticsData.last7Days.map(x => x.amount)) || 1)) * 100, 5)}%` }}></div>
                         <span className="text-[10px] font-bold text-slate-400">{d.day}</span>
                      </div>
                   ))}
                </div>
             </div>
             <div className="grid grid-cols-2 gap-4">
                <div className="bg-white p-6 rounded-[35px] border border-slate-100 shadow-sm text-center">
                   <p className="text-[10px] font-black text-slate-400 uppercase mb-2">বাজেট ব্যবহার</p>
                   <div className="text-[24px] font-black">{analyticsData.budgetUsage.toFixed(0)}%</div>
                </div>
                <div className="bg-white p-6 rounded-[35px] border border-slate-100 shadow-sm text-center">
                   <p className="text-[10px] font-black text-slate-400 uppercase mb-2">দৈনিক গড়</p>
                   <div className="text-[24px] font-black text-indigo-600">৳{analyticsData.avgDailySpend.toFixed(0)}</div>
                </div>
             </div>
             <div className="bg-slate-900 p-8 rounded-[40px] text-white shadow-2xl relative overflow-hidden">
                <h3 className="text-[13px] font-black uppercase tracking-widest text-indigo-400 mb-4">এআই পর্যবেক্ষণ</h3>
                {insights.length > 0 ? (
                  <div className="space-y-4 animate-fadeIn">
                    {insights.slice(0, 1).map((insight, idx) => (
                      <div key={idx} className="space-y-2">
                        <p className="font-black text-[18px] text-white">{insight.title}</p>
                        <p className="text-slate-400 text-[14px] font-bold">{insight.description}</p>
                      </div>
                    ))}
                  </div>
                ) : (
                  <button onClick={fetchAIAdvice} disabled={loadingAI} className="w-full py-4 bg-indigo-600 rounded-2xl font-black uppercase text-[11px] tracking-widest active:scale-95 transition-all">
                    {loadingAI ? 'বিশ্লেষণ হচ্ছে...' : 'এআই পরামর্শ পান'}
                  </button>
                )}
             </div>
          </div>
        ) : (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
             <div className="bg-white p-8 rounded-[40px] border border-slate-100 shadow-sm space-y-6">
               <h3 className="font-black text-slate-800 uppercase text-[13px] tracking-widest">বিল স্প্লিটার</h3>
               <div className="space-y-4">
                 <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ মোট বিল" className="w-full p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
                 <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} placeholder="মানুষের সংখ্যা" className="w-full p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
               </div>
               <div className="p-8 bg-indigo-600 rounded-[30px] text-center text-white">
                 <p className="text-[11px] font-bold uppercase mb-2">মাথা পিছু</p>
                 <p className="text-[32px] font-black">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
               </div>
             </div>
          </div>
        )}
      </main>

      {/* FLOATING ACTION BUTTONS */}
      <div className="fixed bottom-28 right-6 z-[100] flex flex-col items-end gap-4">
        {showAddMenu && (
          <div className="flex flex-col gap-3 mb-2 animate-slideUp items-end">
            <button onClick={() => { setModalType(TransactionType.INCOME); setShowAddMenu(false); }} className="bg-green-600 text-white px-5 py-3 rounded-[18px] font-black text-[12px] uppercase tracking-widest shadow-xl border-none active:scale-95">
              <i className="fas fa-arrow-up mr-2"></i> আয়
            </button>
            <button onClick={() => { setModalType(TransactionType.EXPENSE); setShowAddMenu(false); }} className="bg-rose-600 text-white px-5 py-3 rounded-[18px] font-black text-[12px] uppercase tracking-widest shadow-xl border-none active:scale-95">
              <i className="fas fa-arrow-down mr-2"></i> ব্যয়
            </button>
          </div>
        )}

        {/* MAIN ADD BUTTON (+) WITH ANIMATION AND COLOR */}
        <button 
            onClick={() => setShowAddMenu(!showAddMenu)} 
            className={`w-16 h-16 rounded-full flex items-center justify-center text-white text-2xl transition-all active:scale-90 border-none 
                ${showAddMenu 
                    ? 'bg-slate-800 rotate-45 shadow-xl' 
                    : 'bg-gradient-to-br from-indigo-500 via-indigo-600 to-indigo-700 shadow-[0_8px_30px_rgba(79,70,229,0.5)] btn-glow'
                }`}
        >
          <i className="fas fa-plus"></i>
        </button>
      </div>

      {/* BOTTOM NAVIGATION */}
      <div className="fixed bottom-6 left-6 right-6 z-[150]">
        <nav className="bg-white/90 backdrop-blur-xl border border-slate-200/50 px-6 py-3 flex justify-between items-center rounded-[30px] shadow-xl overflow-hidden">
          <button onClick={() => setActiveTab('home')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'home' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-house-chimney text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">হোম</span>
          </button>
          <button onClick={() => setActiveTab('analytics')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'analytics' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-chart-pie text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">বিশ্লেষণ</span>
          </button>
          <button onClick={() => setActiveTab('tools')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'tools' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-layer-group text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">টুলস</span>
          </button>
        </nav>
      </div>

      {/* TRANSACTION MODAL */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/70 backdrop-blur-sm z-[200] flex items-center justify-center p-6 animate-fadeIn">
          <div className="bg-white w-full max-w-[340px] rounded-[35px] shadow-2xl border-none animate-slideUp overflow-hidden">
            <div className="p-8 space-y-6">
              <h2 className="text-[18px] font-black text-slate-800 text-center">{modalType === TransactionType.INCOME ? 'আয় যোগ' : 'ব্যয় যোগ'}</h2>
              <form onSubmit={addTransaction} className="space-y-6">
                <input ref={amountInputRef} type="number" name="amount" required placeholder="৳ টাকার পরিমাণ" className="w-full text-[24px] font-black text-center outline-none border-none bg-slate-50 py-4 rounded-[20px]" />
                <select name="category" className="w-full py-4 px-6 bg-slate-50 rounded-[20px] font-black text-slate-800 outline-none border-none">
                  {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                </select>
                <input type="text" name="note" placeholder="নোট..." className="w-full py-4 px-6 bg-slate-50 rounded-[20px] font-black outline-none border-none" />
                <button type="submit" className={`w-full py-5 rounded-[22px] text-white font-black uppercase text-[13px] tracking-widest border-none ${modalType === TransactionType.INCOME ? 'bg-green-600' : 'bg-rose-600'}`}>সংরক্ষণ করুন</button>
                <button type="button" onClick={() => setModalType(null)} className="w-full py-2 text-slate-400 font-black uppercase text-[10px] border-none bg-transparent">বাতিল করুন</button>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* BUDGET MODAL (SIMPLIFIED) */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[300] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[40px] p-8 animate-slideUp overflow-y-auto max-h-[85vh] border-none">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-[18px] font-black uppercase">বাজেট সেটিংস</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-8 h-8 bg-slate-100 rounded-xl flex items-center justify-center border-none"><i className="fas fa-times"></i></button>
             </div>
             <div className="space-y-6 mb-8">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="flex flex-col gap-2">
                    <label className="text-[11px] font-black text-slate-500 uppercase">{cat}</label>
                    <input type="number" value={budgets[cat] || ''} onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })} placeholder="৳ মাসিক বাজেট" className="w-full p-4 bg-slate-50 rounded-[18px] font-black outline-none border-none shadow-inner" />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-5 bg-indigo-600 text-white rounded-[22px] font-black uppercase text-[12px] border-none">সেভ করুন</button>
          </div>
        </div>
      )}

      {/* HISTORY MODAL (SIMPLIFIED) */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[300] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[40px] p-8 overflow-y-auto animate-slideUp border-none">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-[18px] font-black uppercase tracking-widest">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-8 h-8 bg-slate-100 rounded-xl flex items-center justify-center border-none"><i className="fas fa-times"></i></button>
             </div>
             <p className="text-center py-24 text-slate-400 font-black uppercase text-[12px]">পুরানো রেকর্ড নেই</p>
          </div>
        </div>
      )}

    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo, useRef } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';
import TransactionList from './components/TransactionList';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'analytics' | 'tools'>('home');
  const [isShrunk, setIsShrunk] = useState(false);
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));
  const amountInputRef = useRef<HTMLInputElement>(null);

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {
      'খাবার': 5000,
      'যাতায়াত': 2000,
      'ভাড়া': 10000,
      'বিল': 3000,
      'কেনাকাটা': 4000,
      'স্বাস্থ্য': 2000,
      'অন্যান্য': 2000
    };
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showAddMenu, setShowAddMenu] = useState(false);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);

  // Tools state
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  const summary = useMemo<FinancialSummary>(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum: number, t: Transaction) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum: number, t: Transaction) => sum + t.amount, 0);
    const categoryBreakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      categoryBreakdown[t.category] = (categoryBreakdown[t.category] || 0) + t.amount;
    });
    return { 
      totalIncome: income, 
      totalExpenses: expense, 
      balance: income - expense, 
      categoryBreakdown, 
      savings: income - expense, 
      previousMonthSavings: 0, 
      monthlyHistory: [] 
    };
  }, [transactions]);

  const analyticsData = useMemo(() => {
    const sortedBreakdown = (Object.entries(summary.categoryBreakdown) as [string, number][]).sort(([, a], [, b]) => b - a).slice(0, 5);
    const now = new Date();
    const last7Days = Array.from({ length: 7 }, (_, i) => {
      const d = new Date();
      d.setDate(now.getDate() - i);
      const dateStr = d.toISOString().split('T')[0];
      const dayTotal = transactions.filter(t => t.type === TransactionType.EXPENSE && t.date.startsWith(dateStr)).reduce((sum, t) => sum + t.amount, 0);
      return { day: d.toLocaleDateString('bn-BD', { weekday: 'short' }), amount: dayTotal };
    }).reverse();
    const totalBudget = (Object.values(budgets) as number[]).reduce((sum: number, b: number) => sum + b, 0);
    const budgetUsage = totalBudget > 0 ? (summary.totalExpenses / totalBudget) * 100 : 0;
    return { sortedBreakdown, last7Days, budgetUsage, avgDailySpend: summary.totalExpenses / Math.max(now.getDate(), 1) };
  }, [summary, transactions, budgets]);

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
    setShowAddMenu(false);
  };

  const deleteTransaction = (id: string) => {
    setTransactions(prev => prev.filter(t => t.id !== id));
  };

  const fetchAIAdvice = async () => {
    if (transactions.length === 0) return;
    setLoadingAI(true);
    try {
      const results = await getFinancialInsights(transactions, summary);
      setInsights(results);
    } catch (error) {
      console.error("AI Error:", error);
    } finally {
      setLoadingAI(false);
    }
  };

  return (
    <div className="max-w-md mx-auto h-screen bg-slate-50 shadow-2xl flex flex-col font-sans relative overflow-hidden text-slate-900 border-x border-slate-100">

      {/* HEADER */}
      <header className={`header-container bg-gradient-to-br from-indigo-600 to-indigo-800 text-white relative z-[60] shrink-0 transition-all duration-300 ${isShrunk ? 'p-4 rounded-b-[20px] shadow-lg' : 'p-8 rounded-b-[40px] shadow-xl'}`}>
        <div className="flex justify-between items-center mb-4 transition-all">
          <div className="flex items-center gap-3">
             <div className={`bg-white/10 backdrop-blur-md rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/20 transition-all ${isShrunk ? 'w-10 h-10' : 'w-14 h-14'}`}>
                {profileImage ? <img src={profileImage} className="w-full h-full object-cover" /> : <i className={`fas fa-user-circle ${isShrunk ? 'text-lg' : 'text-2xl'}`}></i>}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div className={isShrunk ? 'hidden' : 'block animate-fadeIn'}>
                <h1 className="font-black uppercase tracking-tight text-white text-[24px]">স্পার্ক</h1>
                <p className="text-[12px] font-bold text-indigo-200 uppercase tracking-widest opacity-90">পার্সোনাল ফিন্যান্স</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center border-none active:scale-90 transition-all"><i className="fas fa-bullseye text-[13px]"></i></button>
            <button onClick={() => setShowHistory(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center border-none active:scale-90 transition-all"><i className="fas fa-history text-[13px]"></i></button>
          </div>
        </div>
        <div className="text-center transition-all">
            <p className={`text-indigo-100 font-bold uppercase tracking-widest ${isShrunk ? 'hidden' : 'text-[13px] mb-2 animate-fadeIn'}`}>বর্তমান ব্যালেন্স</p>
            <h2 className={`font-black tracking-tighter text-white transition-all ${isShrunk ? 'text-[28px]' : 'text-[44px]'}`}>{formatCurrency(summary.balance)}</h2>
            {!isShrunk && (
              <div className="mt-6 grid grid-cols-2 gap-4 animate-fadeIn">
                <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10 shadow-sm text-center">
                  <p className="text-[11px] uppercase font-bold text-indigo-200 mb-1 tracking-wider">মোট আয়</p>
                  <p className="text-[18px] font-black text-green-300">৳{summary.totalIncome}</p>
                </div>
                <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10 shadow-sm text-center">
                  <p className="text-[11px] uppercase font-bold text-indigo-200 mb-1 tracking-wider">মোট ব্যয়</p>
                  <p className="text-[18px] font-black text-rose-300">৳{summary.totalExpenses}</p>
                </div>
              </div>
            )}
        </div>
      </header>

      {/* MAIN CONTENT AREA */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-4 scroll-smooth" onScroll={(e) => {
        const scrollPos = e.currentTarget.scrollTop;
        setIsShrunk(scrollPos > 40);
      }}>
        {activeTab === 'home' ? (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
            <div className="space-y-4">
              <div className="flex justify-between items-center px-2">
                <h3 className="font-black text-slate-400 text-[13px] uppercase tracking-widest">সাম্প্রতিক লেনদেন</h3>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3">
                  {transactions.slice(0, 6).map(t => (
                    <div key={t.id} className="bg-white p-4 rounded-[24px] shadow-sm flex items-center justify-between border border-slate-100">
                      <div className="flex items-center gap-4">
                        <div className={`w-10 h-10 rounded-2xl flex items-center justify-center text-lg ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-800 text-[14px]">{t.category}</p>
                          <p className="text-[11px] text-slate-400 font-bold uppercase">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <p className={`font-black text-[15px] ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                        {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                      </p>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="py-16 text-center border-2 border-dashed border-slate-200 rounded-[30px]">
                   <p className="text-slate-400 font-black text-[12px]">কোনো লেনদেন নেই</p>
                </div>
              )}
            </div>
            {/* BUDGET PREVIEW */}
            <div className="space-y-4">
               <h3 className="font-black text-slate-400 text-[13px] uppercase tracking-widest px-2">বাজেট ট্র্যাকার</h3>
               <div className="space-y-3">
                {EXPENSE_CATEGORIES.slice(0, 3).map(cat => {
                  const spent = summary.categoryBreakdown[cat] || 0;
                  const budget = budgets[cat] || 0;
                  if (budget === 0) return null;
                  return (
                    <div key={cat} className="bg-white p-4 rounded-[24px] shadow-sm border border-slate-100">
                      <div className="flex justify-between items-center mb-2 text-[12px] font-black">
                        <p>{cat}</p>
                        <p>৳{spent} / ৳{budget}</p>
                      </div>
                      <div className="w-full h-1.5 bg-slate-100 rounded-full overflow-hidden">
                        <div className={`h-full bg-indigo-500`} style={{ width: `${Math.min((spent / budget) * 100, 100)}%` }}></div>
                      </div>
                    </div>
                  );
                })}
               </div>
            </div>
          </div>
        ) : activeTab === 'analytics' ? (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
             <div className="bg-white p-8 rounded-[40px] border border-slate-100 shadow-sm space-y-6">
                <h3 className="font-black text-slate-800 uppercase text-[13px] tracking-widest">সাপ্তাহিক ট্রেন্ড</h3>
                <div className="flex items-end justify-between h-32 gap-2">
                   {analyticsData.last7Days.map((d, i) => (
                      <div key={i} className="flex-1 flex flex-col items-center gap-2">
                         <div className="w-full bg-indigo-600 rounded-full" style={{ height: `${Math.max((d.amount / (Math.max(...analyticsData.last7Days.map(x => x.amount)) || 1)) * 100, 5)}%` }}></div>
                         <span className="text-[10px] font-bold text-slate-400">{d.day}</span>
                      </div>
                   ))}
                </div>
             </div>
             <div className="grid grid-cols-2 gap-4">
                <div className="bg-white p-6 rounded-[35px] border border-slate-100 shadow-sm text-center">
                   <p className="text-[10px] font-black text-slate-400 uppercase mb-2">বাজেট ব্যবহার</p>
                   <div className="text-[24px] font-black">{analyticsData.budgetUsage.toFixed(0)}%</div>
                </div>
                <div className="bg-white p-6 rounded-[35px] border border-slate-100 shadow-sm text-center">
                   <p className="text-[10px] font-black text-slate-400 uppercase mb-2">দৈনিক গড়</p>
                   <div className="text-[24px] font-black text-indigo-600">৳{analyticsData.avgDailySpend.toFixed(0)}</div>
                </div>
             </div>
             <div className="bg-slate-900 p-8 rounded-[40px] text-white shadow-2xl relative overflow-hidden">
                <h3 className="text-[13px] font-black uppercase tracking-widest text-indigo-400 mb-4">এআই পর্যবেক্ষণ</h3>
                {insights.length > 0 ? (
                  <div className="space-y-4 animate-fadeIn">
                    {insights.slice(0, 1).map((insight, idx) => (
                      <div key={idx} className="space-y-2">
                        <p className="font-black text-[18px] text-white">{insight.title}</p>
                        <p className="text-slate-400 text-[14px] font-bold">{insight.description}</p>
                      </div>
                    ))}
                  </div>
                ) : (
                  <button onClick={fetchAIAdvice} disabled={loadingAI} className="w-full py-4 bg-indigo-600 rounded-2xl font-black uppercase text-[11px] tracking-widest active:scale-95 transition-all">
                    {loadingAI ? 'বিশ্লেষণ হচ্ছে...' : 'এআই পরামর্শ পান'}
                  </button>
                )}
             </div>

             {/* Transaction History with Filters */}
             <div className="space-y-4">
                <h3 className="font-black text-slate-400 text-[13px] uppercase tracking-widest px-2">লেনদেন ইতিহাস ও ফিল্টার</h3>
                <TransactionList 
                  transactions={transactions} 
                  onDelete={deleteTransaction} 
                  title="ইতিহাস" 
                  budgets={budgets}
                  categoryBreakdown={summary.categoryBreakdown}
                />
             </div>
          </div>
        ) : (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
             <div className="bg-white p-8 rounded-[40px] border border-slate-100 shadow-sm space-y-6">
               <h3 className="font-black text-slate-800 uppercase text-[13px] tracking-widest">বিল স্প্লিটার</h3>
               <div className="space-y-4">
                 <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ মোট বিল" className="w-full p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
                 <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} placeholder="মানুষের সংখ্যা" className="w-full p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
               </div>
               <div className="p-8 bg-indigo-600 rounded-[30px] text-center text-white">
                 <p className="text-[11px] font-bold uppercase mb-2">মাথা পিছু</p>
                 <p className="text-[32px] font-black">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
               </div>
             </div>
          </div>
        )}
      </main>

      {/* FLOATING ACTION BUTTONS */}
      <div className="fixed bottom-28 right-6 z-[100] flex flex-col items-end gap-4">
        {showAddMenu && (
          <div className="flex flex-col gap-3 mb-2 animate-slideUp items-end">
            <button onClick={() => { setModalType(TransactionType.INCOME); setShowAddMenu(false); }} className="bg-green-600 text-white px-5 py-3 rounded-[18px] font-black text-[12px] uppercase tracking-widest shadow-xl border-none active:scale-95">
              <i className="fas fa-arrow-up mr-2"></i> আয়
            </button>
            <button onClick={() => { setModalType(TransactionType.EXPENSE); setShowAddMenu(false); }} className="bg-rose-600 text-white px-5 py-3 rounded-[18px] font-black text-[12px] uppercase tracking-widest shadow-xl border-none active:scale-95">
              <i className="fas fa-arrow-down mr-2"></i> ব্যয়
            </button>
          </div>
        )}

        {/* MAIN ADD BUTTON (+) WITH ANIMATION AND COLOR */}
        <button 
            onClick={() => setShowAddMenu(!showAddMenu)} 
            className={`w-16 h-16 rounded-full flex items-center justify-center text-white text-2xl transition-all active:scale-90 border-none 
                ${showAddMenu 
                    ? 'bg-slate-800 rotate-45 shadow-xl' 
                    : 'bg-gradient-to-br from-indigo-500 via-indigo-600 to-indigo-700 shadow-[0_8px_30px_rgba(79,70,229,0.5)] btn-glow'
                }`}
        >
          <i className="fas fa-plus"></i>
        </button>
      </div>

      {/* BOTTOM NAVIGATION */}
      <div className="fixed bottom-6 left-6 right-6 z-[150]">
        <nav className="bg-white/90 backdrop-blur-xl border border-slate-200/50 px-6 py-3 flex justify-between items-center rounded-[30px] shadow-xl overflow-hidden">
          <button onClick={() => setActiveTab('home')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'home' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-house-chimney text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">হোম</span>
          </button>
          <button onClick={() => setActiveTab('analytics')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'analytics' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-chart-pie text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">বিশ্লেষণ</span>
          </button>
          <button onClick={() => setActiveTab('tools')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'tools' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-layer-group text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">টুলস</span>
          </button>
        </nav>
      </div>

      {/* TRANSACTION MODAL */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/70 backdrop-blur-sm z-[200] flex items-center justify-center p-6 animate-fadeIn">
          <div className="bg-white w-full max-w-[340px] rounded-[35px] shadow-2xl border-none animate-slideUp overflow-hidden">
            <div className="p-8 space-y-6">
              <h2 className="text-[18px] font-black text-slate-800 text-center">{modalType === TransactionType.INCOME ? 'আয় যোগ' : 'ব্যয় যোগ'}</h2>
              <form onSubmit={addTransaction} className="space-y-6">
                <input ref={amountInputRef} type="number" name="amount" required placeholder="৳ টাকার পরিমাণ" className="w-full text-[24px] font-black text-center outline-none border-none bg-slate-50 py-4 rounded-[20px]" />
                <select name="category" className="w-full py-4 px-6 bg-slate-50 rounded-[20px] font-black text-slate-800 outline-none border-none">
                  {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                </select>
                <input type="text" name="note" placeholder="নোট..." className="w-full py-4 px-6 bg-slate-50 rounded-[20px] font-black outline-none border-none" />
                <button type="submit" className={`w-full py-5 rounded-[22px] text-white font-black uppercase text-[13px] tracking-widest border-none ${modalType === TransactionType.INCOME ? 'bg-green-600' : 'bg-rose-600'}`}>সংরক্ষণ করুন</button>
                <button type="button" onClick={() => setModalType(null)} className="w-full py-2 text-slate-400 font-black uppercase text-[10px] border-none bg-transparent">বাতিল করুন</button>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* BUDGET MODAL (SIMPLIFIED) */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[300] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[40px] p-8 animate-slideUp overflow-y-auto max-h-[85vh] border-none">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-[18px] font-black uppercase">বাজেট সেটিংস</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-8 h-8 bg-slate-100 rounded-xl flex items-center justify-center border-none"><i className="fas fa-times"></i></button>
             </div>
             <div className="space-y-6 mb-8">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="flex flex-col gap-2">
                    <label className="text-[11px] font-black text-slate-500 uppercase">{cat}</label>
                    <input type="number" value={budgets[cat] || ''} onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })} placeholder="৳ মাসিক বাজেট" className="w-full p-4 bg-slate-50 rounded-[18px] font-black outline-none border-none shadow-inner" />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-5 bg-indigo-600 text-white rounded-[22px] font-black uppercase text-[12px] border-none">সেভ করুন</button>
          </div>
        </div>
      )}

      {/* HISTORY MODAL (SIMPLIFIED) */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[300] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[40px] p-8 overflow-y-auto animate-slideUp border-none">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-[18px] font-black uppercase tracking-widest">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-8 h-8 bg-slate-100 rounded-xl flex items-center justify-center border-none"><i className="fas fa-times"></i></button>
             </div>
             <p className="text-center py-24 text-slate-400 font-black uppercase text-[12px]">পুরানো রেকর্ড নেই</p>
          </div>
        </div>
      )}

    </div>
  );
};

export default App;

import React, { useState, useMemo } from 'react';
import { Transaction, TransactionType } from '../types';
import { formatCurrency, CATEGORY_ICONS, INCOME_CATEGORIES, EXPENSE_CATEGORIES } from '../constants';

interface TransactionListProps {
  transactions: Transaction[];
  onDelete: (id: string) => void;
  title: string;
  budgets?: Record<string, number>;
  categoryBreakdown?: Record<string, number>;
}

const TransactionList: React.FC<TransactionListProps> = ({ transactions, onDelete, title, budgets, categoryBreakdown }) => {
  const [categoryFilter, setCategoryFilter] = useState<string>('all');
  const [startDate, setStartDate] = useState<string>('');
  const [endDate, setEndDate] = useState<string>('');

  const filteredTransactions = useMemo(() => {
    return transactions.filter(t => {
      const matchCategory = categoryFilter === 'all' || t.category === categoryFilter;
      const tDate = new Date(t.date).getTime();

      // Handle date filters precisely
      const start = startDate ? new Date(startDate).setHours(0, 0, 0, 0) : null;
      const end = endDate ? new Date(endDate).setHours(23, 59, 59, 999) : null;

      const matchStart = !start || tDate >= start;
      const matchEnd = !end || tDate <= end;

      return matchCategory && matchStart && matchEnd;
    });
  }, [transactions, categoryFilter, startDate, endDate]);

  const allCategories = useMemo(() => {
    const cats = new Set<string>();
    // Add all standard categories to the filter dropdown for better user choice
    [...INCOME_CATEGORIES, ...EXPENSE_CATEGORIES].forEach(c => cats.add(c));
    return Array.from(cats);
  }, []);

  const activeBudget = categoryFilter !== 'all' && budgets ? budgets[categoryFilter] : null;
  const activeSpent = categoryFilter !== 'all' && categoryBreakdown ? categoryBreakdown[categoryFilter] : null;

  return (
    <div className="space-y-6">
      {/* Filters Section */}
      <div className="bg-white p-6 rounded-[35px] border border-slate-100 shadow-sm space-y-4">
        <div className="grid grid-cols-1 gap-4">
          <div>
            <label className="block text-[10px] font-black text-slate-400 uppercase mb-2 ml-1 tracking-widest">ক্যাটাগরি ফিল্টার</label>
            <select 
              value={categoryFilter}
              onChange={(e) => setCategoryFilter(e.target.value)}
              className="w-full bg-slate-50 border-none rounded-[20px] px-4 py-4 text-[13px] font-black text-slate-800 outline-none appearance-none cursor-pointer shadow-inner"
            >
              <option value="all">সব ক্যাটাগরি</option>
              {allCategories.map(c => <option key={c} value={c}>{c}</option>)}
            </select>
          </div>
          <div className="grid grid-cols-2 gap-3">
            <div>
              <label className="block text-[10px] font-black text-slate-400 uppercase mb-2 ml-1 tracking-widest">শুরু</label>
              <input 
                type="date"
                value={startDate}
                onChange={(e) => setStartDate(e.target.value)}
                className="w-full bg-slate-50 border-none rounded-[20px] px-4 py-3 text-[12px] font-black text-slate-800 outline-none shadow-inner"
              />
            </div>
            <div>
              <label className="block text-[10px] font-black text-slate-400 uppercase mb-2 ml-1 tracking-widest">শেষ</label>
              <input 
                type="date"
                value={endDate}
                onChange={(e) => setEndDate(e.target.value)}
                className="w-full bg-slate-50 border-none rounded-[20px] px-4 py-3 text-[12px] font-black text-slate-800 outline-none shadow-inner"
              />
            </div>
          </div>
        </div>
        <button 
          onClick={() => { setCategoryFilter('all'); setStartDate(''); setEndDate(''); }}
          className="w-full py-3 text-[11px] font-black text-indigo-600 uppercase tracking-widest border border-indigo-100 rounded-[20px] hover:bg-indigo-50 transition-all"
        >
          ফিল্টার রিসেট
        </button>
      </div>

      {/* Budget Progress for Filtered Category */}
      {activeBudget && activeBudget > 0 && activeSpent !== null && (
        <div className="bg-white p-5 rounded-[30px] border border-slate-100 shadow-sm flex flex-col gap-3 animate-fadeIn">
          <div className="flex justify-between items-center text-[12px] font-black">
            <span className="text-slate-500 uppercase tracking-widest">{categoryFilter} বাজেট</span>
            <span className={activeSpent > activeBudget ? 'text-rose-600' : 'text-slate-800'}>
              {formatCurrency(activeSpent)} / {formatCurrency(activeBudget)}
            </span>
          </div>
          <div className="w-full h-2 bg-slate-100 rounded-full overflow-hidden">
            <div 
              className={`h-full transition-all duration-700 ${activeSpent > activeBudget ? 'bg-rose-500' : 'bg-indigo-600'}`}
              style={{ width: `${Math.min((activeSpent / activeBudget) * 100, 100)}%` }}
            ></div>
          </div>
          {activeSpent > activeBudget && (
            <p className="text-[10px] text-rose-500 font-black uppercase text-center">আপনি বাজেট অতিক্রম করেছেন!</p>
          )}
        </div>
      )}

      {/* Transactions Card List */}
      <div className="space-y-3">
        {filteredTransactions.length > 0 ? (
          filteredTransactions.map((t) => (
            <div key={t.id} className="bg-white p-5 rounded-[28px] shadow-sm flex items-center justify-between border border-slate-100 animate-slideUp group hover:border-indigo-200 transition-all">
              <div className="flex items-center gap-4">
                <div className={`w-12 h-12 rounded-2xl flex items-center justify-center text-xl ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                  <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                </div>
                <div className="flex flex-col">
                  <div className="flex items-center gap-2">
                    <span className="font-black text-slate-800 text-[14px]">{t.category}</span>
                    <span className="text-[9px] font-black text-slate-400 uppercase">{new Date(t.date).toLocaleDateString('bn-BD', { month: 'short', day: 'numeric' })}</span>
                  </div>
                  <p className="text-[11px] text-slate-500 font-bold uppercase truncate max-w-[140px]">{t.note || 'সাধারণ লেনদেন'}</p>
                </div>
              </div>
              <div className="flex items-center gap-4">
                <div className="text-right">
                  <p className={`font-black text-[15px] ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                    {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                  </p>
                </div>
                <button 
                  onClick={() => onDelete(t.id)}
                  className="w-10 h-10 rounded-full bg-slate-50 text-slate-400 hover:bg-rose-50 hover:text-rose-600 transition-all flex items-center justify-center border-none"
                >
                  <i className="fas fa-trash-alt text-[14px]"></i>
                </button>
              </div>
            </div>
          ))
        ) : (
          <div className="py-20 flex flex-col items-center justify-center bg-white rounded-[40px] border border-dashed border-slate-200 text-slate-400 gap-3">
            <div className="w-16 h-16 bg-slate-50 rounded-full flex items-center justify-center text-3xl opacity-20">
              <i className="fas fa-search"></i>
            </div>
            <p className="text-[12px] font-black uppercase tracking-widest">কোনো ফলাফল পাওয়া যায়নি</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default TransactionList;

import React, { useState, useEffect, useMemo, useRef } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';
import TransactionList from './components/TransactionList';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'analytics' | 'tools'>('home');
  const [isShrunk, setIsShrunk] = useState(false);
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));
  const amountInputRef = useRef<HTMLInputElement>(null);

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {
      'খাবার': 5000,
      'যাতায়াত': 2000,
      'ভাড়া': 10000,
      'বিল': 3000,
      'কেনাকাটা': 4000,
      'স্বাস্থ্য': 2000,
      'অন্যান্য': 2000
    };
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showAddMenu, setShowAddMenu] = useState(false);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);

  // Tools state
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('');

  // New Tools state
  const [emiAmount, setEmiAmount] = useState('');
  const [emiInterest, setEmiInterest] = useState('');
  const [emiMonths, setEmiMonths] = useState('');

  const [targetGoal, setTargetGoal] = useState('');
  const [targetTime, setTargetTime] = useState('');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  const summary = useMemo<FinancialSummary>(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum: number, t: Transaction) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum: number, t: Transaction) => sum + t.amount, 0);
    const categoryBreakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      categoryBreakdown[t.category] = (categoryBreakdown[t.category] || 0) + t.amount;
    });
    return { 
      totalIncome: income, 
      totalExpenses: expense, 
      balance: income - expense, 
      categoryBreakdown, 
      savings: income - expense, 
      previousMonthSavings: 0, 
      monthlyHistory: [] 
    };
  }, [transactions]);

  const analyticsData = useMemo(() => {
    const sortedBreakdown = (Object.entries(summary.categoryBreakdown) as [string, number][]).sort(([, a], [, b]) => b - a).slice(0, 5);
    const now = new Date();
    const last7Days = Array.from({ length: 7 }, (_, i) => {
      const d = new Date();
      d.setDate(now.getDate() - i);
      const dateStr = d.toISOString().split('T')[0];
      const dayTotal = transactions.filter(t => t.type === TransactionType.EXPENSE && t.date.startsWith(dateStr)).reduce((sum, t) => sum + t.amount, 0);
      return { day: d.toLocaleDateString('bn-BD', { weekday: 'short' }), amount: dayTotal };
    }).reverse();
    const totalBudget = (Object.values(budgets) as number[]).reduce((sum: number, b: number) => sum + b, 0);
    const budgetUsage = totalBudget > 0 ? (summary.totalExpenses / totalBudget) * 100 : 0;
    return { sortedBreakdown, last7Days, budgetUsage, avgDailySpend: summary.totalExpenses / Math.max(now.getDate(), 1) };
  }, [summary, transactions, budgets]);

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
    setShowAddMenu(false);
  };

  const deleteTransaction = (id: string) => {
    setTransactions(prev => prev.filter(t => t.id !== id));
  };

  const fetchAIAdvice = async () => {
    if (transactions.length === 0) return;
    setLoadingAI(true);
    try {
      const results = await getFinancialInsights(transactions, summary);
      setInsights(results);
    } catch (error) {
      console.error("AI Error:", error);
    } finally {
      setLoadingAI(false);
    }
  };

  // EMI Calculation Logic
  const calculateEMI = () => {
    const P = parseFloat(emiAmount);
    const r = parseFloat(emiInterest) / (12 * 100);
    const n = parseInt(emiMonths);
    if (!P || !r || !n) return 0;
    const emi = (P * r * Math.pow(1 + r, n)) / (Math.pow(1 + r, n) - 1);
    return emi.toFixed(0);
  };

  return (
    <div className="max-w-md mx-auto h-screen bg-slate-50 shadow-2xl flex flex-col font-sans relative overflow-hidden text-slate-900 border-x border-slate-100">

      {/* HEADER */}
      <header className={`header-container bg-gradient-to-br from-indigo-600 to-indigo-800 text-white relative z-[60] shrink-0 transition-all duration-300 ${isShrunk ? 'p-4 rounded-b-[20px] shadow-lg' : 'p-8 rounded-b-[40px] shadow-xl'}`}>
        <div className="flex justify-between items-center mb-4 transition-all">
          <div className="flex items-center gap-3">
             <div className={`bg-white/10 backdrop-blur-md rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/20 transition-all ${isShrunk ? 'w-10 h-10' : 'w-14 h-14'}`}>
                {profileImage ? <img src={profileImage} className="w-full h-full object-cover" /> : <i className={`fas fa-user-circle ${isShrunk ? 'text-lg' : 'text-2xl'}`}></i>}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div className={isShrunk ? 'hidden' : 'block animate-fadeIn'}>
                <h1 className="font-black uppercase tracking-tight text-white text-[24px]">স্পার্ক</h1>
                <p className="text-[12px] font-bold text-indigo-200 uppercase tracking-widest opacity-90">পার্সোনাল ফিন্যান্স</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center border-none active:scale-90 transition-all"><i className="fas fa-bullseye text-[13px]"></i></button>
            <button onClick={() => setShowHistory(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center border-none active:scale-90 transition-all"><i className="fas fa-history text-[13px]"></i></button>
          </div>
        </div>
        <div className="text-center transition-all">
            <p className={`text-indigo-100 font-bold uppercase tracking-widest ${isShrunk ? 'hidden' : 'text-[13px] mb-2 animate-fadeIn'}`}>বর্তমান ব্যালেন্স</p>
            <h2 className={`font-black tracking-tighter text-white transition-all ${isShrunk ? 'text-[28px]' : 'text-[44px]'}`}>{formatCurrency(summary.balance)}</h2>
            {!isShrunk && (
              <div className="mt-6 grid grid-cols-2 gap-4 animate-fadeIn">
                <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10 shadow-sm text-center">
                  <p className="text-[11px] uppercase font-bold text-indigo-200 mb-1 tracking-wider">মোট আয়</p>
                  <p className="text-[18px] font-black text-green-300">৳{summary.totalIncome}</p>
                </div>
                <div className="bg-white/10 backdrop-blur-md p-4 rounded-3xl border border-white/10 shadow-sm text-center">
                  <p className="text-[11px] uppercase font-bold text-indigo-200 mb-1 tracking-wider">মোট ব্যয়</p>
                  <p className="text-[18px] font-black text-rose-300">৳{summary.totalExpenses}</p>
                </div>
              </div>
            )}
        </div>
      </header>

      {/* MAIN CONTENT AREA */}
      <main className="flex-1 overflow-y-auto custom-scrollbar px-6 py-4 scroll-smooth" onScroll={(e) => {
        const scrollPos = e.currentTarget.scrollTop;
        setIsShrunk(scrollPos > 40);
      }}>
        {activeTab === 'home' ? (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
            <div className="space-y-4">
              <div className="flex justify-between items-center px-2">
                <h3 className="font-black text-slate-400 text-[13px] uppercase tracking-widest">সাম্প্রতিক লেনদেন</h3>
              </div>
              {transactions.length > 0 ? (
                <div className="space-y-3">
                  {transactions.slice(0, 6).map(t => (
                    <div key={t.id} className="bg-white p-4 rounded-[24px] shadow-sm flex items-center justify-between border border-slate-100">
                      <div className="flex items-center gap-4">
                        <div className={`w-10 h-10 rounded-2xl flex items-center justify-center text-lg ${t.type === TransactionType.INCOME ? 'bg-green-50 text-green-600' : 'bg-rose-50 text-rose-600'}`}>
                          <i className={`fas ${CATEGORY_ICONS[t.category] || 'fa-tag'}`}></i>
                        </div>
                        <div>
                          <p className="font-black text-slate-800 text-[14px]">{t.category}</p>
                          <p className="text-[11px] text-slate-400 font-bold uppercase">{t.note || 'সাধারণ'}</p>
                        </div>
                      </div>
                      <p className={`font-black text-[15px] ${t.type === TransactionType.INCOME ? 'text-green-600' : 'text-slate-800'}`}>
                        {t.type === TransactionType.INCOME ? '+' : '-'}{t.amount}
                      </p>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="py-16 text-center border-2 border-dashed border-slate-200 rounded-[30px]">
                   <p className="text-slate-400 font-black text-[12px]">কোনো লেনদেন নেই</p>
                </div>
              )}
            </div>
            {/* BUDGET PREVIEW */}
            <div className="space-y-4">
               <h3 className="font-black text-slate-400 text-[13px] uppercase tracking-widest px-2">বাজেট ট্র্যাকার</h3>
               <div className="space-y-3">
                {EXPENSE_CATEGORIES.slice(0, 3).map(cat => {
                  const spent = summary.categoryBreakdown[cat] || 0;
                  const budget = budgets[cat] || 0;
                  if (budget === 0) return null;
                  return (
                    <div key={cat} className="bg-white p-4 rounded-[24px] shadow-sm border border-slate-100">
                      <div className="flex justify-between items-center mb-2 text-[12px] font-black">
                        <p>{cat}</p>
                        <p>৳{spent} / ৳{budget}</p>
                      </div>
                      <div className="w-full h-1.5 bg-slate-100 rounded-full overflow-hidden">
                        <div className={`h-full ${spent > budget ? 'bg-rose-500' : 'bg-indigo-500'}`} style={{ width: `${Math.min((spent / budget) * 100, 100)}%` }}></div>
                      </div>
                    </div>
                  );
                })}
               </div>
            </div>
          </div>
        ) : activeTab === 'analytics' ? (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
             <div className="bg-white p-8 rounded-[40px] border border-slate-100 shadow-sm space-y-6">
                <h3 className="font-black text-slate-800 uppercase text-[13px] tracking-widest">সাপ্তাহিক ট্রেন্ড</h3>
                <div className="flex items-end justify-between h-32 gap-2">
                   {analyticsData.last7Days.map((d, i) => (
                      <div key={i} className="flex-1 flex flex-col items-center gap-2">
                         <div className="w-full bg-indigo-600 rounded-full" style={{ height: `${Math.max((d.amount / (Math.max(...analyticsData.last7Days.map(x => x.amount)) || 1)) * 100, 5)}%` }}></div>
                         <span className="text-[10px] font-bold text-slate-400">{d.day}</span>
                      </div>
                   ))}
                </div>
             </div>
             <div className="grid grid-cols-2 gap-4">
                <div className="bg-white p-6 rounded-[35px] border border-slate-100 shadow-sm text-center">
                   <p className="text-[10px] font-black text-slate-400 uppercase mb-2">বাজেট ব্যবহার</p>
                   <div className="text-[24px] font-black">{analyticsData.budgetUsage.toFixed(0)}%</div>
                </div>
                <div className="bg-white p-6 rounded-[35px] border border-slate-100 shadow-sm text-center">
                   <p className="text-[10px] font-black text-slate-400 uppercase mb-2">দৈনিক গড়</p>
                   <div className="text-[24px] font-black text-indigo-600">৳{analyticsData.avgDailySpend.toFixed(0)}</div>
                </div>
             </div>
             <div className="bg-slate-900 p-8 rounded-[40px] text-white shadow-2xl relative overflow-hidden">
                <h3 className="text-[13px] font-black uppercase tracking-widest text-indigo-400 mb-4">এআই পর্যবেক্ষণ</h3>
                {insights.length > 0 ? (
                  <div className="space-y-4 animate-fadeIn">
                    {insights.slice(0, 1).map((insight, idx) => (
                      <div key={idx} className="space-y-2">
                        <p className="font-black text-[18px] text-white">{insight.title}</p>
                        <p className="text-slate-400 text-[14px] font-bold">{insight.description}</p>
                      </div>
                    ))}
                  </div>
                ) : (
                  <button onClick={fetchAIAdvice} disabled={loadingAI} className="w-full py-4 bg-indigo-600 rounded-2xl font-black uppercase text-[11px] tracking-widest active:scale-95 transition-all">
                    {loadingAI ? 'বিশ্লেষণ হচ্ছে...' : 'এআই পরামর্শ পান'}
                  </button>
                )}
             </div>

             {/* Transaction History with Filters */}
             <div className="space-y-4">
                <h3 className="font-black text-slate-400 text-[13px] uppercase tracking-widest px-2">লেনদেন ইতিহাস ও ফিল্টার</h3>
                <TransactionList 
                  transactions={transactions} 
                  onDelete={deleteTransaction} 
                  title="ইতিহাস" 
                  budgets={budgets}
                  categoryBreakdown={summary.categoryBreakdown}
                />
             </div>
          </div>
        ) : (
          <div className="space-y-8 animate-fadeIn pb-40 pt-2">
             {/* Bill Splitter Tool */}
             <div className="bg-white p-8 rounded-[40px] border border-slate-100 shadow-sm space-y-6">
               <h3 className="font-black text-slate-800 uppercase text-[13px] tracking-widest flex items-center gap-2">
                 <i className="fas fa-divide text-indigo-500"></i> বিল স্প্লিটার
               </h3>
               <div className="space-y-4">
                 <input type="number" value={splitAmount} onChange={(e) => setSplitAmount(e.target.value)} placeholder="৳ মোট বিল" className="w-full p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
                 <input type="number" value={splitPeople} onChange={(e) => setSplitPeople(e.target.value)} placeholder="মানুষের সংখ্যা" className="w-full p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
               </div>
               <div className="p-8 bg-indigo-600 rounded-[30px] text-center text-white">
                 <p className="text-[11px] font-bold uppercase mb-2">মাথা পিছু</p>
                 <p className="text-[32px] font-black">৳ {splitAmount && splitPeople ? (parseFloat(splitAmount) / parseInt(splitPeople)).toFixed(0) : '০'}</p>
               </div>
             </div>

             {/* EMI Calculator Tool */}
             <div className="bg-white p-8 rounded-[40px] border border-slate-100 shadow-sm space-y-6">
               <h3 className="font-black text-slate-800 uppercase text-[13px] tracking-widest flex items-center gap-2">
                 <i className="fas fa-calculator text-indigo-500"></i> কিস্তি (EMI) ক্যালকুলেটর
               </h3>
               <div className="space-y-4">
                 <input type="number" value={emiAmount} onChange={(e) => setEmiAmount(e.target.value)} placeholder="৳ ঋণের পরিমাণ" className="w-full p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
                 <div className="grid grid-cols-2 gap-4">
                    <input type="number" value={emiInterest} onChange={(e) => setEmiInterest(e.target.value)} placeholder="% সুদের হার" className="p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
                    <input type="number" value={emiMonths} onChange={(e) => setEmiMonths(e.target.value)} placeholder="মাস" className="p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
                 </div>
               </div>
               <div className="p-8 bg-slate-900 rounded-[30px] text-center text-white">
                 <p className="text-[11px] font-bold uppercase mb-2">প্রতি মাসের কিস্তি</p>
                 <p className="text-[32px] font-black text-indigo-400">৳ {calculateEMI()}</p>
               </div>
             </div>

             {/* Savings Goal Tool */}
             <div className="bg-white p-8 rounded-[40px] border border-slate-100 shadow-sm space-y-6">
               <h3 className="font-black text-slate-800 uppercase text-[13px] tracking-widest flex items-center gap-2">
                 <i className="fas fa-bullseye text-indigo-500"></i> সঞ্চয় লক্ষ্যমাত্রা
               </h3>
               <div className="space-y-4">
                 <input type="number" value={targetGoal} onChange={(e) => setTargetGoal(e.target.value)} placeholder="৳ সঞ্চয়ের লক্ষ্য (মোট)" className="w-full p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
                 <input type="number" value={targetTime} onChange={(e) => setTargetTime(e.target.value)} placeholder="কত মাসে অর্জন করতে চান?" className="w-full p-5 bg-slate-50 rounded-[22px] font-black outline-none border-none shadow-inner" />
               </div>
               <div className="p-8 bg-indigo-50 border border-indigo-100 rounded-[30px] text-center">
                 <p className="text-[11px] font-black uppercase text-indigo-400 mb-2">প্রতি মাসে জমাতে হবে</p>
                 <p className="text-[32px] font-black text-indigo-600">৳ {targetGoal && targetTime ? (parseFloat(targetGoal) / parseInt(targetTime)).toFixed(0) : '০'}</p>
               </div>
             </div>
          </div>
        )}
      </main>

      {/* FLOATING ACTION BUTTONS */}
      <div className="fixed bottom-28 right-6 z-[100] flex flex-col items-end gap-4">
        {showAddMenu && (
          <div className="flex flex-col gap-3 mb-2 animate-slideUp items-end">
            <button onClick={() => { setModalType(TransactionType.INCOME); setShowAddMenu(false); }} className="bg-green-600 text-white px-5 py-3 rounded-[18px] font-black text-[12px] uppercase tracking-widest shadow-xl border-none active:scale-95">
              <i className="fas fa-arrow-up mr-2"></i> আয়
            </button>
            <button onClick={() => { setModalType(TransactionType.EXPENSE); setShowAddMenu(false); }} className="bg-rose-600 text-white px-5 py-3 rounded-[18px] font-black text-[12px] uppercase tracking-widest shadow-xl border-none active:scale-95">
              <i className="fas fa-arrow-down mr-2"></i> ব্যয়
            </button>
          </div>
        )}

        {/* MAIN ADD BUTTON (+) WITH ANIMATION AND COLOR */}
        <button 
            onClick={() => setShowAddMenu(!showAddMenu)} 
            className={`w-16 h-16 rounded-full flex items-center justify-center text-white text-2xl transition-all active:scale-90 border-none 
                ${showAddMenu 
                    ? 'bg-slate-800 rotate-45 shadow-xl' 
                    : 'bg-gradient-to-br from-indigo-500 via-indigo-600 to-indigo-700 shadow-[0_8px_30px_rgba(79,70,229,0.5)] btn-glow'
                }`}
        >
          <i className="fas fa-plus"></i>
        </button>
      </div>

      {/* BOTTOM NAVIGATION */}
      <div className="fixed bottom-6 left-6 right-6 z-[150]">
        <nav className="bg-white/90 backdrop-blur-xl border border-slate-200/50 px-6 py-3 flex justify-between items-center rounded-[30px] shadow-xl overflow-hidden">
          <button onClick={() => setActiveTab('home')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'home' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-house-chimney text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">হোম</span>
          </button>
          <button onClick={() => setActiveTab('analytics')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'analytics' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-chart-pie text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">বিশ্লেষণ</span>
          </button>
          <button onClick={() => setActiveTab('tools')} className={`flex-1 flex flex-col items-center gap-1 border-none bg-transparent ${activeTab === 'tools' ? 'text-indigo-600' : 'text-slate-400'}`}>
            <i className="fas fa-layer-group text-[18px]"></i>
            <span className="text-[9px] font-black uppercase">টুলস</span>
          </button>
        </nav>
      </div>

      {/* TRANSACTION MODAL */}
      {modalType && (
        <div className="fixed inset-0 bg-slate-900/70 backdrop-blur-sm z-[200] flex items-center justify-center p-6 animate-fadeIn">
          <div className="bg-white w-full max-w-[340px] rounded-[35px] shadow-2xl border-none animate-slideUp overflow-hidden">
            <div className="p-8 space-y-6">
              <h2 className="text-[18px] font-black text-slate-800 text-center">{modalType === TransactionType.INCOME ? 'আয় যোগ' : 'ব্যয় যোগ'}</h2>
              <form onSubmit={addTransaction} className="space-y-6">
                <input ref={amountInputRef} type="number" name="amount" required placeholder="৳ টাকার পরিমাণ" className="w-full text-[24px] font-black text-center outline-none border-none bg-slate-50 py-4 rounded-[20px]" />
                <select name="category" className="w-full py-4 px-6 bg-slate-50 rounded-[20px] font-black text-slate-800 outline-none border-none">
                  {(modalType === TransactionType.INCOME ? INCOME_CATEGORIES : EXPENSE_CATEGORIES).map(c => <option key={c} value={c}>{c}</option>)}
                </select>
                <input type="text" name="note" placeholder="নোট..." className="w-full py-4 px-6 bg-slate-50 rounded-[20px] font-black outline-none border-none" />
                <button type="submit" className={`w-full py-5 rounded-[22px] text-white font-black uppercase text-[13px] tracking-widest border-none ${modalType === TransactionType.INCOME ? 'bg-green-600' : 'bg-rose-600'}`}>সংরক্ষণ করুন</button>
                <button type="button" onClick={() => setModalType(null)} className="w-full py-2 text-slate-400 font-black uppercase text-[10px] border-none bg-transparent">বাতিল করুন</button>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* BUDGET MODAL (SIMPLIFIED) */}
      {showBudgetSettings && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[300] flex items-end justify-center">
          <div className="bg-white w-full max-w-md rounded-t-[40px] p-8 animate-slideUp overflow-y-auto max-h-[85vh] border-none">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-[18px] font-black uppercase">বাজেট সেটিংস</h2>
                <button onClick={() => setShowBudgetSettings(false)} className="w-8 h-8 bg-slate-100 rounded-xl flex items-center justify-center border-none"><i className="fas fa-times"></i></button>
             </div>
             <div className="space-y-6 mb-8">
                {EXPENSE_CATEGORIES.map(cat => (
                  <div key={cat} className="flex flex-col gap-2">
                    <label className="text-[11px] font-black text-slate-500 uppercase">{cat}</label>
                    <input type="number" value={budgets[cat] || ''} onChange={(e) => setBudgets({ ...budgets, [cat]: parseFloat(e.target.value) || 0 })} placeholder="৳ মাসিক বাজেট" className="w-full p-4 bg-slate-50 rounded-[18px] font-black outline-none border-none shadow-inner" />
                  </div>
                ))}
             </div>
             <button onClick={() => setShowBudgetSettings(false)} className="w-full py-5 bg-indigo-600 text-white rounded-[22px] font-black uppercase text-[12px] border-none">সেভ করুন</button>
          </div>
        </div>
      )}

      {/* HISTORY MODAL (SIMPLIFIED) */}
      {showHistory && (
        <div className="fixed inset-0 bg-slate-900/80 backdrop-blur-xl z-[300] flex flex-col">
          <div className="bg-white flex-1 mt-20 rounded-t-[40px] p-8 overflow-y-auto animate-slideUp border-none">
             <div className="flex justify-between items-center mb-8">
                <h2 className="text-[18px] font-black uppercase tracking-widest">ইতিহাস</h2>
                <button onClick={() => setShowHistory(false)} className="w-8 h-8 bg-slate-100 rounded-xl flex items-center justify-center border-none"><i className="fas fa-times"></i></button>
             </div>
             <p className="text-center py-24 text-slate-400 font-black uppercase text-[12px]">পুরানো রেকর্ড নেই</p>
          </div>
        </div>
      )}

    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo, useRef } from 'react';
import { Transaction, TransactionType, MonthlyArchive, Insight, FinancialSummary } from './types';
import { INCOME_CATEGORIES, EXPENSE_CATEGORIES, CATEGORY_ICONS, formatCurrency } from './constants';
import { getFinancialInsights } from './services/geminiService';
import TransactionList from './components/TransactionList';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'analytics' | 'tools'>('home');
  const [isShrunk, setIsShrunk] = useState(false);
  const [profileImage, setProfileImage] = useState<string | null>(() => localStorage.getItem('spark_profile'));
  const amountInputRef = useRef<HTMLInputElement>(null);

  const [transactions, setTransactions] = useState<Transaction[]>(() => {
    const saved = localStorage.getItem('spark_tx');
    return saved ? JSON.parse(saved) : [];
  });

  const [budgets, setBudgets] = useState<Record<string, number>>(() => {
    const saved = localStorage.getItem('spark_budgets');
    return saved ? JSON.parse(saved) : {
      'খাবার': 5000,
      'যাতায়াত': 2000,
      'ভাড়া': 10000,
      'বিল': 3000,
      'কেনাকাটা': 4000,
      'স্বাস্থ্য': 2000,
      'অন্যান্য': 2000
    };
  });

  const [archives, setArchives] = useState<MonthlyArchive[]>(() => {
    const saved = localStorage.getItem('spark_archives');
    return saved ? JSON.parse(saved) : [];
  });

  const [insights, setInsights] = useState<Insight[]>([]);
  const [loadingAI, setLoadingAI] = useState(false);
  const [modalType, setModalType] = useState<TransactionType | null>(null);
  const [showAddMenu, setShowAddMenu] = useState(false);
  const [showHistory, setShowHistory] = useState(false);
  const [showBudgetSettings, setShowBudgetSettings] = useState(false);

  // Tools state
  const [splitAmount, setSplitAmount] = useState('');
  const [splitPeople, setSplitPeople] = useState('');

  // New Tools state
  const [emiAmount, setEmiAmount] = useState('');
  const [emiInterest, setEmiInterest] = useState('');
  const [emiMonths, setEmiMonths] = useState('');

  const [targetGoal, setTargetGoal] = useState('');
  const [targetTime, setTargetTime] = useState('');

  useEffect(() => {
    localStorage.setItem('spark_tx', JSON.stringify(transactions));
    localStorage.setItem('spark_archives', JSON.stringify(archives));
    localStorage.setItem('spark_budgets', JSON.stringify(budgets));
    if (profileImage) localStorage.setItem('spark_profile', profileImage);
  }, [transactions, archives, budgets, profileImage]);

  const summary = useMemo<FinancialSummary>(() => {
    const income = transactions.filter(t => t.type === TransactionType.INCOME).reduce((sum: number, t: Transaction) => sum + t.amount, 0);
    const expense = transactions.filter(t => t.type === TransactionType.EXPENSE).reduce((sum: number, t: Transaction) => sum + t.amount, 0);
    const categoryBreakdown: Record<string, number> = {};
    transactions.filter(t => t.type === TransactionType.EXPENSE).forEach(t => {
      categoryBreakdown[t.category] = (categoryBreakdown[t.category] || 0) + t.amount;
    });
    return { 
      totalIncome: income, 
      totalExpenses: expense, 
      balance: income - expense, 
      categoryBreakdown, 
      savings: income - expense, 
      previousMonthSavings: 0, 
      monthlyHistory: [] 
    };
  }, [transactions]);

  const analyticsData = useMemo(() => {
    const sortedBreakdown = (Object.entries(summary.categoryBreakdown) as [string, number][]).sort(([, a], [, b]) => b - a).slice(0, 5);
    const now = new Date();
    const last7Days = Array.from({ length: 7 }, (_, i) => {
      const d = new Date();
      d.setDate(now.getDate() - i);
      const dateStr = d.toISOString().split('T')[0];
      const dayTotal = transactions.filter(t => t.type === TransactionType.EXPENSE && t.date.startsWith(dateStr)).reduce((sum, t) => sum + t.amount, 0);
      return { day: d.toLocaleDateString('bn-BD', { weekday: 'short' }), amount: dayTotal };
    }).reverse();
    const totalBudget = (Object.values(budgets) as number[]).reduce((sum: number, b: number) => sum + b, 0);
    const budgetUsage = totalBudget > 0 ? (summary.totalExpenses / totalBudget) * 100 : 0;
    return { sortedBreakdown, last7Days, budgetUsage, avgDailySpend: summary.totalExpenses / Math.max(now.getDate(), 1) };
  }, [summary, transactions, budgets]);

  const addTransaction = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const amount = parseFloat(formData.get('amount') as string);
    if (!amount || amount <= 0) return;

    const newTx: Transaction = {
      id: `tx_${Date.now()}`,
      type: modalType!,
      amount,
      category: formData.get('category') as string,
      note: (formData.get('note') as string) || '',
      date: new Date().toISOString()
    };
    setTransactions(prev => [newTx, ...prev]);
    setModalType(null);
    setShowAddMenu(false);
  };

  const deleteTransaction = (id: string) => {
    setTransactions(prev => prev.filter(t => t.id !== id));
  };

  const clearAllTransactions = () => {
    if (window.confirm('আপনি কি নিশ্চিত যে আপনি সমস্ত লেনদেন মুছে ফেলে ব্যালেন্স ০ করতে চান?')) {
      setTransactions([]);
      setShowHistory(false);
    }
  };

  const fetchAIAdvice = async () => {
    if (transactions.length === 0) return;
    setLoadingAI(true);
    try {
      const results = await getFinancialInsights(transactions, summary);
      setInsights(results);
    } catch (error) {
      console.error("AI Error:", error);
    } finally {
      setLoadingAI(false);
    }
  };

  // EMI Calculation Logic
  const calculateEMI = () => {
    const P = parseFloat(emiAmount);
    const r = parseFloat(emiInterest) / (12 * 100);
    const n = parseInt(emiMonths);
    if (!P || !r || !n) return 0;
    const emi = (P * r * Math.pow(1 + r, n)) / (Math.pow(1 + r, n) - 1);
    return emi.toFixed(0);
  };

  return (
    <div className="max-w-md mx-auto h-screen bg-slate-50 shadow-2xl flex flex-col font-sans relative overflow-hidden text-slate-900 border-x border-slate-100">

      {/* HEADER */}
      <header className={`header-container bg-gradient-to-br from-indigo-600 to-indigo-800 text-white relative z-[60] shrink-0 transition-all duration-300 ${isShrunk ? 'p-4 rounded-b-[20px] shadow-lg' : 'p-8 rounded-b-[40px] shadow-xl'}`}>
        <div className="flex justify-between items-center mb-4 transition-all">
          <div className="flex items-center gap-3">
             <div className={`bg-white/10 backdrop-blur-md rounded-2xl flex items-center justify-center relative overflow-hidden border border-white/20 transition-all ${isShrunk ? 'w-10 h-10' : 'w-14 h-14'}`}>
                {profileImage ? <img src={profileImage} className="w-full h-full object-cover" /> : <i className={`fas fa-user-circle ${isShrunk ? 'text-lg' : 'text-2xl'}`}></i>}
                <input type="file" accept="image/*" onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => setProfileImage(reader.result as string);
                    reader.readAsDataURL(file);
                  }
                }} className="absolute inset-0 opacity-0 cursor-pointer" />
             </div>
             <div className={isShrunk ? 'hidden' : 'block animate-fadeIn'}>
                <h1 className="font-black uppercase tracking-tight text-white text-[24px]">স্পার্ক</h1>
                <p className="text-[12px] font-bold text-indigo-200 uppercase tracking-widest opacity-90">পার্সোনাল ফিন্যান্স</p>
             </div>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setShowBudgetSettings(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center border-none active:scale-90 transition-all"><i className="fas fa-bullseye text-[13px]"></i></button>
            <button onClick={() => setShowHistory(true)} className="w-10 h-10 bg-white/10 rounded-xl flex items-center justify-center border-none active:scale-90 transition-all"><i className="fas fa-history text-[13px]"></i></button>
          </div>
        </div>
        <div className="text-center transition-all">
